INSERT INTO `articles` (`id`, `category_id`, `top_image`, `title`, `article_slug`, `first_line`, `body`, `audio`, `added_on`, `modified_on`, `views`, `sc_track_id`) VALUES
(1, 1, 'images/aboltabol.png', 'আবোল তাবোল', 'আবোল-তাবোল', 'আয়রে ভোলা খেয়াল-খোলা', 'আয়রে ভোলা খেয়াল-খোলা<br>\n    &nbsp;&nbsp;&nbsp; স্বপনদোলা নাচিয়ে আয়<br>\n    আয়রে পাগল আবোল তাবোল<br>\n    &nbsp;&nbsp;&nbsp; মত্ত মাদল বাজিয়ে আয়।<br>\n    আয় যেখানে খ্যাপার গানে<br>\n    &nbsp;&nbsp;&nbsp; নাইকো মানে নাইকো সুর।<br>\n    আয়রে যেথায় উধাও হাওয়ায়<br>\n    &nbsp;&nbsp;&nbsp; মন ভেসে যায় কোন্‌ সুদূর।<br>\n    আয় খ্যাপা-মন ঘুচিয়ে বাঁধন<br>\n    &nbsp;&nbsp;&nbsp; জাগিয়ে নাচন তাধিন্‌ ধিন্‌,<br>\n    আয় বেয়াড়া সৃষ্টিছাড়া<br>\n    &nbsp;&nbsp;&nbsp; নিয়মহারা হিসাবহীন।<br>\n    আজ্‌গুবি চাল্‌ বেঠিক বেতাল<br>\n    &nbsp;&nbsp;&nbsp; মাতবি মাতাল রঙ্গেতে-<br>\n    আয়রে তবে ভুলের ভবে<br>\n    &nbsp;&nbsp;&nbsp; অসম্ভবের ছন্দেতে।<br>', 'DmFFzSiy_sY', '2013-03-12 00:00:00', '2014-08-21 16:53:01', 1032, ''),
(2, 1, '', 'খিচুড়ি', 'খিচুড়ি', 'হাঁস ছিল, সজারু, (ব্যাকরণ মানি না)', '<table border="0">\r\n  <tr>\r\n    	<td style=''text-align: right;''>\r\n						<img src="images/khichuri_1.gif" width="128" height="422">\r\n				</td>\r\n    <td>\r\nহাঁস ছিল, সজারু, (ব্যাকরণ মানি না)<br>\r\nহয়ে গেল "হাঁসজারু" কেমনে তা জানি না।<br>\r\nবক কহে কচ্ছপে - "বাহবা কি ফুর্তি !<br>\r\nঅতি খাসা আমাদের বকচ্ছপ মূর্তি ।"<br>\r\nটিয়ামুখো গিরগিটি মনে ভারি শঙ্কা-<br>\r\nপোকা ছেড়ে শেষে কিগো খাবে কাঁচা লঙ্কা ?<br>\r\nছাগলের পেটে ছিল না জানি কি ফন্দি,<br>\r\nচাপিল বিছার ঘাড়ে, ধড়ে মুড়ো সন্ধি !<br>\r\nজিরাফের সাধ নাই মাঠে ঘাটে ঘুরিতে,<br>\r\nফড়িঙের ঢং ধরি'' সেও চায় উড়িতে।<br>\r\nগরু বলে "আমারেও ধরিল কি ও রোগে?<br>\r\nমোর পিছে লাগে কেন হতভাগা মোরগে?"<br>\r\n<img src="images/khichuri_2.gif" width="241" height="70"><br>\r\nহাতিমির দশা দেখ, - তিমি ভাবে জলে যাই,<br>\r\nহাতি বলে, "এই বেলা জঙ্গলে চল ভাই।"<br>\r\nসিংহের শিং নেই, এই তার কষ্ট-<br>\r\nহরিণের সাথে মিলে শিং হল পষ্ট।\r\n	</td>\r\n 	<td><img src="images/khichuri_3.gif" width="166" height="303"></td>\r\n</tr>\r\n</table>', 'eM_plWaBVNg', '2013-03-14 00:00:00', '2014-08-21 16:53:01', 1067, '112400718'),
(3, 1, 'images/kathburo.gif', 'কাঠবুড়ো', 'কাঠবুড়ো', 'হাঁড়ি নিয়ে দাড়িমুখো কে যেন কে বৃদ্ধ', 'হাঁড়ি নিয়ে দাড়িমুখো কে যেন কে বৃদ্ধ,<br>\r\nরোদে বসে চেটে খায় ভিজে কাঠ সিদ্ধ ।<br>\r\nমাথা নেড়ে গান করে গুন্‌ গুন্‌ সংগীত-<br>\r\nভাব দেখে মনে হয় না জানি কি পণ্ডিত!<br>\r\n<br>\r\nবিড়্‌ বিড়্‌ কি যে বকে নাহি তার অর্থ-<br>\r\n"আকাশেতে ঝুল ঝোলে, কাঠে তাই গর্ত ।"<br>\r\nটেকো মাথা তেতে ওঠে গায়ে ছোটে ঘর্ম,<br>\r\nরেগে বলে, "কেবা বোঝে এ সবের মর্ম?"<br>\r\n<br>\r\nআরে মোলো, গাধাগুলো একেবারে অন্ধ,<br>\r\nবোঝেনাকো কোনো কিছু খালি করে দ্বন্দ্ব ।<br>\r\nকোন্‌ কাঠে কত রস জানে নাকো তত্ত্ব-<br>\r\nএকাদশী রাতে কেন কাঠে হয় গর্ত?"<br>\r\n<br>\r\nআশে পাশে হিজি বিজি আঁকে কত অঙ্ক-<br>\r\nফাটা কাঠ ফুটো কাঠ হিসাব অসংখ্য ;<br>\r\nকোন্‌ ফুটো খেতে ভাল, কোন্‌টা বা মন্দ,<br>\r\nকোন্‌ কোন্‌ ফাটলের কি রকম গন্ধ ।<br>\r\n<br>\r\nকাঠে কাঠে ঠুকে করে ঠকাঠক্‌ শব্দ,<br>\r\nবলে, "জানি কোন্‌ কাঠ কিসে হয় জব্দ ।<br>\r\nকাঠকুটো ঘেঁটেঘুঁটে জানি আমি পষ্ট,<br>\r\nএ কাঠের বজ্জাতি কিসে হয় নষ্ট ।<br>\r\n<br>\r\nকোন্‌ কাঠ পোষ মানে, কোন্‌ কাঠ শান্ত,<br>\r\nকোন্‌ কাঠ টিম্‌টিমে, কোন্‌টা বা জ্যান্ত ।<br>\r\nকোন্‌ কাঠে জ্ঞান নাই মিথ্যা কি সত্য,<br>\r\nআমি জানি কোন্‌ কাঠে কেন থাকে গর্ত ।"', 'fJ5eP0rhkl8', '2013-03-14 00:00:00', '2014-08-21 16:53:01', 725, '112400165'),
(4, 1, '', 'ভালরে ভাল', 'ভালরে-ভাল', 'দাদা গো ! দেখ্‌‌ছি ভেবে অনেক দূর', 'দাদা গো ! দেখ্‌‌ছি ভেবে অনেক দূর-<br>\r\nএই দুনিয়ার সকল ভাল,<br>\r\nআসল ভাল নকল ভাল,<br>\r\nসস্তা ভাল দামীও ভাল,<br>\r\nতুমিও ভাল আমিও ভাল,<br>\r\nহেথায় গানের ছন্দ ভাল,<br>\r\nহেথায় ফুলের গন্ধ ভাল,<br>\r\nমেঘ-মাখানো আকাশ ভাল,<br>\r\nঢেউ-জাগানো বাতাস ভাল,<br>\r\nগ্রীষ্ম ভাল বর্ষা ভাল,<br>\r\nময়লা ভাল ফরসা ভাল,<br>\r\nপোলাও ভাল কোর্মা ভাল,<br>\r\nমাছপটোলের দোলমা ভাল,<br>\r\nকাঁচাও ভাল পাকাও ভাল,<br>\r\nসোজাও ভাল বাঁকাও ভাল,<br>\r\nকাঁসিও ভাল ঢাক্‌ও ভাল,<br>\r\nটিকিও ভাল টাকও ভাল,<br>\r\nঠেলার গাড়ী ঠেল্‌‌তে ভাল,<br>\r\nখাস্তা লুচি বেলতে ভাল,<br>\r\nগিট্‌‌কিরি গান শুনতে ভাল,<br>\r\nশিমুল তুলো ধুন্‌‌তে ভাল,<br>\r\nঠাণ্ডা জলে নাইতে ভাল,<br>\r\nকিন্তু সবার চাইতে ভাল-<br>\r\n-পাউরুটি আর ঝোলা গুড় ।<br>', '', '2013-03-14 00:00:00', '2014-08-21 16:53:01', 566, ''),
(5, 1, '', 'সৎপাত্র', 'সৎপাত্র', 'শুনতে পেলুম পোস্তা গিয়ে-', 'শুনতে পেলুম পোস্তা গিয়ে-<br>\r\nতোমার নাকি মেয়ের বিয়ে ?<br>\r\nগঙ্গারামকে পাত্র পেলে ?<br>\r\nজান্‌‌তে চাও সে কেমন ছেলে ?<br>\r\nমন্দ নয় সে পাত্র ভাল-<br>\r\nরঙ যদিও বেজায় কালো ;<br>\r\nতার উপরে মুখের গঠন<br>\r\nঅনেকটা ঠিক প্যাঁচার মতন ।<br>\r\nবিদ্যে বুদ্ধি ? বলছি মশাই-<br>\r\nধন্যি ছেলের অধ্যবসায় !<br>\r\nউনিশটি বার ম্যাট্রিকে সে<br>\r\nঘায়েল হয়ে থাম্‌ল শেষে ।<br>\r\nবিষয় আশয় ? গরীব বেজায়-<br>\r\nকষ্টে-সৃষ্টে দিন চলে যায় ।<br>\r\nমানুষ ত নয় ভাই গুলো তার-<br>\r\nএকটা পাগল একটা গোঁয়ার ;<br>\r\nআরেকটি সে তৈরি ছেলে,<br>\r\nজাল ক''রে নোট্‌ গেছেন জেলে ।<br>\r\nকনিষ্ঠটি তব্‌লা বাজায়<br>\r\nযাত্রাদলে পাঁচ টাকা পায় ।<br>\r\nগঙ্গারাম ত কেবল ভোগে<br>\r\nপিলের জ্বর আর পাণ্ডু রোগে ।<br>\r\nকিন্তু তারা উচচ ঘর <br>\r\nকংসরাজের বংশধর !<br>\r\nশ্যাম লাহিড়ী বনগ্রামের <br>\r\nকি যেন হয় গঙ্গারামের ।-<br>\r\nযাহোক্‌, এবার পাত্র পেলে,<br>\r\nএমন কি আর মন্দ ছেলে ?', 'MJxQluMEa0k', '2013-03-15 00:00:00', '2014-08-21 16:53:01', 932, '112400184'),
(6, 1, '', 'কুমড়ো পটাশ', 'কুমড়ো-পটাশ', 'যদি কুম্‌‌ড়োপটাশ নাচে', '<table border="0">\r\n  <tr> \r\n    <td rowspan="2" valign="top">\r\n     (যদি) কুম্‌‌ড়োপটাশ নাচে-<br>\r\nখবরদার এসো না কেউ আস্তাবলের কাছে ;<br>\r\nচাইবে নাকো ডাইনে বাঁয়ে চাইবে নাকো পাছে ;<br>\r\nচার পা তুলে থাকবে ঝুলে হট্টমুলার গাছে !<br>\r\n<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(যদি) কুম্‌‌ড়োপটাশ কাঁদে-<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;খবরদার! খবরদার! বসবে না কেউ ছাদে ;<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;উপুড় হয়ে মাচায় শুয়ে লেপ কম্বল কাঁধে ;<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;বেহাগ সুরে গাইবে খালি ''রাধে কৃষ্ণ রাধে'' !<br>\r\n<br>\r\n(যদি) কুম্‌‌ড়োপটাশ হাসে-<br>\r\nথাকবে খাড়া একটি ঠ্যাঙে রান্নাঘরের পাশে ;<br>\r\nঝাপ্‌সা গলায় ফার্সি কবে নিশ্বাসে ফিস্‌ফাসে ;<br>\r\nতিনটি বেলা উপোস করে থাকবে শুয়ে ঘাসে !<br>\r\n<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(যদি) কুম্‌‌ড়োপটাশ ছোটে-<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;সবাই যেন তড়বড়িয়ে জানলা বেয়ে ওঠে ;<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;হুঁকোর জলে আলতা গুলে লাগায় গালে ঠোঁটে ;<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ভুলেও যেন আকাশ পানে তাকায় নাকো মোটে !<br></p></p></td>\r\n    <td><img src="images/kumropatash.gif" width="386" height="252"></td>\r\n  </tr>\r\n  <tr> \r\n    <td valign="top">\r\n(যদি) কুম্‌‌ড়োপটাশ ডাকে-<br>\r\nসবাই যেন শাম্‌লা এঁটে গামলা চড়ে থাকে ;<br>\r\nছেঁচকি শাকের ঘণ্ট বেটে মাথায় মলম মাখে ;<br>\r\nশক্ত ইঁটের তপ্ত ঝামা ঘষতে থাকে নাকে !<br>\r\n<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;তুচ্ছ ভেবে এ-সব কথা করছে যারা হেলা,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কুম্‌‌ড়োপটাশ জানতে পেলে বুঝবে তখন ঠেলা ।<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;দেখবে তখন কোন্‌ কথাটি কেমন করে ফলে,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;আমায় তখন দোষ দিওনা, আগেই রাখি বলে ।<br>\r\n</td>\r\n</tr>\r\n</table>', 'Q-TNCF0qORA', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 930, '112400321'),
(7, 1, '', 'বুড়ীর বাড়ী', 'বুড়ীর-বাড়ী', 'গালভরা হাসিমুখে চালভাজা মুড়ি', '<table border="0">\r\n<tr>\r\n<td>\r\nগালভরা হাসিমুখে চালভাজা মুড়ি,<br>\r\nঝুরঝুরে প''ড়ো ঘরে থুরথুরে বুড়ী ।<br>\r\nকাঁথাভরা ঝুলকালি, মাথাভরা ধুলো,<br>\r\nমিট্‌‌মিটে ঘোলা চোখ, পিঠখানা কুলো ।<br>\r\nকাঁটা দিয়ে আঁটা ঘর - আঁঠা দিয়ে সেঁটে,<br>\r\nসুতো দিয়ে বেঁধে রাখে থুতু দিয়ে চেটে ।<br>\r\nভর দিতে ভয় হয় ঘর বুঝি পড়ে,<br>\r\nখক্‌ খক্‌ কাশি দিলে ঠক্‌ ঠক্‌ নড়ে ।<br>\r\nডাকে যদি ফিরিওলা হাঁকে যদি গাড়ি,<br>\r\nখসে পড়ে কড়িকাঠ ধসে পড়ে বাড়ী ।<br>\r\nবাঁকাচোরা ঘরদোর ফাঁকা ফাঁকা কত,<br>\r\nঝাঁট, দিলে ঝ''রে পড়ে কাঠকুটো যত ।<br>\r\nছাদগুলো ঝুলে পড়ে বাদ্‌লায় ভিজে,<br>\r\nএকা বুড়ী কাঠি গুজে ঠেকা দেয় নিজে ।<br>\r\nমেরামত দিনরাত কেরামত ভারি,<br>\r\nথুরথুরে বুড়ী তার ঝুরঝুরে বাড়ী ।\r\n</td>\r\n<td align="left" valign="top"><img src="images/burirbari.gif" width="212" height="361"></td>\r\n</tr>\r\n</table>', 'Qi3-ktM5UJ0', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 372, '112400804'),
(8, 1, '', 'বাবুরাম সাপুড়ে', 'বাবুরাম-সাপুড়ে', 'বাবুরাম সাপুড়ে', '<table border="0">\r\n  <tr>\r\n    <td>\r\n       বাবুরাম সাপুড়ে,<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কোথা যাস্‌ বাপুরে?<br>\r\n      আয় বাবা দেখে যা,<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;দুটো সাপ রেখে যা !<br>\r\n      যে সাপের চোখ নেই,<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;শিং নেই নোখ্‌ নেই,<br>\r\n      ছোটে না কি হাঁটে না,<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কাউকে যে কাটে না,<br>\r\n      করে নাকো ফোঁস ফাঁস,<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;মারে নাকো ঢুঁশঢাঁশ,<br>\r\n      নেই কোন উৎপাত,<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;খায় শুধু দুধ ভাত-<br>\r\n      সেই সাপ জ্যান্ত<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;গোটা দুই আনত ?<br>\r\n      তেড়ে মেরে ডাণ্ডা<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ক''রে দেই ঠাণ্ডা ।<br>\r\n      </font></td>\r\n  </tr>\r\n</table>', '5rJsMFsk-wg', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 1098, '112400082'),
(9, 1, '', 'শব্দ কল্প দ্রুম্‌', 'শব্দ-কল্প-দ্রুম্‌', 'ঠাস্‌ ঠাস্‌ দ্রুম্‌ দ্রাম্‌, শুনে লাগে খট্‌কা', 'ঠাস্‌ ঠাস্‌ দ্রুম্‌ দ্রাম্‌, শুনে লাগে খট্‌কা-<br>\r\nফুল ফোটে? তাই বল! আমি ভাবি পট্‌কা !<br>\r\nশাই শাই পন্‌পন্‌, ভয়ে কান বন্ধ-<br>\r\nওই বুঝি ছুটে যায় সে-ফুলের গন্ধ ?<br>\r\nহুড়মুড় ধুপ্‌ধাপ্‌- ওকি শুনি ভাই রে?<br>\r\nদেখ্‌ছ না হিম পড়ে- যেওনাকো বাইরে ।<br>\r\nচুপ্‌ চুপ্‌ ঐ শোন্‌ ! ঝুপ্‌ঝাপ্‌ ঝ-পাস্‌ !<br>\r\nচাঁদ বুঝি ডুবে গেল ?- গব্‌ গব্‌ গবা-স্‌ !<br>\r\nখ্যাশ্‌ খ্যাশ্‌ ঘ্যাচ্‌ ঘ্যাচ্‌, রাত কাটে ঐরে !<br>\r\nদুড়্‌ দুড়্‌ চুরমার- ঘুম ভাঙে কই রে !<br>\r\nঘর্ঘর ভন্‌ভন্‌ ঘোরে কত চিন্তা !<br>\r\nকত মন নাচে শোন্‌ - ধেই ধেই ধিন্‌তা !<br>\r\nঠুংঠাং ঢংঢং, কত ব্যথা বাজে রে-<br>\r\nফট্‌ফট্‌ বুক ফাটে তাই মাঝে মাঝে রে !<br>\r\nহৈ হৈ মার মার , "বাপ্‌ বাপ্‌" চিৎকার-<br>\r\nমালকোঁচা মারে বুঝি? সরে পড়্‌ এইবার ।', 'gTbYaBl6lnE', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 494, '112400341'),
(10, 1, '', 'গোঁফচুরি', 'গোঁফচুরি', 'হেড আফিসের বড় বাবু লোকটি বড়ই শান্ত', '<table border="0">\r\n  <tr>\r\n    <td>হেড আফিসের বড় বাবু লোকটি বড়ই শান্ত,<br>\r\nতার যে এমন মাথার ব্যামো কেউ কখনও জান্‌ত ?<br>\r\nদিব্যি ছিলেন খোস্‌‌মেজাজে চেয়ারখানি চেপে,<br>\r\nএকলা ব''সে ঝিমঝিমিয়ে হঠাৎ গেলেন ক্ষেপে !<br>\r\nআঁৎকে উঠে হাত পা ছুঁড়ে চোখটি ক''রে গোল<br>\r\nহঠাৎ বলেন, "গেলুম গেলুম, আমায় ধ''রে তোল" ।<br>\r\nতাই শুনে কেউ বদ্যি ডাকে, কেউ বা হাঁকে পুলিশ,<br>\r\nকেউবা বলে, "কামড়ে দেবে সাবধানেতে তুলিস্‌।"<br>\r\nব্যস্ত সবাই এদিক ওদিক কর্‌ছে ঘোরাঘুরি-<br>\r\nবাবু হাঁকেন, "ওরে আমার গোঁফ গিয়েছে চুরি" !<br>\r\nগোঁফ হারান ! আজব কথা ! তাও কি হয় সত্যি ? <br>\r\nগোঁফ জোড়া ত তেমনি আছে, কমেনি এক রত্তি ।<br>\r\nসবাই তারে বুঝিয়ে বলে, সাম্‌‌নে ধরে আয়না,<br>\r\nমোটেও গোঁফ হয়নি চুরি, কক্ষনো তা হয় না ।<br>\r\nরেগে আগুন তেলে বেগুন, তেড়ে বলেন তিনি,<br>\r\n"কারো কথার ধার ধারিনে, সব ব্যাটাকেই চিনি ।<br>\r\n"নোংরা ছাঁটা খ্যাংরা ঝাঁটা বিচ্ছিরি আর ময়না,<br>\r\nএমন গোফ ত রাখত জানি শ্যামবাবুদের গয়লা।<br>\r\n"এ গোঁফ যদি আমার বলিস করব তোদের জবাই"-<br>\r\nএই না ব''লে জরিমানা কল্লেন তিনি সবায় ।<br>\r\nভীষণ রেগে বিষম খেয়ে দিলেন লিখে খাতায়-<br>\r\n"কাউকে বেশি লাই দিতে নেই, সবাই চড়ে মাথায় ।<br>\r\n"আফিসের এই বাঁদরগুলো, মাথায় খালি গোবর<br>\r\n"গোঁফ জোড়া যে কোথায় গেল কেউ রাখে না খবর ।<br>\r\n"ইচ্ছে করে এই ব্যাটাদের গোঁফ ধ''রে খুব নাচি,<br>\r\n"মুখ্যুগুলোর মুণ্ডু ধ''রে কোদাল দিয়ে চাঁচি ।<br>\r\n"গোঁফকে বলে তোমার আমার- গোঁফ কি কারও কেনা ?<br>\r\n"গোঁফের আমি গোঁফের তুমি, তাই দিয়ে যায় চেনা ।"\r\n</td>\r\n    <td align="left" valign="middle"> \r\n      <div align="left"><img src="images/gofchuri.gif" width="218" height="266"></div></td>\r\n  </tr>\r\n</table>', 'WyOCBQtTsLk', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 1193, '112400133'),
(11, 1, '', 'প্যাঁচা আর প্যাঁচানি', 'প্যাঁচা-আর-প্যাঁচানি', 'প্যাঁচা কয় প্যাঁচানি', '<table border="0">\r\n  <tr>\r\n    <td valign="top">      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;প্যাঁচা \r\n      কয় প্যাঁচানি,<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;খাসা \r\n      তোর চ্যাঁচানি !<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;শুনে \r\n      শুনে আন্‌মন<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;নাচে \r\n      মোর প্রাণমন !<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;মাজা-গলা \r\n      চাঁচা সুর<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;আহ্লাদে \r\n      ভরপুর !<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;গলা \r\n      চেরা গমকে<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;গাছ \r\n      পালা চমকে,<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;সুরে \r\n      সুরে কত প্যাঁচ<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;গিট্‌কিরি \r\n      ক্যাঁচ্‌ ক্যাঁচ্‌ !<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;যত \r\n      ভয় যত দুখ<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;দুরু \r\n      দুরু ধুক্‌ ধুক্‌,<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;তোর \r\n      গানে পেঁচি রে<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;সব \r\n      ভুলে গেছি রে-<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;চাঁদ \r\n      মুখে মিঠে গান<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;শুনে \r\n      ঝরে দু''নয়ান ।</td>\r\n  </tr>\r\n</table>', 'hIRoXdfa_1M', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 647, ''),
(12, 1, 'images/chordhara.gif', 'চোর ধরা', 'চোর-ধরা', 'আরে ছি ছি ! রাম রাম ! ব''লো না হে ব''লো', 'আরে ছি ছি ! রাম রাম ! ব''লো না হে ব''লো না-<br/>\r\nচল্‌‌ছে যা জুয়াচুরি, নাহি তার তুলনা ।<br/>\r\nযেই আমি দেই ঘুম টিফিনের আগেতে,<br/>\r\nভয়ানক ক''মে যায় খাবারের ভাগেতে ।<br/>\r\nরোজ দেখি খেয়ে গেছে, জানিনেকো কারা সে-<br/>\r\nকালকে যা হয়ে গেল ডাকাতির বাড়া সে !<br/>\r\nপাঁচখানা কাট্‌‌লেট্‌‌ লুচি তিন গণ্ডা,<br/>\r\nগোটা দুই জিবে গজা, গুটি দুই মণ্ডা,<br/>\r\nআরো কত ছিল পাতে আলুভাজা ঘুঙ্‌‌নি-<br/>\r\nঘুম থেকে উঠে দেখি পাতখানা শুন্যি !<br/>\r\nতাই আজ ক্ষেপে গেছি- আর কত পারব ?<br/>\r\nএতদিন স''য়ে স''য়ে এইবারে মারব ।<br/>\r\nখাড়া আছি সারাদিন হুঁসিয়ার পাহারা,<br/>\r\nদেখে নেব রোজ রোজ খেয়ে যায় কাহারা ।<br/>\r\nরামু হও, দামু হও, ওপাড়ার ঘোষ বোস-<br/>\r\nযেই হও এইবারে থেমে যাবে ফোঁস্‌ ফোঁস্‌ ।<br/>\r\nখাট্‌বে না জারিজুরি আঁটবে না মারর্‌প্যাঁচ,<br/>\r\nযারে পাব ঘাড়ে ধ''রে কেটে দেব ঘ্যাঁচ্‌ ঘ্যাঁচ্‌ ।<br/>\r\nএই দেখ ঢাল নিয়ে খাড়া আছি আড়ালে,<br/>\r\nএইবারে টের পাবে মুণ্ডুটা বাড়ালে ।<br/>\r\nরোজ বলি "সাবধান!" কানে তবু যায় না ?<br/>\r\nঠেলাখানা বুঝ্‌‌বি ত এইবারে আয় না !', 'jVpVxRs8Dbg', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 396, '112400647'),
(13, 1, '', 'খুড়োর কল', 'খুড়োর-কল', 'কল করেছেন আজব রকম চণ্ডীদাসের খুড়ো', '<table border="0">\r\n  <tr>\r\n    <td>\r\nকল করেছেন আজব রকম চণ্ডীদাসের খুড়ো-<br>\r\nসবাই শুনে সাবাস্‌ বলে পাড়ার ছেলে বুড়ো ।<br>\r\nখুড়োর যখন অল্প বয়স- বছর খানেক হবে-<br>\r\nউঠ্‌ল কেঁদে ''গুংগা'' বলে ভীষণ অট্টরবে ।<br>\r\nআর সবাই ''মামা'' ''গাগা'' আবোল তাবোল বকে,<br>\r\nখুড়োর মুখে ''গুংগা'' শুনে চম্‌‌কে গেল লোকে ।<br>\r\nবল্‌‌লে সবাই, "এই ছেলেটা বাঁচলে পরে তবে,<br>\r\nবুদ্ধি জোরে এ সংসারেতে একটা কিছু হবে ।"<br>\r\nসেই খুড়ো আজ কল করেছেন আপন বুদ্ধি বলে,<br>\r\nপাঁচ ঘণ্টার রাস্তা যাবে তিন ঘণ্টায় চলে ।<br>\r\nদেখে এলাম কলটি অতি সহজ এবং সোজা,<br>\r\nঘণ্টা পাঁচেক ঘাটলে পরে আপনি যাবে বোঝা ।<br>\r\nবল্‌ব কি আর কলের ফিকির, বলতে না পাই ভাষা,<br>\r\nঘাড়ের সঙ্গে যন্ত্র জুড়ে এক্কেবারে খাসা ।<br>\r\n<br>\r\nসামনে তাহার খাদ্য ঝোলে যার যে রকম রুচি-<br>\r\nমণ্ডা মিঠাই চপ্‌ কাট্‌‌লেট্‌‌ খাজা কিংবা লুচি ।<br>\r\nমন বলে তায় ''খাব খাব'', মুখ চলে তায় খেতে,<br>\r\nমুখের সঙ্গে খাবার ছোটে পাল্লা দিয়ে মেতে ।<br>\r\nএমনি ক''রে লোভের টানে খাবার পানে চেয়ে,<br>\r\nউৎসাহেতে হুঁস্‌ রবে না  চল্‌‌বে কেবল ধেয়ে ।<br>\r\nহেসে খেলে দু''দশ যোজন চলবে বিনা ক্লেশে,<br>\r\nখাবার গন্ধে পাগল হ''য়ে জিভের জলে ভেসে।<br>\r\nসবাই বলে সমস্বরে ছেলে জোয়ান বুড়ো,<br>\r\nঅতুল কীর্তি রাখল ভবে চণ্ডীদাসের খুড়ো ।<br>\r\n\r\n</td>\r\n    <td align="left" valign="middle"><img src="images/khurorkal.gif" width="198" height="283"></td>\r\n  </tr>\r\n</table>', 'XFNgx31HwGY', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 532, '112400530'),
(14, 1, '', 'গানের গুঁতো', 'গানের-গুঁতো', 'গান জুড়েছেন গ্রীষ্মকালে ভীষ্মলোচন শর্মা', 'গান জুড়েছেন গ্রীষ্মকালে ভীষ্মলোচন শর্মা-<br>\r\nআওয়াজখানা দিচ্ছে হানা দিল্লী থেকে বর্মা !<br>\r\nগাইছে ছেড়ে প্রাণের মায়া, গাইছে তেড়ে প্রাণপণ,<br>\r\nছুট্‌‌ছে লোকে চারদিকেতে ঘুরছে মাথা ভন্‌ ভন্‌ ।<br>\r\n<br>\r\nমরছে কত জখম হয়ে কর্‌ছে কত ছট্‌ফট্‌-<br>\r\nবলছে হেঁকে, "প্রাণটা গেল, গানটা থামাও ঝট্‌ পট্‌ ।"<br>\r\nবাঁধন-ছেড়া মহিষ ঘোড়া পথের ধারে চিৎপাত ;<br>\r\nভীষ্মলোচন গাইছে তেড়ে নাইকো তাহে দৃক্‌পাত ।<br>\r\n<br>\r\nচার পা তুলি জন্তুগুলি পড়্‌‌ছে বেগে মুর্ছায়,<br>\r\nলাঙ্গুল খাড়া পাগল পারা বল্‌‌ছে বেগে, "দূর ছাই" ।<br>\r\nজলের প্রানী অবাক মানি গভীর জলে চুপ চাপ,<br>\r\nগাছের বংশ হ''চ্ছে ধ্বংস পড়্‌ছে দেদার ঝুপ্ ঝাপ্‌ ।<br>\r\n<br>\r\nশুন্য মাঝে ঘূর্ণা লেগে ডিগবাজি খায় পক্ষী,<br>\r\nসবাই হাঁকে, ''আর না দাদা, গানটা থামাও লক্ষ্মী ।"<br>\r\nগানের দাপে আকাশ কাঁপে দালান ফাটে বিল্‌কুল্‌<br>\r\nভীষ্মলোচন গাইছে ভীষণ খোশ্‌‌মেজাজে দিল্‌ খুল্‌ ।<br>\r\n<br>\r\nএক যে ছিল পাগলা ছাগল, এম্‌‌নি সেটা ওস্তাদ,<br>\r\nগানের তালে শিং বাগিয়ে মারলে গুঁতো পশ্চাৎ ।<br>\r\nআর কোথা যায় একটি কথায় গানের মাথায় ডাণ্ডা<br>\r\n''বাপরে'' ব''লে ভীষ্মলোচন এক্কেবারে ঠাণ্ডা ।', 'T8L1TJdmYXw', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 695, '112400536'),
(15, 1, '', 'ছায়াবাজি', 'ছায়াবাজি', 'আজগুবি নয়, আজগুবি নয়, সত্যিকারের কথা', '<table border="0">\r\n  <tr>\r\n    <td>\r\nআজগুবি নয়,  আজগুবি নয়, সত্যিকারের কথা-<br>\r\nছায়ার সাথে কুস্তি ক''রে গাত্র হ''ল ব্যথা !<br>\r\nছায়া ধরার ব্যাবসা করি তাও জানোনা বুঝি,<br>\r\nরোদের ছায়া, চাঁদের ছায়া, হরেক রকম পুঁজি !<br>\r\nশিশির ভেজা সদ্য ছায়া, সকাল বেলায় তাজা !<br>\r\nগ্রীষ্মকালে শুক্‌‌নো ছায়া ভীষন রোদে ভাজা,<br>\r\nচিলগুলো যায় দুপুর বেলায় আকাশ পথে ঘুরে<br>\r\nফাঁদ ফেলে তার ছায়ার উপর খাঁচায় রাখি পুরে ।<br>\r\nকাগের ছায়া বগের ছায়া দেখ্‌‌ছি কত ঘেঁটে-<br>\r\nহাল্কা মেঘের পান্‌‌সে ছায়া তাও দেখেছি চেটে ।<br>\r\nকেউ জানেনা এসব কথা কেউ বোঝে না কিছু,<br>\r\nকেউ ঘোরে না আমার মত ছায়ার পিছুপিছু ।<br>\r\nতোমরা ভাব গাছের ছায়া অম্‌‌নি লুটায় ভূঁয়ে,<br>\r\nঅম্‌‌নি শুধু ঘুমায় বুঝি শান্তা মতন শুয়ে ;<br>\r\nআসল ব্যাপার জান্‌‌বে যদি আমার কথা শোনো,<br>\r\nবলছি যা তা সত্যি কথা, সন্দেহ নাই কোনো ।<br>\r\nকেউ যবে তার রয় না কাছে, দেখতে নাহি পায় <br>\r\nগাছের ছায়া ফুটফুটিয়ে এদিক ওদিক চায় ।<br>\r\nসেই সময়ে গুড়গুড়িয়ে পিছন হ''তে এসে<br>\r\nধামায় চেপে ধপাস্‌ করে ধরবে তারে ঠেসে ।<br>\r\nপাৎলা ছায়া, ফোক্‌লা ছায়া, ছায়া গভীর কালো-<br>\r\nগাছের চেয়ে গাছের ছায়া সব রকমেই ভালো ।<br>\r\nগাছ গাছালি শেকড় বাকল মিথ্যে সবাই গেলে,<br>\r\nবাপ্‌রে বলে পালায় ব্যামো ছায়ার ওষুধ খেলে ।<br>\r\nনিমের ছায়া ঝিঙের ছায়া তিক্ত ছায়ার পাক,<br>\r\nযেই খাবে ভাই অঘোর ঘুমে ডাকবে তাহার নাক্‌ ।<br>\r\nচাঁদের আলোয় পেঁপের ছায়া ধরতে যদি পার,<br>\r\nশুঁকলে পরে সর্দিকাশি থাকবে না আর কারো ।<br>\r\nআম্‌ড়া গাছের নোংরা ছায়া কাম্‌‌ড়ে যদি খায়<br>\r\nল্যাংড়া লোকের ঠ্যাং গজাবে সন্দেহ নাই তায় ।<br>\r\nআষাঢ় মাসের বাদ্‌লা দিনে বাঁচতে যদি চাও ,<br>\r\nতেঁতুল তলার তপ্ত ছায়া হপ্তা তিনেক খাও ।<br>\r\nমৌয়া গাছের মিষ্টি ছায়া ''ব্লটিং'' দিয়ে শুষে<br>\r\nধুয়ে মুছে সাবধানেতে রাখ্‌‌ছি ঘরে পুষে !<br>\r\nপাক্কা নতুন টাট্‌কা ওষুধ এক্কেবারে দিশি-<br>\r\nদাম রেখেছি সস্তা বড়, চোদ্দ আনা শিশি ।\r\n</font></td>\r\n    <td align="left" valign="top"><img src="images/chhayabaji.gif" width="317" height="291"></td>\r\n  </tr>\r\n</table>', 'T2IPfzBb-wk', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 495, '112400679'),
(16, 1, '', 'ঠিকানা', 'ঠিকানা', 'আরে আরে জগমোহন- এস, এস, এস', 'আরে আরে জগমোহন- এস, এস, এস-<br>\r\nবলতে পার কোথায় থাকে আদ্যানাথের মেসো ?<br>\r\nআদ্যানাথের নাম শোননি ? খগেনকে তো চেনো ?<br>\r\nশ্যাম বাগ্‌চি খগেনেরই মামাশ্বশুর জেনো ।<br>\r\nশ্যামের জামাই কেষ্টমোহন, তার যে বাড়ীওলা-<br>\r\n(কি যেন নাম ভুলে গেছি), তারই মামার শালা ;<br>\r\nতারই পিশের খুড়তুতো ভাই আদ্যানাথের মেশো-<br>\r\nলক্ষ্মী দাদা, ঠিকানা তার একটু জেনে এসো ।<br>\r\n<br>\r\nঠিকানা চাও ? বলছি শোন ; আমড়াতলার মোড়ে<br>\r\nতিন-মুখো তিন রাস্তা গেছে তারি একটা ধ''রে,<br>\r\nচলবে সিধে নাকবরাবর ডানদিকে চোখ রেখে ;<br>\r\nচল্‌‌তে চল্‌‌তে দেখবে শেষে রাস্তা গেছে বেঁকে ।<br>\r\nদেখ্‌‌বে সেথায় ডাইনে বাঁয়ে পথ গিয়েছে কত ,<br>\r\nতারি ভিতর ঘুরবে খানিক গোলকধাঁধার মত ।<br>\r\nতারপরেতে হঠাৎ বেঁকে ডাইনে মোচড় মেরে,<br>\r\nফিরবে আবার বাঁয়ের দিকে তিনটে গলি ছেড়ে ।<br>\r\nতবেই আবার পড়বে এসে আমড়াতলার মোড়ে-<br>\r\nতারপরে যাও ঝেথায় খুশী- জ্বালিও নাকো মোরে ।', 'NCFRHIXdkyo', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 493, '112400355'),
(17, 1, 'images/kimushkil.gif', 'কি মুস্কিল', 'কি-মুস্কিল', 'সব লিখেছে এই কেতাবে দুনিয়ার সব খবর যত', 'সব লিখেছে এই কেতাবে দুনিয়ার সব খবর যত,<br>\r\nসরকারী সব আফিসখানার কোন্‌ সাহেবের কদর কত ।<br>\r\nকেমন ক''রে চাট্‌‌নি বানায়, কেমন করে পোলাও করে,<br>\r\nহরেক্‌ রকম মুষ্টিযোগের বিধান লিখ্‌‌ছে ফলাও ক''রে ।<br>\r\nসাবান কালি দাঁতের মাজন বানাবার সব কায়দাকেতা,<br>\r\nপূজা পার্বণ তিথির হিসাব শ্রাদ্ধবিধি লিখ্‌‌ছে হেথা ।<br>\r\nসব লিখেছে, কেবল দেখ পাচ্ছিনেকো লেখা কোথায়-<br>\r\nপাগ্‌লা ষাঁড়ে করলে তাড়া কেমন ক''রে ঠেকাব তায় !', '', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 397, ''),
(18, 1, '', 'হুঁকোমুখো হ্যাংলা', 'হুঁকোমুখো-হ্যাংলা', 'হুঁকোমুখো হ্যাংলা', '<table border="0" cellpadding="2" cellspacing="5">\r\n  <tr> \r\n    <td rowspan="2">\r\nহুঁকোমুখো হ্যাংলা&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;বাড়ী তার বাংলা<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;মুখে তার হাসি নাই, দেখেছ ?<br>\r\nনাই তার মানে কি ?&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কেউ তাহা জানে কি ?<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কেউ কভু তার কাছে থেকেছ ?<br>\r\n<br>\r\nশ্যামাদাস মামা তার	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;আপিঙের থানাদার,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;আর তার কেউ নাই এছাড়া-<br>\r\nতাই বুঝি একা সে&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;মুখখানা ফ্যাকাশে,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ব''সে আছে কাঁদ-কাঁদ বেচারা ?<br>\r\n<br>\r\nথপ্‌ থপ্‌ পায়ে সে&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;নাচ্‌ত যে আয়েসে,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;গাল ভরা ছিল তার ফুর্তি,<br>\r\nগাইত সে সারাদিন&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;''সারে গামা টিম্‌টিম্‌'',<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;আহ্লাদে গদ-গদ মূর্তি !<br>\r\n			<br>\r\nএইত সে দুপ''রে&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ব''সে ওই উপরে,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;খাচ্ছিল কাঁচকলা চট্‌‌কে-<br>\r\nওর মাঝে হল কি ?&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;মামা তার মোলো কি ?<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;অথবা কি ঠ্যাং গেল মট্‌‌কে ?<br>\r\n	</td>\r\n    <td><img src="images/hukomukhohangla.gif" width="273" height="242"></td>\r\n  </tr>\r\n  <tr> \r\n    <td align="left" valign="top">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;হুঁকোমুখো \r\n      হেঁকে কয়,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;আরে দূর, \r\n      তা তো নয়,<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;দেখ্‌ছ \r\n      না কি রকম চিন্তা ?<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;মাছি মারা ফন্দি এ&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;যত \r\n      ভাবি মন দিয়ে-<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ভেবে \r\n      ভেবে কেটে যায় দিনটা ।<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;বসে যদি ডাইনে,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;লেখে \r\n      মোর আইনে-<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;এই \r\n      ল্যাজে মাছি মারি ত্রস্ত ;<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;বামে যদি বসে তাও,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;নহি \r\n      আমি পিছপাও,<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;এই \r\n      ল্যাজে আছে তার অস্ত্র !<br>\r\n      <br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;যদি দেখি কোন পাজি&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;বসে \r\n      ঠিক মাঝামাঝি,<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কি \r\n      যে করি ভেবে নাহি পাইরে-<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ভেবে দেখ একি দায়,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কোন্‌ \r\n      ল্যাজে মারি তায়<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;দুটি \r\n      বই ল্যাজ মোর নাইরে !"<br>\r\n      </td>\r\n  </tr>\r\n</table>', 'AzejaWWJY30', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 776, '112400289');
INSERT INTO `articles` (`id`, `category_id`, `top_image`, `title`, `article_slug`, `first_line`, `body`, `audio`, `added_on`, `modified_on`, `views`, `sc_track_id`) VALUES
(19, 1, 'images/hature.gif', 'হাতুড়ে', 'হাতুড়ে', 'একবার দেখে যাও ডাক্তারি কেরামৎ', '<table border="0">\r\n  <tr>\r\n      <tr>\r\n      	<td valign="top" style=''padding-right: 10px; border-right: 1px solid #ccc;''>\r\n			\r\n			একবার দেখে যাও ডাক্তারি কেরামৎ-<br>\r\n			কাটা ছেঁড়া ভাঙা চোরা চট্‌পট মেরামৎ ।	<br>	\r\n			কয়েছেন গুরু মোর, "শোন শোন বৎস,<br>\r\n			কাগজের রোগী কেটে আগে কর মক্‌স" ।<br>\r\n			উৎসাহে কি না হয় ? কি না হয় চেষ্টায় ?<br>\r\n			অভ্যাসে চটপট্‌ হাত পাকে শেষটায় ।<br>\r\n			খেটে খুটে জল হ''ল শরীরের রক্ত-<br>\r\n			শিখে দেখি বিদ্যেটা নয় কিছু শক্ত ।<br>\r\n			কাটা ছেঁড়া ঠুক্‌ ঠাক্‌, কত দেখ যন্ত্র,<br>\r\n			ভেঙে চুরে জুড়ে দেই তারও জানি মন্ত্র ।<br>\r\n			চোখ বুজে চট্‌পট বড় বড় মুর্তি,<br>\r\n			যত কাটি ঘ্যাস্‌ ঘ্যাস্‌ তত বাড়ে ফুর্তি ।<br>\r\n			ঠাং-কাটা গলাকাটা কত কাটা হস্ত,<br>\r\n			শিরিষের আঠা দিয়ে জুড়ে দেয় চোস্ত ।<br>\r\n			এইবারে বলি তাই, রোগী চাই জ্যান্ত-<br>\r\n			ওরে ভোলা, গোটাছয় রোগী ধরে আন্‌ত !<br>\r\n			</font>\r\n	  	</td>\r\n	  	\r\n		<td valign="top" style=''padding-left: 10px;''>\r\n						গেঁটেবাতে ভুগে মরে ও পাড়ার নন্দী,<br>\r\n			কিছুতেই সারাবেনা এই তার ফন্দি-<br>\r\n			এক দিন এনে তারে এইখানে ভুলিয়ে,<br>\r\n			গেঁটেবাত ঘেঁটে-ঘুঁটে সব দেব ঘুলিয়ে ।<br>\r\n			কার কানে কট্‌মট্‌ কার নাকে সর্দি,<br>\r\n			এস, এস, ভয় কিসে ?  আমি আছি বদ্যি ।<br>\r\n			শুয়ে কে রে ? ঠাং-ভাঙা ? ধরে আন্‌ এখেনে-<br>\r\n			স্ক্রুপ দিয়ে এঁটে দিব কি রকম দেখেনে ।<br>\r\n			গাল ফোলা কাঁদ কেন ? দাঁতে বুঝি বেদনা ?<br>\r\n			এস এস ঠুকে দেই- আর মিছে কেঁদ না,<br>\r\n			এই পাশে গোটা দুই, ওই পাশে তিনটে-<br>\r\n			দাঁতগুলো টেনে দেখি- কোথা গেল চিম্‌‌টে ?<br>\r\n			ছেলে হও, বুড়ো হও, অন্ধ কি পঙ্গু,<br>\r\n			মোর কাছে ভেদ নাই, কলেরা কি ডেঙ্গু-<br>\r\n			কালাজ্বর, পালাজ্বর, পুরানো কি টাট্‌কা,<br>\r\n			হাতুড়ির এক ঘায়ে একেবারে আট্‌কা !\r\n			</font>\r\n		</td>\r\n  </tr>\r\n</table>', 'OWXuF9bz2h4', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 287, '112400318'),
(35, 1, 'images/bigganshikkha.gif', 'বিজ্ঞান শিক্ষা', 'বিজ্ঞান-শিক্ষা', 'আয় তোর মুণ্ডুটা দেখি, আয়  দেখি ''ফুটোস্কোপ'' দিয়ে', 'আয় তোর মুণ্ডুটা দেখি, আয়  দেখি ''ফুটোস্কোপ'' দিয়ে<br>\r\n      দেখি কত ভেজালের মেকি আছে তোর মগজের ঘিয়ে ।<br>\r\n      কোন্‌ দিকে বুদ্ধিটা খোলে, কোন্‌ দিকে থেকে যায় চাপা ;<br>\r\n      কতখানি ভস্‌ ভস্‌ ঘিলু, কতখানি ঠক্‌ ঠকে কাঁপা ।<br>\r\n      মন তোর কোন্‌ দেশে থাকে , কেন তুই ভুলে যাস্‌ কথা-<br>\r\n      আয় দেখি কোন্‌ ফাঁক দিয়ে, মগজেতে ফুটো তর কোথা ।<br>\r\n      টোল-খাওয়া ছাতাপড়া মাথা, ফাটামত মনে হয় যেন,<br>\r\n      আয় দেখি বিশ্লেষ ক''রে- চোপ্‌ রও ভয় পাস্‌ কেন ?<br>\r\n      কাৎ হ''য়ে কান ধ''রে দাঁড়া, জিভ্‌খানা উল্‌‌টিয়ে দেখা,<br>\r\n      ভালো ক''রে বুঝে শুনে দেখি-  বিজ্ঞানে যে রকম লেখা ।<br>\r\n      মুণ্ডুতে ''ম্যাগনেট্‌'' ফেলে, বাঁশ দিয়ে ''রিফ্লেক্‌ট্‌'' <br>\r\n      ইঁট দিয়ে ''ভেলসিটি'' ক''ষে  দেখি মাথা ঘোরে কি না ঘোরে ।', '', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 431, ''),
(20, 1, '', 'রামগরুড়ের ছানা', 'রামগরুড়ের-ছানা', 'রামগরুড়ের ছানা হাসতে তাদের মানা', '<table border="0" cellpadding="2" cellspacing="5">\r\n  <tr>\r\n    <td>\r\n\r\nরামগরুড়ের ছানা&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;হাসতে তাদের মানা,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;হাসির কথা শুনলে বলে,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;"হাস্‌ব না-না না-না" ।<br>\r\nসদাই মরে ত্রাসে-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ঐ বুঝি কেউ হাসে !<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;এক চোখে তাই মিট্‌মিটিয়ে<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;তাকায় আশে পাশে ।<br>\r\nঘুম নাহি তার চোখে	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;আপনি ব''কে ব''কে<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;আপনারে কয়, "হাসিস্‌ যদি<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;মারব কিন্তু তোকে !"<br>\r\nযায় না বনের কাছে,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কিম্বা গাছে গাছে,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;দখিন হাওয়ার সুড়্‌সুড়িতে<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;হাসিয়ে ফেলে পাছে !<br>\r\nসোয়াস্তি নেই মনে-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;মেঘের কোণে কোণে<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;হাসির বাস্প উঠছে ফেঁপে<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কান পেতে তাই শোনে ।<br>\r\nঝোপের ধারে ধারে&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;রাতের অন্ধকারে<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;জোনাক্‌ জ্বলে আলোর তালে<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;হাসির ঠারে ঠারে ।<br>\r\nহাসতে হাসতে যারা&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;হচ্ছে কেবল সারা<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;রামগরুড়ের লাগ্‌ছে ব্যথা<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;বুঝছে না কি তারা ?<br>\r\nরামগরুড়ের বাসা&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ধমক দিয়ে ঠাসা,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;হাসির হাওয়া বন্ধ সেথায়<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;নিষেধ সেথায় হাসা ।\r\n</font>\r\n</pre></td>\r\n    <td align="left" valign="top"><img src="images/ramgorurerchhana.gif" width="318" height="292"></td>\r\n  </tr>\r\n</table>', 'J96VOWjPSjQ', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 877, '112400173'),
(21, 1, '', 'কিম্ভুত', 'কিম্ভুত', 'বিদ্‌ঘুটে জানোয়ার কিমাকার কিম্ভুত', '<table border="0" cellpadding="1" cellspacing="10">\r\n  <tr> \r\n    <td rowspan="2" valign="top" style=''border-right: 1px solid #ccc; padding-right: 10px;''> \r\n      <p>বিদ্‌ঘুটে জানোয়ার কিমাকার কিম্ভুত,<br>\r\n        সারাদিন ধ''রে তার শুনি শুধু খুঁৎ খুঁৎ ।<br>\r\n        মাঠপারে ঘাটপারে কেঁদে মরে খালি সে,<br>\r\n        ঘ্যান ঘ্যান আবদারে ঘন ঘন নালিশে ।<br>\r\n        এটা চাই সেটা চাই কত তার বায়না-<br>\r\n        কি যে চায় তাও ছাই বোঝা কিছু যায় না ।<br>\r\n        কোকিলের মত তার কণ্ঠেতে সুর চাই,<br>\r\n        গলা শুনে আপনার বলে, &quot;উঁহু, দূর ছাই !&quot;<br>\r\n        আকাশেতে উড়ে যেতে পাখিদের মানা নেই-<br>\r\n        তাই দেখে মরে কেঁদে- তার কেন ডানা নেই ।<br>\r\n        হাতিটার কী বাহার দাঁতে আর শুণ্ডে-<br>\r\n        ও রকম জুড়ে তার দিতে হবে মুণ্ডে ।<br>\r\n        ক্যাঙ্গারুর লাফ দেখে ভারি তার হিংসে-<br>\r\n        ঠ্যাং চাই আজ থেকে ঢ্যাংঢ্যাঙে চিম্‌সে !<br>\r\n        সিংহের কেশরের মত তার তেজ কৈ ?<br>\r\n        পিছে খাসা গোসাপের খাঁজকাটা লেজ কৈ ?<br>\r\n        একলা সে সব হ''লে মেটে তার প্যাখ্‌না ;<br>\r\n        যারে পায় তারে বলে, &quot;মোর দশা দেখ্‌ না !&quot;</font></p>\r\n      <p>&nbsp;</p></td>\r\n      \r\n    <td><img src="images/kimbhut.gif"><br><br>\r\n    কেঁদে কেঁদে শেষটায়- আষাঢ়ের বাইশে<br>\r\n      হ''ল বিনা চেষ্টায় \r\n      চেয়েছে যা তাই সে ।<br>\r\n      ভুলে গিয়ে কাঁদাকাটি \r\n      আহ্লাদে আবেশে<br>\r\n      চুপি চুপি একলাটি \r\n      ব''সে ব''সে ভাবে সে-<br>\r\n      লাফ দিয়ে হুঁশ \r\n      করে হাতি কভু নাচে কি ?<br>\r\n      কলাগাছ খেলে \r\n      পরে ক্যাঙারুটা বাঁচে কি ?<br>\r\n      ভোঁতামুখ কুহুডাক \r\n      শুনে লোকে কবে কি ?<br>\r\n      এই দেহে শুঁড়ো \r\n      নাক খাপছাড়া হবে কি ?<br>\r\n      বুড়ো হাতি \r\n      উড়ে&quot; ব''লে কেউ যদি গালি দেয় ?<br>\r\n      কান টেনে ল্যাজ \r\n      ম''লে &quot;দুয়ো&quot; ব''লে তালি দেয় ?<br>\r\n      কেউ যদি তেড়েমেড়ে \r\n      বলে তার সামনেই-<br>\r\n      &quot;কোথাকার \r\n      তুই কেরে, নাম নেই ধাম নেই ?&quot;<br>\r\n      জবাব কি দেবে \r\n      ছাই, আছে কিছু বলবার ?<br>\r\n      কাঁচুমাচু ব''সে \r\n      তাই, মনে শুধু তোল্‌পাড়-<br>\r\n      &quot;নই ঘোড়া, \r\n      নই হাতি, নই সাপ বিচ্ছু<br>\r\n      মৌমাছি প্রজাপতি \r\n      নই আমি কিচ্ছু ।<br>\r\n      মাছ ব্যাং গাছপাতা \r\n      জলমাটি ঢেউ নই,<br>\r\n      নই জুতা নই ছাতা, \r\n      আমি তবে কেউ নই !&quot;</td>\r\n  </tr>\r\n</table>', 'Uc2e3AmLofo', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 591, '112400824'),
(22, 1, 'images/nbtjk.gif', 'ন্যাড়া বেল তলা যায় ক''বার', 'ন্যাড়া-বেল-তলা-যায়-ক''বার', 'রোদে রাঙা ইটের পাঁজা', '<table border="0" cellpadding="1" cellspacing="10">\r\n  <tr>\r\n    <td valign="top" align="left"> রোদে রাঙা ইটের পাঁজা&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;তার \r\n      উপরে বস্‌ল রাজা-<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ঠোঙা ভরা বাদাম ভাজা খাচ্ছে কিন্তু গিল্‌ছে \r\n      না ।<br>\r\n      গায়ে আটা গরম জামা&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;পুড়ে পিঠ হচ্ছে ঝামা \r\n      ;<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;রাজা বলে "বৃষ্টি নামা- নইলে কিচ্ছু মিলছে \r\n      না ।"<br>\r\n      থাকে সারা দুপুর ধ''রে&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ব''সে ব''সে চুপটি \r\n      ক''রে,<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;হাঁড়িপানা মুখটি ক''রে আঁক্‌ড়ে ধ''রে শ্লেটটুকু \r\n      ;<br>\r\n      ঘেমে ঘেমে উঠছে ভিজে&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ভ্যাবাচ্যাকা একলা \r\n      নিজে<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;হিজিবিজি লিখছে কি যে বুঝ্‌ছে না কেউ \r\n      একটুকু ।<br>\r\n      ঝাঁঝাঁ রোদ আকাশ জুড়ে,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;মাথাটার ঝাঁঝরা \r\n      ফুঁড়ে,<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;মগজেতে নাচ্‌ছে ঘুরে রক্তগুলো ঝনর্‌ ঝন্‌ \r\n      ;<br>\r\n      ঠাঠা-পড়া দুপুর দিনে,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;রাজা বলে, "আর বাচিনে,<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ছুটে আন বরফ কিনে- করছে কেমন গা ছন্‌ছন্‌ \r\n      ।"<br>\r\n      সবে বলে, "হায় কি হ্ল !&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; রাজা বুঝি ভেবেই \r\n      মোলো !<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ওগো রাজা মুখটি খোলো- কওনা ইহার কারণ \r\n      কি ?<br>\r\n      রাঙামুখ পান্‌সে যেন &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;তেলে ভাজা আম্‌সি \r\n      হেন,<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;রাজা এত ঘামসে কেন- শুনতে মোদের বারণ \r\n      কি ?"<br>\r\n       </td>\r\n      <td align="right" bgcolor="#cccccc" height="1px"></td>\r\n    <td align="left" valign="top">    \r\n      রাজা \r\n      বলে , "কেই বা শোনে&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;যে কথাটি ঘুরছে মনে,<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;মগজের \r\n      নানান কোণে- আনছি টেনে বাইরে তায়,<br>\r\n      সে কথাটি বলছি \r\n      শোন,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;যতই ভাব যতই গোন,<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;নাহি \r\n      তার জবাব কোন কুলকিনারা নাই রে হায় ।<br>\r\n      লেখা আছে পুঁথির \r\n      পাতে,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;"ন্যাড়া যায় বেলতলাতে,"<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;নাহি \r\n      কোনো সন্ধ তাতে- কিন্তু প্রশ্ন ''ক''বার যায় ?''<br>\r\n      এ কথাটা এদ্দিনেও&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;পারে \r\n      নিকো বুঝতে কেও,<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;লিখে \r\n      নিকো পুস্তকেও, দিচ্ছে না কেউ জবাব তায় ।<br>\r\n      লাখোবার যায় \r\n      যদি সে&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;যাওয়া তার ঠেকায় কিসে ?<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ভেবে \r\n      তাই পাইনে দিশে নাই কি কিচ্ছু উপায় তার ?"<br>\r\n      একথাটা যেমনি \r\n      বলা \r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;রোগা এক ভিস্তিও''লা<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ঢিপ্‌ \r\n      ক''রে বাড়িয়ে গলা প্রণাম কর্‌ল দুপায় তাঁর ।<br>\r\n      হেসে বলে, "আজ্ঞে  সে কি ?\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;এতে আর গোল হবে কি?<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;নেড়াকে \r\n      তো নিত্য দেখি আপন চোখে পরিষ্কার-<br>\r\n      আমাদেরি বেলতলা \r\n      যে&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;নেড়া সেথা খেলতে আসে<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;হরে \r\n      দরে হয় ত মাসে নিদেন পক্ষে পঁচিশবার ।"<br>\r\n      </td>\r\n  </tr>\r\n</table>', 'Hg7crAwRE44', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 946, '112400327'),
(23, 1, '', 'লড়াই-ক্ষ্যাপা', 'লড়াই-ক্ষ্যাপা', 'ওই আমাদের পাগলা জগাই, নিত্যি হেথায় আসে', '<table border="0">\r\n  <tr>\r\n    <td>\r\nওই আমাদের পাগলা জগাই, নিত্যি হেথায় আসে ;<br>\r\nআপন মনে গুন্‌ গুনিয়ে মুচকি হাসি হাসে ।<br>\r\nচলতে গিয়ে হঠাৎ যেন ধ্মক লেগে থামে,<br>\r\nতড়াক্‌ করে লাফ দিয়ে যায় ডাইনে থেকে বামে ।<br>\r\nভীষণ রোখে হাত গুটিয়ে সামলে নিয়ে কোঁচা ;<br>\r\n"এইয়ো" বলে ক্ষ্যাপার মতো শূন্যে মারে খোঁচা ।<br>\r\nচেঁচিয়ে বলে, "ফাঁদ পেতেছ ? জগাই কি তায় পড়ে ?<br>\r\nসাত জার্মান, জগাই একা, তবুও জগাই লড়ে ।"<br>\r\nউৎসাহেতে গরম হয়ে তিড়িংবিড়িং নাচে,<br>\r\nকখনও যায় সামনে তেড়ে, কখনও যায় পাছে ।<br>\r\nএলোপাতাড়ি ছাতার বাড়ি ধুপুস্‌ধাপুস্‌ কত !<br>\r\nচক্ষু বুজে কায়দা খেলায় চর্কিবাজির মত ।<br>\r\nলাফের চোটে হাঁফিয়ে ওঠে গায়েতে ঘাম ঝরে,<br>\r\nদুড়ুম ক''রে মাটির পরে লম্বা হয়ে পড়ে ।<br>\r\nহাত পা ছুঁড়ে চেঁচায় খালি চোখটি ক''রে ঘোলা,<br>\r\n"জগাই মলো হঠাৎ খেয়ে কামানের এক গোলা" !<br>\r\nএই না বলে মিনিট খানেক ছট্‌ফটিয়ে খুব,<br>\r\nমড়ার মত শক্ত হয়ে এক্কেবারে চুপ ।<br>\r\nতার পরেতে সটান্‌ ব''সে চুলকে খানিক মাথা,<br>\r\nপকেট থেকে বার করে তার হিসেব লেখার খাতা ।<br>\r\nলিখলে তাতে, "শোন্‌‌রে জগাই, ভীষণ লড়াই হলো<br>\r\nপাঁচ ব্যাটাকে খতম ক''রে জগাই দাদা মলো ।"\r\n\r\n</font>\r\n</td>\r\n    <td align="left" valign="top"><img src="images/laraikhapa.gif" width="254" height="282"></td>\r\n  </tr>\r\n</table>', '94IsgK7Uo9E', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 379, '112400678'),
(24, 1, '', 'শুনেছ কি বলে গেল', 'শুনেছ-কি-বলে-গেল', 'শুনেছ কি বলে গেল সীতানাথ বন্দ্যো?', 'শুনেছ কি বলে গেল সীতানাথ বন্দ্যো?<br>\r\nআকাশের গায়ে নাকি টকটক গন্ধ ?<br>\r\nটকটক থাকে নাকো হ''লে পরে বৃষ্টি-<br>\r\nতখনও দেখেছি চেটে একেবারে মিষ্টি ।', '', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 438, ''),
(25, 1, '', 'পালোয়ান', 'পালোয়ান', 'খেলার ছলে ষষ্ঠিচরণ হাতী লোফেন যখন তখন', '<table border="0">\r\n  <tr>\r\n    <td> \r\nখেলার ছলে ষষ্ঠিচরণ হাতী লোফেন যখন তখন,<br>\r\nদেহের ওজন উনিশটি মণ, শক্ত যেন লোহার গঠন ।<br>\r\nএকদিন এক গুণ্ডা তাকে বাঁশ বাগিয়ে মার্‌ল বেগে&mdash;<br>\r\nভাঙল সে বাঁশ শোলার মত মট্‌ করে তার কনুই লেগে ।<br>\r\nএইত সেদিন রাস্তা দিয়ে চল্‌তে গিয়ে দৈব বশে,<br>\r\nউপর থেকে প্রকাণ্ড ইট পড়্‌ল তাহার মাথায় খ''সে ।<br>\r\nমুণ্ডুতে তার যেম্‌নি ঠেকা অম্‌নি সে ইঁট এক নিমেষে<br>\r\nগুঁড়িয়ে হ''ল ধুলোর মত, ষষ্ঠি চলেন মুচকি হেসে। <br>\r\nষষ্ঠি যখন ধ্মক হাঁকে কাঁপতে থাকে দালান বাড়ী,<br>\r\nফুঁয়ের জোরে পথের মোড়ে উল্টে পড়ে গরুর গাড়ী ।<br>\r\nধুম্‌সো কাঠের তক্তা ছেঁড়ে মোচড় মেরে মুহূর্তেকে,<br>\r\nএকশ জালা জল ঢালে রোজ স্নানের সময় পুকুর থেকে ।<br>\r\nসকাল বেলার জলপানি তার তিনটি ধামা পেস্তা মেওয়া,<br>\r\nসঙ্গেতে তার চৌদ্দ হাঁড়ি দৈ কি মালাই মুড়কি দেওয়া ।<br>\r\nদুপুর হ''লে খাবার আসে কাতার দিয়ে ডেক্‌চি ভ''রে,<br>\r\nবরফ দেওয়া উনিশ কুঁজো সরবতে তার তৃষ্ণা হরে ।<br>\r\nবিকাল বেলা খায়না কিছু গণ্ডা দশেক মণ্ডা ছাড়া,<br>\r\nসন্ধা হ''লে লাগায় তেড়ে দিস্তা দিস্তা লুচির তাড়া ।<br>\r\nরাত্রে সে তার হাত পা টেপায় দশটি চেলা মজুত থাকে,<br>\r\nদুম্‌দুমাদুম্‌ সবাই মিলে মুগুর দিয়ে পেটায় তাকে ।<br>\r\nবল্‌লে বেশি ভাব্‌বে শেষে এসব কথা ফেনিয়ে বলা&mdash;<br>\r\nদেখবে যদি আপন চোখে যাওনা কেন বেনিয়াটোলা ।<br>\r\n</font>\r\n</td>\r\n    <td align="left" valign="top"><img src="images/paloan.gif" width="243" height="384"></td>\r\n  </tr>\r\n</table>', '', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 378, ''),
(26, 1, 'images/sabdhan.gif', 'সাবধান', 'সাবধান', 'আরে আরে, ওকি কর প্যালারাম বিশ্বাস', '<table border="0" cellpadding="1" cellspacing="5">\r\n  <tr align="center"> \r\n  </tr>\r\n  <tr> \r\n    <td valign="top" style=''border-right: 1px solid #ccc; padding-right: 10px;''>আরে আরে, ওকি কর প্যালারাম বিশ্বাস \r\n      ?<br>\r\n      ফোঁস্‌ ফোঁস্‌ অত জোরে ফেলোনাকো নিঃশ্বাস ।<br>\r\n      জানোনা কি সে বছর ওপাড়ার ভূতোনাথ,<br>\r\n      নিঃশ্বাস নিতে গিয়ে হয়েছিল কুপোকাৎ ?<br>\r\n      হাঁপ ছাড় হ্যাস্‌ফ্যাঁস্‌ ও রকম হাঁ ক''রে-<br>\r\n      মুখে যদি ঢুকে বসে পোকা মাছি মাকড়ে ?<br>\r\n      বিপিনের খুড়ো হয় বুড়ো সেই হল'' রায়,<br>\r\n      মাছি খেয়ে পাঁচ মাস ভুগেছিল কলেরায় ।<br>\r\n      তাই বলি- সাবধান ! ক''রোনাকো ধুপ্‌ধাপ্‌,<br>\r\n      টিপি টিপি পায় পায় চলে যাও চুপ্‌ চাপ্‌ ।<br>\r\n      চেয়োনাকো আগে পিছে, যেয়োনাকো ডাইনে<br>\r\n      সাবধানে বাঁচে লোকে,- এই লেখা আইনে ।<br>\r\n      পড়েছ ত কথামালা ? কে যেন সে কি ক''রে<br>\r\n      পথে যেতে পড়ে গেল পাতকো''র ভিতরে ।<br>\r\n      \r\n</td>\r\n\r\n    <td valign="top" style=''padding-left: 10px;''> \r\n    ভালো কথা- আর যেন সকালে কি দুপুরে,<br>\r\n      নেয়োনাকো কোনো দিন ঘোষেদের পুকুরে ।<br>\r\n    এরকম মোটা দেহে কি যে হবে কোন্‌ দিন,<br>\r\n      কথাটাকে ভেবে দেখ কি রকম সঙ্গিন !<br>\r\n      চটো কেন ? হয় নয় কেবা জানে পষ্ট,<br>\r\n      যদি কিছু হ''য়ে পড়ে পাবে শেষে কষ্ট ।<br>\r\n      মিছিমিছি ঘ্যান্‌ঘ্যান্‌ কেন কর তক্ক ?<br>\r\n      শিখেছ জ্যাঠামো খালি, ইঁচড়েতে পক্ক ।<br>\r\n      মানবে না কোন কথা চলা ফেরা আহারে,<br>\r\n      একদিন টের পাবে ঠেলা কয় কাহারে ।<br>\r\n      রমেশের মেঝ মামা সেও ছিল সেয়না,<br>\r\n      যত বলি ভালো কথা কানে কিছু নেয় না<br>\r\n      শেষকালে একদিন চান্নির বাজারে<br>\r\n      প''ড়ে গেল গাড়ি চাপা রাস্তার মাঝারে !<br>\r\n</td>\r\n</tr>\r\n</table>', '', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 275, ''),
(27, 1, '', 'একুশে আইন', 'একুশে-আইন', 'শিবঠাকুরের আপন দেশে', '<table border="0">\r\n  <tr> \r\n    <td valign="top" style=''padding-right: 10px; border-right: 1px solid #ccc;''>শিবঠাকুরের আপন দেশে,<br>\r\n      আইন কানুন সর্বনেশে !<br>\r\n      কেউ যদি পা পিছলে প''ড়ে,<br>\r\n      প্যায়দা এসে পাক্‌‌ড়ে ধরে,<br>\r\n      কাজির কাছে হয় বিচার-<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;একুশ টাকা দণ্ড তার ।।<br>\r\n      <br>\r\n      সেথায় সন্ধে ছটার আগে<br>\r\n      হাঁচ্‌‌তে হ''লে টিকিট লাগে<br>\r\n      হাঁচ্‌‌লে পরে বিন্‌ টিকিটে<br>\r\n      দম্‌দমাদম্‌ লাগায় পিঠে,<br>\r\n      কোটাল এসে নস্যি ঝাড়ে-<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;একুশ দফা হাঁচিয়ে মারে ।।<br>\r\n      <br>\r\n      কারুর যদি দাঁতটি নড়ে,<br>\r\n      চারটি টাকা মাশুল ধরে,<br>\r\n      কারুর যদি গোঁফ গজায়,<br>\r\n      একশো আনা ট্যাকসো চায়-<br>\r\n      খুঁচিয়ে পিঠে গুঁজিয়ে ঘাড়,<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;সেলাম ঠেকায় একুশ বার।।</font></td>\r\n    <td valign="top" style=''padding-left: 10px;''>\r\n      চল্‌‌তে গিয়ে কেউ যদি চায়<br>\r\n      এদিক্‌ ওদিক্‌ ডাইনে বাঁয়,<br>\r\n      রাজার কাছে খবর ছোটে,<br>\r\n      পল্টনেরা লাফিয়ে ওঠে,<br>\r\n      দুপুর রোদে ঘামিয়ে তায়<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;একুশ হাতা জল \r\n      গেলায়।।<br>\r\n      <br>\r\n      যে সব লোকে পদ্য লেখে,<br>\r\n      তাদের ধ''রে খাঁচায় রেখে,<br>\r\n      কানের কাছে নানান্‌ সুরে<br>\r\n      নামতা শোনায় একশো উড়ে,<br>\r\n      সামনে রেখে মুদির খাতা-<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;হিসেব কষায় একুশ \r\n      পাতা।।<br>\r\n      <br>\r\n      হঠাৎ সেথায় রাত দুপুরে<br>\r\n      নাক ডাকালে ঘুমের ঘোরে,<br>\r\n      অম্‌নি তেড়ে মাথায় ঘষে,<br>\r\n      গোবর গুলে বেলের কষে,<br>\r\n      একুশটি পাক ঘুরিয়ে তাকে<br>\r\n      &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;একশ ঘণ্টা ঝুলিয়ে \r\n      রাখে।।</td>\r\n  </tr>\r\n</table>', 'bJudcaLEy08', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 1358, '112400621'),
(28, 1, 'images/bombagarerraja2.gif', 'বোম্বাগড়ের রাজা', 'বোম্বাগড়ের-রাজা', 'কেউ কি জান সদাই কেন বোম্বাগড়ের রাজা', '<table border="0">\r\n  <tr> \r\n    <td valign="top"> \r\nকেউ কি জান সদাই কেন বোম্বাগড়ের রাজা-<br>\r\nছবির ফ্রেমে বাঁধিয়ে রাখে আমসত্ত্ব ভাজা ?<br>\r\nরানীর মাথায় অষ্টপ্রহর কেন বালিশ বাঁধা ?<br>\r\nপাঁউরুটিতে পেরেক ঠোকে কেন রানীর দাদা ?<br>\r\nকেন সেথায় সর্দি হলে ডিগবাজি খায় লোকে ?<br>\r\nজোছ্‌না রাতে সবাই কেন আলতা মাখায় চোখে ?<br>\r\nওস্তাদেরা লেপ মুড়ি দেয় কেন মাথায় ঘাড়ে ?<br>\r\nটাকের ''পরে পণ্ডিতেরা ডাকের টিকিট মারে ।<br>\r\nরাত্রে কেন ট্যাঁক্‌ঘড়িটা ডুবিয়ে রাখে ঘিয়ে ?<br>\r\nকেন রাজার বিছ্‌না পাতে শিরীষ কাগজ দিয়ে ?<br>\r\nসভায় কেন চেঁচায় রাজা "হুক্কা হুয়া" ব''লে ?<br>\r\nমন্ত্রী কেন কলসী বাজায় ব''সে রাজার কোলে ?<br>\r\nসিংহাসনে ঝোলায় কেন ভাঙা বোতল শিশি ?<br>\r\nকুমড়ো দিয়ে ক্রিকেট খেলে কেন রাজার পিসী ?<br>\r\nরাজার খুড়ো নাচেন কেন হুঁকোর মালা প''রে ?<br>\r\nএমন কেন ঘটছে তা কেউ বলতে পার মোরে ?<br>\r\n</font></td>\r\n    <td valign="middle"> \r\n      <pre><img src="images/bombagarerraja1.gif" width="270" height="200">\r\n</pre></td>\r\n  </tr>\r\n  <tr>\r\n    <td valign="top"><img src="images/bombagarerraja3.gif" width="246" height="215"></td>\r\n    <td valign="top">&nbsp;</td>\r\n  </tr>\r\n</table>', 'g0sT9WITUL0', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 558, '112400102'),
(29, 1, 'images/bujhiyebala.gif', 'বুঝিয়ে বলা', 'বুঝিয়ে-বলা', 'ও শ্যামাদাস ! আয় ত দেখি ব''স তো দেখি এখেনে,', '<table border="0">\r\n  <tr> \r\n    <td valign="top" style=''border-right: 1px solid #ccc; padding-right: 10px;''>ও শ্যামাদাস ! আয় ত দেখি \r\n        ব''স তো দেখি এখেনে,<br>\r\n        সেই কথাটা বুঝিয়ে দেব পাঁচ মিনিটে, দেখে নে ।<br>\r\n        জ্বর হয়েছে ? মিথ্যে কথা ! ওসব তোদের চালাকি-<br>\r\n        এই যে বাবা চেঁচাচ্ছিলে, শুনতে পাইনি ? কালা কি?<br>\r\n        মামার ব্যামো ? বদ্যি ডাকবি ? ডাকিস নাহয় বিকেলে ;<br>\r\n        না হয় আমি বাৎলে দেব বাঁচবে মামা কি খেলে ।<br>\r\n        আজকে তোকে সেই কথাটা বোঝাবই বোঝাব-<br>\r\n        না বুঝ্‌‌বি ত মগজে তোর গজাল দিয়ে গোঁজাব ।<br>\r\n        কোন্‌ কথাটা ? তাও ভুলেছিস ? ছেড়ে দিছিস্‌ হাওয়াতে ?<br>\r\n        কি বলছিলেম পরশু রাতে বিষ্টুবোসের দাওয়াতে ?<br>\r\n        ভুলিস্‌‌নি ত বেশ করেছিস্‌, আবার শুনলে ক্ষেতি কি ?<br>\r\n        বড় যে তুই পালিয়ে বেড়াস্‌, মাড়াস্‌‌নে যে এদিক্‌ই !<br>\r\n        বলছি দাঁড়া, ব্যস্ত কেন ? বইগুলো আন্‌ নামিয়ে-<br>\r\n        তুই থাকতে মুটের বোঝা বইতে যাব আমি এ ? <br>সাবধানেতে \r\n        আন্‌, ধরছি দাঁড়া- সেই আমাকেই ঘামালি-<br>\r\n        এই খেয়েছে ! কোন্‌ আক্কেলে শব্দকোষটা নামালি ?<br>\r\n	ঢের হয়েছে ! আয় দেখি তুই বোস্‌  ত দেখি এদিকে-<br>\r\nওরে গোপাল, গোটাকয়েক পান দিতে বল্‌ খেঁদিকে ।<br>\r\n        \r\n      </td>\r\n      \r\n    <td valign="top" style=''padding-left: 10px;''> বলছিলেম \r\n      কি, বস্তুপিণ্ড সূক্ষ্ণ হতে স্থূলেতে,<br>\r\n      অর্থাৎ কিনা লাগ্‌ছে ঠেলা পঞ্চভূতের মূলেতে-<br>\r\n      গোড়ায় তবে দেখতে হবে কোত্থেকে আর কি ক''রে,<br>\r\n      রস জমে এই প্রপঞ্চময় বিশ্বতরুর শিকড়ে ।<br>\r\n      অর্থাৎ কিনা, এই মনে কর রোদ পড়েছে ঘাসেতে,<br>\r\n      এই মনে কর চাঁদের আলো পড়ল তারই পাশেতে-<br>\r\n      আবার দেখ ! এরই মধ্যে হাই তোলবার মানে কি ?<br>\r\n      আকাশপানে তাকাস্‌ খালি, যাচ্ছে কথা কানে কি \r\n      ?<br>\r\n      কি বল্লি তুই ? এ সব শুধু আবোল তাবোল বকুনি \r\n      ?<br>\r\n      বুঝতে হলে মগজ লাগে, ব''লেছিলেম তখুনি ।<br>\r\n      মগজভরা গোবর তোদের হচ্ছে ঘুঁটে শুকিয়ে,<br>\r\n      যায় কি দেওয়া কোন কথা তার ভিতরে ঢুকিয়ে ?-<br>\r\n      ও শ্যামাদাস ! উঠলি কেন ? কেবল যে চাস পালাতে \r\n      !<br>\r\n      না শুনবি ত মিথ্যে সবাই আসিস কেন জ্বালাতে ?<br>\r\n      তত্ত্বকথা যায় না কানে যত মরি চেঁচিয়ে-<br>\r\n      ইচ্ছে করে ডান্‌‌পিটেদের কান ম''লে দি পেঁচিয়ে \r\n      ।\r\n      </td>\r\n</tr>\r\n</table>', 'ntK87jPI5cI', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 544, '112400631'),
(30, 1, '', 'নোট বই', 'নোট-বই', 'এই দেখ পেন্সিল, নোটবুক এ হাতে', '<table border="0">\r\n  <tr>\r\n    <td valign="top"> \r\n      এই দেখ পেন্সিল, নোটবুক এ হাতে,<br>\r\n      এই দেখ ভরা সব কিল্‌বিল লেখাতে ।<br>\r\n      ভালো কথা শুনি যেই চট্‌ পট্‌ লিখি তায়-<br>\r\n      ফড়িঙের ক''টা ঠ্যাং, আরশুলা কি কি খায় ।<br>\r\n      আঙুলেতে আঠা দিলে লাগে কেন চট্‌চট্‌,<br>\r\n      কাতুকুতু দিলে কেন গরু করে ছট্‌ফট্‌ ।<br>\r\n      দেখে শিখে প''ড়ে শুনে ব''সে মাথা ঘামিয়ে<br>\r\n      নিজে নিজে আগাগোড়া লিখে গেছি আমি এ ।<br>\r\n      কান করে কট্‌ কট্‌, ফোড়া করে টন্‌ টন্‌-<br>\r\n      ওরে রামা ছুটে আয়, নিয়ে আয় লণ্ঠন ।<br>\r\n      কাল থেকে মনে মোর লেগে আছে খট্‌কা<br>\r\n      ঝোলাগুড় কিসে দেয়, সাবান না পট্‌কা ?<br>\r\n      এই বেলা প্রশ্নটা লিখে রাখি গুছিয়ে<br>\r\n      জবাবটা জেনে নেব মেজদাকে খুঁচিয়ে ।<br>\r\n      পেট কেন কাম্‌ড়ায়, বল দেখি পার কে ?<br>\r\n      বল দেখি ঝাঁজ কেন জোয়ানের আরকে ?<br>\r\n      তেজপাতে তেজ কেন ? ঝাল কেন লঙ্কায় ?<br>\r\n      নাক কেন ডাকে আর পিলে কেন চমকায় ?<br>\r\n      কার নাম দুন্দুভি ? কাকে বলে অরণি ?<br>\r\n      বল্‌বে কি ? তোমরা ও নোট্‌বই পড় নি ।<br>\r\n      </td>\r\n    <td align="left" valign="top"><img src="images/noteboi.gif" width="262" height="360"></td>\r\n  </tr>\r\n</table>', 'lRW0tEHhv4c', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 440, '112400337'),
(31, 1, '', 'হুলোর গান', 'হুলোর-গান', 'বিদ্‌ঘুটে রাত্তিরে ঘুট্‌ঘুটে ফাঁকা', 'বিদ্‌ঘুটে রাত্তিরে ঘুট্‌ঘুটে ফাঁকা,<br>\r\nগাছপালা মিশ্‌মিশে মখ্‌মলে ঢাকা ।<br>\r\nজট্‌বাঁধা ঝুল কালো বটগাছতলে,<br>\r\nধক্‌ধক্‌ জোনাকির চক্‌মকি জ্বলে,<br>\r\nচুপ্‌চাপ্‌ চারিদিকে ঝোপঝাড়গুলো-<br>\r\nআয় ভাই গান গাই আয় ভাই হুলো ।<br>\r\n<br>\r\nগীত গাই কানে কানে চীৎকার ক''রে,<br>\r\nকোন্‌ গানে মন ভাঙে শোন্‌ বলি তোরে-<br>\r\nপূবদিকে মাঝ রাতে ছোপ্‌ দিয়ে রাঙা<br>\r\nরাতকানা চাঁদ ওঠে আধখানা ভাঙা ।<br>\r\nচট্‌ ক''রে মনে পড়ে মট্‌কার কাছে<br>\r\nমালপোয়া আধাখানা কাল থেকে আছে ।<br>\r\n<br>\r\nদুড়্‌ দুড়্‌ ছুটে যাই দূর থেকে দেখি<br>\r\nপ্রাণপণে ঠোঁট চাটে কানকাটা নেকী ।<br>\r\nগালফোলা মুখে তার মালপোয়া ঠাসা<br>\r\nধুক ক''রে নিভে গেল বুকভরা আশা ;<br>\r\nমন বলে আর কেন সংসারে থাকি<br>\r\nবিল্‌কুল্‌ সব দেখি ভেল্‌কির ফাঁকি ।<br>\r\n<br>\r\nসব যেন বিচ্ছিরি সব যেন খালি,<br>\r\nগিন্নীর মুখ যেন চিম্‌নির কালি ।<br>\r\nমন ভাঙা দুখ্‌ মোর কণ্ঠেতে পুরে<br>\r\nগান গাই আয় ভাই প্রাণফাটা সুরে । ', '', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 476, ''),
(32, 1, '', 'গন্ধ বিচার', 'গন্ধ-বিচার', 'সিংহাসনে বস্‌ল রাজা বাজল কাঁসর ঘণ্টা', '<table border="0">\r\n  <tr> \r\n    <td style=''padding-right: 10px; border-right:1px solid #ccc;''>সিংহাসনে বস্‌ল রাজা বাজল কাঁসর ঘণ্টা,<br>\r\n      ছট্‌ফটিয়ে উঠ্‌ল কেঁপে মন্ত্রীবুড়োর মনটা ।<br>\r\n      বল্‌‌লে রাজা, "মন্ত্রী, তোমার জামায় কেন গন্ধ ?"<br>\r\n      মন্ত্রী বলে, "এসেন্স দিছি- গন্ধ ত নয় মন্দ !"<br>\r\n      রাজা বলে, "মন্দ ভালো দেখুক শুঁকে বদ্যি, "<br>\r\n      বদ্যি বলে, "আমার নাকে বেজায় হল সর্দি ।"<br>\r\n      রাজা হাঁকেন, "বোলাও তবে- রাম নারায়ণ পাত্র ।"<br>\r\n      পাত্র বলে, "নস্যি নিলাম এক্ষনি এইমাত্র-<br>\r\n      নস্যি দিয়ে বন্ধ যে নাক, গন্ধ কোথায় ঢুকবে ?"<br>\r\n      রাজা বলেন, "কোটাল তবে এগিয়ে এস, শুঁকবে ।"<br>\r\n      কোটাল কহে, "পান খেয়েছি মশলা তাহে কর্পূর,<br>\r\n      গন্ধে তারি মুণ্ডু আমার এক্কেবারে ভরপুর ।"<br>\r\n      রাজা বলেন, "আসুক তবে শের পালোয়ান ভীমসিং,"<br>\r\n      ভীম বলে, "আজ কচ্ছে আমার সমস্ত গা ঝিম্‌ ঝিম্‌ ।<br>\r\n      রাত্রে আমার বোখার হল, বল্‌ছি হুজুর ঠিক বাৎ"-<br>\r\n      ব''লেই শুল রাজসভাতে চক্ষু বুজে চিৎপাত ।</td>\r\n   <td align="left" valign="top" style=''padding-left: 10px;''>রাজার \r\n      শালা চন্দ্রকেতু তারেই ধ''রে শেষটা<br>\r\n      বল্‌ল রাজা, "তুমিই না হয় কর না ভাই চেষ্টা \r\n      ।"<br>\r\n      চন্দ্র বলেন, "মারতে চাও ত ডাকাও না হয় জল্লাদ,<br>\r\n      গন্ধ শুঁকে মর‌‌তে হবে এ আবার কি আহ্লাদ ?"<br>\r\n      ছিল হাজির বৃদ্ধ নাজির বয়সটি তার নব্বই,<br>\r\n      ভাব্‌‌লে মনে, "ভয় কেন আর একদিন তো মরবই-"<br>\r\n      সাহস ক''রে বল্‌‌লে বুড়ো, "মিথ্যে সবাই বক্‌‌ছিস,<br>\r\n      শুঁকতে পারি হুকুম পেলে এবং পেলে বক্‌‌শিস \r\n      ।"<br>\r\n      রাজা বলেন, "হাজার টাকা ইনাম পাবে সদ্য,"<br>\r\n      তাই না শুনে উৎসাহেতে উঠ্‌ল বুড়ো মদ্দ ।<br>\r\n      জামার পরে নাক ঠেকিয়ে- শুঁক্‌ল কত গন্ধ,<br>\r\n      রইল অটল, দেখ্‌‌লে লোকে বিষ্ময়ে বাক্‌ বন্ধ ।<br>\r\n      রাজ্যে হল জয় জয়কার বাজ্‌ল কাঁসর ঢক্কা,<br>\r\n      বাপ্‌‌রে কি তেজ বুড়োর হাড়ে, পায় না সে যে অক্কা \r\n      ! </td>\r\n  </tr>\r\n</table>', 'NxY9XYXy61w', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 685, '112400562');
INSERT INTO `articles` (`id`, `category_id`, `top_image`, `title`, `article_slug`, `first_line`, `body`, `audio`, `added_on`, `modified_on`, `views`, `sc_track_id`) VALUES
(33, 1, '', 'ট্যাঁশ গরু', 'ট্যাঁশ-গরু', 'ট্যাঁশ গরু গরু নয়, আসলেতে পাখি সে', '<table border="0">\r\n  <tr>\r\n    <td valign="top" style=''padding-right: 20px;''> \r\n\r\nট্যাঁশ গরু গরু নয়, আসলেতে পাখি সে ;<br>\r\nযার খুশি দেখে এস হারুদের আফিসে ।<br>\r\nচোখ দুটি ঢুলু ঢুলু, মুখখানা মস্ত,<br>\r\nফিট্‌ফাট্‌ কালোচুলে টেরিখানা চোস্ত ।<br>\r\nতিন-বাঁকা শিং তার, ল্যাজখানি প্যাঁচান-<br>\r\nএকটুকু ছোও যদি, বাপ্‌রে কি চ্যাঁচান !<br>\r\nলট্‌খটে হাড়গোড় খট্‌খট্‌ ন''ড়ে যায়,<br>\r\nধম্‌কালে ল্যাগ্‌ব্যাগ্‌ চমকিয়ে প''ড়ে যায় ।<br>\r\nবর্ণিতে রূপ গুণ সাধ্য কি কবিতার,<br>\r\nচেহারার কি বাহার- ঐ দেখ ছবি তার ।<br>\r\nট্যাঁশ গরু খাবি খায় ঠ্যাস্‌ দিয়ে দেয়ালে,<br>\r\nমাঝে মাঝে কেঁদে ফেলে না জানি কি খেয়ালে ;<br>\r\nমাঝে মাঝে তেড়ে ওঠে, মাঝে মাঝে রেগে যায়,<br>\r\nমাঝে মাঝে কুপোকাৎ দাঁতে দাঁত লেগে যায় ।<br>\r\n\r\n</td>\r\n    <td align="left" valign="top">খায় না সে দানাপানি- ঘাস পাতা বিচালি<br>\r\nখায় না সে ছোলা ছাতু ময়দা কি পিঠালি ;<br>\r\nরুচি নাই আমিষেতে, রুচি নাই পায়সে<br>\r\nসাবানের সুপ আর মোমবাতি খায় সে ।<br>\r\nআর কিছু খেলে তার কাশি ওঠে খক্‌খক্‌,<br>\r\nসারা গায়ে ঘিন্‌ ঘিন্‌, ঠ্যাং কাঁপে ঠক্‌ঠক্‌ ।<br>\r\nএকদিন খেয়েছিল ন্যাক্‌ড়ার ফালি সে-<br>\r\nতিন মাস আধমরা শুয়েছিল বালিশে ।<br>\r\nকারো যদি শখ্‌ থাকে ট্যাঁশ গরু কিন্‌তে,<br>\r\nসস্তায় দিতে পারি, দেখ ভেবে চিন্তে ।<br>\r\n      </font> <img src="../images/tashgoru.gif" width="356" height="302" align="middle"> \r\n    </td>\r\n  </tr>\r\n</table>', 'cWehiIot1Xg', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 805, '112400835'),
(34, 1, '', 'কাতুকুতু বুড়ো', 'কাতুকুতু-বুড়ো', 'আর যেখানে যাও না রে ভাই সপ্তসাগর পার', '<table border="0">\r\n  <tr>\r\n    <td>\r\nআর যেখানে যাও না রে ভাই সপ্তসাগর পার,<br>\r\nকাতুকুতু বুড়োর কাছে যেওনা খবরদার !<br> \r\nসর্বনেশে বৃদ্ধ সে ভাই যেওনা তার বাড়ি-<br>\r\nকাতুকুতুর কুল্‌‌পি খেয়ে ছিঁড়বে পেটের নাড়ি ।<br>\r\nকোথায় বাড়ি কেউ জানে না, কোন্‌ সড়কের মোড়ে,<br>\r\nএকলা পেলে জোর ক''রে ভাই গল্প শোনায় প''ড়ে ।<br>\r\nবিদ্‌ঘুটে তার গল্পগুলো, না জানি কোন্‌ দেশী,<br>\r\nশুনলে পরে হাসির চেয়ে কান্না আসে বেশি ।<br>\r\nনা আছে তার মুণ্ডু মাথা, না আছে তার মানে,<br>\r\nতবুও তোমায় হাসতে হবে তাকিয়ে বুড়োর পানে ।<br>\r\nকেবল যদি গল্প বলে তাও থাকা যায় সয়ে,<br>\r\nগায়ের উপর সুড়সুড়ি দেয় লম্বা পালক লয়ে ।<br>\r\nকেবল বলে, "হোঃ হোঃ হোঃ, কেষ্টদাসের পিসি-<br>\r\nবেচ্ত খালি কুমড়ো কচু হাঁসের ডিম আর তিসি ।<br>\r\nডিমগুলো সব লম্বা মতন, কুমড়োগুলো বাঁকা,<br>\r\nকচুর গায়ে রঙ-বেরঙের আল্‌পনা সব আঁকা ।<br>\r\nঅষ্ট প্রহর গাইত পিসি আওয়াজ ক''রে মিহি,<br>\r\nম্যাও ম্যাও ম্যাও বাকুম বাকুম ভৌ ভৌ ভৌ চীহি ।"<br>\r\nএই না বলে কুটুৎ ক''রে চিমটি কাটে ঘাড়ে,<br>\r\nখ্যাংরা মতন আঙুল দিয়ে খোঁচায় পাঁজর হাড়ে ।<br>\r\nতোমায় দিয়ে সুড়সুড়ি সে আপনি লুটোপুটি,<br>\r\nযতক্ষণ না হাসবে তোমার কিচ্ছুতে নাই ছুটি ।\r\n</td>\r\n    <td align="left" valign="top"><img src="../images/katukutuburo.gif" width="269" height="298"></td>\r\n  </tr>\r\n</table>', '', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 870, ''),
(36, 1, '', 'খুচরো ছড়া', 'খুচরো-ছড়া', 'খুচরো ছড়া', '<table border="0">\r\n    <tr>\r\n    <td valign="top" style=''border-right: 1px solid #aaa; padding-right: 10px;''>\r\n      মাসি গো মাসি, পাচ্ছে হাসি<br />\r\n        নিম গাছেতে হচ্ছে শিম্-<br />\r\n        হাতির মাথায় ব্যাঙের ছাতা<br />\r\n        কাগের বাসায় বগের ডিম ॥<br />\r\n        &ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;<br />\r\n      বল্‌ব কি ভাই হুগ্‌‌লি গেলুম,<br />\r\n        বল্‌‌ছি তোমায় চুপি চুপি-<br />\r\n        দেখতে পেলাম তিনটে শুয়োর<br />\r\n        মাথায় তাদের নেইকো টুপি॥ <br />\r\n        &ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;<br />\r\n      আকাশের গায়ে কিবা রামধনু খেলে,<br />\r\n        দেখে চেয়ে কত লোক সব কাজ ফেলে;<br />\r\n        তাই দেখে খুঁতধরা বুড়ো কয় চটে,<br />\r\n        দেখছ কি, এই রঙ পাকা নয় মোটে ॥<br />\r\n        &ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;<br />\r\n      ঢপ্ ঢপ্ ঢাক ঢোল ভপ্ ভপ্ বাঁশি,<br />\r\n        ঝন্ ঝন্ করতাল্ ঠন্ ঠন্ কাঁসি ।<br />\r\n        ধুমধাম বাপ্ বাপ্ ভয়ে ভ্যাবাচ্যাকা,<br />\r\n        বাবুদের ছেলেটার দাঁত গেছে দেখা ॥<br />\r\n        &ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;<br />\r\n        \r\n        বাস্‌‌রে বাস্‌‌ ! সাবাস্‌‌ বীর !<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ধনুকখানি ধ''রে,<br />\r\nপায়রা দেখে মারলে তীর-<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কাগ্‌‌টা গেল ম্''রে !<br />\r\n&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;<br />\r\n"ম্যাও ম্যাও হুলোদাদা, তোমার যে দেখা নাই?<br />\r\n"গেছিলাম রাজপুরী রানীমার সাথে ভাই!"<br />\r\n"তাই নাকি? বেশ বেশ, কি দেখেছ সেখানে?"<br />\r\n"দেখেছি ইঁদুর এক রানীমার উঠানে।।"<br />\r\n&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;<br />\r\nরং হল চিঁড়েতন, সব গেল ঘুলিয়ে,<br />\r\nগাধা যায় মামাবাড়ি টাকে হাত বুলিয়ে।<br />\r\nবেড়াল মরে বিষম খেয়ে, চাঁদের ধর্‌ল মাথা,<br />\r\nহঠাৎ দেখি ঘর বাড়ি সব ময়দা দিয়ে গাঁথা।।<br />\r\n&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;<br />\r\nবুড়ো তুমি লোকটা ভালো,<br />\r\nচেহারাও নয়তো কালো-<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;তবু কেন তোমায় ভালো বাস্‌ছিনে?<br />\r\nকেন তা তো কেউ না জানে,<br />\r\nভেবে কিছু পাইনে মানে,<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;যত ভাবি ততই ভালো বাস্‌ছিনে।<br />\r\n        </td>\r\n        \r\n        <td valign="top" style=''border-right: 1px solid #aaa; padding-right: 10px; padding-left: 10px;''>\r\n      শুনেছ কি ব''লে গেল সীতানাথ বন্দ্যো?<br />\r\n        আকাশের গায়ে নাকি টক্‌টক্ গন্ধ?<br />\r\n        টক্‌টক্ থাকে নাকো হ''লে পরে বৃষ্টি-<br />\r\n        তখন দেখেছি চেটে একেবারে মিষ্টি ।<br />\r\n        &ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;<br />\r\n      কহ ভাই কহ রে, ‌অ্যাঁকাচোরা শহরে,<br />\r\n        বদ্যিরা কেন কেউ আলুভাতে খায় না?<br />\r\n        লেখা আছে কাগজে আলু খেলে মগজে,<br />\r\n        ঘিলু যায় ভেস্তিয়ে বুদ্ধি গজায় না!<br />\r\n        &ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;<br />\r\n      শোন শোন গল্প শোন, `এক যে ছিল গুরু,''<br />\r\n        এই আমার গল্প হল শুরু ।<br />\r\n        যদু আর বংশীধর যমজ ভাই তারা,<br />\r\n        এই আমার গল্প হল সারা ।<br />\r\n        &ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;<br />\r\n        \r\n        \r\n        বল্‌‌ছি, ওরে ছাগল ছানা,<br />\r\n		উড়িস্‌‌নে রে উড়িস্‌‌নে।<br />\r\n		জানিস্‌‌নে তোর উড়তে মানা-<br />\r\n		হাত পাগুলো ছুড়িস্‌‌নে।<br />\r\n		&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;<br />\r\n		খিল্‌‌খিল্লির মুল্লুকেতে থাকত নাকি দুই বেড়াল,<br />\r\n		একটা শুধায় আরেকটাকে, "তুই বেড়াল না মুই বেড়াল?"<br />\r\n		তাই থেকে হয় তর্ক শুরু, চীৎ‌কারে তার ভূত পালায়,<br />\r\n		আঁচড় কামড় চর্কিবাজী ধাঁই চটাপট চড় চালায় । <br />\r\n		খাম্‌‌চা খাবল ডাইনে বাঁয়ে হুড়মুড়িয়ে হুলোর মতো,<br />\r\n		উড়ল রোঁয়া চারদিকেতে রাম্-ধুনুরীর তুলোর মতো । <br />\r\n		তর্ক যখন শন্ত হল, ক্ষান্ত হল আঁচড় দাগা,<br />\r\n		থাকত দুটো আস্ত বেড়াল, র‌‌ইল দুটো ল্যাজের ডগা ।<br />\r\n		&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;<br />\r\n		তিন বুড়ো পণ্ডিত টাকচূড়ো নগরে<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;চ''ড়ে এক গাম্‌লায় পাড়ি দেয় সাগরে।<br />\r\nগাম্‌লাতে ছেঁদা ছিল আগে কেউ দেখেনি,<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;গানখানি তাই মোর থেমে গেল এখনি।।<br />\r\n&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;<br />\r\nজংলা বরে পাগলা বুড়ো আমায় এসে বলে,<br />\r\n"আড়াই বিঘে সমুদ্রেতে কাঁঠাল কত ফলে?"<br />\r\nআমিও বলি আন্দাজেতে; "বল্‌ছি শোন কত&mdash;<br />\r\nতোমাদের ঐ ঝিঙের ক্ষেতে চিংড়ি গজায় যত।।"<br />\r\n&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;<br />নাচ্ছি মোরা মনের সাধে গাচ্ছি তেড়ে গান<br />\r\nহুলো মেনি যে যার গলায় কালোয়াতীর তান।<br />\r\nনাচ্ছি দেখে চাঁদা মামা হাসছে ভরে গাল<br />\r\nচোখটি ঠেরে ঠাট্টা করে, দেখনা বুড়ার চাল।\r\n        </td>\r\n		<td valign="top" style=''padding-left: 10px;''>\r\n			চলে হন্‌ হন্‌&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ছোটে পন্‌ পন্‌<br />\r\n			ঘোরে বন্‌ বন্‌&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কাজে ঠন্‌ ঠন্‌<br />\r\n			বায়ু শন্‌ শন্‌&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;শীতে কন্‌ কন্‌<br />\r\n			কাশি খন্‌ খন্‌&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ফোড়া টন্‌ টন্‌<br />\r\n			মাছি ভন্‌ ভন্‌&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;থালা ঝন্‌ ঝন্‌<br />\r\n			&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;<br />\r\n			নন্দ ঘোষের শাম্‌লা গরু<br />\r\nভাগলো কোথায় লক্ষ্মীছাড়া?<br />\r\nনন্দ ছোটে বনবাদাড়ে,<br />\r\nসন্ধানে যায় বদ্যিপাড়া;<br />\r\nশেষকালেতে অর্ধরাতে,<br />\r\nহদ্দ হয়ে ফিরলে পরে&mdash;<br />\r\nবাসায় দেখে, ঘুমোয় গরু<br />\r\nল্যাজ গুটিয়ে গোয়াল ঘরে।<br />\r\n&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;<br />\r\nউঠোন-কোণে কড়াই ছিল,<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;পায়েস ছিল তাতে,<br />\r\nতাই নিয়ে কাল লড়াই করে<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;কুঁকড়ো বুড়োর সাথে,<br />\r\nযুদ্ধ জিতে বড়াই ভারি,<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;তখন দেখে চেয়ে-<br />\r\nকখন এসে চড়াই পাখি<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;পায়েস গেছে খেয়ে।<br />\r\n&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;<br />\r\nগাধাটার বুদ্ধি দেখ!- চাঁট মেরে সে নিজের গালে,<br />\r\nকে মেরেছে দেখবে ব''লে চড়তে গেছে ঘরের চালে।<br />\r\n&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;<br />\r\nছোট ছোট ছেলেগুলো কিসে হয় তৈরি,<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&mdash;কিসে হয় তৈরি?<br />\r\nকাদা আর ময়লা, ধুলো মাটি ময়লা,<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;এই দিয়ে ছেলেগুলো তৈরি।<br />\r\nছোট ছোট মেয়েগুলি কিসে হয় তৈরি,<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&mdash;কিসে হয় তৈরি?<br />\r\nক্ষীর ননী চিনি আর ভাল যাহা দুনিয়ার<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;মেয়েগুলি তাই দিয়ে তৈরি।।<br />\r\n&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;<br />\r\nআরে ছি ছি, রাম রাম ! কলকেতা শহরে,<br />\r\nলাল ধুতি পরে মুদি, তিন হাত বহরে।<br />\r\nমখ্‌মলি জামা জুতো ঝক্‌মকে টোপরে,<br />\r\nখায় দায় গান গায় রাস্তার উপরে।<br />\r\n		</td>\r\n  </tr>\r\n</table>', '', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 789, ''),
(37, 1, '', 'দাঁড়ে দাঁড়ে দ্রুম', 'দাঁড়ে-দাঁড়ে-দ্রুম', 'ছুট্‌‌ছে মোটর ঘটর ঘটর ছুট্‌‌ছে গাড়ী জুড়ি', '<table border="0">\r\n  <tr>\r\n    <td>\r\nছুট্‌‌ছে মোটর ঘটর ঘটর ছুট্‌‌ছে গাড়ী জুড়ি ;<br>\r\nছুট্‌‌ছে লোকে নানান্‌ ঝোঁকে করছে হুড়োহুড়ি ;<br>\r\nছুট্‌‌ছে কত ক্ষ্যাপার মত পড়্‌‌ছে কত চাপা-<br>\r\nসাহেব মেম থম্‌‌কে থেমে বল্‌‌ছে ''মামা ! পাপা !''<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-আমরা তবু তবলা ঠুকে গাচ্ছি কেমন তেড়ে,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;"দাঁড়ে দাঁড়ে দ্রুম ! দেড়ে দেড়ে দেড়ে !"<br>\r\n\r\nবর্ষাকালের বৃষ্টিবাদল রাস্তা জুড়ে কাদা,<br>\r\nঠাণ্ডা রাতে সর্দিবাতে মরবি কেন দাদা ?<br>\r\nহোক্‌ না সকাল হোক্‌ না বিকেল হোক্‌ না দুপুর বেলা,<br>\r\nথাক্‌ না তোমার আফিস যাওয়া থাক্‌ না কাজের ঠেলা-<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;এই দেখ না চাঁদনি রাতের গান এনেছি কেড়ে,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;"দাঁড়ে দাঁড়ে দ্রুম ! দেড়ে দেড়ে দেড়ে !"<br>\r\n\r\nমুখ্যু যারা হচ্ছে সারা পড়্‌ছে ব''সে একা,<br>\r\nকেউ বা দেখ কাঁচুর মাচুর কেউ বা ভ্যাবাচ্যাকা ;<br>\r\nকেউ বা ভেবে হদ্দ হল, মুখটি যেন কালি ;<br>\r\nকেউ বা ব''সে বোকার মত মুণ্ডু নাড়ে খালি ।<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;তার চেয়ে ভাই, ভাবনা ভুলে গাও না গলা ছেড়ে,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;"দাঁড়ে দাঁড়ে দ্রুম ! দেড়ে দেড়ে দেড়ে !"<br>\r\n\r\nবেজার হয়ে যে যার মত করছ সময় নষ্ট-<br>\r\nহাঁট্‌ছ কত খাট্‌ছ কত পাচ্ছ কত কষ্ট !<br>\r\nআসল কথা বুঝছ না যে, করছ না যে চিন্তা,<br>\r\nশুনছ না যে গানের মাঝে তব্‌লা বাজে ধিন্‌তা ?<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;"পাল্লা ধ''রে গায়ের জোরে গিট্‌কিরি দাও ঝেড়ে,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;"দাঁড়ে দাঁড়ে দ্রুম ! দেড়ে দেড়ে দেড়ে !"<br>\r\n</td>\r\n    <td valign="top"><img src="../images/daredaredroom.gif"></td>\r\n  </tr>\r\n</table>', '1JGN-UWqWLc', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 431, '112400116'),
(38, 1, '', 'গল্প বলা', 'গল্প-বলা', 'এক যে রাজা- থাম না দাদা,', '<table border="0">\r\n<tr>\r\n<td valign="top" style=''padding-right: 10px;border-right: 1px solid #ccc;''>\r\n&quot;এক যে রাজা&quot;- &quot;থাম না দাদা,<br>\r\nরাজা নয় সে, রাজ পেয়াদা ।&quot;<br>\r\n&quot;তার যে মাতুল&quot;- &quot;মাতুল কি সে ?<br>\r\nসবাই জানে সে তার পিসে ।&quot;<br>\r\n&quot;তার ছিল এক ছাগল ছানা&quot;-<br>\r\n&quot;ছাগলের কি গজায় ডানা ?&quot;<br>\r\n&quot;একদিন তার ছাতের ''পরে&quot;-<br>\r\n&quot;ছাত কোথায় হে টিনের ঘরে ?&quot;<br>\r\n&quot;বাগানের এক উড়ে মালী&quot;-<br>\r\n&quot;মালি নয় ত ? মেহের আলি-&quot;<br>\r\n&quot;মনের সাধে গাইছে বেহাগ&quot;-<br>\r\n&quot;বেহাগ তো নয় বসন্ত রাগ ।&quot;</td>\r\n<td valign="top" style=''padding-left: 10px;''>\r\n&quot;থোও না বাপু ঘ্যাঁচা ঘেঁচি&quot;-<br>\r\n&quot;আচ্ছা বল চুপ্‌ করেছি ।&quot;<br>\r\n&quot;এমন সময় বিছনা ছেড়ে<br>\r\nহঠাৎ মামা আস্‌ল তেড়ে,<br>\r\nধরল সে তার ঝুঁটির গোড়া-&quot;<br>\r\n&quot;কোথায় ঝুঁটি ? টাক যে ভরা ।&quot;<br>\r\n&quot;হোক না টেকো, তোর তাতে কি ?<br>\r\nলক্ষীছাড়া মুখ্যু ঢেঁকি ।<br>\r\nধরব ঠেসে টুঁটির ''পরে,<br>\r\nপিট্‌ব তোমার মুণ্ডু ধরে-<br>\r\nকথার উপর কেবল কথা,<br>\r\nএখন বাপু পালাও কোথা ?&quot;<br>\r\n</td>\r\n  </tr>\r\n</table>', 'aXs2acfZNa4', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 388, '112400146'),
(39, 1, '', 'ডানপিটে', 'ডানপিটে', 'বাপ্‌রে কি ডানপিটে ছেলে', '<table border="0">\r\n  <tr>\r\n    <td>\r\nবাপ্‌রে কি ডানপিটে ছেলে !-<br>কোন্‌ দিন ফাঁসি যাবে নয় যাবে জেলে ।<br>একটা সে ভূত সেজে আঠা মেখে মুখে,<br>ঠাঁই ঠাঁই শিশি ভাঙে শ্লেট দিয়ে ঠুকে ।<br>অন্যটা হামা দিয়ে আলমারি চড়ে,<br>খাট থেকে রাগ ক''রে দুম্‌দাম্‌ পড়ে !\r\n      <p>বাপ্‌রে কি ডানপিটে ছেলে !-<br>\r\n        শিলনোড়া খেতে চায় দুধভাত ফেলে !<br>\r\n        একটার দাঁত নেই, জিভ দিয়ে ঘষে,<br>\r\n        এক মনে মোমবাতি দেশলাই চোষে !<br>\r\n        আরজন ঘরময় নীল কালি গুলে,<br>\r\n        কপ্‌ কপ্‌ মাছি ধ''রে মুখে দেয় তুলে !</p>\r\n      <p>বাপ্‌রে কি ডানপিটে ছেলে !-<br>\r\n        খুন হত টম চাচা ওই রুটি খেলে !<br>\r\n        সন্দেহে শুঁকে বুড়ো মুখে নাহি তোলে,<br>\r\n        রেগে তাই দুই ভাই ফোঁস ফোঁস ফোলে !<br>\r\n        নেড়াচুল খাড়া হয়ে রাঙা হয় রাগে,<br>\r\n        বাপ্‌ বাপ্‌ ব''লে চাচা লাফ দিয়ে ভাগে । </p>\r\n      </td>\r\n    <td align="left" valign="top"><img src="../images/danpite.gif" width="367" height="221"></td>\r\n  </tr>\r\n</table>', '1bM38948zrs', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 621, '112400626'),
(40, 1, 'images/naradnarad.gif', 'নারদ! নারদ!', 'নারদ!-নারদ!', 'হ্যাঁরে হ্যাঁরে তুই নাকি কাল সাদাকে বলেছিলি লাল ?', '&quot;হ্যাঁরে হ্যাঁরে তুই নাকি কাল সাদাকে বলেছিলি লাল ?<br>\r\n      (আর) সেদিন নাকি রাত্রি জুড়ে নাক ডেকেছিস বিশ্রী সুরে ?<br>\r\n      (আর) তোদের পোষা বেড়ালগুলো  শুন্‌‌ছি নাকি বেজায় হুলো ?<br>\r\n      (আর) এই যে শুনি তোদের বাড়ি  কেউ নাকি রাখেনা দাড়ি ?<br>\r\n      ক্যান্‌ রে ব্যাটা ইসটুপিড ? ঠেঙিয়ে তোরে করব ঢিট্‌ !&quot;<br>\r\n      &quot;চোপরাও তুম্‌ স্পিকটি নট্‌ মারব রেগে পটাপট্‌-<br>\r\n      ফের যদি ট্যারাবি চোখ, কিম্বা আবার করবি রোখ,<br>\r\n      কিম্বা যদি অমনি ক''রে মিথ্যেমিথ্যি চ্যাঁচাস্‌ জোরে-<br>\r\n      আই ডোন্ট কেয়ার কানাকড়ি- জানিস আমি স্যান্ডো করি ?&quot;<br>\r\n      &quot;ফের লাফাচ্ছিস্‌ ! অলরাইট্‌ কামেন্‌ ফাইট্‌ ! কামেন্‌ ফাইট্‌ !&quot;<br>\r\n      &quot;ঘুঘু দেখেছ, ফাঁদ দেখনি, টেরটা পাবে আজ এখনি !<br>\r\n      আজকে যদি থাকত মামা পিটিয়ে তোমায় কর্‌ত ধামা ।&quot;<br>\r\n      &quot;আরে ! আরে ! মারবি নাকি ? দাঁড়া একটা পুলিশ ডাকি !&quot;<br>\r\n      &quot;হাঁহাঁহাঁহাঁ ! রাগ করো না, করতে চাও কি তাই বল না !&quot;<br>\r\n      &quot;হাঁ হাঁ তাতো সত্যি বটেই আমি তো আর চটিনি মোটেই !<br>\r\n      মিথ্যে কেন লড়তে যাবি ? ভেরি-ভেরি সরি, মশলা খাবি ?&quot;<br>\r\n      &quot;শেকহ্যান্ড'' আর ''দাদা'' বল সব শোধ বোধ ঘরে চল ।&quot;<br>\r\n      &quot;ডোন্ট পরোয়া অল্‌ রাইট্‌ হাউ ডুয়ুডু গুড্‌ নাইট্‌ ।&quot;', 'qu6bSq-tlqc', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 606, '112400649'),
(41, 1, 'images/faskegelo1.gif', 'ফস্‌‌কে গেল', 'ফস্‌‌কে-গেল', 'দেখ্‌  বাবাজি দেখ্‌‌বি নাকি দেখ্‌‌রে খেলা দেখ্‌ চালাকি', 'দেখ্‌  বাবাজি দেখ্‌‌বি নাকি দেখ্‌‌রে খেলা দেখ্‌ চালাকি,<br>\r\nভোজের বাজি ভেল্কি ফাঁকি পড়্‌ পড়্‌ পড়্‌ পড়্‌‌বি পাখি- ধপ্‌ !<br /><br />\r\nলাফ দিয়ে তাই তালটি ঠুকে তাক ক''রে যাই তীর ধনুকে,<br />\r\nছাড়্‌ব সটান উর্ধ্বমুখে হুশ্‌ ক''রে তোর লাগবে বুকে- খপ্‌ ! <br /><br />\r\nগুড়্‌  গুড়্‌ গুড়্‌ গুড়িয়ে হামা খাপ্‌ পেতেছেন গোষ্ঠো মামা<br>\r\nএগিয়ে আছেন বাগিয়ে ধামা এইবার বাণ চিড়িয়া নামা- চট্‌ !<br /><br />\r\nএই  যা ! গেল ফস্কে ফেঁসে- হেঁই মামা তুই ক্ষেপলি শেষে ?<br />\r\nঘ্যাঁচ্‌  ক''রে তোর পাঁজর ঘেঁষে লাগ্‌ল কি বাণ ছট্‌‌কে এসে- ফট্‌ ?<br /><br />\r\n<img src="../images/faskegelo2.gif" width="348" height="212"></font>', 'n0_0BzMnGk8', '2013-03-17 00:00:00', '2014-08-21 16:53:01', 452, '112400121'),
(42, 1, 'images/ahladi.gif', 'আহ্লাদী', 'আহ্লাদী', 'হাসছি মোরা হাসছি দেখ, হাসছি মোরা আহ্লাদী', 'হাসছি মোরা হাসছি দেখ, হাসছি মোরা আহ্লাদী,<br>\r\n      তিনজনেতে জট্‌লা ক''রে ফোক্‌লা হাসির পাল্লা দি ।<br>\r\n      হাসতে হাসতে আসছে দাদা, হাসছি আমি, হাসছে ভাই,<br>\r\n      হাসছি কেন কেউ জানে না, পাচ্ছে হাসি হাসছি তাই ।<br>\r\n      ভাবছি মনে, হাসছি কেন ? থাকব হাসি ত্যাগ ক''রে,<br>\r\n      ভাবতে গিয়ে ফিকফিকিয়ে ফেল্‌ছি হাসি ফ্যাক্‌ ক''রে ।<br>\r\n      পাচ্ছে হাসি চাপতে গিয়ে, পাচ্ছে হাসি চোখ বুজে,<br>\r\n      পাচ্ছে হাসি চিম্‌টি কেটে নাকের ভিতর নোখ্‌ গুঁজে ।<br>\r\n      হাসছি দেখে চাঁদের কলা জোলার মাকু জেলের দাঁড় <br>\r\n      নৌকা ফানুস পিঁপড়ে মানুষ রেলের গাড়ী তেলের ভাঁড় ।<br>\r\n      পড়তে গিয়ে ফেলছি হেসে ''ক খ গ'' আর শ্লেট দেখে-<br>\r\n      উঠ্‌ছে হাসি ভস্‌ভসিয়ে সোডার মতন পেট থেকে ।', '0EV7NfdIKJg', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 452, '112400063'),
(43, 1, 'images/bhoypeyona.png', 'ভয় পেয়ো না', 'ভয়-পেয়ো-না', 'ভয় পেয়ো না, ভয় পেয়ো না, তোমায় আমি মারব না', 'ভয় পেয়ো না, ভয় পেয়ো না, তোমায় আমি মারব না<br>\r\nসত্যি বলছি কুস্তি ক''রে তোমার সঙ্গে পারব না।<br>\r\nমনটা আমার বড্ড নরম, হাড়ে আমার রাগটি নেই,<br>\r\nতোমায় আমি চিবিয়ে খাব এমন আমার সাধ্যি নেই!<br>\r\nমাথায় আমার শিং দেখে ভাই ভয় পেয়েছ কতই না---<br>\r\nজানো না মোর মাথার ব্যারাম, কাউকে আমি গুঁতোই না?<br>\r\nএস এস গর্তে এস, বাস ক''রে যাও চারটি দিন,<br>\r\nআদর ক''রে শিকেয় তুলে রাখব তোমায় রাত্রিদিন।<br>\r\nহাতে আমার মুগুর আছে তাই কি হেথায় থাকবে না?<br>\r\nমুগুর আমার হাল্‌কা এমন মারলে তোমার লাগবে না।<br>\r\nঅভয় দিচ্ছি শুনছ না যে? ধরব নাকি ঠ্যাং দুটা?<br>\r\nবসলে তোমার মুণ্ডু চেপে বুঝবে তখন কাণ্ডটা!<br>\r\nআমি আছি, গিন্নী আছেন, আছেন আমার নয় ছেলে<br>\r\nসবাই মিলে কামড়ে দেব মিথ্যে অমন ভয় পেলে।', '-Nv6-DSb5gc', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 965, '112400191'),
(44, 1, 'images/hatganana.gif', 'হাত গণনা', 'হাত-গণনা', 'ও পাড়ার নন্দ গোঁসাই, আমাদের নন্দ খুড়ো', 'ও পাড়ার নন্দ গোঁসাই, আমাদের নন্দ খুড়ো,<br>\r\nস্বভাবেতে সরল সোজা অমায়িক শান্ত বুড়ো।<br>\r\nছিল না তার অসুখবিসুখ, ছিল যে সে মনের সুখে,<br>\r\nদেখা যেত সদাই তারে হুঁকোহাতে হাস্যমুখে।<br>\r\nহঠাত্‍‌ কি তার খেয়াল হল, চল্‌ল সে তার হাত দেখাতে-<br>\r\nফিরে এল শুকনো সরু, ঠকাঠক্ কাঁপছে দাঁতে!<br>\r\nশুধালে সে কয় না কথা, আকাশেতে রয় সে চেয়ে,<br>\r\nমাঝে মাঝে শিউরে ওঠে, পড়ে জল চক্ষু বেয়ে।<br>\r\nশুনে লোক দৌড়ে এল, ছুটে এলেন বদ্যিমশাই,<br>\r\nসবাই বলে, `কাঁদছ কেন? কি হয়েছে নন্দগোঁসাই?''<br>\r\nখুড়ো বলে, `বলব কি আর, হাতে আমার পষ্ট লেখা<br>\r\nআমার ঘাড়ে আছেন শনি, ফাঁড়ায় ভরা আয়ুর রেখা।<br>\r\nএতদিন যায় নি জানা ফিরছি কত গ্রহের ফেরে-<br>\r\nহঠাত্‍‌ আমার প্রাণটা গেলে তখন আমায় রাখবে কে রে?<br>\r\nষাটটা বছর পার হয়েছি বাপদাদাদের পুণ্যফলে-<br>\r\nওরে তোদের নন্দখুড়ো এবার বুঝি পটোল তোলে।<br>\r\nকবে যে কি ঘটবে বিপদ কিছু হায় যায় না বলা-''<br>\r\nএই ব''লে সে উঠল কেঁদে ছেড়ে ভীষণ উচ্চ গলা।<br>\r\nদেখে এলাম আজ সকালে গিয়ে ওদের পাড়ার মুখো,<br>\r\nবুড়ো আছে নেই কো হাসি, হাতে তার নেই কো হুঁকো।', 'XWRBfkROmAw', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 360, '112400601'),
(45, 1, 'images/bhuturekhela.gif', 'ভুতুড়ে খেলা', 'ভুতুড়ে-খেলা', 'পরশু রাতে পষ্ট চোখে দেখনু বিনা চশমাতে', 'পরশু রাতে পষ্ট চোখে দেখনু বিনা চশমাতে,<br>\r\nপান্তভূতের জ্যান্ত ছানা করছে খেলা জোছ্‌নাতে।<br>\r\nকচ্ছে খেলা মায়ের কোলে হাত-পা নেড়ে উল্লাসে,<br>\r\nআহ্লাদেতে ধুপধুপিয়ে কচ্ছে কেমন হল্লা সে। <br>\r\nশুনতে পেলাম ভুতের মায়ের মুচকি হাসি কট্‌কটে-<br>\r\nদেখছে নেড়ে ঝুন্‌‌টি ধ''রে বাচ্চা কেমন চট্‌পটে। <br>\r\nউঠছে তাদের হাসির হানা কাষ্ঠ সুরে ডাক ছেড়ে,<br>\r\nখ্যাঁশ্ খ্যাঁশানি শব্দে যেন করাত দিয়ে কাঠ চেরে! <br>\r\nযেমন খুশি মারছে ঘুঁষি, দিচ্ছে কষে কানমলা,<br>\r\nআদর ক''রে আছাড় মেরে শূন্যে ঝোলে চ্যাং দোলা। <br>\r\nবলছে আবার, ``আয় রে আমার নোংরামুখো সুঁটকো রে,<br>\r\nদেখ না ফিরে প্যাখ্‌না ধরে হুতোম-হাসি মুখ করে!<br>\r\nওরে আমার বাঁদর-নাচন আদর-গেলা কোঁত্কা রে,<br>\r\nঅন্ধবনের গন্ধ-গোকুল, ওরে আমার হোঁত্কা রে!<br>\r\nওরে আমার বাদলা রোদে জষ্ঠি মাসের বিষ্টি রে,<br>\r\nওরে আমার হামান-ছেঁড়া জষ্ঠিমধুর মিষ্টি রে। <br>\r\nওরে আমার রান্না হাঁড়ির কান্না হাসির ফোড়নদার,<br>\r\nওরে আমার জোছ্‌না হাওয়ার স্বপ্নঘোড়ার চড়নদার।<br>\r\nওরে আমার গোবরাগণেশ ময়দাঠাসা নাদুস্ রে,<br>\r\nছিঁচকাঁদুনে ফোক্‌লা মানিক, ফের যদি তুই কাঁদিস রে-''''<br>\r\nএই না বলে যেই মেরেছে কাদার চাপটি ফট্ ক''রে,<br>\r\nকোথায়-বা কি, ভুতের ফাঁকি- মিলিয়ে গেল চট্ ক''রে!', '-Mg7tPGKn6A', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 409, '112400803'),
(46, 1, '', 'কাঁদুনে', 'কাঁদুনে', 'ছিঁচ্‌কাঁদুনে মিচ্‌‌কে যারা শস্তা কেঁদে নাম কেনে', '<table >\r\n		<tr>\r\n			<td>\r\n				ছিঁচ্‌কাঁদুনে মিচ্‌‌কে যারা \r\n      শস্তা কেঁদে নাম কেনে,<br>\r\n      ঘ্যাঁঙায় শুধু ঘ্যানর ঘ্যানর ঘ্যানঘ্যানে আর প্যানপ্যানে-<br>\r\n      কুঁকিয়ে কাঁদে ক্ষিদের সময়, ফুঁপিয়ে কাঁদে ধমকালে,<br>\r\n      কিম্বা হঠাত্‍‌ লাগলে ব্যথা, কিম্বা ভয়ে চম্‌কালে ;<br>\r\n      অল্পে হাসে অল্পে কাঁদে, কান্না থামায় অল্পেতেই,<br>\r\n      মায়ের আদর দুধের বোতল কিম্বা দিদির গল্পেতেই-<br>\r\n      তারেই বলি মিথ্যে কাঁদন; আসল কান্না শুনবে কে? <br>\r\n      অবাক্ হবে থম্‌কে রবে সেই কাঁদনের গুণ দেখে !<br>\r\n      নন্দঘোষের পাশের বাড়ি বুথ্ সাহেবের বাচ্চাটার <br>\r\n      কান্নাখানা শুনলে বলি কান্না বটে সাচ্চা তার ।<br>\r\n      কাঁদবে না সে যখন তখন, রাখবে কেবল রাগ পুষে,<br>\r\n      কাঁদবে যখন খেয়াল হবে খুন-কাঁদুনে রাক্ষুসে !<br>\r\n\r\n			</td>\r\n			<td>\r\n			<img src="../images/kadune.gif">\r\n			</td>\r\n			<td>\r\n			নাইকো কারণ নাইকো বিচার মাঝরাতে কি ভোরবেলা,<br>\r\n      হঠাৎ‌  শুনি অর্থবিহীন আকাশ-ফাটন জোর গলা ।<br>\r\n			হাঁকড়ে ছোটে কান্না যেমন জোয়ার বেগে নদীর বান,<br>\r\n      বাপ-মা বসেন হতাশ হয়ে শব্দ শুনে বধির কান ।<br>\r\n      বাস রে সে কি লোহার গলা? এক মিনিটও শান্তি নেই?<br>\r\n      কাঁদন ঝরে শ্রাবণ ধারে, ক্ষান্ত দেবার নামটি নেই ! <br>\r\n      ঝুমঝুমি দাও, পুতুল নাচাও, মিষ্টি খাওয়াও একশোবার,<br>\r\n      বাতাস কর, চাপড়ে ধর, ফুটবে নাকো হাস্য তার ।<br>\r\n      কান্নাভরে উল্‌টে পড়ে কান্না ঝরে নাক দিয়ে,<br>\r\n      গিল্‌তে চাহে দালানবাড়ি হাঁ''খানি তার হাঁক্ দিয়ে,<br>\r\n      ভূত-ভাগানো শব্দে লোকে ত্রাহি ত্রাহি ডাক ছাড়ে-<br>\r\n      কান্না শুনে ধন্যি বলি বুথ সাহেবের বাচ্চারে । \r\n			</td>\r\n		</tr>\r\n	</table>', 'BU3G-Xo7OHE', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 378, '112400160'),
(47, 1, '', 'আবোল তাবোল', 'আবোল-তাবোল-১', 'মেঘ মুলুকে ঝাপ্‌সা রাতে', '<table border="0">\r\n  <tr> \r\n    <td valign="top">মেঘ মুলুকে ঝাপ্‌সা রাতে,<br>\r\n      রামধনুকের আব্‌ছায়াতে,<br>\r\n      তাল বেতালে খেয়াল সুরে,<br>\r\n      তান ধরেছি কণ্ঠ পুরে ।<br>\r\n      হেথায় নিষেধ নাই রে দাদা,<br>\r\n      নাই রে বাঁধন নাইরে বাধা ।<br>\r\n      হেথায় রঙিন্‌ আকাশ তলে<br>\r\n      স্বপন দোলা হাওয়ায় দোলে,<br>\r\n      সুরের নেশায় ঝরনা ছোটে,<br>\r\n      আকাশ কুসুম আপনি ফোটে,<br>\r\n      রঙিয়ে আকাশ, রঙিয়ে মন<br>\r\n      চমক জাগে ক্ষণে ক্ষণ !<br>\r\n      আজকে দাদা যাবার আগে<br>\r\n      বল্‌ব যা মোর চিত্তে লাগে-<br>\r\n      নাই বা তাহার অর্থ হোক্‌<br>\r\n      নাইবা বুঝুক বেবাক্‌ লোক ।<br>\r\n      আপনাকে আজ আপন হতে<br>\r\n      ভাসিয়ে দিলাম খেয়াল স্রোতে ।</td>\r\n    <td valign="top"><img src="../images/aboltabol_last.gif" width="179" height="423"></td>\r\n    <td valign="top">ছুট্‌লে কথা থামায় কে ?<br>\r\n      আজকে ঠেকায় আমায় কে ?<br>\r\n      আজকে আমার মনের মাঝে<br>\r\n      ধাঁই ধপাধপ্‌ তব্‌লা বাজে-<br>\r\n      রাম-খটাখট্‌ ঘ্যাচাং ঘ্যাঁচ্‌<br>\r\n      কথায় কাটে কথার প্যাঁচ্‌ ।<br>\r\n      আলোয় ঢাকা অন্ধকার,<br>\r\n      ঘণ্টা বাজে গন্ধে তার ।<br>\r\n      গোপন প্রাণে স্বপন দূত,<br>\r\n      মঞ্চে নাচেন পঞ্চ ভূত !<br>\r\n      হ্যাংলা হাতি চ্যাং-দোলা,<br>\r\n      শূন্যে তাদের ঠ্যাং তোলা ।<br>\r\n      মক্ষি রাণী পক্ষীরাজ-<br>\r\n      দস্যি ছেলে লক্ষী আজ ।<br>\r\n      আদিম কালের চাঁদিম হিম<br>\r\n      তোড়ায় বাঁধা ঘোড়ার ডিম ।<br>\r\n      ঘনিয়ে এল ঘুমের ঘোর,<br>\r\n      গানের পালা সাঙ্গ মোর ।</td>\r\n  </tr>\r\n</table>', 'Rwj4WvskU9c', '2013-03-18 00:00:00', '2014-08-21 16:56:23', 978, '112400042'),
(48, 1, '', 'দাঁড়ের কবিতা', 'দাঁড়ের-কবিতা', 'চুপ কর, শোন্‌ শোন্‌, বেয়াকুল হোস্‌‌নে', '<table border="0">\r\n  <tr> \r\n    <td valign="top"><br>\r\n        <img src="../images/darerkabita.jpg" width="196" height="450">\r\n      </td>\r\n    <td valign="bottom">\r\n       চুপ কর, শোন্‌ শোন্‌, বেয়াকুল হোস্‌‌নে-<br>\r\n      ঠেকে গেছি বাপ্‌‌রে কি ভয়ানক প্রশ্নে !<br>\r\n      ভেবে ভেবে লিখে লিখে বসে বসে দাঁড়েতে<br>\r\n      ঝিম্‌ঝিম্‌ টন্‌টন্‌ ব্যথা করে হাড়েতে ।<br>\r\n      এক ছিল দাঁড়িমাঝি- দাড়ি তার মস্ত,<br>\r\n      দাড়ি দিয়ে দাঁড়ি তার দাঁড়ে খালি ঘষ্‌ত ।<br>\r\n      সেই দাঁড়ে একদিন দাঁড়কাক দাঁড়াল,<br>\r\n      কাঁকড়ার দাঁড়া দিয়ে দাঁড়ি তারে তাড়াল ।<br>\r\n      কাক বলে রেগেমেগে, &quot;বাড়াবাড়ি ঐ ত !<br>\r\n      না দাঁড়াই দাঁড়ে তবু দাঁড়কাক হই ত ?<br>\r\n      ভারি তোর দাঁড়িগিরি, শোন্‌ বলি তবে রে-<br>\r\n      দাঁড় বিনা তুই ব্যাটা দাঁড়ি হোস্‌ কবে রে ?<br>\r\n      পাখা হলে পাখি হয় ব্যাকরণ বিশেষে-<br>\r\n      কাঁকড়ার ''দাঁড়া'' আছে, দাঁড়ি নয় কিসে সে ?<br>\r\n      দ্বারে বসে দারোয়ান, তারে যদি ''দ্বারী'' কয়,<br>\r\n      দাঁড়ে-বসা যত পাখি সব তবে দাঁড়ি হয় !<br>\r\n      দূর দূর ! ছাই দাঁড়ি ! দাড়ি নিয়ে পাড়ি দে !&quot;<br>\r\n      দাঁড়ি বলে, &quot;ব্যাস্‌ ব্যাস্‌ ! ঐখেনে দাঁড়ি দে ।&quot;</td>\r\n  </tr>\r\n</table>', 'B2k0vZdUVTw', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 334, '112400338');
INSERT INTO `articles` (`id`, `category_id`, `top_image`, `title`, `article_slug`, `first_line`, `body`, `audio`, `added_on`, `modified_on`, `views`, `sc_track_id`) VALUES
(49, 1, '', 'পাকাপাকি', 'পাকাপাকি', 'আম পাকে বৈশাখে, কুল পাকে ফাগুনে', 'আম পাকে বৈশাখে, কুল পাকে ফাগুনে,<br>\r\nকাঁচা ইঁট পাকা হয় পোড়ালে তা আগুনে ।<br>\r\nরোদে জলে টিকে রঙ পাকা কই তাহারে ।<br>\r\nফলারটি পাকা হয় লুচি দই আহারে ।<br>\r\nহাত পাকে লিখে লিখে চুল পাকে বয়সে,<br>\r\nজ্যাঠামিতে পাকা ছেলে বেশি কথা কয় সে ।<br>\r\nলোকে বলে কাঁঠাল সে পাকে নাকি কিলিয়ে ?<br>\r\nবুদ্ধি পাকিয়ে তোলে লেখাপড়া গিলিয়ে !<br>\r\nকান পাকে ফোড়া পাকে, পেকে করে টন্‌টন্‌-<br>\r\nকথা যার পাকা নয় কাজে তার ঠন্‌ঠন্‌ ।<br>\r\nরাঁধুনী বসিয়ে পাকে পাক দেয় হাঁড়িতে,<br>\r\nসজোরে পাকালে চোখ ছেলে কাঁদে বাড়িতে ।<br>\r\nপাকায়ে পাকায়ে দড়ি টান হয়ে থাকে সে ।<br>\r\nদুহাতে পাকালে গোঁফ তবু নাহি পাকে সে ।।', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 327, ''),
(50, 1, '', 'অসম্ভব নয় !', 'অসম্ভব-নয়-!', 'এক যে ছিল সাহেব, তাহার', '<table border="0">\r\n  <tr> \r\n    <td valign="top"><img src="../images/asambhabnoy.jpg" width="305" height="245"></td>\r\n    <td valign="top">এক যে ছিল সাহেব, তাহার<br>\r\n      গুণের মধ্যে নাকের বাহার ।<br>\r\n      তার যে গাধা বাহন, সেটা<br>\r\n      যেমন পেটুক তেমনি ঢ্যাঁটা ।<br>\r\n      ডাইনে বললে যায় সে বামে<br>\r\n      তিন পা যেতে দুবার থামে ।<br>\r\n      চল্‌‌তে চল্‌‌তে থেকে থেকে<br>\r\n      খানায় খন্দে পড়ে বেঁকে ।<br>\r\n      ব্যাপার দেখে এম্নিতরো<br>\r\n      সাহেব বললে, &quot;সবুর করো-<br>\r\n      মামদোবাজি আমার কাছে ?<br>\r\n      এ রোগেরও ওষুধ আছে ।&quot;<br>\r\n      এই না ব''লে ভীষণ ক্ষেপে<br>\r\n      গাধার পিঠে বস্‌ল চেপে<br>\r\n      মুলোর ঝুঁটি ঝুলিয়ে নাকে ।<br>\r\n      আর কি গাধা ঝিমিয়ে থাকে ?<br>\r\n      মুলোর গন্ধে টগবগিয়ে<br>\r\n      দৌড়ে চলে লম্ফ দিয়ে-<br>\r\n      যতই চলে ধরব ব''লে<br>\r\n      ততই মুলো এগিয়ে চলে!<br>\r\n      খাবার লোভে উদাস প্রাণে<br>\r\n      কেবল ছোটে মুলোর টানে-<br>\r\n      ডাইনে বাঁয়ে মুলোর তালে<br>\r\n      ফেরেন গাধা নাকের চালে ।</td>\r\n  </tr>\r\n</table>', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 441, ''),
(51, 1, '', 'বেশ বলেছ', 'বেশ-বলেছ', 'এসব কথা শুন্‌‌লে তোদের লাগবে মনে ধাঁধা', 'এসব কথা শুন্‌‌লে তোদের লাগবে মনে ধাঁধা,<br>\r\nকেউ বা বুঝে পুরোপুরি, কেউ বা বুঝে আধা । <br>\r\nকারে বা কই কিসের কথা কই যে দফে দফে,<br>\r\nগাছের ''পরে কাঁঠাল দেখে তেল মেখ না গোঁফে ।<br>\r\nএকটি একটি কথায় যেন সদ্য দাগে কামান,<br>\r\nমন-বসনের ময়লা ধুতে তত্ত্বকথাই সমান ।<br>\r\nবেশ বলেছ, ঢের বলেছ, ঐখেনে দাও দাঁড়ি,<br>\r\nহাঁটের মাঝে ভাঙবে কেন বিদ্যে বোঝাই হাঁড়ি !', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 358, ''),
(52, 1, '', 'খাই খাই', 'খাই-খাই', 'খাই খাই কর কেন, এস বস আহারে', '<table border="0">\r\n  <tr>\r\n<td valign="top"><img src="images/khaikhai.jpg"></td>\r\n    <td valign="top" style=''padding-right: 10px; border-right: 1px solid #ccc;''>খাই খাই কর কেন, এস বস আহারে-<br>\r\n      খাওয়াব আজব খাওয়া, ভোজ কয় যাহারে ।<br>\r\n      যত কিছু খাওয়া লেখে বাঙালির ভাষাতে,<br>\r\n      জড় করে আনি সব, -থাক সেই আশাতে ।<br>\r\n      ডাল ভাত তরকারি ফলমূল শস্য,<br>\r\n      আমিষ ও নিরামিষ, চর্ব্য ও চোষ্য,<br>\r\n      রুটি লুচি, ভাজাভুজি, টক ঝাল মিষ্টি,<br>\r\n      ময়রা ও পাচকের যত কিছু সৃষ্টি,<br>\r\n      আর যাহা খায় লোকে স্বদেশে ও বিদেশে-<br>\r\n      খুঁজে পেতে আনি খেতে- নয় বড় সিধে সে !<br>\r\n      জল খায়, দুধ খায়, খায় যত পানীয়,<br>\r\n      জ্যাঠাছেলে বিড়ি খায়, কান ধরে টানিও ।<br>\r\n      ফল বিনা চিঁড়ে দৈ, ফলাহার হয় তা,<br>\r\n      জলযোগে জল খাওয়া শুধু জল নয় তা ।<br>\r\n      ব্যাঙ খায় ফরাসিরা (খেতে নয় মন্দ) ,<br>\r\n      বার্মার ''ঙাম্পি''তে বাপরে কি গন্ধ !<br>\r\n      মাদ্রাজী ঝাল খেলে জ্বলে যায় কণ্ঠ,<br>\r\n      জাপানেতে খায় নাকি ফড়িঙের ঘণ্ট !<br>\r\n      আরশুলা মুখে দিয়ে সুখে খায় চীনারা,<br>\r\n      কত কি যে খায় লোকে নাহি তার কিনারা ।<br>\r\n      দেখে শুনে চেয়ে খাও, যেটা চায় রসনা ।<br>\r\n      তা না হলে কলা খাও, চটো কেন বস না-<br>\r\n      সবে হল খাওয়া শুরু, শোন শোন আরো খায়-<br>\r\n      সুদ খায় মহাজনে, ঘুষ খায় দারোগায় ।<br>\r\n      বাবু যান হাওয়া খেতে চড়ে জুড়ি-গাড়িতে,<br>\r\n      খাসা দেখ ''খাপ্‌ খায়'' চাপকানে দাড়িতে ।<br>\r\n      তেলে জলে ''মিশ খায়'', শুনেছ তা কেও কি ?<br>\r\n      যুদ্ধে যে গুলি খায় গুলিখোর সেও কি ?<br>\r\n      ডিঙি চড়ে স্রোতে প''ড়ে পাক খায় জেলেরা,<br>\r\n      ভয় খেয়ে খাবি খায় পাঠশালে ছেলেরা ;<br>\r\n</td>\r\n    <td valign="top" style=''padding-left: 10px;''>বেত খেয়ে কাঁদে কেউ, কেউ শুধু \r\n      গালি খায়,<br>\r\n      কেউ খেয়ে থতমত- তাও লিখি তালিকায় ।<br>\r\n      ভিখারিটা তাড়া খায়, ভিখ্‌ নাহি পায় রে-<br>\r\n      ''দিন আনে দিন খায়'' কত লোক হায় রে ।<br>\r\n      হোঁচটের চোট খেয়ে খোকা ধরে কান্না,<br>\r\n      মা বলেন চুমু খেয়ে, ''সেরে গেছে আর না ।''<br>\r\n      ধমক বকুনি খেয়ে নয় যারা বাধ্য,<br>\r\n      কিলচড় লাথি ঘুঁষি হয় তার খাদ্য ।<br>\r\n      জুতো খায়, গুঁতো খায়, চাবুক যে খায় রে,<br>\r\n      তবু যদি নুন খায়, সেও গুণ গায় রে ।<br>\r\n      গরমে বাতাস খাই, শীতে খাই হিম্‌সিম্‌,<br>\r\n      পিছলে আছাড় খেয়ে মাথা করে ঝিম্‌ঝিম্‌ ।<br>\r\n      কত যে মোচড় খায় বেহালার কানটা, <br>\r\n      কানমলা খেলে তবে খোলে তার গানটা ।<br>\r\n      টোল খায় ঘটি বাটি, দোল খায় খোকারা,<br>\r\n      ঘাবড়িয়ে ঘোল খায় পদে পদে বোকারা ।<br>\r\n      আকাশেতে কাৎ হ''য়ে গোঁৎ খায় ঘুড়িটা,<br>\r\n      পালোয়ান খায় দেখ ডিগবাজি কুড়িটা ।<br>\r\n      ফুটবলে ঠেলা খাই, ভিড়ে খাই ধাক্কা,<br>\r\n      কাশিতে প্রসাদ খেয়ে সাধু হই পাক্কা !<br>\r\n      কথা শোন, মাথা খাও, রোদ্দুরে যেও না-<br>\r\n      আর যাহা খাও বাপু বিষমটি খেও না ।<br>\r\n      ''ফেল্‌'' ক''রে মুখ খেয়ে কেঁদেছিলে সেবারে,<br>\r\n      আদা-নুন খেয়ে লাগো, পাশ কর এবারে ।<br>\r\n      ভ্যাবাচ্যাকা খেও নাকো, যেয়ো নাকো ভড়কে,<br>\r\n      খাওয়াদাওয়া শেষ হলে বসে খাও খড়্‌কে ।<br>\r\n      এত খেয়ে তবু যদি নাহি ওঠে মনটা-<br>\r\n      খাও তবে কচু পোড়া, খাও তবে ঘণ্টা ।<br>\r\n      </td>\r\n  </tr>\r\n</table>', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 1178, ''),
(53, 1, '', 'বিষম চিন্তা', 'বিষম-চিন্তা', 'মাথায় কত প্রশ্ন আসে, দিচ্ছে না কেউ জবাব তার', 'মাথায় কত প্রশ্ন আসে, দিচ্ছে না কেউ জবাব তার&mdash;<br>\r\nসবাই বলে, &quot;মিথ্যে বাজে বকিস্‌নে আর খবরদার !&quot;<br>\r\nঅমন ধারা ধমক দিলে কেমন করে শিখব সব ?<br>\r\nবলবে সবাই, &quot;মুখ্যু ছেলে&quot;, বলবে আমায় &quot;গো গর্ধভ !&quot;<br>\r\nকেউ কি জানে দিনের বেলায় কোথায় পালায় ঘুমের ঘোর ?<br>\r\nবর্ষা হলেই ব্যাঙের গলায় কোত্থেকে হয় এমন জোর ?<br>\r\nগাধার কেন শিং থাকেনা, হাতির কেন পালক নেই ?<br>\r\nগরম তেলে ফোড়ন দিলে লাফায় কেন তাধেই ধেই ?<br>\r\nসোডার বোতল খুললে কেন ফঁসফঁসিয়ে রাগ করে ?<br>\r\nকেমন করে রাখবে টিকি মাথায় যাদের টাক পড়ে ?<br>\r\nভূত যদি না থাকবে তবে কোত্থেকে হয় ভূতের ভয় ?<br>\r\nমাথায় যাদের গোল বেধেছে তাদের কেন &quot;পাগোল&quot; কয় ?<br>\r\nকতই ভাবি এসব কথা, জবাব দেবার মানুষ কই ?<br>\r\nবয়স হলে কেতাব খুলে জানতে পাব সমস্তই ।', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 455, ''),
(54, 1, '', 'হরিষে বিষাদ', 'হরিষে-বিষাদ', 'দেখছে খোকা পঞ্জিকাতে এই বছরে কখন কবে', '<table border="0">\r\n<tr> \r\n<td>দেখছে খোকা পঞ্জিকাতে এই বছরে কখন কবে<br>\r\nছুটির কত খবর লেখে, কিসের ছুটি ক''দিন হবে ।<br>\r\nঈদ্‌ মহরম দোল্‌ দেওয়ালি বড়দিন আর বর্ষশেষে-<br>\r\nভাবছে যত, ফুল্লমুখে ফুর্তিভরে ফেলছে হেসে ।<br>\r\nএমনকালে নীল আকাশে হঠাৎ-খ্যাপা মেঘের মত,<br>\r\nউথ্‌‌লে ছোটে কান্নাধারা ডুবিয়ে তাহার হর্ষ যত ।<br>\r\n&quot;কি হল তোর ?&quot; সবাই বলে, &quot;কলমটা কি বিধল হাতে ?<br>\r\nজিবে কি তোর দাঁত বসালি ? কামড়াল কি ছারপোকাতে ?&quot;<br>\r\nপ্রশ্ন শুনে কান্না চড়ে অশ্রু ঝরে দ্বিগুণ বেগে,<br>\r\nপঞ্জিকাটি আছড়ে ফেলে বললে কেঁদে আগুন রেগে ;<br>\r\n&quot;ঈদ্‌ পড়েছে জষ্ঠি মাসে গ্রীষ্মে যখন থাকেই ছুটি,<br>\r\nবর্ষাশেষ আর দোল্‌ ত দেখি রোব্‌বারেতেই পড়ল দুটি ।<br>\r\nদিনগুলোকে করলে মাটি মিথ্যে পাজি পঞ্জিকাতে-<br>\r\nমুখ ধোব না ভাত খাব না ঘুম যাব না আজকে রাতে ।&quot;</td>\r\n<td align="center"><img src="../images/harishebishad.jpg" width="168" height="200"></td>\r\n</tr>\r\n</table>', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 352, ''),
(55, 1, '', 'পরিবেষণ', 'পরিবেষণ', 'পরি''পূর্বক ''বিষ''ধাতু তাহে ''অনট্‌'' ব''সে', '<table border="0">\r\n<tr> \r\n<td valign="top" style=''border-right:1px solid #ccc; padding-right: 10px;''>পরি''পূর্বক ''বিষ''ধাতু তাহে ''অনট্‌'' ব''সে<br>\r\nতবে ঘটায় পরিবেষণ, লেখে অমরকোষে ।<br>\r\n-অর্থাৎ ভোজের ভাণ্ড হাতে লয়ে মেলা<br>\r\nডেলা ডেলা ভাগ করি পাতে পাতে ফেলা ।<br>\r\nএই দিকে এস তবে লয়ে ভোজভাণ্ড<br>\r\nসমুখে চাহিয়া দেখ কি ভীষণ কাণ্ড ।<br>\r\nকেহ কহে &quot;দৈ আন&quot; কেহ হাঁকে &quot;লুচি&quot;<br>\r\nকেহ কাঁদে শূন্য মুখে পাতখানি মুছি ।<br>\r\nহোথা দেখি দুই প্রভু পাত্র লয়ে হাতে<br>\r\n<img src="../images/paribeshan.jpg" width="200" height="166"></td>\r\n<td valign="top" style=''padding-left: 10px;''>হাতাহাতি গুঁতাগুঁতি দ্বন্দরণে মাতে ।<br>\r\nকেবা শোনে কার কথা সকলেই কর্তা-<br>\r\nঅনাহারে কতধারে হল প্রাণ হত্যা ।<br>\r\nকোন প্রভু হস্তিদেহ ভুঁড়িখানা ভারি<br>\r\nঊর্ধ্ব হতে থপ্‌ করি খাদ্য দেন ছাড়ি ।<br>\r\nকোন চাচা অন্ধপ্রায় (''মাইনাস কুড়ি'')<br>\r\nছড়ায় ছোলার ডাল পথঘাট জুড়ি ।<br>\r\nমাতব্বর বৃদ্ধ যায় মুদি চক্ষু দুটি,<br>\r\n&quot;কারো কিছু চাই&quot; বলি তড়বড় ছুটি-<br>\r\nবীরোচিত ধীর পদে এসে দেখি ত্রস্তে-<br>\r\nওই দিকে খালি পাত, চল হাঁড়ি হস্তে ।<br>\r\nতবে দেখ, খাদ্য দিতে অতিথির থালে<br>\r\nদৈবাৎ না ঢোকে কভু যেন নিজ গালে ।<br>\r\nছুটোনাকো ওরকম মিছে খালি হাতে<br>\r\nদিও না মাছের মুড়া নিরামিষ পাতে ।<br>\r\nঅযথা আক্রোশে কিবা অন্যায় আদরে<br>\r\nঢেলো না অম্বল কারো নূতন চাদরে ।<br>\r\nবোকাবৎ দন্তপাটি করিয়া বাহির<br>\r\nকরোনাকো অকারণ কৃতিত্ব জাহির ।</td>\r\n</tr>\r\n</table>', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 224, ''),
(56, 1, '', 'জালা-কুঁজো সংবাদ', 'জালা-কুঁজো-সংবাদ', 'পেটমোটা জালা কয়, "হেসে আমি মরিরে"', 'পেটমোটা জালা কয়, &quot;হেসে আমি মরিরে<br>\r\nকুঁজো তোর হেন গলা এতটুকু শরীরে !&quot;<br>\r\nকুঁজো কয়, &quot;কথা কস্‌ আপনাকে না চিনে,<br>\r\nভুঁড়িখানা দেখে তোর কেঁদে আর বাঁচিনে ।&quot;<br>\r\nজালা কয়, &quot;সাগরের মাপে গড়া বপুখান,<br>\r\nডুবুরিরা কত তোলে তবু জল অফুরান ।&quot;<br>\r\nকুঁজো কয়, &quot;ভালো কথা ! তবে যদি দৈবে,<br>\r\nভুঁড়ি যায় ভেস্‌‌তিয়ে, জল কোথা রইবে ?&quot;<br>\r\n&quot;নিজ কথা ভুলে যাস্‌ ?&quot; জালা কয় গর্জে,<br>\r\n&quot;ঘাড়ে ধরে হেঁট ক''রে জল নেয় তোর যে !&quot;<br>\r\nকুঁজো কয়, &quot;নিজ পায়ে তবু খাড়া রই তো-<br>\r\nবিঁড়ে বিনা কুপোকাৎ, তেজ তোর ঐতো !&quot;', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 265, ''),
(57, 1, '', 'আড়ি', 'আড়ি', 'কিসে কিসে ভাব নেই ? ভক্ষক ও ভক্ষ্যে', 'কিসে কিসে ভাব নেই ? ভক্ষক ও ভক্ষ্যে-<br>\r\nবাঘে ছাগে মিল হলে আর নেই রক্ষে ।<br>\r\nশেয়ালের সাড়া পেলে কুকুরেরা তৈরি,<br>\r\nসাপে আর নেউলে ত চিরকাল বৈরী !</p>\r\nআদা আর কাঁচকলা মেলে কোনদিন্‌ সে ?<br>\r\nকোকিলের ডাক শুনে কাক জ্বলে হিংসেয় ।<br>\r\nতেলে দেওয়া বেগুনের ঝগড়াটা দেখনি ?<br>\r\nছ্যাঁক্‌ ছ্যাঁক্‌ রাগ যেন খেতে আসে এখনি ।<br>\r\nতার চেয়ে বেশি আড়ি আমি পারি কহিতে-<br>\r\nতোমাদের কারো কারো কেতাবের সহিতে ।', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 681, ''),
(58, 1, 'images/nacherbatik.jpg', 'নাচের বাতিক', 'নাচের-বাতিক', 'বয়স হল অষ্টাআশি, চিম্‌‌সে গায়ে ঠুন্‌‌কো হাড়', '<table border="0">\r\n  <tr> \r\n    <td valign="top" style="padding-right: 10px; border-right: 1px solid #ccc;">\r\n      বয়স হল অষ্টাআশি, চিম্‌‌সে গায়ে ঠুন্‌‌কো হাড়,<br>\r\n        নাচ্‌‌ছে বুড়ো উল্টোমাথায়- ভাঙলে বুঝি মুণ্ডু ঘাড় !<br>\r\n        হেঁইয়ো ব''লে হাত পা ছেড়ে পড়ছে তেড়ে চিৎপটাং,<br>\r\n        উঠ্‌‌ছে আবার ঝট্‌পটিয়ে এক্কেবারে পিঠ সটান্‌ ।<br>\r\n        বুঝিয়ে বলি, &quot;বৃদ্ধ তুমি এই বয়েসে করছো কি ?<br>\r\n        খাও না খানিক মশ্‌লা গুলে হুঁকোর জল আর হর্‌তকী ।<br>\r\n        ঠাণ্ডা হবে মাথার আগুন, শান্ত হবে ছট্‌ফটি-&quot;<br>\r\n        বৃদ্ধ বলে, &quot;থাম না বাপু, সব তাতে তোর পট্‌পটি !<br>\r\n        ঢের খেয়েছি মশ্‌লা পাঁচন, ঢের মেখেছি চর্বি তেল ;<br>\r\n        তুই ভেবেছিস আমায় এখন চাল্‌ মেরে তুই করবি ফেল ?&quot;<br>\r\n        এই না ব''লে ডাইনে বাঁয়ে লম্ফ দিয়ে হুঁশ ক''রে<br>\r\n        হঠাৎ খেয়ে উল্টোবাজি ফেল্‌‌লে আমায় ''পুশ'' ক''রে ।<br>\r\n        </font></p></td>\r\n        \r\n    <td valign="top" style="padding-left: 10px;">&quot;নাচ্‌‌লে অমন উল্টো রকম,&quot; আবার \r\n        বলি বুঝিয়ে তায়,<br>\r\n        &quot;রক্তগুলো হুড়্‌হুড়িয়ে মগজ পানে উজিয়ে যায় ।&quot;<br>\r\n        বললে বুড়ো, &quot;কিন্তু বাবা, আসল কথা সহজ এই-<br>\r\n        ঢের দেখেছি পরখ করে কোথাও আমার মগজ নেই ।<br>\r\n        তাইতো আমার হয় না কিছু- মাথায় যে সব ফক্কিফাঁক-<br>\r\n        যতটা নাচি উল্টো নাচন, যতই না খাই চর্কিপাক ।&quot;<br>\r\n        বলতে গেলাম, &quot;তাও কি হয়&quot;- অম্নি হঠাৎ ঠাং নেড়ে<br>\r\n        আবার বুড়ো হুড়মুড়িয়ে ফেললে আমায় ল্যাং মেরে ।<br>\r\n        ভাবছি সবে মারব ঘুঁষি এবার বুড়োর রগ্‌ ঘেঁষে,<br>\r\n        বললে বুড়ো, &quot;করব কি বল্‌ ? এ সব করায় অভ্যেসে ।<br>\r\n        ছিলাম যখন রেল-দারোগা চড়্‌তে হত ট্রেইনেতে<br>\r\n        চলতে গিয়ে ট্রেনগুলো সব পড়ত প্রায়ই ড্রেইনেতে ।<br>\r\n        তুব্‌ড়ে যেত রেলের গাড়ি লাগত গুঁতা চাক্কাতে,<br>\r\n        ছিট্‌কে যেতাম যখন তখন হঠাৎ এক এক ধাক্কাতে ।<br>\r\n        নিত্যি ঘুমাই এক চোখে তাই, নড়লে গাড়ি- অম্নি ''বাপ্‌-<br>\r\n        এম্‌নি ক''রে ডিগবাজিতে এক্কেবারে শূন্য লাফ ।<br>\r\n        তাইতে হল নাচের নেশা, হঠাৎ হঠাৎ নাচন পায়,<br>\r\n        বসতে শুতে আপ্‌নি ভুলে ডিগ্‌বাজি খাই আচম্‌কায় !<br>\r\n        নাচতে গিয়ে দৈবে যদি ঠাং লাগে তো পাঁজরাতে,<br>\r\n        তাই ব''লে কি চটতে হবে ? কিম্বা রাগে গজ্‌রাতে ?&quot; <br>\r\n        আমিও বলি, &quot;ঘাট হয়েছে, তোমার খুরে দণ্ডবৎ !<br>\r\n        লাফাও তুমি যেমন খুশি, আমরা দেখি অন্য পথ ।&quot;\r\n      </td>\r\n  </tr>\r\n</table>', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 391, ''),
(59, 1, '', 'অবুঝ', 'অবুঝ', 'চুপ করে থাক্‌, তর্ক করার বদ্‌অভ্যাসটি ভাল না', '<table border="0">\r\n  <tr> \r\n    <td colspan="2"><h1 align="center"><font face="Bangla">&nbsp;</font></h1></td>\r\n    <td rowspan="2" valign="top"><img src="../images/abujh2.jpg" width="154" height="202"></td>\r\n  </tr>\r\n  <tr> \r\n    <td valign="bottom"><img src="../images/abujh1.jpg" width="145" height="307"></td>\r\n    <td valign="top">চুপ করে থাক্‌, তর্ক করার বদ্‌অভ্যাসটি ভাল \r\n      না,<br>\r\n      এক্কেবারেই হয় না ওতে বুদ্ধিশক্তির চালনা ।<br>\r\n      দেখ্‌ ত দেখি আজও আমার মনের তেজটি নেভেনি-<br>\r\n      এইবার শোন্‌ বলছি এখন- কি বলছিলেম ভেবেনি !<br>\r\n      &nbsp;বলছিলেম কি, আমি একটা বই লিখেছি কবিতার,<br>\r\n      উঁচু রকম পদ্যে লেখা আগাগোড়াই সবি তার ।<br>\r\n      তাইতে আছে &quot;দশমুখে চায়, হজম করে দশোদর,<br>\r\n      শ্মশানঘাটে শষ্পানি খায় শশব্যস্ত শশধর ।&quot;<br>\r\n      এই কথাটার অর্থ যে কি, ভাবছে না কেউ মোটেও-<br>\r\n      বুঝছে না কেউ লাভ হবে কি, অর্থ যদি জোটেও ।<br>\r\n      এরই মধ্যে হাই তুলিস্‌ যে ? পুঁতে ফেল্‌ব এখনি,<br>\r\n      ঘুঘু দেখেই নাচতে শুরু, ফাঁদ ত বাবা দেখনি !<br>\r\n      কি বললি তুই ? সাতান্নবার শুনেছিস্‌ ঐ কথাটা ?<br>\r\n      এমন মিথ্যে কইতে পারিস্‌ লক্ষ্মীছাড়া বখাটা !<br>\r\n      আমার সঙ্গে পাল্লা দিয়ে সাধ্যি নেই কো পেরোবার<br>\r\n      হিসেব দেব, বলেছি এই চোদ্দবার কি তেরোবার ।<br>\r\n      সাতান্ন তুই গুনতে পারিস্‌ ? মিথ্যেবাদি গুনে যা-<br>\r\n      ও শ্যামাদাস ! পালাস্‌ কেন ? রাগ করিনি, শুনে যা ।<br></td>\r\n  </tr>\r\n</table>', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 371, ''),
(60, 1, '', 'কাজের লোক', 'কাজের-লোক', 'বাঃ- আমার নাম ''বাঃ''', '<table border="0">\r\n  <tr> \r\n    <td width="18%" valign="top">প্রথম ।</td>\r\n    <td width="82%">বাঃ- আমার নাম ''বাঃ'',<br>\r\n      বসে থাকি তোফা তুলে পায়ের উপর পা !<br>\r\n      লেখাপড়ার ধার ধারিনে, বছর ভরে ছুটি,<br>\r\n      হেসে খেলে আরাম ক''রে দুশো মজা লুটি ।<br>\r\n      কারে কবে কেয়ার করি, কিসের করি ডর ?<br>\r\n      কাজের নামে কম্প দিয়ে গায়ে আসে জ্বর ।<br>\r\n      গাধার মত খাটিস্‌ তোরা মুখটা করে চুন-<br>\r\n      আহাম্মুকি কাণ্ড দেখে হেসেই আমি খুন ।</td>\r\n  </tr>\r\n  <tr> \r\n    <td valign="top">সকলে ।</td>\r\n    <td>আস্ত একটি গাধা তুমি স্পষ্ট গেল দেখা,<br>\r\n      হাস্‌ছ যত, কান্না তত কপালেতে লেখা ।</td>\r\n  </tr>\r\n  <tr align="center"> \r\n    <td colspan="2" valign="top"><img src="../images/kajerlok1.jpg" width="438" height="120"></td>\r\n  </tr>\r\n  <tr> \r\n    <td valign="top">দ্বিতীয় ।</td>\r\n    <td>''যদি'' বলে ডাকে আমায় নামটি আমার ''যদি''-<br>\r\n      আশায় আশায় বসে থাকি হেলান দিয়ে গদি ।<br>\r\n      সব কাজেতে থাকত যদি খেলার মত মজা,<br>\r\n      লেখাপড়া হত যদি জলের মত সোজা-<br>\r\n      স্যান্ডো সমান ষণ্ডা হতাম যদি গায়ের জোরে,<br>\r\n      প্রশংসাতে আকাশ পাতাল যদি যেত ভরে-<br>\r\n      উঠে পড়ে লেগে যেতাম বাজে তর্ক ফেলে ।<br>\r\n      করতে পারি সবি- যদি সহজ উপায় মেলে ।</td>\r\n  </tr>\r\n  <tr> \r\n    <td valign="top">সকলে ।</td>\r\n    <td>হাতের কাছে সুযোগ, তবু ''যদি''র আশায় বসে<br>\r\n      নিজের মাথা খাচ্ছ বাপু নিজের বুদ্ধি দোষে ।</td>\r\n  </tr>\r\n  <tr align="center" valign="top"> \r\n    <td colspan="2"><img src="../images/kajerlok2.jpg" width="461" height="120"></td>\r\n  </tr>\r\n  <tr valign="top"> \r\n    <td>তৃতীয় ।</td>\r\n    <td>আমার নাম ''বটে'' ! আমি সদাই আছি চটে-<br>\r\n      কট্‌মটিয়ে তাকাই যখন, সবাই পালায় ছুটে ।<br>\r\n      চশমা পরে বিচার ক''রে, চিরে দেখাই চুল-<br>\r\n      উঠ্‌তে বস্‌তে কচ্ছে সবাই হাজার গণ্ডা ভুল ।<br>\r\n      আমার চোখে ধুলো দেবে সাধ্যি আছে কার ?<br>\r\n      ধমক শুনে ভূতের বাবা হচ্ছে পগার পার !<br>\r\n      হাস্‌ছ ? বটে ! ভাবছ বুঝি মস্ত তুমি লোক,<br>\r\n      একটি আমার ভেংচি খেলে উল্টে যাবে চোখ ।</td>\r\n  </tr>\r\n  <tr valign="top"> \r\n    <td>সকলে ।</td>\r\n    <td>দিচ্ছ গালি, লোকের তাতে কিবা এল গেল ?<br>\r\n      আকাশেতে থুতু ছুঁড়ে- নিজের গায়েই ফেল ।</td>\r\n  </tr>\r\n  <tr align="center" valign="top"> \r\n    <td colspan="2"><img src="../images/kajerlok3.jpg" width="337" height="120"></td>\r\n  </tr>\r\n  <tr valign="top"> \r\n    <td>চতুর্থ ।</td>\r\n    <td>আমার নাম ''কিন্তু'', আমায় ''কিন্তু'' বলে ডাকে,<br>\r\n      সকল কাজে একটা কিছু গলদ লেগে থাকে ।<br>\r\n      দশটা কাজে লাগি কিন্তু আটটা করি মাটি,<br>\r\n      ষোল আনা কথায় কিন্তু সিকি মাত্র খাঁটি ।<br>\r\n      লম্ফ ঝম্ফ বহুৎ কিন্তু কাজের নাইকো ছিরি-<br>\r\n      ফোঁস্‌ ক''রে যাই তেড়ে- আবার ল্যাজ গুটিয়ে ফিরি ।<br>\r\n      পাঁচটা জিনিস গড়্‌তে গেলে, দশটা ভেঙে চুর-<br>\r\n      বল্‌ দেখি ভাই কেমন আমি সাবাস বাহাদূর !</td>\r\n  </tr>\r\n  <tr valign="top"> \r\n    <td>সকলে ।</td>\r\n    <td>উচিত তোমায় বেঁধে রাখা নাকে দিয়ে দড়ি,<br>\r\n      বেগারখাটা পণ্ডকাজের মূল্য কানাকড়ি ।</td>\r\n  </tr>\r\n  <tr align="center" valign="top"> \r\n    <td colspan="2"><img src="../images/kajerlok4.jpg" width="375" height="120"></td>\r\n  </tr>\r\n  <tr valign="top"> \r\n    <td>পঞ্চম ।</td>\r\n    <td>আমার নাম ''তবু'', তোমরা কেউ কি আমায় চেনো ?<br>\r\n      দেখ্‌তে ছোট তবু আমার সাহস আছে জেনো ।<br>\r\n      এতটুকু মানুষ তবু দ্বিধা নাইকো মনে,<br>\r\n      যে কাজেতেই লাগি আমি খাটি প্রাণপণে ।<br>\r\n      এমনি আমার জেদ, যখন অঙ্ক নিয়ে বসি,<br>\r\n      একুশ বারে না হয় যদি, বাইশ বারে কষি ।<br>\r\n      হাজার আসুক বাধা তবু উৎসাহ না কমে,<br>\r\n      হাজার লোকে চোখ রাঙালে তবু না যাই দ''মে ।</td>\r\n  </tr>\r\n  <tr valign="top"> \r\n    <td>সকলে ।</td>\r\n    <td>নিষ্কম্মারা গেল কোথা, পালাল কোন দেশে ?<br>\r\n      কাজের মানুষ কারে বলে দেখুন এখন এসে ।<br>\r\n      হেসে খেলে, শুয়ে বসে কত সময় যায়,<br>\r\n      সময়টা যে কাজে লাগায়, চালাক বলে তায় ।</td>\r\n  </tr>\r\n  <tr align="center" valign="top"> \r\n    <td colspan="2"><img src="../images/kajerlok5.jpg" width="273" height="120"></td>\r\n  </tr>\r\n</table>', '0JwWoV-VRWE', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 449, '112400575'),
(61, 1, '', 'হিংসুটিদের গান', 'হিংসুটিদের-গান', 'আমরা ভাল লক্ষ্মী সবাই, তোমরা ভারি বিশ্রী', 'আমরা ভাল লক্ষ্মী সবাই, তোমরা ভারি বিশ্রী,<br>\r\nতোমরা খাবে নিমের পাঁচন, আমরা খাব মিশ্রী ।<br>\r\nআমরা পাব খেলনা পুতুল, আমরা পাব চম্‌চম্‌,<br>\r\nতোমরা ত তা পাচ্ছ না কেউ, পেলেও পাবে কম কম ।<br>\r\nআমরা শোব খাট্‌ পালঙে মায়ের কাছে ঘেঁষটে,<br>\r\nতোমরা শোবে অন্ধকারে একলা ভয়ে ভেস্তে ।<br>\r\nআমরা যাব জামতাড়াতে চড়ব কেমন ট্রেইনে,<br>\r\nচেঁচাও যদি &quot;সঙ্গে নে যাও&quot; বল্‌ব &quot;কলা এই নে&quot; !<br>\r\nআমরা ফিরি বুক ফুলিয়ে রঙিন জুতোয় মচ্‌মচ্‌,<br>\r\nতোমরা হাঁদা নোংরা ছিছি হ্যাংলা নাকে ফঁচ্‌ফঁচ্‌ ।<br>\r\nআমরা পরি রেশমি জরি, আমরা পরি গয়না,<br>\r\nতোমরা সেসব পাওনা ব''লে তাও তোমাদের সয় না ।<br>\r\nআমরা হব লাটমেজাজি, তোমরা হব কিপ্‌টে,<br>\r\nচাইবে যদি কিচ্ছু তখন ধরব গলা চিপ্‌টে ।<br><br><img src="images/hingshutidergaan.jpg" width="297" height="120">', 'NKG1lzksLgc', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 876, '112400700'),
(62, 1, '', 'সাধে কি বলে গাধা', 'সাধে-কি-বলে-গাধা', 'বললে গাধা মনের দুঃখে অনেকখানি ভেবে', 'বললে গাধা মনের দুঃখে অনেকখানি ভেবে-<br>\r\n&quot;বয়েস গেল খাটতে খাটতে, বৃদ্ধ হলাম এবে,<br>\r\nকেউ না করে তোয়াজ তবু, সংসারের কি রীতি !<br>\r\nইচ্ছে করে এক্ষুনি দিই কাজে কর্মে ইতি ।<br>\r\nকোথাকার ঐ নোংরা কুকুর, আদর যে তার কত-<br>\r\nযখন তখন ঘুমোচ্ছে সে লাটসাহেবের মত !<br>\r\nল্যাজ নেড়ে যেই, ঘেউ ঘেউ ঘেউ, লাফিয়ে দাঁড়ায় কোলে,<br>\r\nমনিব আমার বোক্‌চন্দর, আহ্লাদে যান গলে ।<br>\r\nআমিও যদি সেয়ানা হতুম, আরামে চোখ মুদে<br>\r\nরোজ মনিবের মন ভোলাতুম অম্নি নেচে কুঁদে ।<br>\r\nঠাং নাচাতুম, ল্যাজ দোলাতুম, গান শোনাতুম সাধা-<br>\r\nএ বুদ্ধিটা হয়নি আমার- সাধে কি বলে গাধা !&quot; <p>বুদ্ধি \r\nএঁটে বসল গাধা আহ্লাদে ল্যাজ নেড়ে,<br>\r\nনাচ্‌ল কত, গাইল কত, প্রাণের মায়া ছেড়ে ।<br>\r\nতারপরেতে শেষটা ক্রমে স্ফুর্তি এল প্রাণে<br>\r\nচলল গাধা খোদ্‌ মনিবের ড্রইংরুমের পানে ।<br>\r\nমনিবসাহেব ঝিমুচ্ছিলেন চেয়ারখানি জুড়ে,<br>\r\nগাধার গলার শব্দে হঠাৎ তন্দ্রা গেল উড়ে ।<br>\r\nচম্‌কে উঠে গাধার নাচন যেমনি দেখেন চেয়ে,<br>\r\nহাসির চোটে সাহেব বুঝি মরেন বিষম খেয়ে ।</p>\r\n<p>ভাব্‌লে গাধা- এই তো মনিব জল হয়েছেন হেসে<br>\r\nএইবারে যাই আদর নিতে কোলের কাছে ঘেঁষে ।<br>\r\nএই না ভেবে এক্কেবারে আহ্লাদেতে ক্ষেপে<br>\r\nচড়্‌ল সে তার হাঁটুর উপর দুই পা তুলে চেপে ।<br>\r\nসাহেব ডাকেন ''ত্রাহি ত্রাহি'' গাধাও ডাকে ''ঘ্যাঁকো''<br>\r\n(অর্থাৎ কিনা ''কোলে চড়েছি, এখন আমার দ্যাখো !'')</p>\r\n<p>ডাক শুনে সব দৌড়ে এল ব্যস্ত হয়ে ছুটে,<br>\r\nদৌড়ে এল চাকর বাকর মিস্ত্রী মজুর মুটে,<br>\r\nদৌড়ে এল পাড়ার লোকে, দৌড়ে এল মালী-<br>\r\nকারুর হাতে ডাণ্ডা লাঠি, কারু বা হাত খালি ।<br>\r\nব্যাপার দেখে অবাক সবাই, চক্ষু ছানাবড়া-<br>\r\nসাহেব বললে, &quot;উচিত মতন শাসনটি চাই কড়া ।&quot;<br>\r\nহাঁ হাঁ ব''লে ভীষন রকম উঠ্‌ল সবাই চটে<br>\r\nদে দমাদম্‌ মারের চোটে গাধার চমক্‌ ছোটে ।</p>\r\n<p>ছুটল গাধা প্রাণের ভয়ে গানের তালিম ছেড়ে,<br>\r\nছুটল পিছে একশো লোকে হুড়মুড়িয়ে তেড়ে ।<br>\r\nতিন পা যেতে দশ ঘা পড়ে, রক্ত ওঠে মুখে-<br>\r\nকষ্টে শেষে রক্ষা পেল কাঁটার ঝোপে ঢুকে ।<br>\r\nকাঁটার ঘায়ে চামড়া গেল, সার হল তার কাঁদা ;<br>\r\nব্যাপার শুনে বললে সবাই, &quot;সাধে কি বলে গাধা !&quot;</p>\r\n<img src="../images/sadhekibolegadha.jpg" width="184" height="240">', 'FpKBSW5ut_8', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 510, '112400348'),
(63, 1, '', 'তেজিয়ান', 'তেজিয়ান', 'চলে খচ্‌খচ্‌', '<table border="0">\r\n  <tr> \r\n    <td rowspan="2" valign="bottom"><img src="images/tejian1.jpg" width="125" height="299"></td>\r\n    <td valign="bottom">চলে খচ্‌খচ্‌ <br>\r\n      ভুরু কট্‌মট্‌ <br>\r\n      দেখে বাঘ-রাগ <br>\r\n      বয়ে লাফ ঝাঁপ<br>\r\n      লাথি চার চার <br>\r\n      মহা উৎপাত <br>\r\n      ঝাড়ুবর্দার <br>\r\n      তারি বালতিএ- <br>\r\n      রেগে লালমুখে <br>\r\n      মারে ঠন্‌ ঠন্‌ <br>\r\n      পায়ে কাল্‌সিটে <br>\r\n      বুঝি ঠাং যায় <br>\r\n      </td>\r\n    <td valign="bottom">রাগে গজ্‌গজ্‌ <br>\r\n      ছড়ি ফট্‌ফট্‌ <br>\r\n      লোকে ''ভাগ্‌ ভাগ্‌'' <br>\r\n      বলে ''বাপ্‌ বাপ্‌'' <br>\r\n      খেয়ে মার্জার <br>\r\n      ক''রে হুটপাট্‌ <br>\r\n      হারুসর্দার <br>\r\n      দেখে ফাল্‌ দিয়ে <br>\r\n      হেঁকে গাল রুখে <br>\r\n      হাড়ে টন্‌ টন্‌ <br>\r\n      কেন বালতিতে <br>\r\n      খোঁড়া ল্যাংচায় <br>\r\n      </td>\r\n    <td valign="bottom">জুতা মচ্‌মচ্‌ তানে,<br>\r\n      লাথি চট্‌পট্‌ হানে ।<br>\r\n      করে আগভাগ থেকে,<br>\r\n      সরে হাবভাব দেখে ।<br>\r\n      ছোটে যার যার ঘরে,<br>\r\n      চলে ফুটপাথ্‌ পরে ।<br>\r\n      ফেরে ঘরদ্বার ঝেড়ে,<br>\r\n      আসে পালটিয়ে তেড়ে <br>\r\n      মারে তাল ঠুকে দাপে<br>\r\n      মাথা ঝন ঝন কাঁপে <br>\r\n      মেরে চাল্‌ দিতে গেলে ?<br>\r\n      দেখে ভ্যাংচায় ছেলে । </td>\r\n  </tr>\r\n  <tr> \r\n    <td colspan="3" valign="top"><img src="../images/tejian2.jpg" width="193" height="139"></td>\r\n  </tr>\r\n</table>', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 206, ''),
(64, 1, '', 'লোভী ছেলে', 'লোভী-ছেলে', 'কি ভেবে যে আপন মনে', 'কি ভেবে যে আপন মনে<br>\r\nহাসি আসে ঠোঁটের কোণে,-<br> \r\nআধ আধ ঝাপসা বুলি<br>\r\nকোন কথা কয়না খুলি ।<br>\r\nবসে বসে একলা নিজে<br>\r\nলোভী ছেলে ভাবেন কি যে-<br>\r\nশুধু শুধু চামচ চেটে<br>\r\nমনে মনে সাধ কি মেটে ?<br>\r\nএকটুখানি মিষ্টি দিয়ে<br>\r\nরাখ আমায় চুপ করিয়ে,<br>\r\nনইলে পরে চেঁচিয়ে জোরে<br>\r\nতুলব বাড়ি মাথায় ক''রে ।', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 417, '');
INSERT INTO `articles` (`id`, `category_id`, `top_image`, `title`, `article_slug`, `first_line`, `body`, `audio`, `added_on`, `modified_on`, `views`, `sc_track_id`) VALUES
(65, 1, '', 'জীবনের হিসাব', 'জীবনের-হিসাব', 'বিদ্যেবোঝাই বাবুমশাই চড়ি সখের বোটে', '<table border="0">\r\n  <tr valign="top"> \r\n    <td valign="top" style=''padding-right: 10px; border-right: 1px solid #ccc;''>বিদ্যেবোঝাই বাবুমশাই চড়ি সখের বোটে<br>\r\n      মাঝিরে কন, &quot;বলতে পারিস সূর্যি কেন ওঠে ?<br>\r\n      চাঁদটা কেন বাড়ে কমে ? জোয়ার কেন আসে ?&quot;<br>\r\n      বৃদ্ধ মাঝি অবাক হয়ে ফ্যাল্‌ফেলিয়ে হাসে ।<br>\r\n      বাবু বলেন, &quot;সারা জনম মরলিরে তুই খাটি,<br>\r\n      জ্ঞান বিনা তোর জীবনটা যে চারি আনাই মাটি ।  \r\n      <p>খানিক বাদে কহেন বাবু, &quot;বলত দেখি ভেবে<br>\r\n        নদীর ধারা  কেম্‌‌নে আসে পাহাড় হতে নেবে ?<br>\r\n        বল্‌ত কেন লবণপোরা সাগরভরা পানি?&quot;<br>\r\n        মাঝি সে কয়, &quot;আরে মশাই, অত কি আর জানি ?&quot;<br>\r\n        বাবু বলেন, &quot;এই বয়সে জানিসনেও তাকি ?<br>\r\n        জীবনটা তোর নেহাৎ খেলো, অষ্ট আনাই ফাঁকি ।&quot;</p>\r\n      </td>\r\n    <td valign="top" style=''padding-left: 10px;''>আবার ভেবে কহেন বাবু, &quot;বলত ওরে বুড়ো,<br>\r\n        কেন এমন নীল দেখা যায় আকাশের ঐ চুড়ো ?<br>\r\n        বলত দেখি সূর্য চাঁদে গ্রহণ লাগে কেন ?&quot;<br>\r\n        বৃদ্ধ বলে, &quot;আমায় কেন লজ্জা দেছেন হেন ?&quot;<br>\r\n        বাবু বলেন, &quot;বলব কি আর, বলব তোরে কি তা,-<br>\r\n        দেখছি এখন জীবনটা তোর বারো আনাই বৃথা ।&quot;</font><br><br>\r\n      <font size="4">খানিক বাদে ঝড় উঠেছে, ঢেউ উঠেছে ফুলে,<br>\r\n        বাবু দেখেন নৌকাখানি ডুব্‌ল বুঝি দুলে ।<br>\r\n        মাঝিরে কন, &quot;একি আপদ ! ওরে ও ভাই মাঝি,<br>\r\n        ডুব্‌ল নাকি নৌকা এবার ? মরব নাকি আজি ?&quot;<br>\r\n        মাঝি শুধায়, &quot;সাঁতার জানো ?&quot; মাথা নাড়েন বাবু,<br>\r\n        মূর্খ মাঝি বলে, &quot;মশাই, এখন কেমন কাবু ?<br>\r\n        বাঁচলে শেষে আমার কথা হিসেব করো পিছে,<br>\r\n        তোমার দেখি জীবনখানা ষোল আনাই মিছে ।&quot;\r\n      </td>\r\n  </tr>\r\n  <tr align="center" valign="bottom"> \r\n    <td colspan="3"><img src="../images/jibanerhishab.jpg" width="411" height="172"></td>\r\n  </tr>\r\n</table>', 'AfubrUetS28', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 672, '112400302'),
(66, 1, '', 'আশ্চর্য', 'আশ্চর্য', 'নিরীহ কলম, নিরীহ কালি', 'নিরীহ কলম, নিরীহ কালি,<br>\r\nনিরীহ কাগজে লিখিল গালি-<br>\r\n&quot;বাঁদর বেকুব আজব হাঁদা<br>\r\nবকাট্‌ ফাজিল অকাট্‌ গাধা ।&quot;<br>\r\nআবার লিখিল কলম ধরি<br>\r\nবচন মিষ্টি, যতন করি-<br>\r\n&quot;শান্ত মানিক শিষ্ট সাধু<br>\r\nবাছারে ধনরে, লক্ষ্মী যাদু ।&quot;<br>\r\nমনের কথাটি ছিল যে মনে,<br>\r\nরটিয়া উঠিল খাতার কোণে,<br>\r\nআঁচড়ে আঁকিতে আখর ক''টি<br>\r\nকেহ খুশি কেহ উঠিল চটি !<br>\r\nরকম রকম কালির টানে<br>\r\nকারো হাসি কারো অশ্রু আনে,<br>\r\nমারে না, ধরে না, হাঁকে না বুলি<br>\r\nলোকে হাসে কাঁদে কি দেখি ভুলি ?<br>\r\nসাদায় কালো কি খেলা জানে ?<br>\r\nভাবিয়া ভাবিয়া না পাই মানে ।', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 318, ''),
(67, 1, '', 'নিরুপায়', 'নিরুপায়', 'বসি বছরের পয়লা তারিখে', 'বসি বছরের পয়লা তারিখে<br>\r\nমানের খাতায় রাখিলাম লিখে-<br>\r\n&quot;সহজ উদরে ধরিবে যেটুক্‌,<br>\r\nসেইটুকু খাব হব না পেটুক ।&quot;<br>\r\nমাস দুই যেতে খাতা খুলে দেখি,<br>\r\nএরি মাঝে মন লিখিয়াছে একি !<br>\r\nলিখিয়াছে, &quot;যদি নেমন্তন্নে<br>\r\nকেঁদে ওঠে প্রাণ লুচির জন্যে,<br>\r\nউচিত কি হবে কাঁদান তাহারে ?<br>\r\nকিম্বা যখন বিপুল আহারে,<br>\r\nতেড়ে দেয় পাতে পোলাও কালিয়া<br>\r\nপায়েস অথবা রাবড়ি ঢালিয়া-<br>\r\nতখন কি করি, আমি নিরুপায় !<br>\r\nতাড়াতে না পারি, বলি আয় আয়,<br>\r\nঢুকে আয় মুখে দুয়ার ঠেলিয়া<br>\r\nউদার রয়েছি উদর মেলিয়া !&quot;', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 239, ''),
(68, 1, '', 'হিতে-বিপরীত', 'হিতে-বিপরীত', 'ওরে ছাগল, বল্‌ত আগে', '<table border="0">\r\n  <tr valign="top"> \r\n    <td>ওরে ছাগল, বল্‌ত আগে<br>\r\n      সুড়সুড়িটা কেমন লাগে ?<br>\r\n      কই গেল তোর জারিজুরি<br>\r\n      লম্ফঝম্ফ বাহাদুরি \r\n      ।<br>\r\n      নিত্যি যে তুই আসতি তেড়ে<br>\r\n      শিং নেড়ে আর দাড়ি নেড়ে,<br>\r\n      ওরে ছাগল করবি রে কি ?<br>\r\n      গুঁতোবি তো আয়না দেখি । &nbsp;  </td>\r\n    <td><img src="../images/hitebiparit1.jpg"></td>\r\n  </tr>\r\n  <tr valign="top"> \r\n    <td><img src="../images/hitebiparit2.jpg"></td>\r\n    <td>হাঁ হাঁ হাঁ, এ কেমন কথা ?<br>\r\n      এমন ধারা অভদ্রতা !<br>\r\n      শান্ত যারা ইতরপ্রাণী,<br>\r\n      তাদের পরে চোখরাঙানি !<br>\r\n      ঠাণ্ডা মেজাজ কয় না কিছু,<br>\r\n      লাগতে গেছ তারই পিছু ?<br>\r\n      শিক্ষা তোদের এম্নিতর<br>\r\n      ছি-ছি-ছি ! লজ্জা বড় ।</td>\r\n  </tr>\r\n  <tr valign="top"> \r\n    <td>ছাগল \r\n      ভাবে সামনে একি !<br>\r\n      একটুখানি গুঁতিয়ে \r\n      দেখি ।<br>\r\n      গুঁতোর চোটে \r\n      ধড়াধ্বড়<br>\r\n      হুড়মুড়িয়ে ধুলোয় \r\n      পড় ।<br>\r\n      তবে রে পাজি \r\n      লক্ষ্মীছাড়া<br>\r\n      আমার ''পরেই বিদ্যেঝাড়া,<br>\r\n      পাত্রাপাত্র \r\n      নাই কিরে হুঁশ্‌<br>\r\n      দে দমাদম ধুপুস্‌ \r\n      ধাপুস্‌ ।</td>\r\n    <td><img src="../images/hitebiparit3.jpg"></td>\r\n  </tr>\r\n</table>', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 417, ''),
(69, 1, '', 'নন্দগুপি', 'নন্দগুপি', 'হঠাৎ কেন দুপুর রোদে চাদর দিয়ে মুড়ি', '<table border="0">\r\n  <tr> \r\n    <td align="left" valign="top"> হঠাৎ কেন দুপুর \r\n      রোদে চাদর দিয়ে মুড়ি,<br>\r\n      চোরের মতন নন্দগোপাল চল্‌ছে গুড়ি গুড়ি ?<br>\r\n      লুকিয়ে বুঝি মুখোশখানা রাখছে চুপি চুপি ?<br>\r\n      আজকে রাতে অন্ধকারে টেরটা পাবেন গুপি !<br>\r\n	 <p style=''margin-left: 40px;''>\r\n     আয়না হাতে দাঁড়িয়ে গুপি হাসছে কেন খালি ?<br>\r\n     বিকট রকম পোশাক করে মাখ্‌ছে মুখে কালি !<br>\r\n     এমনি করে লম্ফ দিয়ে ভেংচি যখন দেবে<br>\r\n     নন্দ কেমন আঁৎকে যাবে- হাসছে সে তাই ভেবে ।&nbsp;<p>\r\n      আঁধার রাতে পাতার ফাঁকে ভূতের মতন কেরে ?<br>\r\n      ফন্দি এঁটে নন্দগোপাল মুখোশ মুখে ফেরে !<br>\r\n      কোথায় গুপি, আসুক না সে ইদিক্‌ পানে ঘুরে-<br>\r\n      নন্দদাদার হুঙ্কারে তার প্রাণটি যাবে উড়ে ।<br>\r\n      </td>\r\n    <td align="left" valign="middle"><img src="../images/nandagupi1.jpg" width="147" height="240"></td>\r\n  </tr>\r\n  <tr> \r\n    <td align="left" valign="top"><img src="../images/nandagupi2.jpg"></td>\r\n    <td align="left" valign="top">\r\n	<p style=''margin-left: 40px;''>\r\n	 হেথায় কেরে মূর্তি ভীষণ মুখটি ভরা গোঁফে ?<br>\r\n     চিমটে হাতে জংলা গুপি বেড়ায় ঝাড়ে ঝোপে !<br>\r\n     নন্দ যখন বাড়ির পথে আসবে গাছের আড়ে,<br>\r\n     &quot;মার্‌ মার্‌ মার্‌ কাট্‌রে&quot; বলে পড়বে তাহার ঘাড়ে ।</p>\r\n      নন্দ চলেন এক পা দু পা আস্তে ধীরে গতি,<br>\r\n      টিপটিপিয়ে চলেন গুপি সাবধানেতে অতি-<br>\r\n      মোড়ের মুখে ঝোপের কাছে মার্‌তে গিয়ে উঁকি<br>\r\n      দুই সেয়ানে এক্কেবারে হঠাৎ মুখোমুখি !<br>\r\n	 <p style=''margin-left: 40px;''>\r\n     নন্দ তখন ফন্দি ফাঁদন কোথায় গেল ভুলি<br>\r\n     কোথায় গেল গুপির মুখে মার্‌ মার্‌ মার্‌ বুলি ।<br>\r\n     নন্দ পড়েন দাঁতকপাটি মুখোশ টুখোশ ছেড়ে</br>\r\n     গুপির গায়ে জ্বরটি এল কম্প দিয়ে তেড়ে ।</p>\r\n     গ্রামের লোকে দৌড়ে তখন বদ্যি আনে ডেকে,<br>\r\n     কেউ বা নাচে কেউ বা কাঁদে রকম সকম দেখে ।<br>\r\n     নন্দগুপির মন্দ কপাল এম্‌নি হল শেষে,<br>\r\n     দেখ্‌লে তাদের লুটোপুটি সবাই মরে হেসে ! \r\n    </td>\r\n  </tr>\r\n</table>', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 285, ''),
(70, 1, '', 'বর্ষ শেষ', 'বর্ষ-শেষ', 'শুন রে আজব কথা, শুন বলি ভাইরে', 'শুন রে আজব কথা, শুন বলি ভাইরে-<br>\r\nবছরের আয়ু দেখ বেশিদিন নাই রে ।<br>\r\nফেলে দিয়ে পুরাতন জীর্ণ এ খোলসে<br>\r\nনূতন বরষ আসে, কোথা হতে বল সে !<br>\r\nকবে যে দিয়েছে চাবি জগতের যন্ত্রে,<br>\r\nসেই দমে আজও চলে না জানি কি মন্ত্রে !<br>\r\nপাকে পাকে দিনরাত ফিরে আসে বার বার,<br>\r\nফিরে আসে মাস ঋতু- এ কেমন কারবার ।<br>\r\nকোথা আসে কোথা যায় নাহি কোন উদ্দেশ,<br>\r\nহেসে খেলে ভেসে যায় কত দূর দূর দেশ ।<br>\r\nরবি যায় শশী যায় গ্রহ তারা সব যায়,<br>\r\nবিনা কাঁটা কম্পাসে বিনা কল কব্জায় ।<br>\r\nঘুরপাকে ঘুরে চলে, চলে কত ছন্দে,<br>\r\nতালে তালে হেলে দুলে চলে রে আনন্দে ।', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 221, ''),
(71, 1, '', 'বর্ষার কবিতা', 'বর্ষার-কবিতা', 'কাগজ কলম লয়ে বসিয়াছি সদ্য', 'কাগজ কলম লয়ে বসিয়াছি সদ্য,<br>\r\nআষাঢ়ে লিখিতে হবে বরষার পদ্য ।<br>\r\nকি যে লিখি কি যে লিখি ভাবিয়া না পাই রে,<br>\r\nহতাশে বসিয়া তাই চেয়ে থাকি বাইরে ।<br>\r\nসারাদিন ঘনঘটা কালো মেঘ আকাশে,<br>\r\nভিজে ভিজে পৃথিবীর মুখখানা ফ্যাকাশে ।<br>\r\nবিনা কাজে ঘরে বাঁধা কেটে যায় বেলাটা,<br>\r\nমাটি হল ছেলেদের ফুটবল খেলাটা ।<br>\r\nআপিসের বাবুদের মুখে নাই ফুর্তি,<br>\r\nছাতা কাঁধে জুতা হাতে ভ্যাবাচ্যাকা মূর্তি ।<br>\r\nকোনখানে হাঁটু জল, কোথা ঘন কর্দম-<br>\r\nচলিতে পিছল পথে পড়ে লোকে হর্‌দম ।<br>\r\nব্যাঙেদের মহাসভা আহ্লাদে গদ্‌গদ্‌,<br>\r\nগান করে সারারাত অতিশয় বদ্‌খদ্‌ ।', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 384, ''),
(72, 1, '', 'গ্রীষ্ম', 'গ্রীষ্ম', 'ঐ এল বৈশাখ, ঐ নামে গ্রীষ্ম', 'ঐ এল বৈশাখ, ঐ নামে গ্রীষ্ম,<br>\r\nখাইখাই রবে যেন ভয়ে কাঁপে বিশ্ব !<br>\r\nচোখে যেন দেখি তার ধুলিময় অঙ্গ,<br>\r\nবিকট কুটিলজটে ভ্রুকুটির ভঙ্গ,<br>\r\nরোদে রাঙা দুই আঁখি শুকায়েছে কোটরে,<br>\r\nক্ষুধার আগুন যেন জ্বলে তার জঠরে !<br>\r\nমনে হয় বুঝি তার নিঃশ্বাস মাত্রে<br>\r\nতেড়ে আসে পালাজ্বর পৃথিবীর গাত্রে !<br>\r\nভয় লাগে হয় বুঝি ত্রিভুবন ভস্ম-<br>\r\nওরে ভাই ভয় নাই পাকে ফল শস্য !<br>\r\nতপ্ত ভীষণ চুলা জ্বালি নিজ বক্ষে<br>\r\nপৃথিবী বসেছে পাকে, চেয়ে দেখ চক্ষে,-<br>\r\nআম পাকে, জাম পাকে, ফল পাকে কত যে,<br>\r\nবুদ্ধি যে পাকে কত ছেলেদের মগজে !', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 236, ''),
(73, 1, '', 'শ্রাবণে', 'শ্রাবণে', 'জল ঝরে জল ঝরে সারাদিন সারারাত', 'জল ঝরে জল ঝরে সারাদিন সারারাত-<br>\r\nঅফুরান্‌ নামতায় বাদলের ধারাপাত ।<br>\r\nআকাশের মুখ ঢাকা, ধোঁয়ামাখা চারিধার,<br>\r\nপৃথিবীর ছাত পিটে ঝামাঝম্‌ বারিধার ।<br>\r\nস্নান করে গাছপালা প্রাণখোলা বরষায়,<br>\r\nনদীনালা ঘোলাজল ভরে ওঠে ভরসায় ।<br>\r\nউৎসব ঘনঘোর উন্মাদ শ্রাবণের<br>\r\nশেষ নাই শেষ নাই বরষার প্লাবনের ।<br>\r\nজলেজলে জলময় দশদিক্‌ টলমল্‌, <br>\r\nঅবিরাম একই গান, ঢালো জল ঢালো জল ।<br>\r\nধুয়ে যায় যত তাপ জর্জর গ্রীষ্মের,<br>\r\nধুয়ে যায় রৌদ্রের স্মৃতিটুকু বিশ্বের ।<br>\r\nশুধু যেন বাজে কোথা নিঃঝুম ধুক্‌ধুক্‌,<br>\r\nধরণীর আশাভয় ধরণীর সুখদুখ ।', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 236, ''),
(74, 1, '', 'নিঃস্বার্থ', 'নিঃস্বার্থ', 'গোপ্‌লাটা কি হিংসুটে মা ! খাবার দিলেম ভাগ করে', 'গোপ্‌লাটা কি হিংসুটে মা ! খাবার দিলেম ভাগ করে,<br>\r\nবললে নাকো মুখেও কিছু, ফেল্লে ছুঁড়ে রাগ করে ।<br>\r\nজ্যাঠাইমা যে মেঠাই দিলেন, &quot;দুই ভায়েতে খাও&quot; ব''লে&mdash;<br>\r\nদশটি ছিল, একটি তাহার চাখতে নিলেম ফাও বলে ।<br>\r\nআর যে ন''টি, ভাগ করে তায়, তিন্‌টে দিলেম গোপ্‌লাকে&mdash;<br>\r\nতবুও কেবল হ্যাংলা ছেলে আমার ভাগেই চোখ রাখে ।<br>\r\nবুঝিয়ে বলি, &quot;কাঁদিস কেন ? তুই যে নেহাত কনিষ্ঠ&mdash;<br>\r\nবয়স বুঝে সাম্‌লে খাবি, তা নইলে হয় অনিষ্ট ।<br>\r\nতিনটি বছর তফাৎ মোদের, জ্যায়দা হিসাব গুন্‌তি তাই,<br>\r\nমোদ্দা আমার ছয়খানি হয়, তিন বছরে তিন্‌টি পাই ।&quot;<br>\r\nতাও মানে না, কেবল কাঁদে&mdash; স্বার্থপরের শয়তানী&mdash;<br>\r\nশেষটা আমায় মেঠাইগুলো খেতেই হল সবখানি ।', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 379, ''),
(75, 1, '', 'বর্ষ গেল বর্ষ এল', 'বর্ষ-গেল-বর্ষ-এল', 'বর্ষ গেল বর্ষ এল গ্রীষ্ম এলেন বাড়ি', 'বর্ষ গেল বর্ষ এল গ্রীষ্ম এলেন বাড়ি-<br>\r\nপৃথ্বী এলেন চক্র দিয়ে এক বছরের পাড়ি ।<br>\r\nসত্যিকারের এই পৃথিবীর বয়স কেবা জানে,<br>\r\nলক্ষ হাজার বছর ধরে চল্‌ছে একই টানে ।<br>\r\nআপন তালে আকাশ পথে আপনি চলে বেগে,<br>\r\nগ্রীষ্মকালের তপ্তরোদে বর্ষাকালের মেঘে,<br>\r\nশরৎকালের কান্নাহাসি হাল্কা বাদল হাওয়া,<br>\r\nকুয়াশা-ঘেরা পর্দা ফেলে হিমের আসা যাওয়া-<br>\r\nশীতের শেষে রিক্ত বেশে শূন্য করে ঝুলি,<br>\r\nতার প্রতিশোধ ফুলে ফলে বসন্তে লয় তুলি ।<br>\r\nনা জানি কোন নেশার ঝোঁকে যুগযুগান্ত ধরে,<br>\r\nছয়টি ঋতু দ্বারে দ্বারে পাগল হয়ে ঘোরে !<br>\r\nনা জানি কোন ঘূর্ণীপাকে দিনের পর দিন,<br>\r\nএমন ক''রে ঘোরায় তারে নিদ্রাবিরামহীন !<br>\r\nকাঁটায় কাঁটায় নিয়ম রাখে লক্ষযুগের প্রথা,<br>\r\nনা জানি তার চাল চলনের হিসাব রাখে কোথা !<br><br>\r\n<img src="../images/barshagelobarshaelo.jpg" width="219" height="289">', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 318, ''),
(76, 1, '', 'মূর্খমাছি', 'মূর্খমাছি', 'সান্‌-বাঁধা মোর আঙিনাতে', '<table border="0">\r\n  <tr> \r\n    <td align="left" valign="top" style=''padding-right: 10px; border-right: 1px solid #ccc;''> <strong>মাকড়সা</strong><br>\r\n      সান্‌-বাঁধা মোর আঙিনাতে<br>\r\n      জাল বুনেছি কালকে রাতে,<br>\r\n      &nbsp; &nbsp; &nbsp; &nbsp;ঝুল ঝেড়ে সব সাফ করেছি বাসা ।<br>\r\n      আয় না মাছি আমার ঘরে,<br>\r\n      আরাম পাবি বসলে পরে,<br>\r\n      &nbsp; &nbsp; &nbsp; &nbsp;ফরাশ পাতা দেখবি কেমন খাসা ! \r\n      <p><strong>মাছি</strong><br>\r\n        থাক্‌ থাক্‌ থাক্‌ আর বলে না,<br>\r\n        আন্‌কথাতে মন গলে না-<br>\r\n        &nbsp; &nbsp; &nbsp; &nbsp;ব্যবসা তোমার সবার আছে জানা ।<br>\r\n        ঢুক্‌‌লে তোমার জালের ঘেরে<br>\r\n        কেউ কোনদিন আর কি ফেরে ?<br>\r\n        &nbsp; &nbsp; &nbsp; &nbsp;বাপ্‌‌রে ! সেথায় ঢুক্‌‌তে মোদের মানা ।</p>\r\n      <p><strong>মাকড়সা</strong><br>\r\n        হাওয়ায় দোলে জালের দোলা<br>\r\n        চারদিকে তার জান্‌লা খোলা<br>\r\n        &nbsp; &nbsp; &nbsp; &nbsp;আপ্‌নি ঘুমে চোখ যে আসে জুড়ে !<br>\r\n        আয় না হেথা হাত পা ধুয়ে<br>\r\n        পাখ্‌না মুড়ে থাক্‌ না শুয়ে-<br>\r\n        &nbsp; &nbsp; &nbsp; &nbsp;ভন্‌ ভন্‌ ভন্‌ মরবি কেন উড়ে ?</p>\r\n      <p><strong>মাছি</strong><br>\r\n        কাজ নেই মোর দোলায় দুলে,<br>\r\n        কোথায় তোমার কথায় ভুলে<br>\r\n        &nbsp; &nbsp; &nbsp; &nbsp;প্রাণটা নিয়ে টান্‌ পড়ে ভাই শেষে ।<br>\r\n        তোমার ঘরে ঘুম যদি পায়<br>\r\n        সে ঘুম কভু ভাঙবে না হায়-<br>\r\n        &nbsp; &nbsp; &nbsp; &nbsp;সে ঘুম নাকি এমন সর্বনেশে !</p>\r\n      <p><strong>মাকড়সা</strong><br>\r\n        মিথ্যে কেন ভাবিস্‌ মনে ?<br>\r\n        দেখ্‌না এসে ঘরের কোণে<br>\r\n        &nbsp; &nbsp; &nbsp; &nbsp;ভাঁড়ার ভরা খাবার আছে কত !<br>\r\n        দে-টাপাটপ ফেলবি মুখে<br>\r\n        নাচ্‌‌বি গাবি থাক্‌‌বি সুখে<br>\r\n        <font face="Bangla">&nbsp; &nbsp; \r\n        &nbsp; &nbsp;ভাবনা ভুলে বাদ্‌শা-রাজার \r\n        মতো ।<br>\r\n        </p>\r\n       \r\n    </td>\r\n    <td align="left" valign="top" style=''padding-left: 10px;''> <p><strong>মাছি</strong><br>\r\n        লোভ দেখালেই ভুলবে ভবি,<br>\r\n        ভাবছ আমায় তেমনি লোভী !<br>\r\n        &nbsp; &nbsp; &nbsp; &nbsp;মিথ্যে দাদা ভোলাও কেন খালি,<br>\r\n        করব কি ছাই ভাড়ার দেখে ?<br>\r\n        প্রণাম করি আড়াল থেকে-<br>\r\n        &nbsp; &nbsp; &nbsp; &nbsp;আজকে তোমার সেই গুড়ে ভাই বালি ।</p>\r\n      <p><strong>মাকড়সা</strong><br>\r\n        নধর কালো বদন ভরে<br>\r\n        রূপ যে কত উপচে পড়ে !<br>\r\n        &nbsp; &nbsp; &nbsp; &nbsp;অবাক দেখি মুকুটমালা শিরে !<br>\r\n        হাজার চোখে মানিক জ্বলে !<br>\r\n        ইন্দ্রধনু পাখার তলে !<br>\r\n        &nbsp; &nbsp; &nbsp; &nbsp;ছয় পা ফেলে আয় না দেখি ধীরে ।</p>\r\n      <p><strong>মাছি</strong><br>\r\n        মন ফুর্‌ফুর্‌ ফুর্তি নাচে-<br>\r\n        একটুখানি যাই না কাছে !<br>\r\n        &nbsp; &nbsp; &nbsp; &nbsp;যাই যাই যাই- বাপ্‌‌রে একি বাঁধা ।<br>\r\n        ও দাদা ভাই রক্ষে কর !<br>\r\n        ফাঁদ পাতা এ কেমন তরো ।<br>\r\n        &nbsp; &nbsp; &nbsp; &nbsp;পড়ে হাত পা হল বাঁধা ।</p>\r\n      <p><br>\r\n        <strong>দুষ্টুলোকের মিষ্টি কথায়</strong><br>\r\n        দুষ্টুলোকের মিষ্টি কথায়<br>\r\n        নাচলে লোকের স্বস্তি কোথায় ?<br>\r\n        &nbsp; &nbsp; &nbsp; &nbsp;এমনি দশাই তার কপালে লেখা ।<br>\r\n        কথার পাকে মানুষ মেরে<br>\r\n        মাকড়জীবী ঐ যে ফেরে,<br>\r\n        &nbsp; &nbsp; &nbsp; &nbsp;গড় করি তার অনেক তফাৎ থেকে ।</p>\r\n      </td>\r\n  </tr>\r\n</table>\r\n<p class=''ex_link_container''>\r\n<a href=''http://en.wikipedia.org/wiki/The_Spider_and_the_Fly_(poem)'' class=''ex_link'' target=''_blank''>মূল ইংরাজি কবিতা</a><br />\r\n<a href=''http://www.wussu.com/poems/lctlq.htm'' class=''ex_link'' target=''_blank''>মূল কবিতা ও লুইস ক্যারলের প্যারডি</a>\r\n</p>', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 379, ''),
(77, 1, '', 'হারিয়ে পাওয়া', 'হারিয়ে-পাওয়া', 'ঠাকুরদাদার চশমা কোথা?', '<p>ঠাকুরদাদার চশমা কোথা?<br>\r\nওরে গণ্‌শা, হাবুল, ভোঁতা,<br>\r\nদেখ্‌না হেথা, দেখ্‌না হোথা- খোঁজ না নিচে গিয়ে ।</p>\r\n<p> কই কই কই? কোথায় গেল ?<br>\r\nটেবিল টানো, ডেস্কো ঠেল,<br>\r\nঘরদোর সব উলটে ফেল- খোঁচাও লাঠি দিয়ে ।</p>\r\n<p>খুঁজছে মিছে কুঁজোর পিছে,<br>\r\nজুতোর ফাঁকে, খাটের নিচে,<br>\r\nকেউ বা জোরে পর্দা খিঁচে- বিছনা দেখে ঝেড়ে-</p>\r\n<p>লাফিয়ে ঘুরে হাঁফিয়ে ঘেমে<br>\r\nক্লান্ত সবে পড়্‌ল থেমে,<br>\r\nঠাকুরদাদা আপনি নেমে আসেন তেড়েমেড়ে ।</p>\r\n<p>বলেন রেগে, &quot;চশমাটা কি<br>\r\nঠাং গজিয়ে ভাগ্‌ল নাকি ?<br>\r\nখোঁজার নামে কেবল ফাঁকি- দেখছি আমি এসে !&quot;</p>\r\n<p>যেমন বলা দারূণ রোষে,<br>\r\nকপাল থেকে অম্নি খ''সে<br>\r\nচশমা পড়ে তক্তপোশে- সবাই ওঠে হেসে !</p>', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 304, ''),
(78, 1, '', 'দাদা গো দাদা', 'দাদা-গো-দাদা', 'দাদা গো দাদা, সত্যি তোমার সুরগুলো খুব খেলে', 'দাদা গো দাদা, সত্যি তোমার সুরগুলো খুব খেলে !<br>\r\nএম্‌‌নি মিঠে- ঠিক যেন কেউ গুড় দিয়েছে ঢেলে !<br>\r\nদাদা গো দাদা, এমন খাসা কণ্ঠ কোথায় পেলে?-<br>\r\nএই খেলে যা ! গান শোনাতে আমার কাছেই এলে ?<br>\r\nদাদা গো দাদা, পায়ে পড়ি তোর, ভয় পেয়ে যায় ছেলে-<br>\r\nগাইবে যদি ঐখেনে গাও, ঐ দিকে মুখ মেলে ।<br><br>\r\n<img src="../images/dadagodada.jpg" width="129" height="171">', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 611, ''),
(79, 1, '', 'সঙ্গীহারা', 'সঙ্গীহারা', 'সবাই নাচে ফূর্তি করে সবাই করে গান', 'সবাই নাচে ফূর্তি করে সবাই করে গান,<br>\r\nএকলা বসে হাঁড়িচাঁচার মুখটি কেন ম্লান ?<br>\r\nদেখ্‌ছ নাকি আমার সাথে সবাই করে আড়ি-<br>\r\nতাইত আমার মেজাজ খ্যাপা মুখটি এমন হাঁড়ি ।<br>\r\nতাও কি হয় ! ঐ যে মাঠে শালিখ পাখি ডাকে<br>\r\nতার কাছে কৈ যাওনিকো ভাই শুধাওনিতো তাকে !<br>\r\nশালিখ পাখি বেজায় ঠ্যাঁটা চেঁচায় মিছিমিছি,<br>\r\nহল্লা শুনে হাড় জ্বলে যায় কেবল কিচিমিচি ।<br>\r\nমিষ্টি সুরে দোয়েল পাখি জুড়িয়ে দিল প্রাণ<br>\r\nতার কাছে কৈ বস্‌লে নাতো শুনলে না তার গান ।<br>\r\nদোয়েল পাখির ঘ্যান্‌ঘ্যানানি আর কি লাগে ভালো ?<br>\r\nযেমন রূপে তেমন গুণে তেমনি আবার কালো ।<br>\r\nরূপ যদি চাও যাও না কেন মাছরাঙার কাছে,<br>\r\nঅমন খাসা রঙের বাহার আর কি কারো আছে ?<br>\r\nমাছরাঙা ? তারেও কি আর পাখির মধ্যে ধরি<br>\r\nরকম সকম সঙের মতন, দেমাক দেখে মরি ।<br>\r\nপায়রা ঘুঘু কোকিল চড়াই চন্দনা টুনটুনি<br>\r\nকারে তোমার পছন্দ হয়, সেই কথাটি শুনি !<br>\r\nএই গুলো সব ছ্যাবলা পাখি নেহাৎ ছোট জাত-<br>\r\nদেখলে আমি তফাৎ হাটি অমনি পঁচিশ হাত ।<br>\r\nএতক্ষণে বুঝতে পারি ব্যাপারখানা কি যে-<br>\r\nসবার তুমি খুঁৎ পেয়েছ নিখুঁৎ কেবল নিজে !<br>\r\nমনের মতন সঙ্গী তোমার কপালে নাই লেখা<br>\r\nতাইতে তোমায় কেউ পোঁছে না তাইতে থাক একা ।<br><br><img src="images/sangihara.jpg" width="212" height="150">', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 237, ''),
(80, 1, '', 'কেন সব কুকুরগুলো', 'কেন-সব-কুকুরগুলো', 'কেন সব কুকুরগুলো খামখা চ্যাঁচায় রাতে', 'কেন সব কুকুরগুলো খামখা চ্যাঁচায় রাতে ?<br>\r\nকেন বল দাঁতের পোকা থাকেনা ফোক্‌লা দাঁতে ?<br>\r\nপৃথিবীর চ্যাপ্টা মাথা, কেন সে কাদের দোষে?-<br>\r\nএস ভাই চিন্তা করি দুজনে ছায়ায় বসে ।', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 327, ''),
(81, 1, '', 'নূতন বৎসর', 'নূতন-বৎসর', 'নূতন বছর ! নূতন বছর ! সবাই হাঁকে সকাল সাঁঝে', '''নূতন বছর ! নূতন বছর !'' সবাই হাঁকে সকাল সাঁঝে<br>\r\nআজকে আমার সূর্যি মামার মুখটি জাগে মনের মাঝে ।<br>\r\nমুস্কিলাসান করলে মামা, উস্কিয়ে তার আগুনখানি,<br>\r\nইস্কুলেতে লাগ্‌ল তালা, থাম্‌ল সাধের পড়ার ঘানি ।<br>\r\nএক্‌জামিনের বিষম ঠেলা চুক্‌ল রে ভাই ঘুচ্ল জ্বালা,<br>\r\nনূতন সালের নূতন তালে হোক্‌ তবে আস ''হকির'' পালা ।<br>\r\nকোন্‌খানে কোন্‌ মেজের কোণে, কলম কানে, চশমা নাকে,<br>\r\nবিরামহারা কোন্‌ বেচারা দেখেন কাগজ, ভয় কি তাঁকে ? <br>\r\nঅঙ্কে দিবেন হকির গোলা, শঙ্কা ত নাই তাহার তরে,<br>\r\nতঙ্কা হাজার মিলুক তাঁহার, ডঙ্কা মেরে চলুন ঘরে ।<br>\r\nদিনেক যদি জোটেন খেলায় সাঁঝের বেলায় মাঠের মাঝে,<br>\r\n''গোল্লা'' পেয়ে ঝোল্লা ভরে আবার না হয় যাবেন কাজে !<br>\r\nআয় তবে আয়, নবীন বরষ ! মলয় বায়ের দোলায় দুলে,<br>\r\nআয় সঘনে গগন বেয়ে, পাগলা ঝড়ের পালটি তুলে ।<br>\r\nআয় বাংলার বিপুল মাঠে শ্যামল ধানের ঢেউ খেলিয়ে,<br>\r\nআয়রে সুখের ছুটির দিনে আম-কাঁটালের খবর নিয়ে !<br>\r\nআয় দুলিয়ে তালের পাখা, আয় বিছিয়ে শীতল ছায়া,<br>\r\nপাখির নীড়ে চাঁদের হাটে আয় জাগিয়ে মায়ের মায়া ।<br>\r\nতাতুক না মাঠ, ফাটুক না কাঠ, ছুটুক না ঘাম নদীর মত,<br>\r\nজয় হে তোমার, নূতন বছর ! তোমার যে গুণ, গাইব কত ?<br>\r\nপুরান বছর মলিন মুখে যায় সকলের বালাই নিয়ে,<br>\r\nঘুচ্‌ল কি ভাই মনের কালি সেই বুড়োকে বিদায় দিয়ে ?<br>\r\nনূতন সালে নূতন বলে, নূতন আশায়, নুতন সাজে,<br>\r\nআয় দয়ালের নাম লয়ে ভাই, যাই সকলে যে যার কাজে !', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 272, ''),
(82, 1, '', 'মনের মতন', 'মনের-মতন', 'কান্না হাসির পোঁটলা বেঁধে, বর্ষভরা পুঁজি', 'কান্না হাসির পোঁটলা বেঁধে, বর্ষভরা পুঁজি,<br>\r\nবৃদ্ধ বছর উধাও হ''ল ভূতের মুলুক খুঁজি ।<br>\r\nনূতন বছর এগিয়ে এসে হাত পাতে ঐ দ্বারে,<br>\r\nবল্‌ দেখি মন মনের মতন কি দিবি তুই তারে ?<br>\r\nআর কি দিব ?- মুখের হাসি, ভরসাভরা প্রাণ,<br>\r\nসুখের মাঝে দুখের মাঝে আনন্দময় গান ।', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 294, ''),
(83, 1, '', 'মেঘ', 'মেঘ', 'সাগর যেথা লুটিয়ে পড়ে নতুন মেঘের দেশে', 'সাগর যেথা লুটিয়ে পড়ে নতুন মেঘের দেশে-<br>\r\nআকাশ-ধোয়া নীল যেখানে সাগর জলে মেশে ।<br>\r\nমেঘের শিশু ঘুমায় সেথা আকাশ-দোলায় শুয়ে-<br>\r\nভোরের রবি জাগায় তারে সোনার কাঠি ছুঁয়ে ।<br>\r\nসন্ধ্যা সকাল মেঘের মেলা- কুলকিনারা ছাড়ি<br>\r\nরং বেরঙের পাল তুলে দেয় দেশবিদেশে পাড়ি ।<br>\r\nমাথায় জটা, মেঘের ঘটা আকাশ বেয়ে ওঠে,<br>\r\nজোছনা রাতে চাঁদের সাথে পাল্লা দিয়ে ছোটে ।<br>\r\nকোন্‌ অকুলের সন্ধানেতে কোন্‌ পথে যায় ভেসে-<br>\r\nপথহারা কোন্‌ গ্রামের পরে নাম-জানা-নেই-দেশে ।<br>\r\nঘূর্ণীপথের ঘোরের নেশা দিক্‌বিদিকে লাগে,<br>\r\nআগল ভাঙা পাগল হাওয়া বাদল রাতে জাগে ;<br>\r\nঝড়ের মুখে স্বপন ছুটে আঁধার আসে ঘিরে !<br>\r\nমেঘের প্রাণে চমক হানে আকাশ চিরে চিরে !<br>\r\nবুকের মাঝে শঙ্খ বাজে- দুন্দুভি দেয় সাড়া !<br>\r\nমেঘের মরণ ঘনিয়ে নামে মত্ত বাদল ধারা ।', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 231, ''),
(84, 1, '', 'শিশুর দেহ', 'শিশুর-দেহ', 'চশমা-আঁটা পণ্ডিতে কয় শিশুর দেহ দেখে', 'চশমা-আঁটা পণ্ডিতে কয় শিশুর দেহ দেখে-<br>\r\n&quot;হাড়ের পরে মাংস দিয়ে, চামড়া দিয়ে ঢেকে,<br>\r\nশিরার মাঝে রক্ত দিয়ে, ফুসফুসেতে বায়ু,<br>\r\nবাঁধল দেহ সুঠাম করে পেশী এবং স্নায়ু ।&quot;<br>\r\nকবি বলেন, &quot;শিশুর মুখে হেরি তরুণ রবি,<br>\r\nউৎসারিত আনন্দে তার জাগে জগৎ ছবি ।<br>\r\nহাসিতে তার চাঁদের আলো, পাখির কলকল,<br>\r\nঅশ্রুকণা ফুলের দলে শিশির ঢলঢল ।&quot;<br>\r\nমা বলেন, &quot;এই দুরুদুরু মোর বুকেরই বাণী,<br>\r\nতারি গভীর ছন্দে গড়া শিশুর দেহখানি ।<br>\r\nশিশুর প্রাণে চঞ্চলতা আমার অশ্রুহাসি,<br>\r\nআমার মাঝে লুকিয়েছিল এই আনন্দরাশি ।<br>\r\nগোপনে কোন্‌ স্বপ্নে ছিল অজানা কোন আশা,<br>\r\nশিশুর দেহে মূর্তি নিল আমার ভালবাসা ।&quot;', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 228, ''),
(85, 1, '', 'কানে খাটো বংশীধর', 'কানে-খাটো-বংশীধর', 'কানে খাটো বংশীধর যায় মামাবাড়ি', 'কানে খাটো বংশীধর যায় মামাবাড়ি,<br>\r\nগুনগুন গান গায় আর নাড়ে দাড়ি ।।<br>\r\nচলেছে সে একমনে ভাবে ভরপুর,<br>\r\nসহসা বাজিল কানে সুমধুর সুর ।।<br>\r\nবংশীধর বলে, &quot;আহা, না জানি কি পাখি<br>\r\nসুদুরে মধুর গায় আড়ালেতে থাকি ।।<br>\r\nদেখ, দেখ সুরে তার কত বাহাদুরি,<br>\r\nকালোয়াতি গলা যেন খেলে কারিকুরি ।।&quot;<br>\r\nএদিকে বেড়াল ভাবে, &quot;এযে বড় দায়,<br>\r\nপ্রাণ যদি থাকে তবে ল্যাজখানি যায় ।।<br>\r\nগলা ছেড়ে এত চেঁচামেচি এত করি হায়<br>\r\nতব যে ছাড়ে না বেটা, কি করি উপায় ।।<br>\r\nআর তো চলে না সহা এত বাড়াবাড়ি,<br>\r\nযা থাকে কপালে, দেই এক থাবা মারি ।।&quot;<br>\r\nবংশীধর ভাবে, &quot;একি ! বেসুরা যে করে,<br>\r\nগলা গেছে ভেঙে তাই &quot;ফ্যাঁস্‌&quot; সুর ধরে ।।&quot;<br>\r\nহেনকালে বেরসিক বেড়ালের চাঁটি,<br>\r\nএকেবারে সব গান করে দিল মাটি ।<br><br>\r\n<img src="images/kanekhatobangshidhar.jpg" width="200" height="402">', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 296, ''),
(86, 1, '', 'আদুরে পুতুল', 'আদুরে-পুতুল', 'যাদুরে আমার আদুরে গোপাল, নাকটি নাদুস থোপ্‌না গাল', 'যাদুরে আমার আদুরে গোপাল, নাকটি নাদুস থোপ্‌না গাল,<br>\r\nঝিকিমিকি চোখ মিট্‌মিটি চায়, ঠোঁট দুটি তায় টাট্‌কা লাল ।<br>\r\nমোমের পুতুল ঘুমিয়ে থাকুক্‌ দাঁত মেলে আর চুল খুলে-<br>\r\nটিনের পুতুল চীনের পুতুল কেউ কি এমন তুলতুলে ?<br>\r\nগোব্‌দা গড়ন এমনি ধরন আব্‌দারে কেউ ঠোঁট ফুলোয় ?<br>\r\nমখমলি রং মিষ্টি নরম- দেখ্‌ছ কেমন হাত বুলোয় !<br>\r\nবল্‌বি কি বল্‌ হাব্‌লা পাগল আবোল তাবোল কান ঘেঁষে,<br>\r\nফোক্‌লা গদাই যা বলবি তাই ছাপিয়ে পাঠাই &quot;সন্দেশে&quot;', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 510, ''),
(87, 1, '', 'মেঘের খেয়াল', 'মেঘের-খেয়াল', 'আকাশের ময়দানে বাতাসের ভরে', 'আকাশের ময়দানে বাতাসের ভরে,<br>\r\nছোট বড় সাদা কালো কত মেঘ চরে ।<br>\r\nকচি কচি থোপা থোপা মেঘেদের ছানা<br>\r\nহেসে খেলে ভেসে যায় মেলে কচি ডানা ।<br>\r\nকোথা হতে কোথা যায় কোন্‌ তালে চলে,<br>\r\nবাতাসের কানে কানে কত কথা বলে ।<br>\r\nবুড়ো বুড়ো ধাড়ি মেঘ ঢিপি হয়ে উঠে-<br>\r\nশুয়ে ব''সে সভা করে সারাদিন জুটে ।<br>\r\nকি যে ভাবে চুপ্‌চাপ, কোন ধ্যানে থাকে,<br>\r\nআকাশের গায়ে গায়ে কত ছবি আঁকে ।<br>\r\nকত আঁকে কত মোছে, কত মায়া করে,<br>\r\nপলে পলে কত রং কত রূপ ধরে ।<br>\r\nজটাধারী বুনো মেঘ ফোঁস ফোঁস ফোলে,<br>\r\nগুরুগুরু ডাক ছেড়ে কত ঝড় তোলে ।<br>\r\nঝিলিকের ঝিকিমিকি চোখ করে কানা,<br>\r\nহড়্‌ হড়্‌ কড়্‌ কড়্‌ দশদিকে হানা ।<br>\r\nঝুল্‌ কালো চারিধার, আলো যায় ঘুচে,<br>\r\nআকাশের যত নীল সব দেয় মুছে । ', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 300, ''),
(88, 1, '', 'বেজায় খুশি', 'বেজায়-খুশি', 'বাহবা বাবুলাল ! গেলে যে হেসে', 'বাহবা বাবুলাল ! গেলে যে হেসে !<br>\r\nবগলে কাতুকুতু কে দিল এসে ?<br>\r\nএদিকে মিটিমিটি দেখ কি চেয়ে ?<br>\r\nহাসি যে ফেটে পড়ে দু''গাল বেয়ে !<br>\r\nহাসে যে রাঙা ঠোঁট দন্ত মেলে<br>\r\nচোখের কোণে কোণে বিজলী খেলে ।<br>\r\nহাসির রসে গ''লে ঝরে যে লালা<br>\r\nকেন এ খি-খি-খি-খি হাসির পালা ?<br>\r\nযে দেখে সেই হাসে হাহাহা হাহা<br>\r\nবাহবা বাবুলাল বাহবা বাহা !', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 366, '');
INSERT INTO `articles` (`id`, `category_id`, `top_image`, `title`, `article_slug`, `first_line`, `body`, `audio`, `added_on`, `modified_on`, `views`, `sc_track_id`) VALUES
(89, 1, '', 'আবোল তাবোল', 'আবোল-তাবোল-২', 'এক যে ছিল রাজা- (থুড়ি', 'এক যে ছিল রাজা&mdash; (থুড়ি,<br>\r\nরাজা নয় সে ডাইনি বুড়ি) !<br>\r\nতার যে ছিল ময়ূর&mdash; (না না,<br>\r\nময়ূর কিসের ? ছাগল ছানা) ।<br>\r\nউঠানে তার থাক্‌ত পোঁতা&mdash;<br>\r\n&mdash;(বাড়িই নেই, তার উঠান কোথা) ?<br>\r\nশুনেছি তাত পিশতুতো ভাই&mdash;<br>\r\n&mdash;(ভাই নয়ত, মামা-গোঁসাই ) ।<br>\r\nবল্‌ত সে তার শিষ্যটিরে&mdash;<br>\r\n&mdash;(জন্ম-বোবা বলবে কিরে) ।<br>\r\nযা হোক, তারা তিনটি প্রানী&mdash;<br>\r\n&mdash;(পাঁচটি তারা, সবাই জানি !)<br>\r\nথও না বাপু খ্যাঁচাখেচি<br>\r\n&mdash;(আচ্ছা বল, চুপ করেছি) ।।<br>\r\nতারপরে যেই সন্ধ্যাবেলা,<br>\r\nযেম্নি না তার ওষুধ গেলা,<br>\r\nঅম্‌নি তেড়ে জটায় ধরা&mdash;<br>\r\n&mdash;(কোথায় জটা ? টাক যে ভরা !)<br>\r\nহোক্‌ না টেকো তোর তাতে কি ?<br>\r\nগোমরামুখো মুখ্যু ঢেঁকি !<br>\r\nধরব ঠেসে টুটির পরে<br>\r\nপিট্‌ব তোমার মুণ্ডু ধরে ।<br>\r\nএখন বাপু পালাও কোথা ?<br>\r\nগল্প বলা সহজ কথা ?', '', '2013-03-18 00:00:00', '2014-08-21 16:56:43', 1744, ''),
(90, 1, '', 'বাবু', 'বাবু', 'অতি খাসা মিহি সূতি', '<table border="0">\r\n  <tr> \r\n    <td align="left" valign="top"><img src="images/babu1.jpg" width="120" height="300"> \r\n    </td>\r\n    <td align="left" valign="top"><p><font size="4" face="Bangla">অতি খাসা মিহি \r\n        সূতি<br>\r\n        ফিনফিনে জামা ধুতি,<br>\r\n        চরণে লপেটা জুতি জরিদার ।<br>\r\n        এ হাতে সোনার ঘড়ি,<br>\r\n        ও হাতে বাঁকান ছড়ি,<br>\r\n        আতরের ছড়াছড়ি চারিধার ।<br>\r\n        চক্‌চকে চুল ছাঁটা<br>\r\n        তায় তোফা টেরিকাটা-<br>\r\n        সোনার চশমা আঁটা নাসিকায় ।<br>\r\n        ঠোঁট দুটি এঁকে বেঁকে<br>\r\n        ধোঁয়া ছাড়ে থেকে থেকে,<br>\r\n        হালচাল দেখে দেখে হাসি পায় ।</p>\r\n      <p>ঘোষেদের ছোট মেয়ে<br>\r\n        পিক্‌ ফেলে পান খেয়ে,<br>\r\n        নিচু পানে নাহি চায় হায়রে ।<br>\r\n        সেই পিক থ্যাপ ক''রে<br>\r\n        লেগেছে চাদর ভরে<br>\r\n        দেখে বাবু কেঁদে মরে যায়রে ।<br>\r\n        ওদিকে ছ্যাকরাগাড়ি<br>\r\n        ছুটে চলে তাড়াতাড়ি<br>\r\n        ছিট্‌কিয়ে কাঁড়ি কাঁড়ি ঘোলাজল ।<br>\r\n        সহসা সে জল লাগে<br>\r\n        জামার পিছন বাগে,<br>\r\n        বাবু করে মহারাগে কোলাহল ।।</p></td>\r\n    <td align="left" valign="bottom"><img src="images/babu2.jpg" width="222" height="300"></td>\r\n  </tr>\r\n</table>', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 388, ''),
(91, 1, '', 'বেজায় রাগ', 'বেজায়-রাগ', 'ও হাড়্‌গিলে, হাড়্‌গিলে ভাই, খাপ্পা বড্ড আজ !', 'ও হাড়্‌গিলে, হাড়্‌গিলে ভাই, খাপ্পা বড্ড আজ !<br>\r\nঝগড়া কি আর সাজে তোমার ? এই কি তোমার যোগ্য কাজ ?<br>\r\nহোম্‌রা চোম্‌রা মান্য তোমরা বিদ্যেবুদ্ধি মর্যাদায়<br>\r\nওদের সঙ্গে তর্ক করছ- নাই কি কোন লজ্জা তায় ?<br>\r\nজান্‌ছ নাকি বলছে ওরা ? &quot;কিচির মিচির কিচ্চিরি,&quot;<br>\r\nঅর্থাৎ কিনা, তোমার নাকি চেহারাটা বিচ্ছিরি !<br>\r\nবল্‌ছে আচ্ছা বলুক, তাতে ওদেরই তো মুখ ব্যথা,<br>\r\nঠ্যাঁটা লোকের শাস্তি যত, ওরাই শেষে ভুগবে তা ।<br>\r\nওরা তোমায় খোঁড়া বল্‌ছে ? বেয়াদব তো খুব দেখি !<br>\r\nতোমার পায়ে বাতের কষ্ট- ওরা সেসব বুঝবে কি ?<br>\r\nতাই বলে কি নাচবে রাগে ? উঠ্‌বে চটে চট্‌ ক''রে ?<br>\r\nমিথ্যে আরো ত্যাক্ত হবে ওদের সাথে টক্করে ।<br>\r\nঐ শোন কি বলছে আবার, কচ্ছে কত বক্তৃতা-<br>\r\nবলছে তোমার ন্যাড়া মাথায় ঘোল ঢালবে- সত্যি তা ?<br>\r\nচড়াই পাখির বড়াই দেখ তোমায় দিচ্ছে টিট্‌কিরি-<br>\r\nবল্‌ছে, তোমার মিষ্টি গলায় গান ধরতে গিট্‌কিরি ।<br>\r\nবল্‌ছে, তোমার কাঁথাটাকে ''রিফুকর্ম'' করবে কি ?<br>\r\nখোঁড়া ঠ্যাঙে নামবে জলে ? আর কোলা ব্যাঙ করবে কি ?<br>\r\nআর চ''টো না, আর শুনো না, ঠ্যাঁটা মুখের টিপ্পনি,<br>\r\nওদের কথায় কান দিতে নেই, স''রে পড় এক্ষনি ।', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 343, ''),
(92, 1, '', 'আজব খেলা', 'আজব-খেলা', 'সোনার মেঘে আল্‌তা ঢেলে সিঁদুর মেখে গায়', 'সোনার মেঘে আল্‌তা ঢেলে সিঁদুর মেখে গায়<br>\r\nসকাল সাঁঝে সূর্যি মামা নিত্যি আসে যায় ।<br>\r\nনিত্যি খেলে রঙের খেলা আকাশ ভ''রে ভ''রে<br>\r\nআপন ছবি আপনি মুছে আঁকে নূতন ক''রে ।<br>\r\nভোরের ছবি মিলিয়ে দিল দিনের আল জ্বেলে<br>\r\nসাঁঝের আঁকা রঙিন ছবি রাতের কালি ঢেলে ।<br>\r\nআবার আঁকে আবার মোছে দিনের পরে দিন<br>\r\nআপন সাথে আপন খেলা চলে বিরামহীন ।<br>\r\nফুরায় নাকি সোনার খেলা ? রঙের নাহি পার ?<br>\r\nকেউ কি জানে কাহার সাথে এমন খেলা তার ?<br>\r\nসেই খেলা, যে ধরার বুকে আলোর গানে গানে<br>\r\nউঠ্‌ছে জেগে- সেই কথা কি সুর্যিমামা জানে ?', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 325, ''),
(93, 1, '', 'আয়রে আলো আয়', 'আয়রে-আলো-আয়', 'পুব গগনে রাত পোহাল', 'পুব গগনে রাত পোহাল,<br>\r\nভোরের কোণে লাজুক আলো<br>\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;নয়ন মেলে চায় ।<br>\r\nআকাশতলে ঝলক জ্বলে,<br>\r\nমেঘের শিশু খেলার ছলে<br>\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;আলোক মাখে গায় ।।<br>\r\nসোনার আলো, রঙিন্‌ আলো,<br>\r\nস্বপ্নে আঁকা নবীন আলো-<br>\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;আয়রে আলো আয় ।<br>\r\nআয়রে নেমে আঁধার পরে,<br>\r\nপাষাণ কালো ধৌত ক''রে<br>\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;আলোর ঝরণায় ।।<br>\r\nঘুম ভাঙান পাখির তানে<br>\r\nজাগ্‌রে আলো আকুল গানে<br>\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;আকূল নীলিমায় ।<br>\r\nআলসভরা আঁখির কোণে,<br>\r\nদুঃখ ভয়ে আঁধার মনে,<br>\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;আয়রে আলো আয় ।।', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 340, ''),
(94, 1, '', 'আলোছায়া', 'আলোছায়া', 'হোক্‌না কেন যতই কালো', 'হোক্‌না কেন যতই কালো<br>\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;এমন ছায়া নাইরে নাই-<br>\r\nলাগ্‌লে পরে রোদের আলো<br>\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;পালায় না যে আপ্‌নি ভাই !.. \r\n<p>শুষ্কমুখে আঁধার ধোঁয়া<br>\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;কঠিন হেন কোথায় বল্‌,<br>\r\nলাগ্‌লে যাতে হাসির ছোঁয়া<br>\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;আপ্‌নি গলে হয় না জল ।।</p?', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 248, ''),
(95, 1, '', 'কিছু চাই?', 'কিছু-চাই?', 'কারোর কিছু চাই গো চাই?', 'কারোর কিছু চাই গো চাই?<br>\r\nএই যে খোকা, কি নেবে ভাই ?<br>\r\nজলছবি আর লাট্টু লাটাই<br>\r\nকেক বিস্কুট লাল দেশলাই<br>\r\nখেলনা বাঁশি কিংবা ঘুড়ি <br>\r\nলেড্‌ পেনসিল রবার ছুরি ?<br>\r\nএসব আমার কিছুই নাই,<br>\r\nকারোর কিছু চাই গো চাই ?\r\n<p>কারোর কিছু চাই গো চাই ?<br>\r\nবৌমা কি চাও শুনতে পাই ?<br>\r\nছিটের কাপড় চিকন লেস্‌<br>\r\nফ্যান্সি জিনিস ছুঁচের কেস্‌<br>\r\nআল্‌তা সিঁদুর কুন্তলীন<br>\r\nকাঁচের চুড়ি বোতাম পিন্‌ ?<br>\r\nআমার কাছে ওসব নাই,<br>\r\nকারোর কিছু চাই গো চাই ?</p>\r\n<p>কারোর কিছু চাই গো চাই ?<br>\r\nআপনি কি চান কর্তামশাই ?<br>\r\nপকেট বই কি খেলার তাস<br>\r\nচুলের কলপ জুতোর ব্রাশ্‌<br>\r\nকলম কালি গঁদের তুলি<br>\r\nনস্যি চুরুট সুর্তি গুলি ?<br>\r\nওসব আমার কিছুই নাই,<br>\r\nকারোর কিছু চাই গো চাই ?</p>', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 497, ''),
(96, 1, '', 'ছুটি', 'ছুটি', 'ঘুচবে জ্বালা পুঁথির পালা ভাবছি সারাক্ষণ', 'ঘুচবে জ্বালা পুঁথির পালা ভাবছি সারাক্ষণ-<br>\r\nপোড়া স্কুলের পড়ার পরে আর কি বসে মন ?<br>\r\nদশটা থেকেই নষ্ট খেলা, ঘণ্টা হতেই শুরু<br>\r\nপ্রাণটা করে ''পালাই পালাই'' মনটা উড়ু উড়ু&mdash;<br>\r\nপড়ার কথা খাতায়, পাতায়, মাথায় নাহি ঢোকে!<br>\r\nমন চলে না- মুখ চলে যায় আবোলতাবোল ব''কে!<br>\r\nকানটা ঘোরে কোন্‌ মুলুকে হুঁশ থাকে না তার,<br>\r\nএ কান দিয়ে ঢুকলে কথা, ও কান দিয়ে পার।<br>\r\nচোখ থাকে না আর কিছুতেই, কেবল দেখে ঘড়ি;<br>\r\nবোর্ডে আঁকা অঙ্ক ঠেকে আঁচড়কাটা খড়ি।<br>\r\nকল্পনাটা স্বপ্নে চ''ড়ে ছুটছে মাঠে ঘাটে&mdash;<br>\r\nআর কি রে মন বাঁধন মানে? ফিরতে কি চায় পাঠে?<br>\r\nপড়ার চাপে ছট্‌ফটিয়ে আর কিরে দিন চলে ?<br>\r\nঝুপ্‌ ক''রে মন ঝাঁপ দিয়ে পড়্‌ ছুটির বন্যাজলে ।', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 446, ''),
(97, 1, '', 'বুঝবার ভুল', 'বুঝবার-ভুল', 'এমনি পড়ায় মন বসেছে, পড়ার নেশায় টিফিন ভোলে', '<table border="0">\r\n  <tr> \r\n    <td align="center" valign="top">&nbsp;&nbsp;&nbsp;&nbsp;<img src="../images/bujhbarbhul1.jpg" width="255" height="150"></td>\r\n  </tr>\r\n  <tr>\r\n    <td valign="top">এমনি পড়ায় মন বসেছে, পড়ার নেশায় টিফিন ভোলে ।<br>\r\n      সাম্‌নে গিয়ে উৎসাহ দেই মিষ্টি দুটো বাক্য বলে ।</td>\r\n  </tr>\r\n  <tr>\r\n    <td align="center" valign="top"><img src="../images/bujhbarbhul2.jpg" width="251" height="150"></td>\r\n  </tr>\r\n  <tr>\r\n    <td valign="top">পড়ছ বুঝি ? বেশ বেশ বেশ ! এক মনেতে পড়লে পরে<br>\r\n      &quot;লক্ষ্মীছেলে - সোনার ছেলে&quot; বলে সবাই আদর করে ।</td>\r\n  </tr>\r\n  <tr>\r\n    <td align="center" valign="top"><img src="../images/bujhbarbhul3.jpg" width="243" height="150"></td>\r\n  </tr>\r\n  <tr>\r\n    <td valign="top">এ আবার কি ? চিত্র নাকি ? বাঁদর পাজি লক্ষ্মীছাড়া-<br>\r\n      আমায় নিয়ে রংতামাসা ! পিটিয়ে তোমায় কর্‌ছি খাড়া ।\r\n      </td>\r\n  </tr>\r\n</table>', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 414, ''),
(98, 1, '', 'ছবি ও গল্প', 'ছবি-ও-গল্প', 'ছবির টানে গল্প লিখি নেইক এতে ফাঁকি', '<table border="0">\r\n  <tr> \r\n    <td colspan="4">ছবির টানে গল্প লিখি নেইক এতে ফাঁকি<br>\r\n      যেমন ধারা কথায় শুনি হুবহু তাই আঁকি ।</font></td>\r\n  </tr>\r\n  <tr> \r\n    <td><img src="../images/chhabiogalpa1.jpg" width="214" height="150"></td>\r\n    <td><img src="../images/chhabiogalpa2.jpg" width="208" height="150"></td>\r\n    <td><img src="../images/chhabiogalpa3.jpg" width="205" height="150"></td>\r\n    <td><img src="../images/chhabiogalpa4.jpg" width="203" height="150"></td>\r\n  </tr>\r\n  <tr> \r\n    <td>পরীক্ষাতে গোল্লা পেয়ে<br>\r\n      হাবু ফেরেন বাড়ি</td>\r\n    <td>চক্ষু দুটি ছানাবড়া<br>\r\n      মুখখানি তার হাঁড়ি</td>\r\n    <td>রাগে আগুন হলেন বাবা<br>\r\n      সকল কথা শুনে</td>\r\n    <td>আচ্ছা ক''রে পিটিয়ে তারে<br>\r\n      দিলেন তুলো ধুনে</td>\r\n  </tr>\r\n  <tr> \r\n    <td><img src="../images/chhabiogalpa5.jpg" width="213" height="150"></td>\r\n    <td><img src="../images/chhabiogalpa6.jpg" width="214" height="150"></td>\r\n    <td><img src="../images/chhabiogalpa7.jpg" width="204" height="150"></td>\r\n    <td><img src="../images/chhabiogalpa8.jpg" width="196" height="150"></td>\r\n  </tr>\r\n  <tr> \r\n    <td>মারের চোটে চেঁচিয়ে বাড়ি<br>\r\n      মাথায় ক''রে তোলে</td>\r\n    <td>শুনে মায়ের বুক ফেটে যায়<br>\r\n      ''হায় কি হল'' ব''লে</td>\r\n    <td>পিসী ভাসেন চোখের জলে<br>\r\n      কুট্‌নো কোটা ফেলে</td>\r\n    <td>আহ্লাদেতে পাশের বাড়ি<br>\r\n      আটখানা হয় ছেলে ।</td>\r\n  </tr>\r\n</table>', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 384, ''),
(99, 1, '', 'সম্পাদকের দশা', 'সম্পাদকের-দশা', 'একদা নিশীথে এক সম্পাদক গোবেচারা', 'সম্পাদকীয়-<br>\r\nএকদা নিশীথে এক সম্পাদক গোবেচারা ।<br>\r\nপোঁটলা পুঁটলি বাঁধি হইলেন দেশছাড়া ।।<br>\r\nঅনাহারী সম্পাদকী হাড়ভাঙা খাটুনি সে ।<br>\r\nজানে তাহা ভুক্তভোগী অপরে বুঝিবে কিসে ?<br>\r\nলেখক পাঠক আদি সকলেরে দিয়া ফাঁকি ।<br>\r\nবেচারী ভাবিল মনে- বিদেশে লুকায়ে থাকি ।।<br>\r\nএদিকে ত ক্রমে ক্রমে বৎসরেক হল শেষ ।<br>\r\n''নোটিশ'' পড়িল কত ''সম্পাদক নিরুদ্দেশ'' ।।<br>\r\nলেখক পাঠকদল রুষিয়া কহিল তবে ।<br>\r\nজ্যান্ত হোক মৃত হোক ব্যাটারে ধরিতে হবে ।।<br>\r\nবাহির হইল সবে শব্দ করি ''মার মার'' ।<br>\r\n-দৈবের লিখন, হায়, খণ্ডাইতে সাধ্য কার ।।<br>\r\nএকদা কেমনি জানি সম্পাদক মহাশয় ।<br>\r\nপড়িলেন ধরা- আহা দুরদৃষ্ট অতিশয় ।।<br>\r\nতারপরে কি ঘটিল কি করিল সম্পাদক ।<br>\r\nসে সকল বিবরণে নাহি তত আবশ্যক ।।<br>\r\nমোট কথা হতভাগ্য সম্পাদক অবশেষে ।<br>\r\nবসিলেন আপনার প্রাচীন গদিতে এসে ।।<br>\r\n(অর্থাৎ লেখকদল লাঠৌষধি শাসনেতে ।<br>\r\nবসায়েছে তারে পুনঃ সম্পাদকী আসনেতে ।।)<br>\r\nঘুচে গেছে বেচারীর ক্ষণিক সে শান্তি সুখ ।<br>\r\nলেখকের তাড়া খেয়ে সদা তার শুষ্কমুখ ।।<br>\r\nদিস্তা দিস্তা পদ্য গদ্য দর্শন সাহিত্য প''ড়ে ।<br>\r\nপুনরায় বেচারির নিত্যি নিত্যি মাথা ধরে ।।<br>\r\nলোলচর্ম অস্থি সার জীর্ণ বেশ রুক্ষ্ণ কেশ ।<br>\r\nমুহূর্ত সোয়াস্তি নাই- লাঞ্ছনার নাহি শেষ ।। ', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 345, ''),
(100, 1, '', 'সন্দেশ', 'সন্দেশ', 'সন্দেশের গন্ধে বুঝি দৌড়ে এলে মাছি', 'সন্দেশের গন্ধে বুঝি দৌড়ে এলে মাছি?<br>\r\nকেন ভন্‌ ভন্‌ হাড় জ্বালাতন ছেড়ে দাওনা বাঁচি !<br>\r\nনাকের গোড়ায় সুড়্‌সুড়ি দাও শেষটা দিবে ফাঁকি ?<br>\r\nসুযোগ বুঝে সুড়ুৎ ক''রে হুল ফোটাবে নাকি ?', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 376, ''),
(101, 1, '', 'বিষম ভোজ', 'বিষম-ভোজ', 'অবাক কাণ্ড ! বললে পিসী, এক চাঙাড়ি মেঠাই এল', '&quot;অবাক কাণ্ড !&quot; বললে পিসী, &quot;এক চাঙাড়ি মেঠাই এল&mdash;<br>\r\nএই ছিল সব খাটের তলায়, এক নিমিষে কোথায় গেল ?&quot;<br>\r\n&quot;সত্যি বটে&quot; বল্‌‌লে খুড়ী, &quot;আনল দু''সের মেঠাই কিনে&mdash;<br>\r\nহঠাৎ কোথায় উপ্‌‌সে গেল ? ভেল্কিবাজি দুপুর দিনে ?&quot;<br>\r\n&quot;দাঁড়াও দেখি&quot; বল্‌‌লে  দাদা, &quot;করছি আমি এর কিনারা&mdash;<br>\r\nকোথায় গেল পট্‌লা ট্যাঁপা&mdash; পাচ্ছিনে যে তাদের সাড়া ?&quot;<br>\r\nপর্দাঘেরা আড়াল দেওয়া বারান্দাটার ঐ কোণেতে<br>\r\nচল্‌ছে কি সব ফিস্‌ ফিস্‌ ফিস্‌ শুনল দাদা কানটি পেতে।<br>\r\nপট্‌লা ট্যাঁপা ব্যস্ত দুজন টপ্‌টপাটপ্‌ মিঠাই ভোজে,<br>\r\nহঠাৎ দেখে কার দুটো হাত এগিয়ে তাদের কানটি খোঁজে।<br>\r\nকানের উপর প্যাঁচ্‌ ঘোরাতেই দুচোখ বেয়ে অশ্রু ছোটে,<br>\r\nগিল্‌‌বে কি ছাই মুখের মিঠাই, কান বুঝি যায় টানের চোটে।<br>\r\nপটলবাবুর হোম্‌রা গলা মিল্‌ল ট্যাঁপার চিকন সুরে<br>\r\nজাগ্‌ল করুণ রাগরাগিণী বিকট তানে আকাশ জুড়ে।', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 418, ''),
(102, 1, '', 'বিষম কাণ্ড', 'বিষম-কাণ্ড', 'কর্তা চলেন, গিন্নী চলেন, খোকাও চলেন সাথে', 'কর্তা চলেন, গিন্নী চলেন, খোকাও চলেন সাথে,<br>\r\nতড়্‌বড়িয়ে বুক ফুলিয়ে শুতে যাচ্ছেন রাতে ।<br>\r\nতেড়ে হন্‌হন্‌ চলে তিনজন যেন পল্টন চলে,<br>\r\nসিঁড়ি উঠ্‌‌তেই, একি কাণ্ড ! এ আবার কি বলে !<br>\r\nল্যাজ লম্বা, কান গোল্‌ গোল্‌, তিড়িং বিড়িং ছোটে,<br>\r\nচোখ্‌ মিট্‌মিট্‌, কুটুস্‌ কাটুস্‌- এটি কোন্‌জন বটে !<br>\r\nহেই ! হুস্‌ ! হ্যাস্‌ ! ওরে বাস্‌‌রে মতলবখানা কিরে,<br>\r\nকরলে তাড়া যায় না তবু, দেখ্‌‌ছে আবার ফিরে ।<br>\r\nভাবছে বুড়ো, করবো গুঁড়ো ছাতার বাড়ি মেরে,<br>\r\nআবার ভাবে ফস্‌‌কে গেলে কাম্‌‌ড়ে দেবে তেড়ে ।<br>\r\nআরে বাপ্‌‌রে ! বস্‌ল দেখ দুই পায়ে ভর ক''রে,<br>\r\nবুক দুর দুর বুড়ো ভল্লুর, মোমবাতি যায় প''ড়ে ।<br>\r\nভীষণ ভয়ে দাঁত কপাটি তিন মহাবীর কাঁপে,<br>\r\nগড়িয়ে নামে হুড়মুড়িয়ে সিঁড়ির ধাপে ধাপে ।', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 312, ''),
(103, 1, '', 'অন্ধ মেয়ে', 'অন্ধ-মেয়ে', 'গভীর কালো মেঘের পরে রঙিন ধনু বাঁকা', 'গভীর কালো মেঘের পরে রঙিন ধনু বাঁকা,<br>\r\nরঙের তুলি বুলিয়ে মেঘে খিলান যেন আঁকা!<br>\r\nসবুজ ঘাসে রোদের পাশে আলোর কেরামতি<br>\r\nরঙিন্ বেশে রঙিন্ ফুলে রঙিন্ প্রজাপতি!<br>\r\n<br>\r\nঅন্ধ মেয়ে দেখ্‌‌ছে না তা&mdash; নাইবা যদি দেখে&mdash;<br>\r\nশীতল মিঠা বাদল হাওয়া যায় যে তারে ডেকে!<br>\r\nশুন্‌‌ছে সে যে পাখির ডাকে হরষ কোলাকুলি<br>\r\nমিষ্ট ঘাসের গন্ধে তারও প্রাণ গিয়েছে ভুলি!<br><br>\r\nদুঃখ সুখের ছন্দে ভরা জগৎ তারও আছে,<br>\r\nতারও আধার জগৎখানি মধুর তারি কাছে।', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 543, ''),
(104, 1, '', 'আনন্দ', 'আনন্দ', 'যে আনন্দ ফুলের বাসে', 'যে আনন্দ ফুলের বাসে,<br>\r\nযে আনন্দ পাখির গানে,<br>\r\nযে আনন্দ অরুণ আলোয়,<br>\r\nযে আনন্দ শিশুর প্রাণে,<br>\r\nযে আনন্দ বাতাস বহে,<br>\r\nযে আনন্দ সাগরজলে,<br>\r\nযে আনন্দ ধুলির কণায়,<br>\r\nযে আনন্দ তৃণের দলে,<br>\r\nযে আনন্দ আকাশ ভরা ,<br>\r\nযে আনন্দ তারায় তারায়,<br>\r\nযে আনন্দ সকল সুখে,<br>\r\nযে আনন্দ রক্তধারায়<br>\r\nসে আনন্দ মধুর হয়ে<br>\r\nতোমার প্রাণে পড়ুক ঝরি,<br>\r\nসে আনন্দ আলোর মত<br>\r\nথাকুক তব জীবন ভরি।', '8K-IBgz-zX0', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 516, '112400352'),
(105, 1, '', 'কত বড়', 'কত-বড়', 'ছোট্ট সে একরতি ইঁদুরের ছানা', 'ছোট্ট সে একরতি ইঁদুরের ছানা,<br>\r\nফোটে নাই চোখ তার, একেবারে কানা।<br>\r\nভাঙা এক দেরাজের ঝুলমাখা কোণে<br>\r\nমার বুকে শুয়ে শুয়ে মার কথা শোনে।<br>\r\n<br>\r\nযেই তার চোখ ফোটে সেই দেখে চেয়ে-<br>\r\nদেরাজের ভারি কাঠ চারিদিক ছেয়ে।<br>\r\nচেয়ে বলে মেলি তার গোল গোল আঁখি-<br>\r\n"ওরে বাবা! পৃথিবীটা এত বড় নাকি?"', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 305, ''),
(106, 1, '', 'ভাল ছেলের নালিশ', 'ভাল-ছেলের-নালিশ', 'মাগো! প্রসন্নটা দুষ্টু এমন! খাচ্ছিল সে পরোটা', 'মাগো!<br>\r\nপ্রসন্নটা দুষ্টু এমন! খাচ্ছিল সে পরোটা<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;গুড় মাখিয়ে আরাম ক''রে বসে -<br>\r\nআমায় দেখে একটা দিল ,নয়কো তাও বড়টা,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;দুইখানা সেই আপনি খেল ক''ষে!<br>\r\nতাইতে আমি কান ধরে তার একটুখানি পেঁচিয়ে<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কিল মেরেছি ''হ্যাংলা ছেলে'' বলে-<br>\r\nঅম্‌‌নি কিনা মিথ্যা করে ষাঁড়ের মত চেচিয়ে<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;গেল সে তার মায়ের কাছে চলে!<br>\r\n<br>\r\nমাগো!<br>\r\nএম্‌‌নিধারা শয়তানি তার, খেলতে গেলাম দুপুরে,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;বল্‌ল, ''এখন খেলতে আমার মানা''-<br>\r\nঘন্টাখানেক পরেই দেখি দিব্যি ছাতের উপরে<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ওড়াচ্ছে তার সবুজ ঘুড়ি খানা।<br>\r\nতাইতে আমি দৌড়ে গিয়ে ঢিল মেরে আর খুঁচিয়ে<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ঘুড়ির পেটে দিলাম করে ফুটো-<br>\r\nআবার দেখ বুক ফুলিয়ে সটান মাথা উঁচিয়ে<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;আনছে কিনে নতুন ঘুড়ি দুটো!', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 499, '');
INSERT INTO `articles` (`id`, `category_id`, `top_image`, `title`, `article_slug`, `first_line`, `body`, `audio`, `added_on`, `modified_on`, `views`, `sc_track_id`) VALUES
(107, 1, '', 'কানা-খোঁড়া সংবাদ', 'কানা-খোঁড়া-সংবাদ', 'পুরাতন কালে ছিল দুই রাজা', '<table border="0">\r\n  <tr align="center"> \r\n    <td align="center" colspan="3"><img src="images/kanakhorasangbad.jpg"></td>\r\n  </tr>\r\n  <tr align="center" valign="middle"> \r\n    <td align="left" valign="top" style=''padding-right: 10px; border-right: 1px solid #ccc;''> \r\n      <div align="left">\r\n      পুরাতন কালে &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ছিল দুই রাজা,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;নাম ধাম নাহি জানা,<br>\r\nএকজন তার &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;খোড়া অতিশয়,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;অপর ভূপতি কানা।<br>\r\nমন ছিল খোলা,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; অতি আলো ভোলা<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ধরমেতে ছিল মতি,<br>\r\nপর ধনে সদা &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ছিল দোঁহাকার<br>\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;বিরাগ বিকট অতি।<br>\r\nপ্রতাপের কিছু &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;নাহি ছিল ত্রুটি<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;মেজাজ রাজারি মত,<br>\r\nশুনেছি কেবল,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;বুদ্ধিটা নাকি<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;নাহি ছিল সরু তত,<br>\r\nভাই ভাই মত  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ছিল দুই রাজা,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;না ছিল ঝগড়াঝাঁটি,<br>\r\n\r\nহেনকালে আসি &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;তিন হাত জমি<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;সকল করিল মাটি।<br>\r\nতিন হাত জমি &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;হেন ছিল, তাহা<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কেহ নাহি জানে কার,<br>\r\nকহে খোঁড়া রায়&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; "এক চক্ষু যার<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;এ জমি হইবে তার"।''<br>\r\nশুনি কানা রাজা &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ক্রোধ করি কয়<br>\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;"আরে অভাগার পুত্র,<br>\r\nএ জমি তোমারি-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; দেখ না এখনি<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;খুলিয়া কাগজ পত্র"।<br>\r\nনক্সা রেখেছে&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; এক বছর<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;বাক্সে বাঁধিয়া আঁটি,<br>\r\nকীট কুটমতি &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কাটিয়া কাটিয়া<br>\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;করিয়াছে তারে মাটি ;<br>\r\nকাজেই তর্ক &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;না মিটিল হায়<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;বিরোধ বাধিল ভারি,<br>\r\nহইল য্দ্দু &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;হদ্দ মতন<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;চৌদ্দ বছর ধরি।<br>\r\nমরিল সৈন্য, &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ভাঙিল অস্ত্র,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;রক্ত চলিল বহি,<br>\r\n\r\nতিন হাত জমি&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; তেমনি রহিল,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কারও হার জিত নাহি<br>\r\nতবে খোঁড়া রাজা &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কহে, হায় হায়,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;তর্ক নাহিক মিটে,<br>\r\nঘোরতর রণে &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;অতি অকারণে<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;মরণ সবার ঘটে"<br>\r\nবলিতে বলিতে &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;চঁটাৎ করিয়া<br>\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;হঠাৎ মাথায় তার<br>\r\nঅদ্ভুত এক &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;বুদ্ধি আসিল<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;অতীব চমৎকার।<br>\r\nকহিল তখন &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;খোঁড়া মহারাজ,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;শুন মোর কানা ভাই,<br>\r\nতুচ্ছ কারণে &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;রক্ত ঢালিয়া<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কখনও সুযশ নাই।<br>\r\n\r\nতার চেয়ে জমি&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; দান করে ফেল<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;আপদ শান্তি হবে।"<br>\r\nকানা রাজা কহে, &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;খাসা কথা ভাই,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কারে দিই কহ তবে।"<br>\r\nকহেন খঞ্জ, &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;" আমার রাজ্যে<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;আছে তিন মহাবীর-<br>\r\nএকটি পেটুক, &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;অপর অলস,<br>\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;তৃতীয় কুস্তিগীর।<br>\r\nতোমার মুলুক &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কে আছে এমন<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;এদের হারাতে পারে?-<br>\r\nসবার সমুখে &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;তিন হাত জমি<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;বকশিস দিব তারে।<br>\r\nকানা রাজা কহে &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ভীমের দোসর<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;আছে ত মল্ল মম,<br>\r\n\r\nফালাহারে পটু, &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;পঁচাশি পেটুক<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;অলস কুমড়া সম।<br>\r\nদেখা যাবে কার &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;বাহাদুরি বেশি<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;আসুক তোমার লোক;<br>\r\nযে  জিতিবে সেই &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;পাবে এই জমি-<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;খোড়া বলে, তাই হোক।<br>\r\nপড়িল নোটিস&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ময়দান মাঝে<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;আলিশান সভা হবে,<br>\r\nতামাসা দেখিতে &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;চারিদিক হতে<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ছুটিয়া আসিল সবে।<br>\r\nভয়ানক ভিড়ে &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ভরে পথঘাট,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;লোকে হল লোকাকার,<br>\r\nমহা কোলাহল &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;দাড়াবার ঠাই<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কোনোখানে নাহি আর।<br>\r\nতারপর ক্রমে &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;রাজার হুকুমে<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;গোলমাল গেল থেমে,<br>\r\n      </div>\r\n    </td>\r\n   \r\n    <td align="left" valign="top" style=''padding-left: 10px;''> \r\n      <div align="left">\r\n      \r\nদুইদিক হতে &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;দুই পালোয়ান<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;আসরে আসিল নেমে।<br>\r\nলম্ফে ঝম্ফে &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;যুঝিল মল্ল<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;গজ-কচ্ছপ হেন,<br>\r\nরুষিয়া মুষ্টি &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;হানিল দোহায়-<br>\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;বজ্র পড়িল যেন!<br>\r\nগুঁতাইল কত &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ভোঁতাইল নাসা<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;উপাড়িল গোফ দাড়ি,<br>\r\nযতেক দন্ত &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;করিল অন্ত<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ভীষণ চাপটি মারি<br>\r\nতারপরে দোঁহে &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;দোঁহারে ধরিয়া<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ছুঁড়িল এমনি জোরে,<br>\r\n\r\nগোলার মতন &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;গেল গো উড়িয়া<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;দুই বীর বেগভরে।<br>\r\nকিহল তাদের &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কেহ নাহি জানে<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;নানা কথা কয় লোকে,<br>\r\nআজও কেহ তার &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;পায়নি খবর,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কেহই দেখেনি চোখে।<br>\r\nযাহোক এদিকে, &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কুস্তির শেষে<br>\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;এল পেটুকের পালা,<br>\r\nযেন অতিকায় &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ফুটবল দুটি,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;অথবা ঢাকাই জালা।<br>\r\nওজনেতে তারা &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কেহ নহে কম,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ভোজনেতে ততোধিক,<br>\r\nবপু সুবিপুল, &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ভুড়ি বিভীষন-<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ভারি সাতমন ঠিক।<br>\r\n\r\nঅবাক দেখিছে &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;সভার সকলে<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;আজব কান্ড ভারি-<br>\r\nধামা ধামা লুচি &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;নিমেষে ফুরায়<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;দই ওঠে হাঁড়ি হাঁড়ি!<br>\r\nদাড়ি পাল্লায় &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;মাপিয়া সকলে<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;দেখে আহারের পরে ,<br>\r\nদুজনেই ঠিক&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; বেড়েছে ওজনে<br>\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;সাড়ে তিন মন করে।<br>\r\nকানা রাজা বলে&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; একি হল জ্বালা,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;আক্কেল নাই কারো ,<br>\r\nকেহ কি বোঝেনা &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;সোজা কথা এই,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;হয় জেতো নয় হারো।"<br>\r\nতার পর এল&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; কুঁড়ে দুই জন<br>\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ঝাকার উপর চড়ে,<br>\r\nসভামাঝে দোহে &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;শুয়ে চিৎপাত<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;চুপ চাপ রহে পড়ে।<br>\r\nহাত নাহি নাড়ে,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; চোখ নাহি মেলে,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কথা নাই কারো মুখে,<br>\r\nদিন দুই তিন&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; রহিল পড়িয়া,<br>\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;নাসা গীত গাহি সুখে।<br>\r\nজঠরে যখন &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;জ্বলিল আগুন,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;পরান কণ্ঠাগত,<br>\r\nতখন কেবল&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; মেলিয়া আনন<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;থাকিল মড়ার মত।<br>\r\nদয়া করে তবে &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;সহৃদয় কেহ<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;নিকটে আসিয়া ছুটি<br>\r\n\r\nমুখের নিকটে &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ধরিল তাদের<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;চাটিম কদলি দুটি।<br>\r\nখঞ্জের লোকে &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কহিল কষ্টে,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;"ছাড়িয়া দে নারে ভাই"<br>\r\nকানার ভৃত্য &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;রহিল হা করে<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;মুখে তার কথা নাই ।<br>\r\nতখন সকলে&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; কাষ্ঠ আনিয়া<br>\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;তায় কেরোসিন ঢালি,<br>\r\nকুড়েদের গায়ে &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;চাপাইয়া রোষে<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;দেশলাই দিল জ্বালি।<br>\r\nখোঁড়ার প্রজাটি &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;বাপরে বলিয়া<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;লাফ দিয়া তাড়াতাড়ি<br>\r\nকম্পিত পদে&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; চম্পট দিল<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;একেবারে সভা ছাড়ি।<br>\r\n\r\nদুয়ো বলি সবে&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;দেয় করতালি<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;পিছু পিছু ডাকে "ফেউ"?<br>\r\nকানার অলস &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;বলে কি আপদ<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ঘুমুতে দিবিনা কেঊ?<br>\r\nশুনে সবে বলে &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;"ধন্য ধন্য<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কুঁড়ে-কুল চুড়ামণি!"<br>\r\nছুটিয়া তাহারে &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;বাহির করিল<br>\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;আগুন হইতে টানি।<br>\r\nকানার লোকের &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;গুণপনা দেখে<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;কানা রাজা খুসী ভারি,<br>\r\nজমিতে দিলেই &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;আরও দিল কত,<br>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;টাকাকড়ি ঘরবাড়ি।<br>\r\n      </div>\r\n    </td>\r\n  </tr>\r\n</table>', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 316, ''),
(108, 1, '', 'সাহস', 'সাহস', 'পুলিশ দেখে ডরাইনে আর, পালাইনে আর ভয়ে', 'পুলিশ দেখে ডরাইনে আর, পালাইনে আর ভয়ে,<br>\r\nআরশুলা কি ফড়িং এলে থাকতে পারি সয়ে।<br>\r\nআধাঁর ঘরে ঢুকতে পারি এই সাহসের গুণে,<br>\r\nআর করে না বুক দুর্ দুর্ জুজুর নামটি শুনে।<br>\r\nরাত্তিরেতে একলা শুয়ে তাও ত থাকি কত,<br>\r\nমেঘ ডাকলে চেঁচাইনেকো আহাম্মুকের মত।<br>\r\nমামার বাড়ির কুকুর দুটোর বাঘের মত চোখ,<br>\r\nতাদের আমি খাবার খাওয়াই এমনি আমার রোখ্!<br>\r\nএম্‌‌নি আরো নানান দিকে সাহস আমার খেলে<br>\r\nসবাই বলে "খুব বাহাদুর" কিংবা "সাবাস ছেলে"।<br>\r\nকিন্তু তবু শীতকালেতে সকালবেলায় হেন<br>\r\nঠান্ডা জলে নাইতে হ''লে কান্না আসে কেন?<br>\r\nসাহস টাহস সব যে তখন কোনখানে যায় উড়ে-<br>\r\nষাড়ের মতন কন্ঠ ছেড়ে চেঁচাই বিকট সুরে!', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 253, ''),
(109, 1, '', 'মন্ডা ক্লাবের  কয়েকটি আমন্ত্রণপত্র', 'মন্ডা-ক্লাবের--কয়েকটি-আমন্ত্রণপত্র', 'সম্পাদক বেয়াকুব', '<table border="0">\r\n  <tr align="center" valign="middle"> \r\n    <td align="left" valign="top" style=''padding-right: 10px; border-right:1px solid #ccc;''> \r\n      <div align="left">\r\n      <b>১</b><br>\r\nসম্পাদক বেয়াকুব<br>\r\nকোথা যে দিয়েছে ডুব-<br>\r\nএদিকেতে হায় হায়<br>\r\nক্লাবটি তো যায় যায়।<br>\r\n<br>\r\nতাই বলি সোমবারে<br>\r\nমদগৃহে গড়পারে<br>\r\nদিলে সবে পদধূলি<br>\r\nক্লাবটিরে ঠেলে তুলি।<br>\r\n<br>\r\nরকমারি পুঁথি কত<br>\r\nনিজ নিজ রুচিমত<br>\r\nআনিবেন সাথে সবে<br>\r\nকিছু কিছু পাঠ হবে<br>\r\n<br>\r\nকরযোড়ে বারবার<br>\r\nনিবেদিছে সুকুমার।<br>\r\n      </div>\r\n    </td>\r\n    <td align="left" valign="top" style=''padding-right: 10px;  padding-left: 10px; border-right:1px solid #ccc;''> \r\n      <div align="left">\r\n      <b>২</b><br>\r\nকেউ বলেছে খাবো খাবো,<br>\r\nকেউ বলেছে খাই<br>\r\nসবাই মিলে গোল তুলেছে-<br>\r\nআমি তো আর নাই।<br>\r\n<br>\r\nছোটকু বলে, রইনু চুপে<br>\r\nক''মাস ধরে কাহিল রূপে!<br>\r\nজংলি বলে "রামছাগলের<br>\r\nমাংস খেতে চাই।"<br>\r\n<br>\r\nযতই বলি "সবুর কর" -<br>\r\nকেউ শোনে না কালা,<br>\r\nজীবন বলে কোমর বেধে,<br>\r\nকোথায় লুচির থালা?<br>\r\n<br>\r\nখোদন বলে রেগেমেগে<br>\r\nভীষণ রোষে বিষম লেগে-<br>\r\nবিষ্যুতে কাল গড়পারেতে<br>\r\nহাজির যেন পাই।<br>\r\n      </div>\r\n    </td>\r\n   \r\n    <td align="left" valign="top" style=''padding-right: 10px; padding-left: 10px; border-right:1px solid #ccc;''> \r\n      <div align="left">\r\n      <b>৩</b><br>\r\nশনিবার ১৭ ই<br>\r\nসাড়ে পাঁচ বেলা,<br>\r\nগড়পারে হৈ হৈ<br>\r\nসরবতী মেলা।<br>\r\n<br>\r\nঅতএব ঘড়ি ধরে<br>\r\nসাবকাশ হয়ে<br>\r\nআসিবেন দয়া করে<br>\r\nহাসিমুখে লয়ে।<br>\r\n<br>\r\nসরবৎ সদালাপ<br>\r\nসঙ্গীত - ভীতি<br>\r\nফাঁকি দিলে নাহি মাপ,<br>\r\nজেনে রাখ-ইতি।<br>\r\n      </div>\r\n    </td>\r\n    <td align="left" valign="top" style=''padding-left: 10px;''> \r\n      <div align="left">\r\n      <b>৪</b><br>\r\nআমি,অর্থাৎ সেক্রেটারি,<br>\r\nমাসতিনেক কল‌কেতা ছাড়ি<br>\r\nযেই গিয়েছি অন্য দেশে<br>\r\nঅমনি কি সব গেছে ফেঁসে।<br>\r\n<br>\r\nবদলে গেছে ক্লাবের হাওয়া,<br>\r\nকাজের মধ্যে কেবল খাওয়া!<br>\r\nচিন্তা নেইক গভীর বিষয়<br>\r\nআমার প্রাণে এসব কি সয়?<br>\r\n<br>\r\nএখন থেকে সম্‌‌ঝে রাখ<br>\r\nএ সমস্ত চলবে নাকো,<br>\r\nআমি আবার এইছি ঘুরে<br>\r\nতান ধরেছি সাবেক সুরে।<br>\r\n<br>\r\nশুনবে এস সুপ্রবন্ধ<br>\r\nগিরিজার বিবেকানন্দ,<br>\r\nমঙ্গলবার আমার বাসায়।<br>\r\n(আর থেক না ভোজের আশায়)<br>\r\n      </div>\r\n    </td>\r\n  </tr>\r\n</table>', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 440, ''),
(110, 1, '', 'ও বাবা!', 'ও-বাবা!', 'পড়তে বসে মুখের কাছে কাগজখানি থুয়ে', 'পড়তে বসে মুখের কাছে কাগজখানি থুয়ে<br/>\r\nরমেশ ভায়া ঘুমোয় পড়ে আরাম ক''রে শুয়ে ।<br/>\r\nশুনছ নাকি ঘডর ঘড়র নাক ডাকার ধূম ?<br/>\r\nসখ যে বড় বেজায় দেখি- দিনের বেলায় ঘুম !<br/><br/>\r\n<img src="images/o_baba_2.gif" alt="O Baba 2"><br/><br/>\r\nবাতাস পোরা এই যে থলি দেখ্‌‌ছ আমার হাতে,<br/>\r\nদুড়ম করে পিট্‌‌লে পরে শব্দ হবে তাতে ।<br/>\r\nরমেশ ভায়া আঁৎ‌কে উঠে পড়্‌‌বে কুপোকাৎ‌ <br/>\r\nলাগাও তবে- ধূমধারাক্কা ! ক্যাবাৎ‌ ! ক্যাবাৎ‌ !<br/><br/>\r\n<img src="images/o_baba_3.gif" alt="O Baba 3"><br/><br/>\r\nও বাবারে ! এ কেরে ভাই ! মারবে নাকি চাঁটি ?<br/>\r\nআমি ভাবছি রমেশ বুঝি ! সব করেছে মাটি !<br/>\r\nআবার দেখ চোখ পাকিয়ে আস্‌‌ছে আমায় তেড়ে-<br/>\r\nআর কেন ভাই ? দৌড়ে পালাই, প্রাণের আশা ছেড়ে !', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 502, ''),
(111, 1, '', 'বিচার', 'বিচার', 'ইঁদুর দেখে মাম্‌‌দো কুকুর বল্‌‌লে তেড়ে হেঁকে', 'ইঁদুর দেখে মাম্‌‌দো কুকুর বল্‌‌লে তেড়ে হেঁকে-<br/>\r\n"বল্‌‌ব কি আর, বড়ই খুশি হলেম তোরে দেখে ।<br/>\r\nআজকে আমার কাজ কিছু নেই, সময় আছে মেলা,<br/>\r\nআয় না খেলি দুইজনেতে মোকদ্দমা খেলা । <br/>\r\nতুই হবি চোর, তোর নামেতে করব নালিশ রুজু"-<br/>\r\n"জজ্‌‌ কে হবে ?" বল্‌‌লে ইঁদুর, বিষম ভয়ে জুজু,<br/>\r\n"কোথায় উকিল, প্যায়দা পুলিশ, বিচার কিসে হবে ?"<br/>\r\nমাম্‌‌দো বলে, "তাও জানিস্‌‌নে ? শোন বলে দেই তবে ।<br/>\r\nআমিই হব উকিল হাকিম, আমিই হব জুরি,<br/>\r\nকান ধ''রে তোর বল্‌‌ব, "ব্যাটা, ফের করেছিস্‌‌ চুরি ?"<br/>\r\nসটান দেব ফাঁসির হুকুম অম্‌‌নি একেবারে-<br/>\r\nবুঝ্‌‌বি তখন চোর বাছাধন বিচার বলে কারে ।"', '', '2013-03-18 00:00:00', '2014-08-21 16:53:01', 282, '');
INSERT INTO `articles` (`id`, `category_id`, `top_image`, `title`, `article_slug`, `first_line`, `body`, `audio`, `added_on`, `modified_on`, `views`, `sc_track_id`) VALUES
(112, 2, '', 'হ য ব র ল', 'হ-য-ব-র-ল', '', '<p><img src="../images/hajabarala1.gif" width="300" height="200" align="middle"></p>\r\n  <p>&nbsp;</p>\r\n  <p> বেজায় গরম । গাছতলায় দিব্যি ছায়ার \r\n    মধ্যে চুপচাপ শুয়ে আছি, তবু ঘেমে অস্থির । ঘাসের উপর রুমালটা ছিল ; ঘাম মুছবার \r\n    জন্য যেই সেটা তুলতে গিয়েছি, অমনি রুমালটা বলল, ''ম্যাও !'' কি আপদ ! রুমালটা ম্যাও \r\n    করে কেন ?<br />\r\n    চেয়ে দেখি রুমাল তো আর রুমাল নেই, দিব্যি মোটা সোটা লাল টক্‌টকে একটা বেড়াল গোঁফ \r\n    ফুলিয়ে প্যাট্‌ প্যাট্‌ করে আমার দিকে তাকিয়ে আছে ।<br />\r\n    আমি বললাম, ''কি মুশকিল ! ছিল রুমাল, হয়ে গেল একটা বেড়াল ।''<br />\r\n    অমনি বেড়ালটা বলে উঠল, ''মুশকিল আবার কি ? ছিল একটা ডিম, হয়ে গেল দিব্যি একটা \r\n    প্যাঁকপ্যাঁকে হাঁস । এ তো হামেশাই হচ্ছে ।''<br />\r\n    আমি খানিক ভেবে বললাম, ''তাহলে তোমায় এখন কি বলে ডাকব ? তুমি তো সত্যিকারের বেড়াল \r\n    নও, আসলে তুমি হচ্ছ রুমাল ।''<br />\r\n    বেড়াল বলল, ''বেড়ালও বলতে পার, রুমালও বলতে পার, চন্দ্রবিন্দুও বলতে পার ।'' আমি \r\n    বললাম, ''চন্দ্রবিন্দু কেন ?''<br />\r\n    বেড়াল বলল, ''তাও জানো না?'' ব''লে এক চোখ বুজে ফ্যাঁচ্‌ ফ্যাঁচ্‌ করে বিশ্রী রকম \r\n    হাসতে লাগল । আমি ভারি অপ্রস্তুত হয়ে গেলাম । মনে হল, ঐ চন্দ্রবিন্দুর কথাটা \r\n    নিশ্চই আমার বোঝা উচিৎ ছিল । তাই থতমত খেয়ে তাড়াতাড়ি বলে ফেললাম, ''ও হ্যাঁ হ্যাঁ, \r\n    বুঝতে পেরেছি ।''<br />\r\n    বেড়ালটা খুশি হয়ে বলল, ''হ্যাঁ, এ তো বোঝাই যাচ্ছে- চন্দ্রবিন্দুর চ, বেড়ালের \r\n    তালব্য শ, আর রুমালের মা- হল চশমা । কেমন, হল তো ?''<br />\r\n    আমি কিছুই বুঝতে পারলাম না, কিন্তু পাছে বেড়ালটা আবার সেই রকম বিশ্রী করে হেসে \r\n    ওঠে, তাই সঙ্গে সঙ্গে হুঁ হুঁ করে গেলাম । তারপর বেড়ালটা খানিকক্ষণ আকাশের দিকে \r\n    তাকিয়ে হঠাৎ বলে উঠল, ''গরম লাগে তো তিব্বত গেলেই পার ।'' আমি বললাম, কিন্তু বললেই \r\n    তো আর যাওয়া যায় না ?''<br />\r\n    বেড়াল বলল, ''কেন ? সে আর মুশকিল কি ?''<br />\r\n    আমি বললাম, ''কি করে যেতে হয় তুমি জানো ?''<br />\r\n    বেড়াল একগাল হেসে বলল, ''তা আর জানিনে ? কলকেতা, ডায়মন্ড হারবার, রানাঘাট, তিব্বত,- \r\n    ব্যাস্‌ ! সিধে রাস্তা, সওয়া<img src="../images/hajabarala2.gif" width="250" height="338" align="right"> \r\n    ঘণ্টার পথ, গেলেই হল ।''<br />\r\n    আমি বললাম, ''তাহলে রাস্তাটা আমায় বাতলে দিতে পার ?''<br />\r\n    শুনে বেড়ালটা কেমন গম্ভীর হয়ে গেল । তারপর মাথা নেড়ে বলল, ''উহুঁ সে আমার কর্ম \r\n    নয় । আমার গেছোদাদা যদি থাকত, তাহলে সে ঠিক বলতে পারত ।''<br />\r\n    আমি বললাম, ''গেছো দাদা কে ? তিনি থাকেন কোথায় ?''<br />\r\n    বেড়াল বলল, ''গেছো দাদা আবার কোথায় থাকবে ? গাছেই থাকে ।''<br />\r\n    আমি বললাম, ''কোথায় তাঁর সঙ্গে দেখা হয় ?''<br />\r\n    বেড়াল খুব জোরে জোরে মাথা নেড়ে বলল, ''সেটি হচ্ছে না, সে হবার যো নেই ।''<br />\r\n    আমি বললাম, ''কি রকম ?''<br />\r\n    বেড়াল বলল, ''সে কি রকম জানো ? মনে কর, তুমি যখন যাবে উলুবেড়ে তাঁর সঙ্গে দেখা \r\n    করতে, তখন তিনি থাকবেন মতিহারি । যদি মতিহারি যাও, তাহলে শুনবে তিনি আছেন রামকিষ্টপুর \r\n    । আবার সেখানে গেলে দেখবে তিনি গেলেন কাশিমবাজার । কিছুতেই দেখা হবার যো নেই \r\n    ।''<br />\r\n    আমি বললাম, তাহলে তোমরা কি করে দেখা কর ?''<br />\r\n    বেড়াল বলল, ''সে অনেক হাঙ্গামা । আগে হিসেব করে দেখতে হবে, দাদা কোথায় কোথায় নেই \r\n    ; তারপর হিসেব করে দেখতে হবে, দাদা কোথায় কোথায় থাকতে পারে ; তারপর দেখতে হবে, \r\n    দাদা এখন কোথায় আছে । তারপর দেখতে হবে, সেই হিসেব মতো যখন সেখানে গিয়ে পৌঁছব, \r\n    তখন দাদা কোথায় থাকবে । তারপর দেখতে হবে-''<br />\r\n    আমি তাড়াতাড়ি বাধা দিয়ে বললাম, ''সে কি রকম হিসেব ?''<br />\r\n    বেড়াল বলল, ''সে ভারি শক্ত । দেখবে কি রকম ?'' এই বলে সে একটা কাঠি দিয়ে ঘাসের \r\n    উপর লম্বা আঁচড় কেটে বলল, ''এই মনে কর গেছোদাদা ।'' বলেই খানিকক্ষণ গম্ভীর হয়ে \r\n    চুপ করে বসে রইল ।<br />\r\n    তারপর আবার ঠিক তেমনি আঁচড় কেটে বলল, ''এই মনে কর তুমি,'' বলে ঘাড় বেঁকিয়ে চুপ \r\n    করে রইল ।<br />\r\n    তারপর হঠাৎ আবার একটা আঁচড় কেটে বলল, ''এই মনে কর চন্দ্রবিন্দু ।'' এমনি করে খানিকক্ষণ \r\n    কি ভাবে আর একটা করে লম্বা আঁচড় কাটে, আর বলে, ''এই মনে কর তিব্বত''- ''এই মনে কর \r\n    গেছোবৌদি রান্না করছে''- ''এই মনে কর গাছের গায়ে একটা ফুটো-''<br />\r\n    এই রকম শুনতে শুনতে শেষটায় আমার কেমন রাগ ধরে গেল । আমি বললাম, ''দূর ছাই ! কি \r\n    সব আবোল-তাবোল বকছ, একটুও ভালো লাগে না ।''<br />\r\n    বেড়াল বলল, ''আচ্ছা তাহলে আর একটু সহজ করে বলছি । চোখ বোজ, আমি যা বলব, মনে মনে \r\n    তার হিসেব কর ।'' আমি চোখ বুজলাম ।<br />\r\n    চোখ বুজেই আছি, বুজেই আছি, বেড়ালের আর কোন সাড়া-শব্দ নেই । হঠাৎ কেমন সন্দেহ \r\n    হল, চোখ চেয়ে দেখি বেড়ালটা ল্যাজ খাড়া করে বাগানের বেড়া টপকিয়ে পালাচ্ছে আর ক্রমাগত \r\n    ফ্যাঁচ্‌ ফ্যাঁচ্‌ করে হাসছে ।<br />\r\n    কি আর করি, গাছ তলায় একটা পাথরের উপর বসে পড়লাম । বসতেই কে যেন ভাঙা মোটা গলায় \r\n    বলে উঠল, ''সাত দুগুণে কত হয় ?''<img src="../images/hajabarala3.gif" width="250" height="355" align="right"><br />\r\n    আমি ভাবলাম, এ আবার কে রে ? এদিক ওদিক তাকাচ্ছি, এমন সময় আবার সেই আওয়াজ হল, \r\n    ''কই জবাব দিচ্ছ না যে ? সাত দুগুণে কত হয় ?'' তখন উপর দিকে তাকিয়ে দেখি, একটা \r\n    দাঁড়কাক শ্লেট পেনসিল দিয়ে কি যেন লিখছে, আর এক একবার ঘাড় বেঁকিয়ে আমার দিকে \r\n    তাকাচ্ছে ।<br />\r\n    আমি বললাম, ''সাত দুগুণে চোদ্দ ।''<br />\r\n    কাকটা অমনি দুলে দুলে মাথা নেড়ে বলল, ''হয়নি, হয়নি, ফেল্‌ ।''<br />\r\n    আমার ভয়ানক রাগ হল । বললাম, ''নিশ্চয় হয়েছে । সাতেক্কে সাত, সাত দুগুণে চোদ্দ, \r\n    তিন সাত্তে একুশ ।''<br />\r\n    কাকটা কিছু জবাব দিল না, খালি পেনসিল মুখে দিয়ে খানিকক্ষণ কি যেন ভাবল ।<br />\r\n    তারপরে বলল, ''সাত দুগুণে চোদ্দর নামে চার, হাতে রইল পেনসিল ।''<br />\r\n    আমি বললাম, ''তবে যে বলছিলে সাত দুগুণে চোদ্দ হয় না ? এখন কেন ?''<br />\r\n    কাক বলল, ''তুমি যখন বলেছিলে, তখনো পুরো চোদ্দ হয়নি । তখন ছিল, তেরো টাকা চোদ্দ \r\n    আনা তিন পাই । আমি যদি ঠিক সময়ে বুঝে ধাঁ করে ১৪ লিখে না ফেলতাম, তাহলে এতক্ষণে \r\n    হয়ে যেত চোদ্দ টাকা এক আনা নয় পাই ।''<br />\r\n    আমি বললাম, ''এমন আনাড়ি কথা তো কখনো শুনিনি । সাত দুগুণে যদি চোদ্দ হয়, তা সে \r\n    সব সময়েই চোদ্দ । একঘণ্টা আগে হলেও যা, দশ দিন পরে হলেও তাই ।''<br />\r\n    কাকটা ভারি অবাক হয়ে বলল, ''তোমাদের দেশে সময়ের দাম নেই বুঝি ?''<br />\r\n    আমি বললাম, ''সময়ের দাম কি রকম ?''<br />\r\n    কাক বলল, ''এখানে কদিন থাকতে, তাহলে বুঝতে । আমাদের বাজারে সময় এখন ভয়ানক মাগ্যি, \r\n    এতটুকু বাজে খরচ করবার যো নেই । এইতো কদিন আগে খেটেখুটে চুরিচামারি করে খানিকটা \r\n    সময় জমিয়েছিলাম, তাও তোমার সঙ্গে তর্ক করতে অর্ধেক খরচ হয়ে গেল ।'' বলে সে আবার \r\n    হিসেব করতে লাগল । আমি অপ্রস্তুত হয়ে বসে রইলাম ।<br />\r\n    এমন সময় গাছের একটা ফোকর থেকে কি যেন একটা সুড়ুৎ করে পিছলিয়ে মাটিতে নামল । চেয়ে \r\n    দেখি, দেড় হাত লম্বা এক বুড়ো, তার পা পর্যন্ত সবুজ রঙের দাড়ি, হাতে একটা হুঁকো, \r\n    তাতে কল্‌কে-টল্‌কে কিচ্ছু নেই, আর মাথা ভরা টাক । টাকের উপর খড়ি দিয়ে কে যেন \r\n    কি সব লিখেছে ।<br />\r\n    বুড়ো এসেই খুব ব্যস্ত হয়ে হুঁকোতে দু-এক টান দিয়েই জিজ্ঞাসা করল, ''কই, হিসেবটা \r\n    হল ?''<br />\r\n    কাক খানিক এদিক ওদিক তাকিয়ে বলল, ''এই হল বলে ।''<br />\r\n    বুড়ো বলল, ''কি আশ্চর্য ! উনিশ দিন পার হয়ে গেল, এখনো হিসেবটা হয়ে উঠল না ?'' কাক \r\n    দু-চার মিনিট খুব গম্ভীর হয়ে পেনসিল চুষল, তারপর জিজ্ঞাসা করল, ''কতদিন বললে ?''<br />\r\n    বুড়ো বলল, ''উনিশ ।''<br />\r\n    কাক অমনি গলা উঁচিয়ে হেঁকে বলল, ''লাগ্‌ লাগ্‌ লাগ্‌ কুড়ি ।''<br />\r\n    বুড়ো বলল, ''একুশ ।'' কাক বলল, ''বাইশ ।'' বুড়ো বলল, ''তেইশ ।'' কাক বলল, ''সাড়ে তেইশ \r\n    ।'' ঠিক যেন নিলেম ডাকছে । ডাকতে ডাকতে কাকটা হঠাৎ আমার দিকে তাকিয়ে বলল, ''তুমি \r\n    ডাকছ না যে ?'' আমি বললাম, ''খামখা ডাকতে যাব কেন ?''<br />\r\n    বুড়ো এতক্ষণ আমায় দেখেনি, হঠাৎ আমার আওয়াজ শুনেই সে বন্‌ বন্‌ করে আট দশ পাক \r\n    ঘুরে আমার দিকে ফিরে দাঁড়াল । তারপর হুঁকোটাকে দূরবীনের মতো করে চোখের সামনে \r\n    ধরে আনেকক্ষণ আমার দিকে তাকিয়ে রইল । তারপর পকেট থেকে কয়েকখানা রঙিন কাচ বের \r\n    করে তাই দিয়ে আমায় বার বার দেখতে লাগল । তারপর কোত্থেকে একটা পুরানো দরজীর ফিতে \r\n    এনে আমার মাপ নিতে শুরু করল, আর হাঁকতে লাগল, ''খাড়াই ছাব্বিশ ইঞ্চি, হাতা ছাব্বিশ \r\n    ইঞ্চি, আস্তিন ছাব্বিশ ইঞ্চি, ছাতি ছাব্বিশ ইঞ্চি, গলা ছাব্বিশ ইঞ্চি ।''<br />\r\n    আমি ভয়ানক আপত্তি করে বললাম, ''এ হতেই পারে না । বুকের মাপও ছাব্বিশ ইঞ্চি, গলাও \r\n    ছাব্বিশ ইঞ্চি ? আমি কি শুওর ?''<br />\r\n    বুড়ো বলল, ''বিশ্বাস না হয়, দেখ ।'' দেখলাম ফিতের লেখা-টেখা সব উঠে গিয়েছে, খালি \r\n    ২৬ লেখাটা একটু পড়া যাচ্ছে, তাই বুড়ো যা কিছু মাপে ছাব্বিশ ইঞ্চি হয়ে যায় । <br />\r\n    তারপর বুড়ো জিজ্ঞেস করল, ''ওজন কত ?''<br />\r\n    আমি বললাম, ''জানি না ।''<br />\r\n    বুড়ো তার দুটো আঙুল দিয়ে আমায় একটুখানি টিপে টিপে বলল, ''আড়াই সের ।'' আমি বললাম, \r\n    ''সেকি, পটলার ওজনই তো একুশ সের, সে আমার চাইতে দেড় বছরের ছোট ।'' কাকটা অমনি তাড়াতাড়ি \r\n    বলে উঠল, ''সে তোমাদের হিসেব অন্য রকম ।'' <br />\r\n    বুড়ো বলল, ''তাহলে লিখে নাও- ওজন আড়াই সের, বয়স সাঁইত্রিশ ।''<br />\r\n    আমি বললাম, ''দুৎ ! আমার বয়স হল আট বছর তিনমাস, বলে কিনা সাঁইত্রিশ ।''<br />\r\n    বুড়ো খানিকক্ষণ কি যেন ভেবে জিজ্ঞাসা করল, ''বাড়তি না কমতি ?'' আমি বললাম, ''সে \r\n    আবার কি ?''<br />\r\n    বুড়ো বলল, ''বলি বয়েসটা এখন কমছে না বাড়ছে ?'' আমি বললাম, ''বয়েস আবার কমবে কি ?'' \r\n    বুড়ো বলল, ''তা নয় তো কেবলই বেড়ে চলবে নাকি ? তাহলেই তো গেছি । কোনদিন দেখবে বয়েস \r\n    বাড়তে বাড়তে একেবারে ষাট সত্তর আশি পার হয়ে গেছে । শেষটায় বুড়ো হয়ে মরি আর কি \r\n    !''<br />\r\n    আমি বললাম, ''তা তো হবেই । আশি বছর বয়েস হলে মানুষ বুড়ো হবে না ?'' বুড়ো বলল, ''তোমার \r\n    যেমন বুদ্ধি ! আশি বছর বয়েস হবে কেন ? চল্লিশ বছর হলেই আমরা বয়েস ঘুরিয়ে দিই \r\n    । তখন আর একচল্লিশ বেয়াল্লিশ হয় না- উনচল্লিশ, আটত্রিশ, সাঁইত্রিশ করে বয়েস নামতে \r\n    থাকে । এমনি করে যখন দশ পর্যন্ত নামে তখন আবার বয়েস বাড়তে দেওয়া হয় । আমার বয়েস \r\n    তো কত উঠল নামল আবার উঠল, এখন আমার বয়েস হয়েছে তেরো ।'' শুনে আমার ভয়ানক হাসি \r\n    পেয়ে গেল ।<br />\r\n    কাক বলল, ''তোমরা একটু আস্তে আস্তে কথা কও, আমার হিসেবটা চট্‌পট্‌ সেরে নি ।''<br />\r\n    বুড়ো অমনি চট্‌ করে আমার পাশে এসে ঠ্যাং ঝুলিয়ে বসে ফিস্‌ ফিস্‌ করে বলতে লাগল, \r\n    ''একটি চমৎকার গল্প বলব । দাঁড়াও একটু ভেবে নি ।'' এই বলে তার হুঁকো দিয়ে টেকো \r\n    মাথা চুলকাতে চুলকাতে চোখ বুজে ভাবতে লাগল । তারপর হঠাৎ বলে উঠল, ''হ্যাঁ, মনে \r\n    হয়েছে, শোনো-<br />\r\n    ''তারপর এদিকে বড়মন্ত্রী তো রাজকন্যার গুলিসুতো খেয়ে ফেলেছে । কেউ কিচ্ছু জানে \r\n    না । ওদিকে রাক্ষসটা করেছে কি, ঘুমুতে ঘুমুতে হাঁউ-মাঁউ-কাঁউ, মানুষের গন্ধ পাঁউ \r\n    বলে হুড়্‌মুড়্‌ করে খাট থেকে পড়ে গিয়েছে । অমনি ঢাক ঢোল সানাই কাঁশি লোক লস্কর \r\n    সেপাই পল্টন হৈ-হৈ রৈ-রৈ মার্‌-মার্‌ কাট্‌-কাট্‌- এর মধ্যে হঠাৎ রাজা বলে উঠলেন, \r\n    পক্ষীরাজ যদি হবে, তাহলে ন্যাজ নেই কেন ? শুনে পাত্র মিত্র ডাক্তার মোক্তার আক্কেল \r\n    মক্কেল সবাই বললে, ভালো কথা ! ন্যাজ কি হল ? কেউ তার জবাব দিতে পারে না, সব সুড়্‌সুড়্‌ \r\n    করে পালাতে লাগল ।''<br />\r\n    এমন সময় কাকটা আমার দিকে তাকিয়ে জিজ্ঞাসা করল, ''বিজ্ঞাপন পেয়েছ ? হ্যান্ডবিল \r\n    ?''<br />\r\n    আমি বললাম, ''কই না, কিসের বিজ্ঞাপন ?'' বলতেই কাকটা একটা কাগজের বন্ডিল থেকে একখানা \r\n    ছাপানো কাগজ বের করে হাতে দিল, আমি পড়ে দেখলাম তাতে লেখা রয়েছে-</p>\r\n  <div style="width: 65%; margin: 5px auto; border: 2px solid #333; padding:5px;">\r\n	<p style="font-weight: bold; text-align: center">শ্রীশ্রীভূষণ্ডি কাগায় নমঃ</p>\r\n	<h1 style="text-align: center">শ্রীকাক্কেশ্বর কুচ্‌কুচে</h1>\r\n	<p style="font-weight: bold; text-align: center">৪১ নং গেছোবাজার, কাগেয়াপটি </p>\r\n	<p>আমরা হিসাবী ও বেহিসাবী খুচরা ও পাইকারী সকল প্রকার গণনার কার্য্য বৈজ্ঞানিক প্রক্রিয়ায় সম্পন্ন করিয়া থাকি । মূল্য এক ইঞ্চি ১|/০ । Children Half price, অর্থাৎ শিশুদের অর্দ্ধমূল্য । আপনার জুতার মাপ, গায়ের রং, কান কট্‌কট্‌ করে কিনা, জীবিত কি মৃত, ইত্যাদি আবশ্যকীয় বিবরণ পাঠাইলেই ফেরত ডাকে ক্যাটালগ্‌ পাঠাইয়া থাকি ।</p>\r\n	<h3 style="text-align: center">সাবধান ! সাবধান !! সাবধান !!!</h3>\r\n	<p>আমরা সনাতন বায়স বংশীয় দাঁড়ি কুলীন, অর্থাৎ দাঁড়কাক । আজকাল নানাশ্রেনীর পাতি কাক, হেড়ে কাক, রাম কাক প্রভৃতি নীচশ্রেণীর কাকেরাও অর্থলোভে নানারূপ ব্যবসা চালাইতেছে । সাবধান ! তাহাদের বিজ্ঞাপনের চটক দেখিয়া প্রতারিত হইবেন না ।</p>\r\n</div>\r\n  <p> আমি বললাম, ''সবটা ভালো করে বোঝা \r\n    গেল না ।''<br />\r\n    কাক গম্ভীর হয়ে বলল, ''হ্যাঁ, ভারি শক্ত, সকলে বুঝতে পারে না । একবার এক খদ্দের \r\n    এয়েছিল, তার ছিল টেকো মাথা-''<br />\r\n    এই কথা বলতেই বুড়ো মাৎ-মাৎ করে তেড়ে উঠে বলল, ''দেখ ! ফের যদি টেকো মাথা বলবি \r\n    তো হুঁকো দিয়ে এক বাড়ি মেরে তোর শ্লেট ফাটিয়ে দেব ।'' কাক একটু থতমত খেয়ে কি যেন \r\n    ভাবল, তারপর বলল, ''টেকো নয়, টেপো মাথা, যে মাথা টিপে টিপে টোল খেয়ে গিয়েছে ।''<br />\r\n    বুড়ো তাতেও ঠাণ্ডা হল না, বসে বসে গজ্‌গজ্‌ করতে লাগল । তাই দেখে কাক বলল, ''হিসেবটা \r\n    দেখবে নাকি ?'' বুড়ো একটু নরম হয়ে বলল, ''হয়ে গেছে ? কই দেখি ।'' কাক অমনি ''এই দেখ'' \r\n    বলে তার শ্লেটখানা ঠকাস্‌ করে বুড়োর টাকের উপর ফেলে দিল । বুড়ো তৎক্ষণাৎ মাথায় \r\n    হাত দিয়ে বসে পড়ল আর ছোট ছেলেদের মতো ঠোঁট ফুলিয়ে ''ও মা, ও পিসি, ও শিবুদা'' বলে \r\n    হাত-পা ছুঁড়ে কাঁদতে লাগল । <br />\r\n    কাকটা খানিকক্ষণ অবাক হয়ে তাকিয়ে বলল, ''লাগল নাকি ! ষাট ষাট ।'' বুড়ো অমনি কান্না \r\n    থামিয়ে বলল, ''একষট্টি, বাষট্টি -'' কাক বলল, ''পঁয়ষট্টি ।''<br />\r\n    আমি দেখলাম আবার বুঝি ডাকাডাকি শুরু হয়, তাই তাড়াতাড়ি বলে উঠলাম, ''কই হিসেবটা \r\n    তো দেখলে না ?&quot;<br />\r\n    বুড়ো বলল, ''হ্যাঁ হ্যাঁ, তাইতো ! কি হিসেব হল পড় দেখি ।'' আমি শ্লেটখানা তুলে \r\n    দেখলাম খুদে খুদে অক্ষরে লেখা রয়েছে- ''ইয়াদি কির্দ অত্র কাকালতনামা লিখিতং শ্রীকাক্কেশ্বর \r\n    কুচকুচে কার্যঞ্চাগে । ইমারৎ খেসারৎ দলিল দস্তাবেজ । তস্য ওয়ারিশাগণ মালিক দখলিকার \r\n    সত্ত্বে অত্র নায়েব সেরেস্তায় দস্ত বদস্ত কায়েম মোকরবী পত্তনী পাট্টা অথবা কাওলা \r\n    কবুলিয়ৎ । সত্যতায় কি বিনা সত্যতায় মুনসেফী আদালতে কিম্বা দায়রায় সোপর্দ আসামী \r\n    ফরিয়াদী সাক্ষী সাবুদ গয়রহ মোকর্দমা দায়ের কিম্বা আপোস মকমল ডিক্রীজারী নিলাম \r\n    ইস্তাহার ইত্যাদি সর্বপ্রকার কর্তব্য বিধায়-'' <br />\r\n    আমার পড়া শেষ না হতেই বুড়ো বলে উঠল, ''এসব কি লিখেছ আবোল-তাবোল ?'' কাক বলল, ''ওসব \r\n    লিখতে হয় । তা না হলে হিসেব টিকবে কেন ? ঠিক চৌকসমত কাজ করতে হলে গোড়ায় এসব বলে \r\n    নিতে হয় ।'' বুড়ো বলল, ''তা বেশ করেছ, কিন্তু আসল হিসেবটা কি হল তা তো বললে না \r\n    ?'' কাক বলল, ''হ্যাঁ, তাও বলা হয়েছে । ওহে শেষ দিকটা পড় তো ?''<br />\r\n    আমি দেখলাম, শেষের দিকে মোটা মোটা অক্ষরে লেখা রয়েছে-<br />\r\n    সাত দুগুণে চোদ্দ, বয়স ছাব্বিশ ইঞ্চি, জমা /২।।সের, খরচ ৩৭ বৎসর ।<br />\r\n    কাক বলল, ''দেখেই বোঝা যাচ্ছে অঙ্কটা এল্‌-সি-এম্‌ও নয়, জি-সি-এম্‌ও নয় । সুতরাং \r\n    হয় এটা ত্রৈরাশিকের অঙ্ক, না হয় ভগ্নাংশ । পরীক্ষা করে দেখলাম, আড়াই সেরটা হচ্ছে \r\n    ভগ্নাংশ । তাহলে বাকি তিনটে হল ত্রৈরাশিক । এখন আমার জানা দরকার, তোমরা ত্রৈরাশিক \r\n    চাও, না ভগ্নাংশ চাও ?'' <br />\r\n    বুড়ো বলল, ''আচ্ছা দাঁড়াও, তাহলে একবার জিগ্‌গেস করে নি ।'' এই বলে সে নিচু হয়ে \r\n    গাছের গোড়ায় মুখ ঠেকিয়ে ডাকতে লাগল, ''ওরে বুধো ! বুধো রে !'' <br />\r\n    খানিক পরে মনে হল কে যেন গাছের ভিতর থেকে রেগে বলে উঠল, ''কেন ডাকছিস ?'' বুড়ো \r\n    বলল, ''কাক্কেশ্বর কি বলছে শোন্‌ ।''<br />\r\n    আবার সেই রকম আওয়াজ হল, ''কি বলছে ?'' বুড়ো বলল, ''বলছে ত্রৈরাশিক না ভগ্নাংশ ?'' \r\n    তেড়ে উত্তর হল, ''কাকে বলছে ভগ্নাংশ, তোকে না আমাকে ?'' বুডো বলল, ''তা নয় । বলছে \r\n    হিসেবটা ভগ্নাংশ চাস, না ত্রৈরাশিক ।''<br />\r\n    একটুক্ষণ পরে জবাব শোনা গেল, ''আচ্ছা ত্রৈরাশিকে দিতে বল ।''<br />\r\n    বুড়ো গম্ভীর ভাবে খানিকক্ষণ দাড়ি হাতরাল, তারপর মাথা নেড়ে বলল, ''বুধোটার যেমন \r\n    বুদ্ধি ! ত্রৈরাশিকে দিতে বলব কেন ? ভগ্নাংশটা খারাপ হল কিসে ? না হে কাক্কেশ্বর, \r\n    তুমি ভগ্নাংশই দাও ।'' কাক বলল, ''তাহলে আড়াই সেরের গোটা সের দুটো বাদ দিলে রইল \r\n    ভগ্নাংশ আধ সের, তোমার হিসেব হল আধ সের । আধ সের হিসেবের দাম পড়ে- খাঁটি হলে \r\n    দু টাকা চোদ্দ আনা, আর জল মেশানো থাকলে ছয় পয়সা ।''<br />\r\n    বুড়ো বলল, ''আমি যখন কাঁদছিলাম, তখন তিন ফোঁটা জল হিসেবের মধ্যে পড়েছিল । এই নাও \r\n    তোমার শ্লেট, আর এই নাও তোমার পয়সা ছটা ।'' পয়সা পেয়ে কাকের মহা ফুর্তি ! সে ''টাক্‌-ডুমাডুম্‌ \r\n    টাক্‌-ডুমাডুম্‌'' বলে শ্লেট বাজিয়ে নাচতে লাগল । <br />\r\n    বুড়ো অমনি আবার তেড়ে উঠল, ''ফের টাক্‌ টাক্‌ বলছিস ? দাঁড়া । ওরে বুধো, বুধো রে \r\n    ! শিগগির আয় । আবার টাক্‌ বলছে । বলতে-না-বলতেই গাছের ফোকর থেকে মস্ত একটা পোঁটলা \r\n    মতন কি যেন হুড়্‌মুড়্‌ করে মাটিতে গড়িয়ে পড়ল । চেয়ে দেখলাম, একটা বুড়ো লোক একটা \r\n    প্রকাণ্ড বোঁচকার নিচে চাপা পড়ে ব্যস্ত হয়ে হাত-পা ছুঁড়ছে । বুড়োটা দেখতে অবিকল \r\n    এই হুঁকোওয়ালা বুড়োর মতো । হুঁকোওয়ালা কোথায় তাকে টেনে তুলবে, না সে নিজেই পোঁটলার \r\n    উপর চড়ে বসে, ''ওঠ বলছি, শিগগির ওঠ্‌'' বলে ধাঁই করে তাকে হুঁকো দিয়ে মারতে লাগল \r\n    । কাক আমার দিকে চোখ মট্‌কিয়ে বলল, ''ব্যাপারটা বুঝতে <center><img src="../images/hajabarala4.gif" width="300" height="170" align="texttop"></center></p>\r\n  <p>পারছ না? উধোর বঝা বুধোর ঘাড়ে । \r\n    এর বোঝা ওর ঘাড়ে চাপিয়ে দিয়েছে, এখন ও আর বোঝা ছাড়তে চাইবে কেন ? এই নিয়ে রোজ \r\n    মারামারি হয় ।''<br />\r\n    এই কথা বলতে বলতেই চেয়ে দেখি, বুধো তার পোঁটলা শুদ্ধ উঠে দাঁড়িয়েছে । দাঁড়িয়েই \r\n    সে পোঁটলা উঁচিয়ে দাঁত কড়মড় করে বলল, ''তবে রে ইসটুপিড উধো !'' উধোও আস্তিন গুটিয়ে \r\n    হুঁকো বাগিয়ে হুংকার দিয়ে উঠল, ''তবে রে লক্ষ্ণীছাড়া বুধো !'' <br />\r\n    কাক বলল, ''লেগে যা- নারদ নারদ !''<br />\r\n    অমনি ঝটাপট্‌, খটাপট্‌, দমাদম্‌, ধপাধপ্‌ ! মুহূর্তের মধ্যে চেয়ে দেখি উধো চিৎপাত \r\n    শুয়ে হাঁপাচ্ছে, আর বুধো ছট্‌ফট্‌ করে টাকে হাত বুলোচ্ছে । <br />\r\n    বুধো কান্না শুরু করল, ''ওরে ভাই উধো রে, তুই এখন কোথায় গেলি রে ?'' উধো কাঁদতে \r\n    লাগল, ''ওরে হায় হায় । আমাদের কি হল রে !'' তারপর দুজনে উঠে খুব খানিক গলা জড়িয়ে \r\n    কেঁদে, আর খুব খানিক কোলাকুলি করে, দিব্যি খোশমেজাজে গাছের ফোকরের মধ্যে ঢুকে \r\n    পড়ল । তাই দেখে কাকটাও তার দোকানপাট বন্ধ করে কোথায় যেন চলে গেল ।<br />\r\n    আমি ভাবছি এই বেলা পথ খুঁজে বাড়ি ফেরা যাক, এমন সময় শুনি পাশেই একটা ঝোপের মধ্যে \r\n    কি রকম শব্দ হচ্ছে, যেন কেউ হাসতে হাসতে আর কিছুতেই হাসি সামলাতে পারছে না । \r\n    উঁকি মেরে দেখি, একটা জন্তু- মানুষ না বাঁদর, প্যাঁচা না ভূত, ঠিক বোঝা যাচ্ছে \r\n    না- খালি হাত-পা ছুঁড়ে হাসছে, আর বলছে, ''এই গেল গেল- নাড়ি-ভুঁড়ি সব ফেটে গেল \r\n    ।''<br />\r\n    হঠাৎ আমায় দেখে সে একটু দ্ম পেয়ে উঠে বলল, ''ভাগ্যিস তুমি এসে পড়লে, তা না হলে \r\n    আর একটু হলেই হাসতে হাসতে পেট ফেটে যাচ্ছিল ।''<br />\r\n    আমি বললাম, ''তুমি এমন সাংঘাতিক রকম হাসছ কেন ?''<br />\r\n    জন্তুটা বলল, ''কেন হাসছি শুনবে ? মনে কর, পৃথিবীটা যদি চ্যাপ্টা হত, আর সব জল \r\n    গড়িয়ে ডাঙায় এসে পড়ত, আর ডাঙার মাটি সব ঘুলিয়ে প্যাচ্‌ প্যাচে কাদা হয়ে যেত, \r\n    আর লোকগুলো সব তার মধ্যে ধপাধপ্‌ আছাড় খেয়ে পড়ত, তা হলে- হোঃ হোঃ হোঃ হোঃ-'' এই \r\n    বলে সে আবার হাসতে হাসতে লুটিয়ে পড়ল ।<br />\r\n    আমি বললাম, ''কি আশ্চর্য ! এর জন্য তুমি এত ভয়ানক করে হাসছ ?''<br />\r\n    সে আবার হাসি থামিয়ে বলল, ''না, না, শুধু এর জন্য নয় । মনে কর, একজন লোক আসছে, \r\n    তার এক হাতে কুলপি বরফ, আর এক হাতে সাজিমাটি, আর লোকটা কুলপি খেতে গিয়ে ভুলে \r\n    সাজিমাটি খেয়ে ফেলেছে- হোঃ হোঃ হোঃ হো হাঃ হাঃ হাঃ হা-'' আবার সে হাসির পালা ।<br />\r\n    আমি বললাম, ''কেন তুমি এই সব অসম্ভব কথা ভেবে খামকা হেসে হেসে কষ্ট পাচ্ছ ?'' সে \r\n    বলল, ''না, না, সব কি আর অসম্ভব ? মনে কর, একজন লোক টিকটিকি পোষে, রোজ তাদের নাইয়ে \r\n    খাইয়ে শুকোতে দেয়, একদিন একটা রামছাগল এসে সব টিকটিকি খেয়ে ফেলেছে- হোঃ হোঃ হোঃ \r\n    হো-''<br />\r\n    জন্তুটার রকম-সকম দেখে আমার ভারি অদ্ভুত লাগল । আমি জিজ্ঞাসা করলাম, ''তুমি কে \r\n    ? তোমার নাম কি?'' সে খানিকক্ষণ ভেবে বলল, ''আমার নাম হিজিবিজ্‌বিজ্‌ । আমার নাম \r\n    হিজিবিজ্‌বিজ্‌, আমার ভায়ের নাম হিজিবিজ্‌বিজ্‌, আমার বাবার নাম হিজিবিজ্‌বিজ্‌, \r\n    আমার পিশের নাম হিজিবিজ্‌বিজ্‌-'' আমি বললাম, ''তার চেয়ে সোজা বললেই হয় তোমার গুষ্টিশুদ্ধ \r\n    সবাই হিজিবিজ্‌বিজ্‌ ।'' <br />\r\n    সে আবার খানিক ভেবে বলল, ''তা তো নয়, আমার মামার নাম তকাই । আমার মামার নাম তকাই, \r\n    আমার খুড়োর নাম তকাই, আমার মেশোর নাম তকাই, আমার শ্বশুরের নাম তকাই-''<br />\r\n    আমি ধ্মল দিয়ে বললাম, ''সত্যি বলছ ? না, বানিয়ে ?'' জন্তুটা কেমন থতমত খেয়ে বলল, \r\n    ''না না, আমার শ্বশুরের নাম বিস্কুট ।'' আমার ভয়ানক রাগ হল, তেড়ে বললাম, ''একটা \r\n    কথাও বিশ্বাস করি না ।''<br />\r\n    অমনি কথা নেই বার্তা নেই, ঝোপের আড়াল থেকে মস্ত একটা দাড়িওয়ালা ছাগল হঠাৎ উঁকি \r\n    মেরে জিজ্ঞাসা করল, ''আমার কথা হচ্ছে বুঝি ?''<br />\r\n    আমি বলতে যাচ্ছিলাম ''না'' কিন্তু কিছু না বলতেই সে তড়তড় করে বলে যেতে লাগল, ''তা \r\n    তোমরা যতই তর্ক কর, এমন অনেক জিনিস আছে যা ছাগলে খায় না । তাই আমি একটা বক্তৃতা \r\n    দিতে চাই, তার বিষয় হচ্ছে- ''ছাগলে কি না খায় ।'' এই বলে সে হঠাৎ এগিয়ে এসে বক্তৃতা \r\n    আরম্ভ করল-<br />\r\n    ''হে বালকবৃন্দ এবং স্নেহের হিজিবিজ্‌বিজ্‌, আমার গলায় ঝুলানো সার্টিফিকেট দেখেই \r\n    তোমরা বুঝতে পারছ যে আমার নাম শ্রীব্যাকরণ শিং, বি. এ. খাদ্য বিশারদ । আমি খুব \r\n    চমৎকার ব্যা করতে পারি, তাই আমার নাম ব্যাকরণ, আর শিং তো দেখতেই পাচ্ছ । ইংরিজিতে \r\n    লিখবার সময় লিখি B. A. অর্থাৎ ব্যা । কোন্‌-কোন্‌ জিনিস খাওয়া যায় আর কোন্‌টা-কোন্‌টা \r\n    খাওয়া যায় না, তা আমি সব নিজে পরীক্ষা করে দেখেছি, তাই আমার উপাধি হচ্ছে খাদ্যবিশারদ \r\n    । তোমরা যে বল- পাগলে কি না বলে, ছাগলে কি না খায়- এটা অত্যন্ত অন্যায় । এই তো \r\n    একটু আগে ঐ হতভাগাটা বলছিল যে রামছাগল টিকটিকি খায় । এটা এক্কেবারে সম্পূর্ণ \r\n    মিথ্যা কথা । আমি অনেক রকম টিকটিকি চেটে দেখেছি, ওতে খাবার মত কিচ্ছু নেই । অবশ্যি \r\n    আমরা মাঝে মাঝে এমন অনেক জিনিস খাই যা তোমরা খাও না, যেমন- খাবারের ঠোঙা, কিম্বা \r\n    নারকেলের ছোবড়া, কিম্বা খবরের কাগজ, কিম্বা সন্দেশের মতো ভালো ভালো অনেক মাসিক \r\n    পত্রিকা । কিন্তু তা বলে মজবুত বাঁধানো কোনো বই আমরা কক্ষনো খাই না । আমরা কচিৎ \r\n    কখনো লেপ কিম্বা তোশক বালিশ এসব একটু আধটু খাই বটে, কিন্তু যারা বলে আমরা খাট \r\n    পালং কিম্বা টেবিল চেয়ার খাই, তারা ভয়ানক মিথ্যাবাদী । <br />\r\n    <center>\r\n      <img src="../images/hajabarala5.gif" width="300" height="275" align="top"> \r\n    </center>\r\n    <br />\r\n  <p>\r\n    যখন আমাদের মনে খুব তেজ আসে, তখন শখ করে অনেক রকম জিনিস আমরা চিবিয়ে কিম্বা চেখে \r\n    দেখি,- যেমন, পেনসিল রবার কিম্বা বোতলের ছিপি কিম্বা শুকনো জুতো কিম্বা ক্যামবিসের \r\n    ব্যাগ । শুনেছি আমার ঠাকুরদাদা একবার ফুর্তির চোটে এক সাহেবের আধখানা তাঁবু প্রায় \r\n    খেয়ে শেষ করে ছিলেন । কিন্তু তা বলে ছুরি কাঁচি কিম্বা শিশি বোতল, এ সব আমরা \r\n    কোনদিন খাই না । কেউ কেউ সাবান খেতে ভালোবাসে, কিন্তু সে সব নেহাৎ ছোটখাট বাজে \r\n    সাবান । আমার ছোটভাই একবার একটা আস্ত বার্‌-সোপ খেয়ে ফেলেছিল-'' বলেই ব্যাকরণ \r\n    শিং আকাশের দিকে চোখ তুলে ব্যা ব্যা করে ভয়ানক কাঁদতে লাগল । তাতে বুঝতে পারলাম \r\n    যে সাবান খেয়ে ভাইটির অকালমৃত্যু হয়েছে । হিজিবিজ্‌বিজ্‌টা এতক্ষণ পড়ে পড়ে ঘুমোচ্ছিল, \r\n    হঠাৎ ছাগলটার বিকট কান্না শুনে সে হাঁউ-মাঁউ করে ধড়মড়িয়ে উঠে বিষম-টিষম খেয়ে \r\n    একেবারে অস্থির ! আমি ভাবলাম বোকাটা মরে বুঝি এবার ! কিন্তু একটু পরেই দেখি, \r\n    সে আবার তেমনি হাত-পা ছুঁড়ে ফ্যাক্‌ ফ্যাক্‌ করে হাসতে লেগেছে ।</p>\r\n    <img src="../images/hajabarala6.gif" width="150" height="290" style=''float: left''><p>আমি \r\n    বললাম, ''এর মধ্যে আবার হাসবার কি হল ?'' সে বলল, ''সেই একজন লোক ছিল, সে মাঝে মাঝে \r\n    এমন ভয়ঙ্কর নাক ডাকাত, যে, সবাই তার উপর চটা ছিল । একদিন তাদের বাড়ি বাজ পড়েছে, \r\n    আর অমনি সবাই দৌড়ে তাকে দমাদম্‌ মারতে লেগেছে- হোঃ হোঃ হোঃ হো-''</p>\r\n  <p>\r\n    আমি বললাম, ''যত সব বাজে কথা ।'' এই বলে যেই ফিরতে গেছি, অমনি চেয়ে দেখি একটা নেড়া \r\n    মাথা আমার দিকে তাকিয়ে আছে । দেখে আমার গা জ্বলে গেল । আমায় ফিরতে দেখেই সে আব্‌দার \r\n    করে আহ্লাদীর মতো ঘাড় বাঁকিয়ে দুহাত নেড়ে বলতে লাগল, ''না ভাই, না ভাই, এখন আমায় \r\n    গাইতে বল না । সত্যি বলছি, আজকে আমার গলা তেমন খুলবে না ।'' আমি বললাম, ''কি আপদ ! কে তোমায় গাইতে বলছে ?''</p>\r\n   <p> লোকটা এমন বেহায়া, সে তবুও আমার কানের কাছে ঘ্যান্‌ঘ্যান করতে লাগল, ''রাগ করলে \r\n    ? হ্যাঁ ভাই, রাগ করলে ? আচ্ছা, না হয় কয়েকটা গান শুনিয়ে দিচ্ছি, রাগ করবার দরকার \r\n    কি ভাই ?''</p>\r\n   <p>আমি কিছু বলবার আগেই ছাগলটা আর হিজিবিজ্‌বিজ্‌টা এক সঙ্গে চেঁচিয়ে উঠল, ''হ্যাঁ-হ্যাঁ-হ্যাঁ, \r\n    গান হোক গান হোক ।'' অমনি নেড়াটা তার পকেট থেকে মস্ত দুই তাড়া গানের কাগজ বার \r\n    করে, সেগুলো চোখের কাছে নিয়ে গুন্‌গুন্‌ করতে করতে হঠাৎ সরু গলায় চীৎকার করে \r\n    গান ধরল- ''লাল গানে নীল সুর, হাসি-হাসি গন্ধ ।'' ঐ একটিমাত্র পদ সে একবার গাইল, \r\n    দুবার গাইল, পাঁচবার, দশবার গাইল ।</p>\r\n   <p> আমি বললাম, ''এতো ভারি উৎপাত দেখছি, গানের কি আর কোনো পদ নেই ?''</p>\r\n  <p>\r\n    নেড়া বলল, ''হাঁ, আছে, কিন্তু সেটা অন্য একটা গান । সেটা হচ্ছে- অলিগলি চলি রাম, \r\n    ফুটপাথে ধুমধাম, কালি দিয়ে চুনকাম । সে গান আজকাল আমি গাই না । আরেকটা গান আছে- \r\n    নাইনিতালের নতুন আলু- সেটা খুব নরল সুরে গাইতে হয় । সেটাও আজকাল গাইতে পারি না \r\n    । আজকাল যেটা গাই, সেটা হচ্ছে শিখিপাখার গান ।'' এই বলে সে গান ধরল-</p>\r\n  \r\n    <p style=''margin-left: 100px;''>মিশিপাখা শিখিপাখা আকাশের কানে কানে<br />\r\n        শিশিবোতল ছিপিঢাকা সরু সরু গানে গানে <br />\r\n        আলোভোলা বাঁকা আলো আধো আধো কতদূরে<br />\r\n        সরু মোটা শাদা কালো ছলছল ছায়াসুরে । </p>\r\n  <p> আমি বললাম, ''এ আবার গান হল নাকি \r\n    । এর তো মাথামুণ্ডু কোনো মানেই হয় না ।''<br />\r\n    হিজিবিজ্‌বিজ্‌ বলল, ''হ্যাঁ, গানটা ভারি শক্ত ।''<br />\r\n    ছাগল বলল, ''শক্ত আবার কোথায় ? ঐ শিশি বোতলের জায়গাটা একটু শক্ত ঠেকল, তাছাড়া \r\n    তো শক্ত কিছু পেলাম না ।''<br />\r\n    নেড়াটা খুব অভিমান করে বলল, ''তা, তোমরা সহজ গান শুনতে চাও তো সে কথা বললেই হয় \r\n    । অত কথা শোনাবার দরকার কি ? আমি কি আর সহজ গান গাইতে পারি না ?'' এই বলে সে গান \r\n    ধরল- </p>\r\n  <p style=''margin-left: 100px;''>বাদুড় বলে, ওরে ও ভাই সজারু,<br />\r\n  আজকে রাতে দেখবে একটা মজারু ।</p>\r\n  <p>আমি বললাম, ''মজারু বলে কোনো কথা \r\n    হয় না ।'' নেড়া বলল, ''কেন হবে না- আলবৎ হয় । সজারু কাঙ্গারু দেবদারু সব হতে পারে, \r\n    মজারু কেন হবে না ?''<br />\r\n    ছাগল বলল, ''ততক্ষণ গানটা চলুক না, হয় কি না-হয় পরে দেখা যাবে ।'' অমনি আবার গান \r\n    শুরু হল- </p>\r\n<p style=''margin-left: 100px;''>  \r\nবাদুড় বলে, ওরে ও ভাই সজারু,<br />\r\n        আজকে রাতে দেখবে একটা মজারু ।<br />\r\n        আজকে হেথায় চামচিকে আর পেঁচারা<br />\r\n        আসবে সবাই, মরবে ইঁদুর বেচারা ।<br />\r\n        কাঁপবে ভয়ে ব্যাঙগুলো আর ব্যাঙাচি,<br />\r\n        ঘামতে ঘামতে ফুটবে তাদের ঘামাচি,<br />\r\n        ছুটবে ছুঁচো লাগবে দাঁতে কপাটি,<br />\r\n        দেখবে তখন ছিম্বি ছ্যাঙা চপাটি ।</p>\r\n  <p> আমি আবার আপত্তি করতে যাচ্ছিলাম, \r\n    কিন্তু সামলে গেলাম । গান চলতে লাগল- </p>\r\n<p style=''margin-left: 100px;''>  \r\nসজারু কয় ঝোপের মাঝে এখনি<br />\r\n        গিন্নী আমার ঘুম দিয়েছেন দেখনি ?<br />\r\n        জেনে রাখুন প্যাঁচা এবং প্যাঁচানী,<br />\r\n        ভাঙলে সে ঘুম শুনে তাদের চ্যাঁচানি,<br />\r\n        খ্যাংরা-খোঁচা করব তাদের খুঁচিয়ে-<br />\r\n        এই কথাটা বলবে তাদের বুঝিয়ে ।<br />\r\n        বাদুড় বলে, পেঁচার কুটুম কুটুমী<br />\r\n        মানবে না কেউ তোমার এসব ঘুঁতুমি ।<br />\r\n        ঘুমোয় কি কেউ এমন ভুসো আঁধারে ?<br />\r\n        গিন্নী তোমার হোৎকা এবং হাঁদাড়ে ।<br />\r\n        তুমিও দাদা হচ্ছ ক্রমে খ্যাপাটে<br />\r\n        চিমনি-চাটা ভোঁপসা-মুখো ভ্যাঁপাটে ।</p>\r\n  <p> গানটা আরও চলত কিনা জানি না, কিন্তু \r\n    এই পর্যন্ত হতেই একটা গোলমাল শোনা গেল । তাকিয়ে দেখি, আমার আশেপাশে চারদিকে ভিড় \r\n    জমে গিয়েছে । একটা সজারু এগিয়ে এসে ফোঁৎফোৎ করে কাঁদছে আর একটা শাম্‌লাপরা কুমির \r\n    মস্ত একটা বই দিয়ে আস্তে আস্তে তার পিঠ থাব্‌রাচ্ছে আর ফিস্‌ফিস্‌ করে বলছে, \r\n    ''কেঁদো না, কেঁদো না, সব ঠিক করে দিচ্ছি ।'' হঠাৎ একটা তকমা-আঁটা পাগড়ি-বাঁধা \r\n    কোলা ব্যাং রুল উচিয়ে চীৎকার করে বলে উঠল- ''মানহানির মোকদ্দমা ।'' \r\n    <center>\r\n      <img src="../images/hajabarala7.gif" width="300" height="219"> \r\n    </center>\r\n    <br />\r\n    <p>অমনি কোত্থেকে একটা কালো ঝোল্‌লা-পরা হুতোম প্যাঁচা এসে সকলের সামনে একটা উঁচু \r\n    পাথরের উপর বসেই চোখ বুজে ঢুলতে লাগল, আর একটা মস্ত ছুঁচো একটা বিশ্রী নোংরা \r\n    হাতপাখা দিয়ে তাকে বাতাস করতে লাগল ।</p>\r\n  <p>\r\n    প্যাঁচা একবার ঘোলা ঘোলা চোখ করে চারদিকে তাকিয়েই তক্ষনি আবার চোখ বুজে বলল, \r\n    ''নালিশ বাতলাও ।''</p>\r\n  <p>\r\n    বলতেই কুমিরটা অনেক কষ্টে কাঁদো কাঁদো মুখ করে চোখের মধ্যে নখ দিয়ে খিম্‌চিয়ে \r\n    পাঁচ ছয় ফোঁটা জল বার করে ফেলল । তারপর সর্দিবসা মোটা গলায় বলতে লাগল, ''ধর্মাবতার \r\n    হুজুর ! এটা মানহানির মোকদ্দমা । সুতরাং প্রথমেই বুঝতে হবে মান কাকে বলে । মান \r\n    মানে কচু । কচু অতি উপাদেয় জিনিস । কচু অনেক প্রকার, যথা- মানকচু, ওলকচু, কান্দাকচু, \r\n    পানিকচু, শঙ্খকচু ইত্যাদি । কচুগাছের মূলকে কচু বলে, সুতরাং বিষয়টা একেবারে মূল \r\n    পর্যন্ত যাওয়া দরকার ।''</p>\r\n  <p>\r\n    এইটুকু বলতেই একটা শেয়াল শাম্‌লা মাথায় তড়াক করে লাফিয়ে উঠে বলল, ''হুজুর, কচু \r\n    অতি অসার জিনিস । কচু খেলে গলা কুট্‌কুট্‌ করে, কচুপোড়া খাও বললে মানুষে চটে \r\n    যায় । কচু খায় কারা ? কচু খায় শুওর আর সজারু । ওয়াক থুঃ ।''</p>\r\n<p>সজারুটা আবার ফ্যাঁৎ ফ্যাঁৎ করে কাঁদতে \r\n    যাচ্ছিল, কিন্তু কুমির সেই প্রকাণ্ড বই দিয়ে তার মাথায় এক থাব্‌ড়া মেরে জিজ্ঞাসা \r\n    করল, ''দলিলপত্র সাক্ষী-সাবুদ কিছু আছে ?'' সজারু ন্যাড়ার দিকে তাকিয়ে বলল, ''ঐ \r\n    তো ওর হাতে সব দলিল রয়েছে ।'' বলতেই কুমিরটা ন্যাড়ার কাছ থেকে একতাড়া গানের কাগজ \r\n    কেড়ে নিয়ে হঠাৎ এক যায়গা থেকে পড়তে লাগল- </p>\r\n<p style=''margin-left: 100px;''>\r\nএকের পিঠে দুই<br />\r\n        চৌকি চেপে শুই<br />\r\n        পোঁটলা বেঁধে থুই<br />\r\n        গোলাপ চাঁপা জুঁই<br />\r\n        ইলিশ মাগুর রুই<br />\r\n        হিন্‌চে পালং পুঁই<br />\r\n        সান্‌ বাঁধানো ভুঁই<br />\r\n        গোবর জলে ধুই<br />\r\n        কাঁদিস কেন তুই ?</p>\r\n  <p> সজারু বলল, ''আহা ওটা কেন ? ওটা \r\n    তো নয় ।'' কুমির বলল, ''তাই নাকি ? আচ্ছা, দাঁড়াও ।'' এই বলে সে আবার একখানা কাগজ \r\n    নিয়ে পড়তে লাগল- </p>\r\n  <p style=''margin-left: 100px;''>\r\nচাঁদনি রাতের পেতনীপিসি সজনেতলায় খোঁজ্‌ \r\n        না রে-<br />\r\n        থ্যাঁৎলা মাথা হ্যাংলা সেথা হাড় কচাকচ্‌ ভোজ মারে ।<br />\r\n        চাল্‌তা গাছে আল্‌তা পরা নাক ঝোলানো শাঁকচুনি<br />\r\n        মাক্‌ড়ি নেড়ে হাঁকড়ে বলে, আমায় তো কেউ ডাঁকছনি !<br />\r\n        মুণ্ডু ঝোলা উলটোবুড়ি ঝুলছে দেখ চুল খুলে,<br />\r\n        বলছে দুলে, মিন্‌সেগুলোর মাংস খাব তুলতুলে ।</p>\r\n  <p> সজারু বলল, ''দূর ছাই ! কি যে পড়ছে \r\n    তার নেই ঠিক ।''<br />\r\n    কুমির বলল, ''তাহলে কোন্‌টা, এইটা !- দই দম্বল, টেকো অম্বল, কাঁথা কম্বল করে সম্বল \r\n    বোকা ভোম্বল- এটাও নয় ? আচ্ছা দাঁড়াও দেখছি- নিঝুম নিশুত রাতে, একা শুয়ে তেতলাতে, \r\n    খালিখালি খিদে পায় কেন রে ?- কি বললে ? ওসব নয় ? তোমার গিন্নীর নামে কবিতা ?<br />\r\n    -তা, সে কথাটা আগে বললেই হত । এই তো- রাম ভজনের গিন্নীটা, বাপরে, যেন সিংহীটা \r\n    ! বাসন নাড়ে ঝনার্‌ঝন্‌, কাপড় কাচে দমদ্দম্‌- এটাও মিলছে না ? তা হলে নিশ্চয়ই \r\n    এটা- </p>\r\n  <p style=''margin-left: 100px;''>\r\nখুস্‌খুসে কাশি ঘুষ্‌ঘুষে জ্বর ফুস্‌ফুসে \r\n        ছ্যাঁদা বুড়ো তুই মর্‌ ।<br />\r\n        মাজ্‌রাতে ব্যাথা পাঁজরাতে বাত, আজ রাতে বুড়ো হবি কুপোকাৎ !</p>\r\n  <p> সজারুটা ভয়ানক কাঁদতে লাগল, ''হায়, \r\n    হায় ! আমার পয়সাগুলো সব জলে গেল ! কোথাকার এক আহাম্মক উকিল, দলিল দিলে খুঁজে \r\n    পায় না !''<br />\r\n    ন্যাড়াটা এতক্ষণ আড়ষ্ট হয়ে ছিল, সে হঠাৎ বলে উঠল, ''কোনটা শুনতে চাও ? সেই যে- \r\n    বাদুড় বলে ওরে ও ভাই সজারু- সেইটে?'' সজারু ব্যস্ত হয়ে বলল, ''হ্যাঁ হ্যাঁ, সেইটে, \r\n    সেইটে ।''<br />\r\n    অমনি শেয়াল আবার তেড়ে উঠল, ''বাদুড় কি বলে ? হুজুর, তাহলে বাদুড়গোপালকে সাক্ষী \r\n    মানতে আজ্ঞা হোক ।'' কোলা ব্যাঙ গলা ফুলিয়ে হেঁকে বলল, ''বাদুড়গোপাল হাজির ?''<br />\r\n    সবাই এদিক ওদিক তাকিয়ে দেখল, কোত্থাও বাদুড় নেই । তখন শেয়াল বলল, ''তাহলে হুজুর, \r\n    ওদের সক্কলের ফাঁসির হুকুম হোক ।'' কুমির বলল, ''তা কেন ? আমরা এখন আপিল করব ।'' \r\n    <br />\r\n    প্যাঁচা চোখ বুজে বলল, ''আপিল চলুক । সাক্ষী আনো ।''<br />\r\n    কুমির এদিক ওদিক তাকিয়ে হিজিবিজ্‌বিজ্‌কে জিজ্ঞাসা করল, ''সাক্ষী দিবি ? চার আনা \r\n    পয়সা পাবি ।'' পয়সার নামে হিজিবিজ্‌বিজ্‌ তড়াক্‌ করে সাক্ষী দিতে উঠেই ফ্যাক্‌ \r\n    ফ্যাক্‌ করে হেসে ফেলল ।<br />\r\n    শেয়াল বলল, ''হাসছ কেন ?'' হিজিবিজ্‌বিজ্‌ বলল, ''একজনকে শিখিয়ে দিয়েছিল, তুই সাক্ষী \r\n    দিবি যে, বইটার সবুজ রঙের মলাট, কানের কাছে নীল চামড়া আর মাথার উপর লালকালির \r\n    ছাপ । উকিল যেই তাকে জিজ্ঞাসা করেছে, তুমি আসামীকে চেন ? অমনি সে বলে উঠেছে, \r\n    আজ্ঞে হ্যাঁ, সবুজ রঙের মলাট, কানের কাছে নীল চামড়া, মাথার উপর লালকালির ছাপ- \r\n    হোঃ হোঃ হোঃ হো-''<br />\r\n    শেয়াল জিজ্ঞাসা করল, ''তুমি সজারুকে চেন ?'' হিজিবিজ্‌বিজ্‌ বলল, ''হ্যাঁ, সজারু \r\n    চিনি, কুমির চিনি, সব চিনি । সজারু গর্তে থাকে, আর গায়ে লম্বা কাঁটা, আর কুমিরের \r\n    গায়ে চাকা চাকা ঢিপির মতো, তারা ছাগল-টাগল ধরে খায় ।'' বলতেই ব্যাকরণ শিং ব্যা \r\n    ব্যা করে ভয়ানক কেঁদে উঠল ।<br />\r\n    আমি বললাম, ''আবার কি হল ?'' ছাগল বলল, ''আমার সেজোমামার আধখানা কুমিরে খেয়েছিল, \r\n    তাই বাকি আধখানা মরে গেল ।'' আমি বললাম, ''গেল তো গেল, আপদ গেল । তুমি এখন চুপ \r\n    কর ।''<br />\r\n    শেয়াল জিজ্ঞাসা করল, ''তুমি মোকদ্দমার কিছু জান ?'' হিজিবিজ্‌বিজ্‌ বলল, ''তা আর \r\n    জানি নে ? একজন নালিশ করে, তার একজন উকিল থাকে, আর একজনকে আসাম থেকে নিয়ে আসে, \r\n    তাকে বলে আসামী, তারও একজন উকিল থাকে । এক-একদিকে দশজন করে সাক্ষী থাকে । আর \r\n    একজন জজ থাকে, সে বসে বসে ঘুমোয় ।''<br />\r\n    প্যাঁচা বলল, কক্ষনো আমি ঘুমোচ্ছি না, আমার চোখে ব্যারাম আছে তাই চোখ বুজে আছি \r\n    ।''<br />\r\n    হিজিবিজ্‌বিজ্‌ বলল, আরও অনেক জজ দেখেছি, তাদের সক্কলেরই চোখে ব্যারাম ।'' বলেই \r\n    সে ফ্যাক্‌ ফ্যাক্‌ করে ভয়ানক হাসতে লাগল ।<br />\r\n    শেয়াল বলল, ''আবার কি হল ?'' হিজিবিজ্‌বিজ্‌ বলল, ''একজনের মাথার ব্যারাম ছিল, সে \r\n    সব জিনিসের নামকরণ করত । তার জুতোর নাম ছিল অবিমৃষ্যকারিতা, আর ছাতার নাম ছিল \r\n    প্রত্যুৎপন্নমতিত্ব, তার গাড়ুর নাম ছিল পরমকল্যাণবরেষু- কিন্তু যেই বাড়ির নাম \r\n    দিয়েছে কিংকর্তব্যবিমুঢ়, অমনি ভূমিকম্প হয়ে বাড়িটাড়ি সব পড়ে গিয়েছে । হোঃ হোঃ \r\n    হোঃ হো-''<br />\r\n    শেয়াল বলল, ''বটে ? তোমার নাম কি শুনি ?'' সে বলল, ''এখন আমার নাম হিজিবিজ্‌বিজ্‌ \r\n    ।''<br />\r\n    শেয়াল বলল, ''নামের আবার এখন-তখন কি ? হিজিবিজ্‌বিজ্‌ বলল, ''তাও জানো না ? সকালে \r\n    আমার নাম থাকে আলু-নারকোল, আবার আর একটু বিকেল হলেই আমার নাম হয়ে যাবে রামতাড়ু \r\n    ।''<br />\r\n    শেয়াল বলল, ''নিবাস কোথায় ?'' হিজিবিজ্‌বিজ্‌ বলল, ''কার কথা বলছ ? শ্রীনিবাস ? \r\n    শ্রীনিবাস দেশে চলে গিয়েছে ।'' অমনি ভিড়ের মধ্যে থেকে উদো আর বুধো একসঙ্গে চেঁচিয়ে \r\n    উঠল, ''তাহলে শ্রীনিবাস নিশ্চয়ই মরে গিয়েছে ।'' উদো বলল, ''দেশে গেলেই লোকেরা সব \r\n    হুস্‌ হুস্‌ করে মরে যায় ।'' বুধো বলল, ''হাবুলের কাক যেই দেশে গেল অমনি শুনি সে \r\n    মরে গিয়েছে ।''<br />\r\n    শেয়াল বলল, ''আঃ, সবাই মিলে কথা বোলো না, ভারি গোলমাল হয় ।'' শুনে উদো বুধোকে বলল, \r\n    ''ফের যদি সবাই মিলে কথা বলবি তো তোকে মারতে মারতে সাবাড় করে ফেলব ।'' বুধো বলল, \r\n    ''আবার যদি গোলমাল করিস তাহলে তোকে ধরে এক্কেবারে পোঁটলা-পেটা করে দেব ।''<br />\r\n    শেয়াল বলল, ''হুজুর, এরা সব পাগল আর আহাম্মক, এদের সাক্ষীর কোনো মূল্য নেই ।'' \r\n    শুনে কুমির রেগে ল্যাজ আছড়িয়ে বলল, ''কে বলল মূল্য নেই ? দস্তুরমতো চার আনা পয়সা \r\n    খরচ করে সাক্ষী দেওয়ানো হচ্ছে ।'' বলেই সে তক্ষুনি ঠক্‌ঠক্‌ করে ষোলটা পয়সা গুণে \r\n    হিজিবিজ্‌বিজের হাতে দিয়ে দিল । অমনি কে যেন উপর থেকে বলে উঠল ''১নং সাক্ষী, নগদ \r\n    হিসাব, মূল্য চার আনা ।'' চেয়ে দেখলাম কাক্কেশ্বর বসে বসে হিসাব লিখছে ।<br />\r\n    শেয়াল আবার জিজ্ঞাসা করল, ''তুমি এ বিষয়ে আর কিছু জানো না ?'' হিজিবিজ্‌বিজ্‌ খানিক \r\n    ভেবে বলল, ''শেয়ালের বিষয়ে একটা গান আছে, সেইটা জানি ।''<br />\r\n    শেয়াল বলল, ''কি গান শুনি ?'' হিজিবিজ্‌বিজ্‌ সুর করে বলতে লাগল, ''আয়, আয়, আয়, \r\n    শেয়ালে বেগুন খায়, তারা তেল আর নুন কোথায় পায়- বলতেই শেয়াল ভয়ানক ব্যস্ত হয়ে \r\n    উঠল, ''থাক্‌ থাক্‌, সে অন্য শেয়ালের কথা, তোমার সাক্ষী দেওয়া শেষ হয়ে গিয়েছে \r\n    ।''<br />\r\n    এদিকে হয়েছে কি, সাক্ষীরা পয়সা পাচ্ছে দেখে সাক্ষী দেবার ভয়ানক হুড়োহুড়ি লেগে \r\n    গিয়েছে । সবাই মিলে ঠেলাঠেলি করছে, এমন সময় হঠাৎ দেখি কাক্কেশ্বর ঝুপ্‌ করে গাছ \r\n    থেকে নেমে এসে সাক্ষীর জায়গায় বসে সাক্ষী দিতে আরম্ভ করেছে । কেউ কিছু জিজ্ঞাসা \r\n    করবার আগেই সে বলতে আরম্ভ করল, ''শ্রীশ্রীভূশণ্ডিকাগায় নমঃ । শ্রীকাক্কেশ্বর কুচকুচে, \r\n    ৪১ নং গেছোবাজার, কাগেয়াপটি । আমরা হিসাবী ও বেহিসাবী খুচরা পাইকারী সকল প্রকার \r\n    গণনার কার্য-''<br />\r\n    শেয়াল বলল, ''বাজে কথা বল না, যা জিজ্ঞাসা করছি তার জবাব দাও । কি নাম তোমার ?''<br />\r\n    কাক বলল, ''কি আপদ ! তাই তো বলছিলাম- শ্রীকাক্কেশ্বর কুচকুচে ।'' শেয়াল বলল, ''নিবাস \r\n    কোথায় ?'' কাক বলল, ''বললাম যে কাগেয়াপটি ।''<br />\r\n    শেয়াল বলল, ''সে এখান থেকে কতদূর ?'' কাক বলল, ''তা বলা ভারি শক্ত । ঘণ্টা হিসাবে \r\n    চার আনা, মাইল হিসাবে দশ পয়সা, নগদ দিলে দু পয়সা কম । যোগ করলে দশ আনা, বিয়োগ \r\n    করলে তিন আনা, ভাগ করলে সাত পয়সা, গুণ করলে একুশ টাকা ।''<br />\r\n    শেয়াল বলল, ''আর বিদ্যে জাহির করতে হবে না । জিজ্ঞাসা করি, তোমার বাড়ি যাবার পথটা \r\n    চেন তো ?'' কাক বলল, ''তা আর চিনিনে ? এইতো সামনেই সোজা পথ দেখা যাচ্ছে ।'' শেয়াল \r\n    বলল, ''এ-পথ কতদূর গিয়েছে ?'' কাক বলল, পথ আবার যাবে কোথায় ? যেখানকার পথ সেখানেই \r\n    আছে । পথ কি আবার এদিক ওদিক চলে বেড়ায় ? না দার্জিলিঙে হাওয়া খেতে যায় ?''<br />\r\n    শেয়াল বলল, ''তুমি তো ভারি বেয়াদব হে ! বলি, সাক্ষী দিতে এসেছ, মোকদ্দমার কিছু \r\n    কি জান ?''<br />\r\n    কাক বলল, ''খুব যা হোক ! এতক্ষণ বসে বসে হিসাব করল কে ? যা কিছু জানতে চাও আমার \r\n    কাছে পাবে । এই তো প্রথমেই, মান কাকে বলে ? মান মানে কচুরি । কচুরি চার প্রকার- \r\n    হিঙে কচুরি, খাস্তা কচুরি, নিমকি কচুরি আর জিবেগজা ! খেলে কি হয় ? খেলে শেয়ালদের \r\n    গলা কুট্‌কুট্‌ করে, কিন্তু কাগেদের করে না । তারপর একজন সাক্ষী ছিল, নগদ মূল্য \r\n    চার আনা, সে আসামে থাকত, তার কানের চামড়া নীল হয়ে গেল- তাকে বলে কালাজ্বর । তারপর \r\n    একজন লোক ছিল, সে সকলের নামকরণ করত- শেয়ালকে বলতো তেলচোরা, কুমিরকে বলতো অষ্টাবক্র, \r\n    প্যাঁচাকে বলতো বিভীষণ-''<br />\r\n    বলতেই বিচার সভায় একটা ভয়ানক গোলমাল বেধে গেল । কুমির হঠাৎ খেপে টপ্‌ করে কোলা \r\n    ব্যাংকে খেয়ে ফেলল, তাই দেখে ছুঁচোটা কিচ্‌কিচ্‌ করে ভয়ানক চ্যাঁচাতে লাগল, শেয়াল \r\n    একটা ছাতা দিয়ে হুস্‌ হুস্‌ করে কাক্কেশ্বরকে তাড়াতে লাগল । প্যাঁচা গম্ভীর হয়ে \r\n    বলল, সবাই চুপ কর, আমি মোকদ্দমার রায় দেব ।'' এই বলেই সে একটা কানে-কলম-দেওয়া \r\n    খরগোশকে হুকুম করল, ''যা বলছি লিখে নাও, মানহানির মোকদ্দমা, ২৪ নম্বর । <br />\r\n    ফরিয়াদী- সজারু । আসামী- দাঁড়াও । আসামী কৈ ?'' তখন সবাই বলল, ঐ যা ! আসামী তো \r\n    কেঊ নেই ।'' তাড়াতাড়ি ভুলিয়ে-ভালিয়ে ন্যাড়াকে আসামী দাঁড় করানো হল । ন্যাড়াটা \r\n    বোকা, সে ভাবল আসামীরাও বুঝি পয়সা পাবে, তাই সে কোনো আপত্তি করল না ।<br />\r\n    হুকুম হল- ন্যাড়ার তিনমাস জেল আর সাতদিনের ফাঁসি । আমি সবে ভাবছি এরকম অন্যায় \r\n    বিচারের বিরুদ্ধে আপত্তি করা উচিত, এমন সময় ছাগলটা হঠাৎ ''ব্যা-করণ শিং'' বলে পিছন \r\n    থেকে তেড়ে এসে আমায় ঢুঁ মারল, তারপরেই আমার কান কামড়ে দিল । অমনি চারদিকে কি \r\n    রকম সব ঘুলিয়ে যেতে লাগল, ছাগলটার মুখটা ক্রমে বদলিয়ে শেষটায় ঠিক মেজোমামার মতো \r\n    হয়ে গেল। তখন ঠাওর করে দেখলাম, মেজোমামা আমার কান ধরে বলছেন, ''ব্যাকরণ শিখবার \r\n    নাম করে বুঝি পড়ে পড়ে ঘুমানো হচ্ছে ?''<br />\r\n    আমি তো অবাক ! প্রথমে ভাবলাম বুঝি এতক্ষণ স্বপ্ন দেখছিলাম । কিন্তু, তোমরা বললে \r\n    বিশ্বাস করবে না, আমার রুমালটা খুঁজতে গিয়ে দেখি কোথাও রুমাল নেই, আর একটা বেড়াল \r\n    বেড়ার উপর বসে বসে গোঁফে তা দিচ্ছিল, হঠাৎ আমায় দেখতে পেয়েই খচ্‌মচ্‌ করে নেমে \r\n    পালিয়ে গেল । আর ঠিক সেই সময়ে বাগানের পিছন থেকে একটা ছাগল ব্যা করে ডেকে উঠল \r\n    ।<br />\r\n    আমি বড়মামার কাছে এসব কথা বলেছিলাম, কিন্তু বড়মামা বললেন, ''যা, যা, কতগুলো বাজে \r\n    স্বপ্ন দেখে তাই নিয়ে গল্প করতে এসেছে ।'' মানুষের বয়স হলে এমন হোঁৎকা হয়ে যায়, \r\n    কিছুতেই কোনো কথা বিশ্বাস করতে চায় না । তোমাদের কিনা এখনও বয়স বেশি হয়নি, তাই \r\n    তোমাদের কাছে ভরসা করে এসব কথা বললাম । </p>\r\n  <p align="center"> ________<br />\r\n    </p>', 'mW-ynnfObb8', '2013-03-19 00:00:00', '2014-08-21 16:53:01', 1824, '');
INSERT INTO `articles` (`id`, `category_id`, `top_image`, `title`, `article_slug`, `first_line`, `body`, `audio`, `added_on`, `modified_on`, `views`, `sc_track_id`) VALUES
(113, 2, '', 'পাগলা দাশু', 'পাগলা-দাশু', '', '<p>আমাদের স্কুলের যত ছাত্র তাহাদের মধ্যে এমন কেহই ছিল না, যে পাগলা \r\n  দাশুকে না চিনে। যে লোক আর কাহাকেও জানে না, সেও সকলের আগে দাশুকে চিনিয়া ফেলে। সেবার এক নতুন দারোয়ান আসিল, একেবারে আন্‌‌কোরা পাড়াগেঁয়ে লোক, কিন্তু প্রথম যখন সে পাগলা দাশুর নাম শুনিল, তখনই আন্দাজে ঠিক করিয়া লইল যে, এই ব্যক্তিই পাগলা দাশু। কারণ মুখের চেহারায়, কথাবার্তায়, চাল-চলনে বোঝা যাইত যে তাহার মাথায় একটু ''ছিট'' \r\nআছে। তাহার চোখ দুটি গোল গোল, কান দুটি অনাবশ্যক রকমের বড়, মাথায় একবস্তা ঝাঁকড়া \r\nচুল। চেহারাটা দেখিলেই মনে হয়&mdash; </p>\r\n\r\n<table width="45%" border="0" align="center">\r\n  <tr>\r\n    <td>ক্ষীণদেহ খর্বকায় মুণ্ডু তাহে ভারি<br>\r\n      যশোরের কই যেন নরমূর্তিধারি।</td>\r\n  </tr>\r\n</table>\r\n\r\n<p> সে যখন তাড়াতাড়ি চলে অথবা ব্যস্ত হইয়া কথা বলে, তখন তাহার হাত \r\n  পা ছোঁড়ার ভঙ্গী দেখিয়া হঠাৎ কেন জানি চিংড়িমাছের কথা মনে পড়ে।</p>\r\n<p>সে যে বোকা ছিল তাহা নয়। অঙ্ক কষিবার সময়, বিশেষত লম্বা লম্বা \r\n  গুণ-ভাগের বেলায় তাহার আশ্চর্য মাথা খুলিত। আবার এক এক সময় সে আমাদের বোকা বানাইয়া \r\n  তামাশা দেখিবার জন্য এমন সকল ফন্দি বাহির করিত যে, আমরা তাহার বুদ্ধি দেখিয়া অবাক \r\n  হইয়া থাকিতাম।</p>\r\n<p>''দাশু'' অর্থাৎ দাশরথি, যখন প্রথম আমাদের ইস্কুলে ভরতি হয়, তখন \r\n  জগবন্ধুকে আমাদের ''ক্লাশের ভালো ছেলে'' বলিয়া সকলে জানিত। সে পড়াশোনায় ভালো হইলেও, \r\n  তাহার মতো অমন একটি হিংসুটে ভিজেবেড়াল আমরা আর দেখি নাই। দাশু একদিন জগবন্ধুর \r\n  কাছে কি একটা ইংরাজি কথার মানে জিজ্ঞাসা করিতে গিয়াছিল। জগবন্ধু তাহাকে খামখা \r\n  দুকথা শুনাইয়া বলিল, &quot;আমার বুঝি আর খেয়ে-দেয়ে কাজ নেই ? আজ একে ইংরাজি বোঝাব, \r\n  কাল ওর অঙ্ক কষে দেব, পরশু আর একজন আসবেন আর এক ফরমাইস নিয়ে&mdash; ঐ করি আর কি !&quot; \r\n  দাশু সাংঘাতিক চটিয়া বলিল, &quot;তুমি তো ভারি ছ্যাঁচড়া ছোটলোক !&quot; জগবন্ধু \r\n  পণ্ডিত মশায়ের কাছে নালিশ করিল, &quot;ঐ নতুন ছেলেটা আমায় গালাগালি দিচ্ছে।&quot; \r\n  পণ্ডিত মহাশয় দাশুকে এমনি ধমক দিয়া দিলেন যে বেচারা একেবারে দমিয়া গেল। </p>\r\n<p>আমাদের ইংরাজি পড়াইতেন বিষ্টুবাবু। জগবন্ধু তাঁহার প্রিয় ছাত্র। পড়াইতে পড়াইতে যখনই তাঁহার বই দরকার হয়, তিনি জগবন্ধুর কাছে বই চাহিয়া লন। একদিন \r\n  তিনি পড়াইবার সময় ''গ্রামার'' চাহিলেন, জগবন্ধু তাড়াতাড়ি তাহার সবুজ কাপড়ের মলাট \r\n  দেওয়া ''গ্রামার'' খানা বাহির করিয়া দিল। মাস্টার মহাশয় বইখানি খুলিয়াই হঠাৎ গম্ভীর \r\n  হইয়া জিজ্ঞাসা করিলেন, &quot;বইখানা কার ?&quot; জগবন্ধু বুক ফুলাইয়া বলিল, &quot;আমার&quot;। মাস্টার মহাশয় বলিলেন, &quot;হুঁ&mdash; নতুন সংস্করণ বুঝি ? বইকে-বই একেবারে বদলে \r\n  গেছে।&quot; এই বলিয়া তিনি পড়িতে লাগিলেন&mdash; ''যশোবন্ত দারোগা&mdash; লোমহর্ষক ডিটেকটিভ \r\n  নাটক।'' জগবন্ধু ব্যাপারখানা বুঝিতে না পারিয়া বোকার মতো তাকাইয়া রহিল। মাস্টার \r\n  মহাশয় বিকট রকম চোখ পাকাইয়া বলিলেন, &quot;এই সব জ্যাঠামি বিদ্যে শিখছ বুঝি ?&quot; \r\n  জগবন্ধু আম্‌তা আম্‌তা করিয়া কি যেন বলিতে যাইতেছিল, কিন্তু মাস্টার মহাশয় এক ধমক \r\n  দিয়া বলিলেন, &quot;থাক্‌ থাক্‌, আর ভালমানুষি দেখিয়ে কাজ নেই&mdash; ঢের হয়েছে।&quot; \r\n  লজ্জায় অপমানে জগবান্ধুর দুই কান লাল হইয়া উঠিল&mdash; আমরা সকলেই তাহাতে বেশ খুশি হইলাম। পরে জানা গেল যে, এটিও দাশু ভায়ার কীর্তি, সে মজা দেখিবার জন্য উপক্রমণিকার জায়গায় \r\n  ঠিক ঐরূপ মলাট দেওয়া একখানা বই রাখিয়া দিয়াছিল। </p>\r\n<p>দাশুকে লইয়া আমরা সর্বদাই ঠাট্টাতামাশা করিতাম এবং তাহার সামনেই \r\n  তাহার বুদ্ধি ও চেহারা সম্বন্ধে অপ্রীতিকর সমালোচনা করিতাম। তাহাতে একদিনও তাহাকে \r\n  বিরক্ত হইতে দেখি নাই। এক এক সময়ে সে নিজেই আমাদের মন্তব্যের উপর রঙ চড়াইয়া নিজের \r\n  সম্বন্ধে নানারকম অদ্ভুত গল্প বলিত। একদিন সে বলিল, &quot;ভাই, আমাদের পাড়ায় যখন \r\n  কেউ আমসত্ত্ব বানায় তখনই আমার ডাক পড়ে। কেন জানিস ?&quot; আমরা বলিলাম, &quot;খুব \r\n  আমসত্ত্ব খাস বুঝি ?&quot; সে বলিল, &quot;তা নয়। যখন আমতত্ত্ব শুকোতে দেয়, আমি \r\n  সেইখানে ছাদের উপর বার দুয়েক চেহারাখানা দেখিয়ে আসি। তাতেই, ত্রিসীমানার যত কাক \r\n  সব ত্রাহি ত্রাহি করে ছুটে পালায় কাজেই আর আমসত্ত্ব পাহারা দিতে হয় না।&quot;</p>\r\n<p>একবার সে হঠাৎ পেন্টেলুন পরিয়া স্কুলে হাজির হইল। ঢল্‌ঢলে পায়জামার \r\n  মতো পেন্টেলুন আর তাকিয়ার খোলের মতো কোট পরিয়া তাহাকে যে কিরূপ অদ্ভুত দেখাইতেছিল, \r\n  তাহা সে নিজেই বুঝিতেছিল এবং তাহার কাছে ভারি একটা ব্যাপার বলিয়া বোধ হইতেছিল। \r\n  আমরা জিজ্ঞাসা করিলাম, &quot;পেন্টেলুন পরেছিস্‌ কেন ?&quot; দাশু এক গাল হাসিয়া \r\n  বলিল, &quot;ভালো ইংরাজি শিখব ব''লে।&quot; আর একবার সে খামখা নেড়া মাথায় এক পট্টি \r\n  বাঁধিয়া ক্লাশে আরম্ভ করিল এবং আমরা সকলে তাহা লইয়া ঠাট্টা তামাশা করায় যারপরনাই \r\n  খুশি হইয়া উঠিল। দাশু আদপেই গান গাহিতে পারে না, তাহার যে তালজ্ঞান বা সুরজ্ঞান \r\n  একেবারে নাই, একথা সে বেশ জানে। তবু সেবার ইনস্পেক্টার সাহেব যখন ইস্কুল দেখিতে \r\n  আসেন, তখন আমাদের খুশি করিবার জন্য চিৎকার করিয়া গান শুনাইয়াছিল। আমরা কেহ ওরূপ \r\n  করিলে সেদিন রীতিমত শাস্তি পাইতাম, কিন্তু দাশু ''পাগলা'' বলিয়া তাহার কোনো শাস্তি \r\n  হইল না।</p>\r\n<p>একবার ছুটির পরে দাশু অদ্ভুত এক বাক্স লইয়া ক্লাসে হাজির হইল। \r\n  মাস্টার মহাশয় জিজ্ঞাসা করিলেন, &quot;কি দাশু, ও বাক্সের মধ্যে কি আছে ?&quot; \r\n  দাশু বলিল, &quot;আজ্ঞে, আমার জিনিসপত্র।&quot; জিনিসপত্রটা কিরূপ হইতে পারে, \r\n  এই লইয়া আমাদের মধ্যে বেশ একটা তর্ক হইয়া গেল। দাশুর সঙ্গে বই, খাতা, পেনসিল, \r\n  ছুরি সবই তো আছে, তবে আবার জিনিসপত্র কি বাপু? দাশুকে জিজ্ঞাসা করিলাম, সে সোজাসুজি \r\n  কোনো উত্তর না দিয়া বাক্সটিকে আঁকড়াইয়া ধরিল এবং বলিল, &quot;খবরদার, আমার বাক্স \r\n  তোমরা কেউ ঘেঁটো না।&quot; তাহার পর চাবি দিয়া বাক্সটাকে একটুখানি ফাঁক করিয়া, \r\n  সে তাহার ভিতর দিয়া কি যেন দেখিল, এবং ''ঠিক আছে'' বলিয়া গম্ভীরভাবে মাথা নাড়িয়া \r\n  বিড় বিড় করিয়া হিসাব করিতে লাগিল। আমি একটুখানি দেখিবার জন্য উঁকি মারিতে গিয়াছিলাম&mdash; \r\n  অমনি পাগলা মহা ব্যস্ত হইয়া তাড়াতাড়ি চাবি ঘুরাইয়া বাক্স বন্ধ করিয়া ফেলিল।</p>\r\n<p>ক্রমে আমাদের মধ্যে তুমুল আলোচনা আরম্ভ হইল। কেহ বলিল, &quot;ওটা \r\n  ওর টিফিনের বাক্স&mdash; ওর মধ্যে খাবার আছে।&quot; কিন্তু একদিনও টিফিনের সময় তাহাকে \r\n  বাক্স খুলিয়া কিছু খাইতে দেখিলাম না। কেহ বলিল, &quot;ওটা বোধ হয় ওর মানি-ব্যাগ&mdash; \r\n  ওর মধ্যে টাকা পয়সা আছে, তাই ও সর্বদা কাছে কাছে রাখতে চায়। আর একজন বলিল, &quot;টাকা \r\n  পয়সার জন্য অত বড় বাক্স কেন ? ও কি ইস্কুলে মহাজনী কারবার খুলবে নাকি ?&quot;</p>\r\n<p>একদিন টিফিনের সময় দাশু হঠাৎ ব্যস্ত হইয়া, বাক্সের চাবিটা আমার \r\n  কাছে রাখিয়া গেল আর বলিল, &quot;ওটা এখন তোমার কাছে রাখো, দেখো হারায় না যেন। \r\n  আর আমার আসতে যদি একটু দেরি হয়, তবে তোমরা ক্লাশে যাবার আগে ওটা দারোয়ানের কাছে \r\n  দিও।&quot; এই বলিয়া বাক্সটি দারোয়ানের জিম্মায় রাখিয়া বাহির হইয়া গেল। তখন আমাদের \r\n  উৎসাহ দেখে কে! এতদিনে সুবিধা পাওয়া গিয়াছে, এখন দারোয়ানটা একটু তফাৎ গেলেই হয়। খানিক বাদে দারোয়ান তাহার রুটি পাকাইবার লোহার উনানটি ধরাইয়া, কতকগুলি বাসনপত্র \r\n  লইয়া কলতলার দিকে গেল। আমরা এই সুযোগের অপেক্ষায় ছিলাম, দারোয়ান আড়াল হওয়া মাত্র, \r\n  আমরা পাঁচ-সাতজনে তাহার ঘরের কাছে সেই বাক্সের উপর ঝুঁকিয়া পড়িলাম। তাহার পর আমি \r\n  চাবি দিয়া বাক্স খুলিয়া দেখি বাক্সের মধ্যে বেশ ভারি একটা কাগজের পোঁটলা ন্যাকড়ার \r\n  ফালি দিয়া খুব করিয়া জড়ানো। তাড়াতাড়ি পোঁটলার প্যাঁচ খুলিয়া দেখা গেল, তাহার মধ্যে \r\n  একখানা কাগজের বাক্স&mdash; তাহার ভিতর আর একটা ছোট পোঁটলা। সেটি খুলিয়া একখানা কার্ড \r\n  বাহির হইল, তাহার এক পিঠে লেখা, ''কাঁচকলা খাও'' আর একটি পিঠে লেখা ''অতিরিক্ত কৌতুহল \r\n  ভালো নয়।'' দেখিয়া আমরা এ-উহার মুখ চাওয়া-চাওয়ি করিতে লাগিলাম। সকলের শেষে একজন \r\n  বলিয়া উঠিল, &quot;ছোকরা আচ্ছা যা হোক, আমাদের বেজায় ঠকিয়েছে।&quot; আর একজন বলিল, \r\n  &quot;যেমন ভাবে বাঁধা ছিল তেমনি করে রেখে দাও, সে যেন টেরও না পায় যে আমরা খুলেছিলাম। তাহলে সে নিজেই জব্দ হবে।&quot; আমি বলিলাম, &quot;বেশ কথা। ও আস্‌‌লে পরে তোমরা \r\n  খুব ভালোমানুষের মতো বাক্সটা দেখাতে বলো আর ওর মধ্যে কি আছে, সেটা বার বার করে \r\n  জানতে চেয়ো।&quot; তখন আমরা তাড়াতাড়ি কগজপত্রগুলি বাঁধিয়া, আগেকার মতো পোঁটলা \r\n  পাকাইয়া বাক্সে ভরিয়া ফেলিলাম।</p>\r\n<p>বাক্সে চাবি দিতে যাইতেছি, এমন সময় হো হো করিয়া একটা হাসির শব্দ \r\n  শোনা গেল&mdash; চাহিয়া দেখি পাঁচিলের উপরে বসিয়া পাগলা দাশু হাসিয়া কুটিকুটি। হতভাগা \r\n  এতক্ষণ চুপি চুপি তামাশা দেখিতেছিল। তাখন বুঝিলাম আমার কাছে চাবি দেওয়া, দারোয়ানের \r\n  কাছে বাক্স রাখা, টিফিনের সময় বাহিরে যাওয়ার ভান করা এ সমস্ত তাহার শয়তানি। খামখা \r\n  আমাদের আহাম্মক বানাইবার জন্যই সে মিছিমিছি এ কয়দিন ক্রমাগত একটা বাক্স বহিয়া বেড়াইতেছে।</p>\r\n<p>সাধে কি বলি ''পাগলা দাশু ?''</p>', '', '2013-03-19 00:00:00', '2014-08-21 16:53:01', 774, ''),
(253, 5, '', 'সমুদ্রের ঘোড়া', 'সমুদ্রের-ঘোড়া', '', '<p>সমুদ্রের ঘোড়া বলতে হঠাৎ যেন সিন্ধুঘোটক মনে করে বসো না। সিন্ধুঘোটক থাকে সমুদ্রের ধারে, কিন্তু তাকে ঘোটক বলা হয় কেন তা জানি না। তার চালচলন চেহারা বা শরীরের গড়ন কিছুই ঘোড়ার \r\nমতো নয়&mdash; ঘোড়ার সঙ্গে তার খুব দূর সম্পর্কেও কোন সম্বন্ধ পাওয়া যায় না&mdash; অথচ তাকে বলি ''সিন্ধুঘোটক''। হিপ্পোপটেমাসকে বাংলায় অনেক সময় ''জলহস্তী'' লেখা হয়। তারও কিন্তু প্রকাণ্ড নাদুসনুদুস \r\nচেহারাটি ছাড়া হাতির সঙ্গে আর কোনরকম মিল খুঁজে পাওয়া যায় না। বরং তাকে শুয়োরের সঙ্গে সমতুলনা করলে তার পরিচয়টা অনেকটা ঠিক হয়।</p>\r\n<p>এখানে যাকে সমুদ্রের ঘোড়া বলছি, তাকে বর্মধারী মাছ বললেই তার ঠিক মত পরিচয় দেওয়া হয়। কিন্তু তার ঐ অদ্ভুত ঘাড় বাঁকান চেহারা আর খাড়া হয়ে চলাফিরা&mdash; এই দেখেই ইংরেজিতে তার নাম \r\nদেওয়া হয়েছে সমুদ্রের ঘোড়া (Sea Horse)। চেহারার বর্ণনা হিসাবে নামট যে চমৎকার হয়েছে, তাতে আর সন্দেহ নেই। এই জন্তুর ল্যাজের দিকটা একেবারের মাছের মতো নয়&mdash; তার উপর গায়ের \r\nচামড়াটিও চিংড়িমাছের খোলার মতো শক্ত। ল্যাজটি থাকাতে তার ভারি সুবিধা। যখন ইচ্ছা জলের নীচে শেওলা গাছে ল্যাজটি জড়িয়ে সে বেশ আরাম করে বিশ্রাম করে। যখন জলের নীচে মাথা উঁচিয়ে \r\nল্যাজ নাড়িয়ে অদ্ভুত ভঙ্গিতে এরা চলে ফিরে, তখন তেজী ঘোড়ার টগ্‌বগ্‌ করে ছুটবার ধরনটাও মনে পড়ে। আসলে এরা যে ''নল'' মাছের জাতভাই, সেটা এদের চোঙের মতো মুখ দেখলেই বোঝা যায়।\r\nনলমাছের চেহারাটা ঠিক তার নামেরই মতো। নলমাছের মুখখানা এমনভাবে তৈরি যে সে হাঁ করতে পারে না। ঐ চোঙার আগায় একটু ফুটো আছে, তাই দিয়ে সে সুড়সুড় করে খাবার টেনে খায়। সমুদ্রের \r\nঘোড়ার মুখখানিও ঠিক এই ধরনের।</p>\r\n<p>এই অদ্ভুত জন্তুগুলির এক একটা আবার ত্রিভঙ্গ ঘোড়ার মতো চেহারা করেও সন্তুষ্ট নয়। তারা নানারকম সাজ করে রংবেরঙের ঝালর ঝুলিয়ে কেমন কিম্ভুত কিমাকার মূর্তি করে থাকে। ঝালরের সাজগুলো \r\nবাস্তবিক তার গায়ের চামড়া। ইংরাজিতে এদের বলে সমুদ্রের ''ড্র্যাগন'' (Sea Dragon) বা রাক্ষস। নামটি ভয়ংকর হলেও জন্তুটি ঠিক সমুদ্রের ঘোড়ার মতোই নিরীহ। তার ঐ রংচঙে পোশাকের বাহারটা \r\nকেবল শত্রুর চোখে ধোঁকা দেবার জন্য। সমুদ্রের নীচে যেসব অদ্ভুত রঙিন বাগান থাকে, তারই মধ্যে ফুল পাতার সঙ্গে রং মিশিয়ে এরা বেমালুম গা ঢাকা দিয়ে থাকে। নানারকম হিংস্র জন্তু আর মাছ \r\nসেখানে ঘোরে ফিরে। তারা এর চেহারা দেখে হঠাৎ বুঝতেই পারে না যে এটাও একটা জানোয়ার।</p>\r\n<p>এদের আর একটি বড় মজার অভ্যাস আছে&mdash; এরা সব সময় ছানার দল সঙ্গে নিয়ে ফেরে। ক্যাঙারুর পেটে যেমন থলি থাকে, তার মধ্যে ছোট ছোট ছানাগুলো দরকার হলেই ঢুকে পড়ে&mdash; তেমনি ওদেরও কারও বুকে, কারও পেটে ছোট ছোট থলির মতো থাকে। ছানারা ভয় পেলে ছুটে তার মধ্যে লুকোয়।</p>', '', '2014-01-06 00:00:00', '2014-08-21 16:53:01', 50, ''),
(114, 2, '', 'দাশুর খ্যাপামি', 'দাশুর-খ্যাপামি', '', '<p>ইস্কুলের ছুটির দিন । ইস্কুলের পরেই ছাত্র-সমিতির \r\n  অধিবেশন হবে, তাতে ছেলেরা মিলে অভিনয় করবে । দাশুর ভারি ইচ্ছে ছিল, সে-ও একটা কিছু \r\n  অভিনয় করে । একে-ওকে দিয়ে সে অনেক সুপারিশ করিয়েছিল, কিন্তু আমরা সবাই কোমর বেঁধে \r\n  বললাম, সে কিছুতেই হবে না ।</p>\r\n<p>সেইতো গতবার যখন আমাদের অভিনয় হয়েছিল, তাতে দাশু সেনাপতি \r\n  সেজেছিল ; সেবার সে অভিনয়টা একেবারে মাটি করে দিয়েছিল । যখন ত্রিচূড়ের গুপ্তচর \r\n  সেনাপতির সঙ্গে ঝগড়া করে তাকে দ্বন্দযুদ্ধে আহ্বান করে বলল, &quot;সাহস থাকিলে \r\n  খোল তলোয়ার !&quot; দাশুর তখন &quot;তবে আয় সম্মুখ সমরে&quot;- ব''লে তখনি তলোয়ার \r\n  খুলবার কথা । কিন্তু দাশুটা আনাড়ির মতো টানাটানি করতে গিয়ে তলোয়ার তো খুলতেই পারল \r\n  না, মাঝ থেকে ঘাবরে গিয়ে কথাগুলোও বলতে ভুলে গেল । তাই দেখে গুপ্তচর আবার &quot;খোল \r\n  তলোয়ার&quot; ব''লে হুঙ্কার দিয়ে উঠল । দাশুটা এমনি বোকা, সে অমনি &quot;দাঁড়া, \r\n  দেখছিস না বক্‌লস আটকিয়ে গেছে&quot; ব''লে চেঁচিয়ে তাকে এক ধমক দিয়ে উঠল । ভাগ্যিস \r\n  আমি তাড়াতাড়ি তলোয়ার খুলে দিলাম, তা না হলে ঐখানেই অভিনয় বন্ধ হয়ে যেত । তারপর \r\n  শেষের দিকে রাজা যখন জিজ্ঞেস করলেন, &quot;কিবা চাহ পুরস্কার কহ সেনাপতি,&quot; \r\n  তখন দাশুর বলবার কথা ছিল &quot;নিত্যকাল থাকে যেন রাজপদে মতি,&quot; কিন্তু দাশুটা \r\n  তা না ব''লে, তার পরের আরেকটা লাইন আরম্ভ করেই, হঠাৎ জিভ কেটে &quot;ঐ যাঃ ! ভুলে \r\n  গেছিলাম&quot; ব''লে আমার দিকে তাকিয়ে হাসতে লাগল । আমি কটমট করে তাকাতে, সে তাড়াতাড়ি \r\n  নিজেকে সামলে নিয়ে ঠিক লাইনটা আরম্ভ করল ।</p>\r\n<p>তাই এবারে তার নাম হতেই আমরা জোর করে ব''লে উঠলাম, &quot;না, \r\n  সে কিছুতেই হবে না ।&quot; বিশু বলল, &quot;দাশু এক্‌‌টিং করবে ? তাহলেই চিত্তির \r\n  !&quot; ট্যাঁপা বলল, &quot;তার চাইতে ভজু মালিকে ডেকে আনলেই হয় !&quot; দাশু বেচারা \r\n  প্রথমে খুব মিনতি করল, তারপর চটে উঠল, তারপর কেমন যেন মুষড়ে গিয়ে মুখ হাঁড়ি করে \r\n  বসে রইল । যে কয়দিন আমাদের তালিম চলছিল, দাশু রোজ এসে চুপটি করে হলের এক কোনায় \r\n  বসে বসে আমাদের অভিনয় শুনত । ছুটির কয়েকদিন আগে থেকে দেখি, ফোর্থ ক্লাশের ছাত্র গণশার \r\n  সঙ্গে দাশুর ভারি ভাব হয়ে গেছে। গনশা ছেলেমানুষ, কিন্তু সে চমচমৎকার আবৃত্তি করতে \r\n  পারে-তাই তাকে দেবদূতের পার্ট দেওয়া হয়েছে। দাশু রোজ তাকে নানারকম খাওয়া এনে খাওয়ায়, \r\n  রঙিন পেনসিল আর ছবির বই এনে দেয়, আর বলে যে ছুটির দিন তাকে একটা ফুটবল কিনে দেবে। \r\n  হঠাৎ গণশার উপর দাশুর এতখানি টান হবার কোন কারণ আমরা বুঝতে পারলাম না । কেবল দেখতে \r\n  পেলাম, গণশা খেলনা আর খাবার পেয়ে ভুলে ''দাশুদা''র একজন পরম ভক্ত হয়ে উঠতে লাগল ।</p>\r\n<p>ছুটির দিনে আমরা যখন অভিনয়ের জন্য প্রস্তুত হচ্ছি, তখন আসল \r\n  ব্যাপারটা বুঝতে পারা গেল । আড়াইটা বাজতে না বাজতেই দেখা গেল, দাশুভায়া সাজঘরে \r\n  ঢুকে পোশাক পরতে আরম্ভ করেছে । আমরা জিজ্ঞেস করলাম, &quot;কিরে ? তুই এখানে কি \r\n  করছিস ?&quot; দাশু বলল &quot;বাঃ, পোশাক পরব না ?&quot; আমি বললাম, &quot;পোশাক \r\n  পরবি কিরে? তুই তো আর এক্‌টিং করবি না ।&quot; দাশু বলল, &quot;বাঃ, খুব তো খবর \r\n  রাখ । আজকে দেবদূত সাজবে কে জানো ?&quot; শুনে হঠাৎ আমাদের মনে কেমন একটা খটকা \r\n  লাগল, আমি বললাম, &quot;কেন গণশার কি হল ?&quot; দাশু বলল, কি হয়েছে তা গণশাকে \r\n  জিজ্ঞেস করলেই পারো ?&quot; তখন চেয়ে দেখি সবাই এসেছে, কেবল গণশাই আসেনি । অমনি \r\n  রামপদ, বিশু আর আমি ছুটে বেরোলাম গণশার খোঁজে । </p>\r\n<p>সারা ইস্কুল খুঁজে, শেষটায় টিফিনঘরের পিছনে হতভাগাকে \r\n  খুঁজে পাওয়া গেল । সে আমাদের দেখেই পালাবার চেষ্টা করছিল, কিন্তু আমরা তাকে চটপট \r\n  গ্রেপ্তার করে টেনে নিয়ে চললাম । গণশা কাঁদতে লাগল, &quot;না আমি কক্ষনো এক্‌টিং \r\n  করব না, তাহলে, দাশুদা আমায় ফুটবল দেবে না ।&quot; আমরা তবু তাকে হিঁচড়ে টেনে নিয়ে \r\n  যাচ্ছি, এমন সময় অঙ্কের মাস্টার হরিবাবু সেখানে এসে উপস্থিত । তিনি আমাদের দেখেই \r\n  ভয়ঙ্কর চোখ লাল করে ধমক দিয়ে উঠলেন, &quot;তিন-তিনটে ধাড়ি ছেলে মিলে ঐ কচি ছেলেটার \r\n  পিছনে লেগেছিস ? তোদের লজ্জাও করে না ?&quot; ব''লেই আমাকে বিশুকে এক একটি চড় মেরে \r\n  আর রামপদর কান ম''লে দিয়ে হন্‌হন্‌ করে চলে গেলেন । এই সুযোগে হাতছাড়া হয়ে গণেশ্চন্দ্র \r\n  আবার চম্পট দিল । আমরাও আপমানটা হজম করে ফিরে এলাম । এসে দেখি, দাশুর সঙ্গে রাখালের \r\n  মহা ঝগড়া লেগে গেছে । রাখাল বলছে, &quot;তোকে আজ কিছুতেই দেবদূত সাজতে দেওয়া হবে \r\n  না ।&quot; দাশু বলছে, &quot;বেশ তো, তাহলে আর কেউ সাজুক, আমি রাজা কিম্বা মন্ত্রী \r\n  সাজি । পাঁচ-ছটা পার্ট আমার মুখস্থ হয়ে আছে ।&quot; এমন সময় আমরা এসে খবর দিলাম, \r\n  যে, গণশাকে কিছুতেই রাজী করানো গেল না । তখন অনেক তর্কবিতর্ক আর ঝগড়াঝাটির পর স্থির \r\n  হল যে, দাশুকে আর ঘাঁটিয়ে দরকার নেই, তাকেই দেবদূত সাজতে দেওয়া হোক । শুনে দাশু \r\n  খুব খুশি হল আর আমাদের শাসিয়ে রাখল যে, &quot;আবার যদি তোরা গোলমাল করিস, তাহলে \r\n  কিন্তু গতবারের মতো সব ভুণ্ডুল করে দেব ।&quot;</p>\r\n<p>তারপর অভিনয় আরম্ভ হল । প্রথম দৃশ্যে দাশু কিছু গোলমাল করেনি, \r\n  খালি স্টেজের সামনে একবার পানের পিক্‌ ফেলেছিল । কিন্তু তৃতীয় দৃশ্যে এসে সে একটু \r\n  বাড়াবাড়ি আরম্ভ করল । এক জায়গায় তার খালি বলবার কথা &quot;দেবতা বিমুখ হলে মানুষ \r\n  কি পারে ?&quot; কিন্তু সে এক কথাটুকুর আগে কোত্থেকে আরও চার-পাঁচ লাইন জুড়ে দিল \r\n  ! আমি তাই নিয়ে আপত্তি করেছিলাম, দাশু বলল, &quot;তোমরা যে লম্বা বক্তৃতা কর সে \r\n  বেলা দোষ হয় না, আমি দুটো কথা বেশি বললেই যত দোষ !&quot; এও সহ্য করা যেত, কিন্তু \r\n  শেষ দৃশ্যের সময় তার মোটেই আসবার কথা নয়, তা জেনেও সে স্টেজে আসবার জন্য জেদ ধরে \r\n  বসল । আমরা অনেক কষ্টে অনেক তোয়াজ করে তাকে বুঝিয়ে দিলাম যে, শেষ দৃশ্যে দেবদূত \r\n  আসতেই পারে না, কারণ তার আগের দৃশ্যেই আছে যে, দেবদূত বিদায় নিয়ে স্বর্গে চলে গেলেন \r\n  । শেষ দৃশ্যেও আছে যে মন্ত্রী রাজাকে সংবাদ দিচ্ছেন যে, দেবদূত মহারাজাকে আশীর্বাদ \r\n  করে স্বর্গপুরীতে প্রস্থান করেছেন । দাশু অগত্যা তার জেদ ছাড়ল বটে, কিন্তু বোঝা \r\n  গেল সে মনে মনে একটুও খুশি হয়নি ।</p>\r\n<p>শেষ দৃশ্যের অভিনয় আরম্ভ হল । প্রথম খানিকটা অভিনয়ের পর মন্ত্রী \r\n  এসে সভায় হাজির হবেন । এ কথা সে কথার পর তিনি রাজাকে সংবাদ দিলেন, &quot;বারবার \r\n  মহারাজে আশিস্‌ করিয়া, দেবদূত গেল চলি স্বর্গ অভিমুখে ।&quot; বলতে বলতেই হঠাৎ \r\n  কোত্থেকে &quot;আবার সে এসেছে ফিরিয়া&quot; ব''লে একগাল হাসতে হাসতে দাশু একেবারে \r\n  সামনে এসে উপস্থিত । হঠাৎ এ রকম বাধা পেয়ে মন্ত্রী তার বক্তৃতার খেই হারিয়ে ফেলল, \r\n  আমরাও সকলে কি রকম যেন ঘাবড়িয়ে গেলাম- অভিনয় হঠাৎ বন্ধ হবার যোগার হয়ে এল । তাই \r\n  দেখে দাশু সর্দারি করে মন্ত্রীকে বলল, &quot;বলে যাও কি বলিতেছিলে ।&quot; তাতে \r\n  মন্ত্রী আরও কেমন ঘাবড়িয়ে গেল । রাখাল প্রতিহারী সেজেছিল, দাশুকে কি যেন বলবার \r\n  জন্যে যেই একটু এগিয়ে গেছে, অমনি দাশু &quot;চেয়েছিল জোর করে ঠেকাতে আমারে এই হতভাগা&quot;- \r\n  বলে এক চাঁটি মেরে তার মাথার পাগড়ি ফেলে দিল । ফেলে দিয়েই সে রাজার শেষ বক্তৃতাটা- \r\n  &quot;এ রাজ্যতে নাহি রবে হিংসা অত্যাচার, নাহি রবে দারিদ্র যাতনা&quot; ইত্যাদি- \r\n  নিজেই গড়গড় করে ব''লে গিয়ে, &quot;যাও সবে নিজ নিজ কাজে&quot; ব''লে অভিনয় শেষ করে \r\n  দিল । আমরা কি করব বুঝতে না পেরে সব বোকার মতো হাঁ করে তাকিয়ে রইলাম । ওদিকে ঢং \r\n  করে ঘণ্টা বেজে উঠল আর ঝুপ্‌ করে পর্দাও নেমে গেল ।</p>\r\n<p>আমরা সব রেগে-মেগে লাল হয়ে দাশুকে তেড়ে ধরে বললাম, &quot;হতভাগা, \r\n  দ্যাখ দেখি সব মাটি করলি, অর্ধেক কথাই বলা হল না ।&quot; দাশু বলল, বা, তোমরা কেউ \r\n  কিছু বলছ না দেখেই তো আমি তাড়াতাড়ি যা মনে ছিল সেইগুলো বলে দিলাম । তা না হলে তো \r\n  আরো সব মাটি হয়ে যেত ।&quot; আমি বললাম, তুই কেন মাঝখানে এসে গোল বাধিয়ে দিলি ? \r\n  তাইতো সব ঘুলিয়ে গেল ।&quot; দাশু বলল, রাখাল কেন বলেছিল যে আমায় জোর করে আটকিয়ে \r\n  রাখবে ? তা ছাড়া তোমরা কেন আমায় গোরা থেকে নিতে চাচ্ছিলে না আর ঠাট্টা করছিলে ? \r\n  আর রামপদ কেন বারবার আমার দিকে কটমট করে তাকাচ্ছিল ?&quot; রামপদ বলল, ওকে ধরে \r\n  ঘা দু চার লাগিয়ে দে ।&quot;</p>\r\n<p>দাশু বলল, &quot;লাগাও না, দেখবে আমি এক্ষুনি চেঁচিয়ে \r\n  সকলকে হাজির করি কি না? &quot;</p>\r\n<p align="center">_______</p>', 'Cs4zPiPyUWI', '2013-03-19 00:00:00', '2014-08-21 16:53:01', 569, '112941350');
INSERT INTO `articles` (`id`, `category_id`, `top_image`, `title`, `article_slug`, `first_line`, `body`, `audio`, `added_on`, `modified_on`, `views`, `sc_track_id`) VALUES
(115, 2, '', 'চীনেপট্‌কা', 'চীনেপট্‌কা', '', '<p><img src=''images/chinepotka.gif'' width="400" height="218"></p>\r\n<p>আমাদের রামপদ তাহার জন্মদিনে একহাঁড়ি \r\n  মিহিদানা লইয়া স্কুলে আসিল । টিফিনের ছুটি হওয়া মাত্র আমরা সকলেই মহা উৎসাহে সেগুলি \r\n  ভাগ করিয়া খাইলাম । খাইল না কেবল দাশু ।</p>\r\n<p> পাগলা দাশু যে মিহিদানা খাইতে ভালোবাসে \r\n  না, তা নয় । কিন্তু রামপদকে সে একেবারেই পছন্দ করিত না, দুজনের মধ্যে প্রায়ই ঝগড়া \r\n  চলিত । আমরা রামপদকে বলিলাম, &quot;দাশুকে কিছু দে ।&quot; রামপদ বলিল, &quot;কি \r\n  রে দাশু, খাবি নাকি ? দেখিস্‌, খাবার লোভ থাকে তো বল্‌ আর আমার সঙ্গে কোনোদিন লাগতে \r\n  আসবিনে- তা হলে মিহিদানা পাবি ।&quot; এমন করিয়া বলিলে তো রাগ হইবারই কথা, কিন্তু \r\n  দাশু কিছু না বলিয়া গম্ভীরভাবে হাত পাতিয়া মিহিদানা লইল, তারপর দারোয়ানের ছাগলটাকে \r\n  ডাকিয়া সকলের সামনে তাহাকে সেই মিহিদানা খাওয়াইল । তারপর খানিকক্ষণ হাঁড়িটার দিকে \r\n  তাকাইয়া, কি যেন ভাবিয়া মুচকি মুচকি হাসিতে হাসিতে স্কুলের বাহিরে চলিয়া গেল । \r\n  </p>\r\n<p>পণ্ডিত মহাশয় মানুষটি মন্দ নহেন । পড়ার জন্য \r\n  প্রায়ই কোনো তাড়াহুড়ো করেন না । কেবল মাঝে মাঝে একটু বেশি গোল করিলে হঠাৎ সাংঘাতিক \r\n  চটিয়া যান । সে সময়ে তাহার মেজাজটি আশ্চর্য রকম ধারাল হইয়া ওঠে । পণ্ডিত মহাশয় \r\n  চেয়ারে বসিয়াই &quot;নদী শব্দের রূপ কর&quot; বলিয়া ঘুমাইয়া পড়িলেন । আমরা বই খুলিয়া \r\n  হড়বড় করিয়া যা-তা খানিকটা বলিয়া গেলাম এবং তাহার উত্তরে, পণ্ডিত মহাশয়ের নাকের \r\n  ভিতর হইতে অতি সুন্দর ঘড়্‌ঘড়্‌ শব্দ শুনিয়া বুঝিলাম, নিদ্রা বেশ গভীর হইয়াছে । \r\n  কাজেই আমরাও শ্লেট লইয়া ''কাটকুট'' আর ''দশপঁচিশ'' খেলা শুরু করিলাম । কেবল মাঝে মাঝে \r\n  যখন ঘড়ঘড়ানি কমিয়া আসিত, তখন সবাই মিলিয়া সুর করিয়া &quot;নদী নদ্যৌ&quot; ইত্যাদি \r\n  আওরাইতাম । দেখিতাম, তাহাতে ঘুমপাড়ানি গানের মতো আশ্চর্য ফল পাওয়া যায় । </p>\r\n<p>সকলে খেলায় মত্ত, কেবল দাশু এক কোনায় বসিয়া কি যেন \r\n  করিতেছে, সেদিকে আমাদের খেয়াল নাই । একটু বাদে পণ্ডিত মহাশয়ের চেয়ারের তলায় তক্তার \r\n  নিচ হইতে ফট্‌ করিয়া কি একটা আওয়াজ হইল । পণ্ডিত মহাশয় ঘুমের ঘোরে ভ্রূকুটি করিয়া \r\n  সবেমাত্র ''উঃ'' বলিয়া কি যেন একটা ধমক দিতে যাইবেন, এময় সময় ফুট্‌ফাট্‌ দুম্‌দাম্‌ \r\n  ধুপ্‌ধাপ্‌ শব্দে তাণ্ডব কোলাহল উঠিয়া সমস্ত ইস্কুলটিকে একেবারে কাঁপাইয়া তুলিল \r\n  । মনে হইল যেন, যত রাজ্যের মিস্ত্রী মজুর সবাই এক জোটে বিকট তালে ছাদ পিটাইতে লাগিয়েছে- \r\n  দুনিয়ার যত কাঁসারি আর লাঠিয়াল সবাই যেন পাল্লা দিয়া হাতুড়ি আর লাঠি ঠুকিতেছে, \r\n  খানিকক্ষণ পর্যন্ত আমরা, যাকে পড়ার বইয়ে ''কিংকর্তব্যবিমুঢ়'' বলে, তেমনি হইয়া হাঁ \r\n  করিয়া রহিলাম । পণ্ডিত মহাশয় একবার মাত্র বিকট শব্দ করিয়া, তার পর হঠাৎ হাত পা \r\n  ছুঁড়িয়া একলাফে টেবিল ডিঙ্গাইয়া, একেবারে ক্লাসের মাঝখানে ধড়্‌ফড় করিয়া পড়িয়া গেলেন \r\n  । সরকারী কলেজের নবীন পাল বরাবরই হাই জাম্পে ফার্স্ট প্রাইজ পায়, তাহাকেও আমরা \r\n  এরকম লাফাইতে দেখি নাই । পাশের ঘরে নিচের ক্লাশের ছেলেরা চিৎকার করিয়া ''কড়াকিয়া'' \r\n  নামতা আওড়াইতেছিল- তাহারাও হঠাৎ ভয়ে আড়ষ্ট হইয়া থামিয়া গেল । দেখিতে দেখিতে ইস্কুলময় \r\n  হুলুস্থুল পড়িয়া গেল- দারোয়ানের কুকুরটা পর্যন্ত যারপরনাই ব্যস্ত হইয়া বিকট ঘেঁউ \r\n  ঘেঁউ শব্দে গোলমালের মাত্রা ভীষণ রকম বাড়াইয়া তুলিল ।</p>\r\n<p>মিনিট পাঁচেক ভয়ানক আওয়াজের পর যখন সব ঠাণ্ডা হইয়া \r\n  আসিল, তখন পণ্ডিত মহাশয় বলিলেন, &quot;কিসের শব্দ হইয়াছিল দেখ ।&quot; দারোয়ানজি \r\n  একটা লম্বা বাঁশ দিয়া অতি সাবধানে আস্তে আস্তে, তক্তার নিচ হইতে একটা হাঁড়ি ঠেলিয়া \r\n  বাহির করিল- রামপদর সেই হাঁড়িটা, তখনও তাহার মুখের কাছে একটুখানি মিহিদানা লাগিয়াছিল \r\n  । পণ্ডিত মহাশয় ভয়ানক ভ্রূকুটি করিয়া বলিলেন, &quot;এ হাঁড়িটা কার ?&quot; রামপদ \r\n  বলিল, &quot;আজ্ঞে আমার ।&quot; আর কোথা যায়- অমনি দুই কানে দুই পাক ! &quot;হাঁড়িতে \r\n  কি রেখেছিলি !&quot; রামপদ তখন বুঝিতে পারিল যে, গোলমালের জন্য সমস্ত দোষ তাহার \r\n  ঘাড়ে আসিয়া পড়িতেছে । সে বেচারা তাড়াতাড়ি বুঝাইতে গেল, &quot;আজ্ঞে ওর মধ্যে মিহিদানা \r\n  এনেছিলাম, তারপর-&quot; মুখের কথা শেষ না হইতেই পণ্ডিত মহাশয় বলিলেন, &quot;তারপর \r\n  মিহিদানাগুলো চীনেপট্‌কা হয়ে ফুটতে লাগল, না ?&quot; বলিয়াই ঠাস্‌ ঠাস্‌ করিয়া \r\n  দুই চড় ।</p>\r\n<p>অন্যান্য মাস্টারেরাও ক্লাশে আসিয়া জড় হইয়াছিলেন ; তাঁহারাও \r\n  এক বাক্যে হাঁ-হাঁ করিয়া রুখিয়া আসিলেন । আমরা দেখিলাম বেগতিক । বিনা দোষে রামপদ \r\n  বেচারা মার খায় বুঝি ! এমন সময় দাশু আমার শ্লেটখানা লইয়া পণ্ডিত মহাশয়কে দেখাইয়া \r\n  বলিল, &quot;এই দেখুন, আপনি যখন ঘুমোচ্ছিলেন, তখন ওরা শ্লেট নিয়ে খেলা করছিল- এই \r\n  দেখুন কাটকুটের ঘর কাটা ।&quot; শ্লেটের উপর আমার নাম লেখা, পণ্ডিত মহাশয় আমার \r\n  উপর প্রচণ্ড এক চড় তুলিয়াই হঠাৎ কেমন থতমত খাইয়া গেলেন । তারপর দাশুর দিকে কটমট \r\n  করিয়া তাকাইয়া বলিলেন, &quot;চোপ্‌ রও, কে বলেছে আমি ঘুমোচ্ছিলাম ?&quot; দাশু \r\n  খানিকক্ষণ হাঁ করিয়া বলিল, &quot;তবে যে আপনার নাক ডাকছিল ?&quot; পণ্ডিত মহাশয় \r\n  তাড়াতাড়ি কথাটা ঘুরাইয়া বলিলেন, &quot;বটে ? ওরা সব খেলা কচ্ছিল ? আর তুমি কি কচ্ছিলে \r\n  ?&quot; দাশু অম্লানবদনে বলিল, &quot;আমি পটকায় আগুন দিচ্ছিলাম ।&quot; শুনিয়াই \r\n  সকলের চক্ষুস্থির ! ছোকরা বলে কি ?</p>\r\n<p>প্রায় আধমিনিট কাহারও মুখে আর কথা নাই । তারপর পণ্ডিত \r\n  মহাশয় হঠাৎ একেবারে হুঙ্কার দিয়া বলিলেন, &quot;কেন পটকায় আগুন দিচ্ছিলে ?&quot; \r\n  দাশু ভয় পাইবার ছেলেই নয়, সে রামপদকে দেখাইয়া বলিল, &quot;ও কেন আমায় মিহিদানা \r\n  দিতে চাচ্ছিল না ?&quot; এমন অদ্ভুত যুক্তি শুনিয়া রামপদ বলিল, &quot;আমার মিহিদানা, \r\n  আমি যা ইচ্ছা তাই করব ।&quot; দাশু তৎক্ষণাৎ বলিয়া উঠিল, &quot;তা হলে আমার পট্‌কা \r\n  আমিও যা ইচ্ছা তা করব ।&quot; এরূপ পাগলের সঙ্গে আর তর্ক করা চলে না ! কাজেই মাস্টারেরা \r\n  সকলেই কিছু-কিছু ধমক-ধামক করিয়া যে যার ক্লাশে চলিয়া গেলেন । সে ''পাগলা'' বলিয়া \r\n  তাহার কোনো শাস্তি হইল না । ছুটির পর আমরা সবাই মিলিয়া চেষ্টা করিয়াও তাহার দোষ \r\n  বুঝাইতে পারিলাম না । সে বলিল, &quot;আমার পট্‌কা, রামপদর হাঁড়ি । যদি আমার দোষ \r\n  হয়, তা হলে রামপদরও দোষ হয়েছে । ব্যাস্‌ ! ওর মার খাওয়াই উচিত ।&quot; </p>\r\n<p align="center">_______</p>', '', '2013-03-19 00:00:00', '2014-08-21 16:53:01', 859, ''),
(116, 2, 'images/dashurkirti.gif', 'দাশুর কীর্তি', 'দাশুর-কীর্তি', '', '<p>নবীনচাঁদ ইস্কুলে এসেই বলল, কাল তাকে \r\n  ডাকাতে ধরেছিল। শুনে স্কুল সুদ্ধ সবাই হাঁ হাঁ করে ছুটে এল। ''ডাকাতে ধরেছিল ? \r\n  বলিস কিরে!'' ডাকাত না তো কি? বিকাল বেলায় সে জ্যোতিলালের বাড়িতে পড়তে গিয়েছিল, \r\n  সেখান থেকে ফিরবার সময় ডাকাতরা তাকে ধরে তার মাথায় চাঁটি মেরে, তার নতুন কেনা শখের \r\n  পিরানটিতে কাদাজলের পিচ্‌কিরি দিয়ে গেল। আর যাবার সময় ব''লে গেল, &quot;চুপ করে \r\n  দাঁড়িয়ে থাক- নইলে দড়াম্‌ করে তোর মাথা উড়িয়ে দেব।&quot; তাই সে ভয়ে আড়ষ্ট হয়ে \r\n  রাস্তার ধারে প্রায় বিশ মিনিট দাঁড়িয়েছিল, এমন সময় তার বড়মামা এসে তার কান ধরে \r\n  বাড়িতে নিয়ে বললেন, &quot;রাস্তায় সঙ্‌ সেজে ইয়ার্কি করা হচ্ছিল ?&quot; নবীনচাঁদ \r\n  কাঁদ-কাঁদ গলায় ব''লে উঠল, &quot;আমি কি করব ? আমায় ডাকাতে ধরেছিল-&quot; শুনে তার \r\n  মামা প্রকাণ্ড এক চড় তুলে বললেন, &quot;ফের জ্যাঠামি !&quot; নবীনচাঁদ দেখল মামার \r\n  সাথে তর্ক করা বৃথা- কারণ, সত্যি সত্যি তাকে ডাকাতে ধরেছিল, একথা তার বাড়ির কাউকে \r\n  বিশ্বাস করানো শক্ত ! সুতরাং তার মনের দুঃখ এতক্ষণ মনের মধ্যেই চাপা ছিল ।</p>\r\n<p>যাহোক ইস্কুলে এসে তার দুঃখ অনেকটা বোধ হয় দূর করতে \r\n  পেরেছিল, কারণ স্কুলে অন্তত অর্ধেক ছেলে তার কথা শুনবার জন্য একেবারে ব্যস্ত হয়ে \r\n  ঝুঁকে পড়েছিল, এবং তার প্রত্যেকটি ঘামাচি, ফুস্‌কুড়ি আর চুলকানির দাগটি পর্যন্ত \r\n  তারা আগ্রহ করে ডাকাতির সুস্পষ্ট প্রমাণ ব''লে স্বীকার করেছিল। দু একজন যারা তার \r\n  কনুয়ের আঁচড়টাকে পুরনো ব''লে সন্দেহ করেছিল, তারাও বলল যে হাঁটুর কাছে যে ছড়ে গেছে, \r\n  সেটা একেবারে টাটকা নতুন। কিন্তু তার পায়ের গোড়ালিতে যে ঘায়ের মতো ছিল সেটাকে \r\n  দেখে কেষ্টা যখন বললে, &quot;ওটা তো জুতোর ফোস্কা&quot;, তখন নবীনচাঁদ ভয়ানক চটে \r\n  বললে, &quot;যাও, তোমাদের কাছে আর কিছুই বলব না।&quot; কেষ্টাটার জন্য আমাদের \r\n  আর কিছু শোনাই হল না।</p>\r\n<p>ততক্ষণে দশটা বেজে গেছে, ঢং ঢং করে ইস্কুলের ঘণ্টা \r\n  পড়ে গেল। সবাই যে যার ক্লাশে চলে গেলাম, এমন সময় দেখি পাগলা দাশু একগাল হাসি নিয়ে \r\n  ক্লাশে ঢুকছে। আমরা বললাম, &quot;শুনেছিস? কাল নবুকে ডাকাতে ধরেছিল।&quot; যেমন \r\n  বলা অমনি দাশরথি হঠাৎ হাত পা ছুঁড়ে বই-টই ফেলে, খ্যাঃ-খ্যাঃ-খ্যাঃ-খ্যাঃ করে হাসতে \r\n  হাসতে একেবারে মেঝের উপর বসে পড়ল। পেটে হাত দিয়ে গড়াগড়ি করে, একবার চিৎ হয়ে, একবার \r\n  উপুড় হয়ে, তার হাসি আর কিছুতেই থামে না।</p>\r\n<p>দেখে আমরা তো অবাক! পণ্ডিত মশাই ক্লাশে এসেছেন, \r\n  তখনও পুরোদমে তার হাসি চলছে। সবাই ভাবলে, ''ছোঁড়াটা ক্ষেপে গেল না কি?'' যাহোক, \r\n  খুব খানিকটা হুটোপাটির পর সে ঠাণ্ডা হয়ে, বই-টই গুটিয়ে বেঞ্চের উপর উঠে বসল। পণ্ডিত \r\n  মশাই বললেন, &quot;ওরকম হাসছিলে কেন?&quot; দাশু নবীনকে দেখিয়ে বললে, &quot;ঐ \r\n  ওকে দেখে।&quot; পণ্ডিত মশাই খুব কড়া ধমক লাগিয়ে তাকে ক্লাশের কোনায় দাঁড় করিয়ে \r\n  রাখলেন। কিন্তু পাগলার তাতেও লজ্জা নেই, সে সারাটি ঘণ্টা থেকে থেকে বই দিয়ে মুখ \r\n  আড়াল করে ফিক্‌ফিক্‌ করে হাসতে লাগল। </p>\r\n<p>টিফিনের ছুটির সময় নবু দাশুকে চেপে ধরল, &quot;কিরে \r\n  দেশো! বড় যে হাসতে শিখেছিস!&quot; দাশু বললে, &quot;হাসব না? তুমি কাল ধুনচি \r\n  মাথায় দিয়ে কি রকম নাচটা নেচেছিলে, সে তো আর তুমি নিজে দেখো নি? দেখলে বুঝতে কেমন \r\n  মজা!&quot; আমরা সবাই বললাম, &quot;সে কি রকম ? ধুনচি মাথায় নাচছিল মানে ?&quot; \r\n  দাশু বললে, &quot;তাও জানো না? ঐ কেষ্টা আর জগাই- ঐ যা! বলতে না বারণ করেছিল \r\n  !&quot; আমি বিরক্ত হয়ে বললাম, &quot;কি বলছিস ভালো করেই বল্‌ না ।&quot; দাশু \r\n  বললে, &quot;কালকে শেঠেদের বাগানের পিছন দিয়ে নবু একলা একলা বাড়ি যাচ্ছিল, এমন \r\n  সময় দুটো ছেলে&mdash; তাদের নাম বলা বারণ&mdash; তারা দৌড়ে এসে নবুর মাথায় ধুনচির মত কি একটা \r\n  চাপিয়ে, তার গায়ের উপর আচ্ছা করে পিচ্‌কিরি দিয়ে পালিয়ে গেল ।&quot; নবু ভয়ানক \r\n  রেগে বলল, &quot;তুই তখন কি করছিলি ?&quot; দাশু বললে, &quot;তুমি তখন মাথার থলি \r\n  খুলবার জন্য ব্যাঙের মত হাত পা ছুঁড়ে লাফাচ্ছিলে দেখে আমি বললাম&mdash; ফের নড়বি তো দড়াম \r\n  করে মাথা উড়িয়ে দেব। শুনে তুমি রাস্তার মধ্যে কাঠ হয়ে দাঁড়িয়ে রইলে, তাই আমি তোমার \r\n  বড়মামাকে ডেকে আনলাম ।&quot; নবীনচাঁদ যেমন বাবুয়ানা, তেমনি তার দেমাক- সেইজন্য \r\n  কেউ তাকে পছন্দ করত না, তার লাঞ্ছনার বর্ণনা শুনে সবাই বেশ খুশি হলাম । ব্রজলাল \r\n  ছেলেমানুষ, সে ব্যাপারটা বুঝতে না পেরে বললে, &quot;তবে যে নবীনদা বলছিল, তাকে \r\n  ডাকাতে ধরেছে ?&quot; দাশু বললে, &quot;দূর বোকা ! কেষ্টা কি ডাকাত ?&quot; বালতে \r\n  না বলতেই কেষ্টা সেখানে হাজির । কেষ্টা আমাদের উপরের ক্লাশে পড়ে, তার গায়েও বেশ \r\n  জোর আছে । নবীনচাঁদ তাকে দেখামাত্র শিকারী বেড়ালের মত ফুলে উঠল । কিন্তু মারামারি \r\n  করতে সাহস পেল না, খানিকক্ষণ কট্‌মট্‌ করে তাকিয়ে সেখান থেকে সরে পড়ল। আমরা ভাবলাম \r\n  গোলমাল মিটে গেল।</p>\r\n<p>কিন্তু তার পরদিন ছুটির সময় দেখি, নবীন তার দাদা মোহনচাঁদকে \r\n  নিয়ে হন্‌হন্‌ করে আমাদের দিকে আসছে । মোহনচাঁদ ম্যাট্রিক ক্লাশে পড়ে, সে আমাদের \r\n  চাইতে অনেক বড়, তাকে ওরকম ভাবে আসতে দেখেই আমরা বুঝলাম, এবার একটা কাণ্ড হবে । \r\n  মোহন এসেই বলল, &quot;কেষ্টা কই ?&quot; কেষ্টা দূর থেকে তাকে দেখেই কোথায় সরে \r\n  পড়েছে, তাই তাকে আর পাওয়া গেল না। তখন নবীনচাঁদ বললে, &quot;ওই দাশুটা সব জানে, \r\n  ওকে জিজ্ঞাসা কর।&quot; মোহন বললে, &quot;কি হে ছোকরা, তুমি সব জানো নাকি?&quot; \r\n  দাশু বললে, &quot;না, সব আর জানব কোত্থেকে- এইতো সবে ফোর্থ ক্লাশে পড়ি, একটু ইংরিজি \r\n  জানি, ভূগোল বাংলা জিওমেট্রি&mdash;&quot; মোহনচাঁদ ধমক দিয়ে বললে, &quot;সেদিন নবুকে \r\n  যে কারা সব ঠেঙিয়েছিল, তুমি তার কিছু জানো কিনা?&quot; দাশু বললে, &quot;ঠাঙায়নি \r\n  তো- মেরেছিল, খুব অল্প মেরেছিল ।&quot; মোহন একটুখানি ভেংচিয়ে বললে, &quot;খুব \r\n  অল্প মেরেছে, না ? তবু কতখানি শুনি ?&quot; দাশু বললে, &quot;সে কিছুই না&mdash; ওরকম \r\n  মারলে একটুও লাগে না ।&quot; মোহন আবার ব্যাঙ্গ করে বললে, &quot;তাই নাকি? কি \r\n  রকম মারলে পরে লাগে ?&quot; দাশু খানিকটা মাথা চুলকিয়ে তারপর বললে, &quot;ঐ সেবার \r\n  হেডমাস্টার মশাই তোমায় যেমন বেত মেরেছিলেন সেই রকম !&quot; এ কথায় মোহন ভয়ানক চটে \r\n  দাশুর কান ম''লে চিৎকার করে বলল, &quot;দেখ্‌ বেয়াদপ ! ফের জ্যাঠামি করবি তো চাব্‌‌কিয়ে \r\n  লাল করে দেব । কাল তুই সেখানে ছিলি কিনা, আর কি কি দেখছিলি খুলে বলবি কিনা?&quot; \r\n  </p>\r\n<p>জানোই তো দাশুর মেজাজ কেমন পাগলাটে গোছের, সে একটুখানি কানে \r\n  হাত বুলিয়ে তারপর হঠাৎ মোহনচাঁদকে ভীষণভাবে আক্রমণ করে বসল । কিল, ঘুষি, চড়, আঁচড়, \r\n  কামড় সে এমনি চটপট চালিয়ে গেল যে আমরা সবাই হাঁ করে তাকিয়ে রইলাম । মোহন বোধহয় \r\n  স্বপ্নেও ভাবেনি যে, ফোর্থ ক্লাশের একটা রোগা ছেলে তাকে অমন ভাবে তেড়ে আসতে সাহস \r\n  পাবে- তাই সে একেবারে থতমত খেয়ে কেমন যেন লড়তেই পারল না । দাশু তাকে পাঁচ সেকেন্ডের \r\n  মধ্যে মাটিতে চিৎ করে ফেলে হাঁপাতে হাঁপাতে বলল, &quot;এর চাইতেও ঢের আস্তে মেরেছিল \r\n  ।&quot; ম্যাট্রিক ক্লাশের কয়েকটি ছেলে সেখানে দাড়িয়েছিল, তারা যদি মোহনকে সামলে \r\n  না ফেলত, তাহলে সেদিন তার হাত থেকে দাশুকে বাঁচানোই মুশকিল হত । </p>\r\n<p>পরে একদিন কেষ্টাকে জিজ্ঞেস করা হয়েছিল, &quot;হ্যাঁরে, নবুকে \r\n  সেদিন তোরা অমন করলি কেন ?&quot; কেষ্টা বললে, &quot;ঐ দাশুটাই তো শিখিয়েছিল ওরকম \r\n  করতে । আর বলছিল, তা হলে একসের জিলিপি পাবি । আমরা বললাম, &quot;কৈ আমাদের তো ভাগ \r\n  দিলিনে ?&quot; কেষ্টা বলল, &quot;সে কথা আর বলিস কেন ? জিলিপি চাইতে গেলুম, হতভাগা \r\n  বলে কিনা আমার কাছে কেন ? ময়রার দোকানে যা, পয়সা ফেলে দে, যত চাস জিলিপি পাবি । \r\n  </p>\r\n<p>আচ্ছা, দাশু কি সত্যি সত্যি পাগল, না কেবল মিচ্‌‌কেমি  করে \r\n  ?</p>\r\n<p align="center">_______</p>', 'DalRU3LQRag', '2013-03-19 00:00:00', '2014-08-21 16:53:01', 801, '112400350'),
(117, 2, '', 'চালিয়াৎ', 'চালিয়াৎ', '', '<p>শ্যামচাঁদের বাবা কোন একটা সাহেব-অফিসে \r\n  মস্ত কাজ করিতেন, তাই শ্যামচাঁদের পোষাক পরিচ্ছদে, রকম-সকমে কায়দার অন্ত ছিল না \r\n  । সে যখন দেড় বিঘৎ চওড়া কলার আঁটিয়া, রঙিন ছাতা মাথায় দিয়া, নতুন জুতার মচ্‌মচ্‌ \r\n  শব্দে গম্ভীর চালে ঘাড় উঁচাইয়া স্কুলে আসিত, তাহার সঙ্গে পাগড়িবাঁধা তক্‌মা-আঁটা \r\n  চাপরাশি এক রাজ্যের বই ও টিফিনের বাক্স বহিয়া আনিত, তখন তাহাকে দেখাইত ঠিক যেন \r\n  পেখমধরা ময়ূরটির মতো ! স্কুলের ছোট ছোট ছেলেরা হাঁ করিয়া অবাক হইয়া থাকিত, কিন্তু \r\n  আমরা সবাই একবাক্যে বলিতাম &quot;চালিয়াৎ&quot; ।<br>\r\n  </p>\r\n<p>বয়সের হিসাবে শ্যামচাঁদ একটু বেঁটে \r\n  ছিল । পাছে কেহ তাকে ছেলেমানুষ ভাবে, এবং যথোপযুক্ত খাতির না করে, এই জন্য সর্বদাই \r\n  সে অত্যন্ত বেশি রকম গম্ভীর হইয়া থাকিত এবং কথাবার্তায় নানা রকম বোলচাল দিয়া এমন \r\n  বিজ্ঞের মত ভাব প্রকাশ করিত যে, স্কুলের দারোয়ান হইতে নিচের ক্লাশের ছাত্র পর্যন্ত \r\n  সকলেই ভাবিত, ''নাঃ, লোকটা কিছু জানে !'' শ্যামচাঁদ প্রথমে যেবার ঘড়ি-চেইন আঁটিয়া \r\n  স্কুলে আসিল, তখন তাহার কাণ্ড যদি দেখিতে ! পাঁচ মিনিট অন্তর ঘড়িটাকে বাহির করিয়া \r\n  সে কানে দিয়া শুনিত, ঘড়িটা চলে কিনা ! পাঁড়েজি দারোয়ানকে রীতিমত ধমক লাগাইয়া বলিত, \r\n  &quot;এইও ! স্কুলের ক্লক্‌টাকে যখন চাবি দাও, তখন সেটাকে রেগুলেট কর না কেন ? \r\n  ওটাকে অয়েল করতে হবে- ক্রমাগতই শ্লো চলছে ।&quot; পাঁড়েজির চৌদ্দ পুরুষে কেউ কখনও \r\n  ঘড়ি ''অয়েল'' বা ''রেগুলেট'' করে নাই । সে যে সপ্তাহে একদিন করিয়া চাবি ঘুরাইতে শিখিয়াছে, \r\n  ইহাতেই তাহার দেশের লোকের বিস্ময়ের সীমা নাই । কিন্তু দেশভাইদের কাছে মানরক্ষা \r\n  করিবার জন্য সে ব্যস্ত হইয়া বলিয়া উঠিল, &quot;হাঁ, হাঁ, আভি হাম্‌ রেংলিট করবে \r\n  ।&quot; পাঁড়েজির উপর একচাল চালিয়া শ্যামচাঁদ ক্লাশে ফিরিতেই এক পাল ছোট ছেলে তাহাকে \r\n  ঘিরিয়া ফেলিল । শ্যামচাঁদ তাহাদের কাছে মহা আড়ম্বর করিয়া শ্লো, ফাস্ট, মেইন স্প্রিং, \r\n  রেগুলেট প্রভৃতি ঘড়ির সমস্ত রহস্য ব্যাখ্যা করিতে লাগিল ।</p>\r\n<p>একবার আমাদের একটি নতুন মাস্টার ক্লাশে \r\n  আসিয়াই শ্যামচাঁদকে ''খোকা'' বলিয়া সম্বোধন করিলেন । লজ্জায় ও অপমানে শ্যামচাঁদের \r\n  মুখ একেবারে লাল হইয়া উঠিল । সে আম্‌তা আম্‌তা করিয়া বলিল, &quot;আজ্ঞে আমার নাম \r\n  শ্যামচাঁদ ঘটক ।&quot; মাস্টার মহাশয় অত কি বুঝিবেন, বলিলেন, &quot;শ্যামচাঁদ ? \r\n  আচ্ছা বেশ, খোকা বস ।&quot; তারপর কয়েকদিন ধরিয়া স্কুল সুদ্ধ ছেলে তাহাকে ''খোকা'' \r\n  ''খোকা'' বলিয়া অস্থির করিয়া তুলিল । কিন্তু কয়দিন পরেই শ্যামচাঁদ ইহার প্রতিশোধ \r\n  লইয়া ফেলিল । সেদিন ক্লাশে আসিয়াই পকেট হইতে কালো চোঙ্গার মতো কি একটা বাহির করিল \r\n  । মাস্টার মহাশয়, শাদাসিধে ভালোমানুষ, তিনি বলিলেন, &quot;কি হে খোকা, থার্‌মোমিটার \r\n  এনেছ যে ! জ্বর-তর হয় নাকি ?&quot; শ্যামচাঁদ বলিল, &quot;আজ্ঞে না- থার্‌মোমিটার \r\n  নয়, ফাউন্টেন পেন !&quot; শুনিয়া সকলের চক্ষু স্থির । ফাউন্টেন পেন ! মাস্টার এবং \r\n  ছেলে সকলেই উদ্‌গ্রীব হইয়া দেখিতে আসিলেন ব্যাপারখানা কি ! শ্যামচাঁদ বলিল, &quot;এই \r\n  একটা ভাল্‌‌কেনাইট টিউব, তার মধ্যে কালি ভরা আছে ।&quot; একটা ছেলে উৎসাহে বলিয়া \r\n  উঠিল, &quot;ও বুঝেছি, পিচকিরি বুঝি ?&quot; শ্যামচাঁদ কিছু জবাব না দিয়া, খুব \r\n  মাতব্বরের মতো একটুখানি মুচকি হাসিয়া, কলমটিকে খুলিয়া তাহার সোনালি নিবখানি দেখাইয়া \r\n  বলিল, &quot;ওতে ইরিডিয়াম আছে- সোনার চেয়েও বেশি দাম ।&quot; তারপর যখন সে একখানা \r\n  খাতা লইয়া, সেই আশ্চর্য কলম দিয়া তর্‌তর্‌ করিয়া নিজের নাম লিখিতে লাগিল, তখন স্বয়ং \r\n  মাস্টার মহাশয় পর্যন্ত বড় বড় চোখ করিয়া দেখিতে লাগিলেন । তারপর, শ্যামচাঁদ কলমটিকে \r\n  তাঁর হাতে দিবামাত্র তিনি ভারি খুশি হইয়া সেটাকে নাড়িয়া চাড়িয়া দুই ছত্র লিখিয়া \r\n  বলিলেন, &quot;কি কলমই না বানিয়েছে, বিলিতি কোম্পানি বুঝি ?&quot; শ্যামচাঁদ চট্‌পট্‌ \r\n  বলিয়া ফেলিল, &quot;আমেরিকান স্টাইলো এন্ড ফাউন্টেন পেন কো, ফিলাডেলফিয়া ।&quot;</p>\r\n<p>ক্রমে পূজার ছুটি আসিয়া পড়িল । ছুটির দিন স্কুলের \r\n  উদ্যানে প্রকাণ্ড শামিয়ানা খাটানো হইল, কলিকাতা হইতে কে এক বাজিওয়ালা আসিয়াছেন, \r\n  তিনি ম্যাজিক দেখাইবেন । যথাসময়ে সকলে আসিলেন, মাস্টার ছাত্র লোকজন নিমন্ত্রিত \r\n  অভ্যাগত সকলে মিলিয়া উঠান সিঁড়ি পাঁচিল একেবারে ভরিয়া ফেলিয়াছে । ম্যাজিক চলিতে \r\n  লাগিল । একখানা সাদা রুমাল চোখের সামনেই লাল নীল সবুজের কারিকুরিতে রঙিন হইয়া উঠিল \r\n  । একজন লোক একটা সিদ্ধ ডিম গিলিয়া মুখের মধ্য হইতে এগারোটি আস্ত ডিম বাহির করিল \r\n  । ডেপুটিবাবুর কোচম্যানের দাড়ি নিংড়াইয়া প্রায় পঞ্চাশটি টাকা বাহির করা হইল । তারপর \r\n  ম্যাজিকওয়ালা জিজ্ঞাসা করিল, &quot;কারও কাছে ঘড়ি আছে ?&quot; শ্যামচাঁদ তাড়াতাড়ি \r\n  ব্যস্ত হইয়া বলিল, &quot;আমার কাছে ঘড়ি আছে ।&quot; ম্যাজিকওয়ালা তাহার ঘড়িটি লইয়া \r\n  খুব গম্ভীরভাবে নাড়িয়া চাড়িয়া দেখিল । ঘড়িটির খুব প্রশংসা করিয়া বলিল, &quot;তোফা \r\n  ঘড়ি তো !&quot; তারপর চেনসুদ্ধ ঘড়িটাকে একটা কাগজে মুড়িয়া, একটা হামানদিস্তায় দমাদম্‌ \r\n  ঠুকিতে লাগিল । তারপর কয়েক টুকরা ভাঙা লোহা আর কাঁচ দেখাইয়া শ্যামচাঁদকে বলিল, \r\n  &quot;এটাই কি তোমার ঘড়ি ?&quot; শ্যামচাঁদের অবস্থা বুঝিতেই পার ! সে হাঁ করিয়া \r\n  তাকাইয়া রহিল, দু-তিন বার কি যেন বলিতে গিয়া আবার থামিয়া গেল । শেষটায় অনেক কষ্টে \r\n  একটু কাষ্ঠহাসি হাসিয়া, রুমাল দিয়া ঘাম মুছিতে মুছিতে বসিয়া পড়িল । যাহা হউক, খানিকবাদে \r\n  যখন একখানা পাঁউরুটির মধ্যে ঘড়িটাকে আস্ত অবস্থায় পাওয়া গেল, তখন চালিয়াৎ খুব হো \r\n  হো করে হাসিয়া উঠিল, যেন তামাশাটা সে আগাগোড়াই বুঝিতে পারিয়াছে । সব শেষে ম্যাজিকওয়ালা \r\n  নানাজনের কাছে নানারকম জিনিস চাহিয়া লইল- চশমা, আংটি, মানিব্যাগ, রুপার পেনসিল \r\n  প্রভৃতি আট-দশটি জিনিস সকলের সামনে এক সঙ্গে পোঁটলা বাধিয়া শ্যামচাঁদকে ডাকিয়া \r\n  তাহার হাতে পোঁটলাটি দেওয়া হইল । শ্যামচাঁদ বুক ফুলাইয়া পোঁটলা হাতে দাঁড়াইয়া রহিল \r\n  আর ম্যাজিকওয়ালা লাঠি ঘুরাইয়া, চোখ-টখ পাকাইয়া, বিড়-বিড় করিয়া কি সব বকিতে লাগিল \r\n  । তারপর হঠাৎ শ্যামচাঁদের দিকে ভ্রুকুটি করিয়া বলিল, &quot;জিনিসগুলো ফেললে কোথায় \r\n  ?&quot; শ্যামচাঁদ পোঁটলা দেখাইয়া বলিল, &quot;এই যে ।&quot; ম্যাজিকওয়ালা মহা \r\n  খুশি হইয়া বলিল, &quot;সাবাস ছেলে ! দাও, পোঁটলা খুলে যার যার জিনিস ফেরৎ দাও ।&quot; \r\n  শ্যামচাঁদ তাড়াতাড়ি পোঁটলা খুলিয়া দেখে, তাহার মধ্যে খালি কয়েক টুকরো কয়লা আর ঢিল \r\n  ! তখন ম্যাজিকওয়ালার তম্বি দেখে কে ? সে কপালে হাত ঠুকিয়া বলিতে লাগিল, &quot;হায়, \r\n  হায়, আমি ভদ্রলোকের কাছে মুখ দেখাই কি করে ? কেনই বা ওর কাছে দিতে গেছিলাম ? ওহে \r\n  ওসব তামাশা এখন রাখ, আমার জিনিসগুলো ফিরিয়ে দাও দেখি ?&quot; শ্যামচাঁদ হাসিবে \r\n  কি কাঁদিবে কিছুই ঠিক করিতে পারিল না- ফ্যাল্‌ ফ্যাল্‌ করিয়া তাকাইয়া রহিল । তখন \r\n  ম্যাজিকওয়ালা তাহার কানের মধ্য হইতে আংটি, চুলের মধ্য হইতে পেনসিল, আস্তিনের মধ্যে \r\n  চশমা- এইরূপে একটি একটি জিনিস উদ্ধার করিতে লাগিল । আমরা হো হো করিয়া হাসিতে লাগিলাম- \r\n  শ্যামচাঁদও প্রাণপণে হাসিবার চেষ্টা করিতে লাগিল । কিন্তু সমস্ত জিনিসের হিসাব \r\n  মিলাইয়া ম্যাজিকওয়ালা যখন তাহাকে বলিল, &quot;আর কি নিয়েছ ?&quot; তখন সে বাস্তবিকই \r\n  ভয়ানক রাগিয়া বলিল, &quot;ফের মিছে কথা ! কখনো আমি কিছু নিইনি ।&quot; তখন ম্যাজিকওয়ালা \r\n  তাহার কোটের পিছন হইতে একটা পায়রা বাহির করিয়া বলিল, &quot;এটা বুঝি কিছু নয় ?&quot;</p>\r\n<p>এবার শ্যামচাঁদ একেবারে ভ্যাঁ করিয়া কাঁদিয়া ফেলিল । তারপর \r\n  পাগলের মতো হাত পা ছুঁড়িয়া সভা হইতে ছুটিয়া বাহির হইল । আমরা সবাই আহ্লাদে আত্মহারা \r\n  হইয়া চেঁচাইতে লাগিলাম- চালিয়াৎ ! চালিয়াৎ !</p>\r\n<p align="center">_______</p>', '', '2013-03-19 00:00:00', '2014-08-21 16:53:01', 3261, '');
INSERT INTO `articles` (`id`, `category_id`, `top_image`, `title`, `article_slug`, `first_line`, `body`, `audio`, `added_on`, `modified_on`, `views`, `sc_track_id`) VALUES
(118, 2, '', 'সবজান্তা', 'সবজান্তা', '', '<p>আমাদের ''সবজান্তা'' দুলিরামের বাবা \r\n  কোন একটা খবরের কাগজের সম্পাদক । সেই জন্য আমাদের মধ্যে অনেকেরই মনে তাহার সমস্ত \r\n  কথার উপরে অগাধ বিশ্বাস দেখা যাইত । যে কোন বিষয়েই হোক, জার্মানির লড়াইয়ের কথাটাই \r\n  হোক আর মোহনবাগানের ফুটবলের ব্যাখ্যাই হোক, দেশের বড় লোকদের ঘরোয়া গল্পই হোক, আর \r\n  নানারকম উৎকট রোগের বর্ণনাই হোক, যে কোন বিষয়ে সে মতামত প্রকাশ করিত । একদল ছাত্র \r\n  অসাধারণ শ্রদ্ধার সঙ্গে সে সকল কথা শুনিত । মাস্টার মহশয়দের মধ্যেও কেহ কেহ এ বিষয়ে \r\n  তাহার ভারি পক্ষপাতী ছিলেন । দুনিয়ার সকল খবর লইয়া সে কারবার করে, সেইজন্য পণ্ডিত \r\n  মহাশয় তাহার নাম দিয়াছিলেন ''সবজান্তা'' । আমার কিন্তু বরাবরই বিশ্বাস ছিল যে, সবজান্তা \r\n  যতখানি পাণ্ডিত্য দেখায়, আসলে তাহার অনেকখানি উপরচালাকি । দু-চারটি বড়-বড় শোনা \r\n  কথা আর খবরের কাগজ পড়িয়া দু-দশটা খবর&mdash; এইমাত্র তাহার পুঁজি, তারই উপর রঙচঙ দিয়া \r\n  নানারকম বাজে গল্প জুড়িয়া সে তাহার বিদ্যা জাহির করিত । </p>\r\n<p>একদিন আমাদের ক্লাশে পণ্ডিত মহাশয়ের \r\n  কাছে সে নায়েগ্রা জলপ্রপাতের গল্প করিয়াছিল । তাহাতে সে বলে যে, নায়েগ্রা দশ মাইল \r\n  উঁচু ও একশত মাইল চওড়া ! একজন ছাত্র বলিল, &quot;সে কি করে হবে ? এভারেস্ট সবচেয়ে \r\n  উঁচু পাহাড়, সে-ই মোটে পাঁচ মাইল ।&quot; সবজান্তা তাকে বাধা দিয়া বলিল, &quot;তোমরা \r\n  তো আজকাল খবর রাখ না !&quot; যখনই তার কথায় আমরা সন্দেহ বা আপত্তি করিতাম, সে একটা \r\n  কিছু যা তা নাম করিয়া আমাদের ধমক দিয়া বলিত, &quot;তোমরা কি অমুকের চেয়ে বেশি জানো \r\n  ?&quot; আমরা বাহিরে সব সহ্য করিয়া থাকিতাম, কিন্তু এক এক সময় রাগে গা জ্বলিয়া \r\n  যাইত । সবজান্তা যে আমাদের মনের ভাবটা বুঝিত না, তাহা নয় । সে তাহা বিলক্ষণ বুঝিত \r\n  এবং সর্বদাই এমন ভাব প্রকাশ করিত যে, আমরা তাহার কথা মানি বা না মানি, তাহাতে কিছুমাত্র \r\n  আসে যায় না । নানারকম খবর ও গল্প জাহির করিবার সময় মাঝে মাঝে আমাদের শুনাইয়া বলিত, \r\n  &quot;অবশ্যি কেউ কেউ আছেন, যাঁরা এসব কথা মানবেন না ।&quot; অথবা &quot;যাঁরা \r\n  না পড়েই খুব বুদ্ধিমান তাঁরা নিশ্চয়ই এ-সব উড়িয়ে দিতে চাইবেন&quot;&mdash; ইত্যাদি । \r\n  ছোকরা বাস্তবিক অনেক রকম খবর রাখিত, তার উপর বোলচালগুলিও বেশ ছিল ঝাঁঝালো রকমের, \r\n  কাজেই আমরা বেশি তর্ক করিতে সাহস পাইতাম না । </p>\r\n<p>তাহার পরে একদিন, কি কুক্ষণে, তাহার এক মামা ডেপুটি \r\n  ম্যাজিস্ট্রেট হইয়া আমাদেরই স্কুলের কাছে বাসা লইয়া বসিলেন । তখন আর সবজান্তাকে \r\n  পায় কে ! তাহার কথাবার্তার দৌড় এমন আশ্চর্য রকম বাড়িয়া চলিল, যে মনে হইত বুঝি বা \r\n  তাহার পরামর্শ ছাড়া ম্যাজিস্ট্রেট হইতে পুলিশের পেয়াদা পর্যন্ত কাহারও কাজ চলিতে \r\n  পারে না । স্কুলের ছাত্র মহলে তাহার খাতির ও প্রতিপত্তি এমন আশ্চর্য রকম জমিয়া \r\n  গেল যে, আমরা কয়েক বেচারা, যাহারা বরাবর তাহাকে ঠাট্টা-বিদ্রূপ করিয়া আসিয়াছি&mdash; \r\n  একেবারে কোণঠাসা হইয়া রহিলাম । এমন কি আমাদের মধ্য হইতে দু-একজন তাহার দলে যোগ \r\n  দিতেও আরম্ভ করিল ।</p>\r\n<p>অবস্থাটা শেষটায় এমন দাঁড়াইল যে, ইস্কুলে আমাদের \r\n  টেকা দায় ! দশটার সময় মুখ কাঁচুমাচু করিয়া ক্লাশে ঢুকিতাম আর ছুটি হইলেই, সকলের \r\n  ঠাট্টা-বিদ্রূপ হাসি-তামাশার হাত এড়াইবার জন্য দৌড়িয়া বাড়ি আসিতাম । টিফিনের সময়টুকু \r\n  হেডমাস্টার মহাশয়ের ঘরের সামনে একখানা বেঞ্চের উপর বসিয়া অত্যন্ত ভালোমানুষের মতো \r\n  পড়াশুনা করিতাম । </p>\r\n<p>এইরকম ভাবে কতদিন চলিত জানি না, কিন্তু একদিনের \r\n  একটা ঘটনায় হঠাৎ সবজান্তা মহাশয়ের জারিজুরি সব এমনই ফাঁস হইয়া গেল যে, তাহার অনেকদিনের \r\n  খ্যাতি ঐ একদিনেই লোপ পাইল&mdash; আর আমরাও সেইদিন হইতে একটু মাথা তুলিতে পারিয়া হাঁপ \r\n  ছাড়িয়া বাঁচিলাম । সেই ঘটনার গল্প বলিতেছি ।</p>\r\n<p>একদিন শোনা গেল, লোহারপুরের জমিদার রামলালবাবু আমাদের স্কুলে \r\n  তিন হাজার টাকা দিয়াছেন&mdash; একটা ফুটবল গ্রাউন্ড ও খেলার সরঞ্জামের জন্য । আরও শুনিলাম, \r\n  রামলালবাবুর ইচ্ছা সেই উপলক্ষে আমাদের একদিন ছুটি ও একদিন রীতিমত ভোজের আয়োজন হয় \r\n  ! কয়দিন ধরিয়া এই খবরটাই আমাদের প্রধান আলোচনার বিষয় হইয়া উঠিল । কবে ছুটি পাওয়া \r\n  যাইবে, কবে খাওয়া এবং কি খাওয়া হইবে, এই সকল বিষয়ে জল্পনা চলিতে লাগিল । সবজান্তা \r\n  দুলিরাম বলিল, যেবার সে দার্জিলিং গিয়াছিল, সেবার নাকি রামলালবাবু তাহাকে কেমন \r\n  খাতির করিতেন, তাহার আবৃত্তি শুনিয়া কি কি প্রশংসা করিয়াছিলেন, এ বিষয়ে সে স্কুলে \r\n  আগে এবং পরে, সারাটি টিফিনের সময় এবং সুযোগ পাইলে ক্লাশের পড়াশুনার ফাঁকে ফাঁকেও \r\n  নানা অসম্ভব রকম গল্প বলিত । ''অসম্ভব'' বলিলাম বটে, কিন্তু তাহার চেলার দল সে সকল \r\n  কথা নির্বিচারে বিশ্বাস করিতে একটুও বাধা বোধ করিত না ।</p>\r\n<p>একদিন টিফিনের সময় উঠানের বড় সিঁড়িটার উপর একদল \r\n  ছেলের সঙ্গে বসিয়া সবজান্তা গল্প আরম্ভ করিল&mdash; একদিন আমি দার্জিলিঙে লাটসাহেবের \r\n  বাড়ির কাছেই ঐ রাস্তাটায় বেড়াচ্ছি, এমন সময় দেখি রামলালবাবু হাসতে হাসতে আমার দিকে \r\n  আসছেন, তাঁর সঙ্গে আবার একটা সাহেব । রামলালবাবু বলিলেন, &quot;দুলিরাম ! তোমার \r\n  সেই ইংরাজি কবিতাটা একবার এঁকে শোনাতে হচ্ছে । আমি এর কাছে তোমার সুখ্যাতি করছিলাম, \r\n  তাই ইনি সেটা শুনবার জন্য ভারি ব্যস্ত হয়েছেন !&quot; উনি নিজে থেকে বলছেন, তখন \r\n  আমি আর কি করি ? আমি সেই ''ক্যাসাবিয়াংকা'' থেকে আবৃত্তি করলুম । তারপর দেখতে দেখতে \r\n  ভিড় জমে গেল ! সবাই শুনতে চায়, সবাই বলে, ''আবার কর ।'' মহা মুশকিলে পড়ে গেলাম, নেহাত \r\n  রামলালবাবু বললেন, তাই আবার করতে হল ।</p>\r\n<p><br>\r\n  এমন সময় কে যেন পিছন হইতে জিজ্ঞাসা করিল, &quot;রামলালবাবু কে ?&quot; সকলে ফিরিয়া \r\n  দেখি, একটা রোগা নিরীহগোছের পাড়াগেঁয়ে ভদ্রলোক সিঁড়ির উপর দাঁড়াইয়া আছেন । সবজান্তা \r\n  বলিল, &quot;রামলালবাবু কে, তাও জানেন না ? লোহারপুরের জমিদার রামলাল রায় ।&quot; \r\n  ভদ্রলোকটি অপ্রস্তুত হইয়া বলিলেন, &quot;হ্যাঁ, তার নাম শুনেছি, সে তোমার কেউ হয় \r\n  নাকি ?&quot; &quot;না, কেউ হয় না, এমনি খুব ভাব আছে । প্রায়ই চিঠিপত্র চলে ।&quot; \r\n  ভদ্রলোকটি আবার বলিলন, &quot;রামলালবাবু লোকটি কেমন ?&quot; সবজান্তা উৎসাহের সঙ্গে \r\n  বলিয়া উঠিল, &quot;চমৎকার লোক । যেমন চেহারা তেমনি কথাবার্তা, তেমনি কায়দা-দুরস্ত \r\n  । এই আপনার চেয়ে প্রায় আধ হাত খানেক লম্বা হবেন, আর সেই রকম তাঁর তেজ । আমাকে তিনি \r\n  কুস্তি শেখাবেন বলেছিলেন, আর কিছুদিন থাকলেই ওটা বেশ রীতিমত শিখে আসতাম ।&quot; \r\n  ভদ্রলোকটি বলিলেন, &quot;বল কি হে ? তোমার বয়স কত ?&quot; &quot;আজ্ঞে এইবার তেরো \r\n  পূর্ণ হবে ।&quot; &quot;বটে ! বয়সের পক্ষে খুব চালাক তো ! বেশ তো কথাবার্তা বলতে \r\n  পার ! কি নাম তোমার ?&quot; সবজান্তা বলিল, &quot;দুলিরাম ঘোষ । রণদাবাবু ডেপুটি \r\n  আমার মামা হন ।&quot; শুনিয়া ভদ্রলোকটি ভারি খুশি হইয়া হেডমাস্টার মহাশয়ের ঘরের \r\n  দিকে চলিয়া গেলেন ।</p>\r\n<p>ছুটির পর আমরা সকলেই বাহির হইলাম । ইস্কুলের সম্মুখেই \r\n  ডেপুটিবাবুর বাড়ি । তার বাহিরের বারান্দায় দেখি, সেই ভদ্রলোকটি দুলিরামের ডেপুটিমামার \r\n  সঙ্গে গল্প করিতেছিলেন । দুলিরামকে দেখিয়াই মামা ডাক দিয়া বলিলেন&mdash; &quot;দুলি, \r\n  এদিকে আয়, এঁকে প্রণাম কর&mdash; এটি আমার ভাগ্নে দুলিরাম ।&quot; ভদ্রলোকটি হাসিয়া \r\n  বলিলেন, &quot;হ্যাঁ, এর পরিচয় আমি আগেই পেয়েছি ।&quot; দুলিরাম আমাদের দেখাইয়া \r\n  খুব আড়ম্বর করিয়া ভদ্রলোকটিকে প্রণাম করিল । ভদ্রলোকটি আবার বলিলেন, &quot;আমার \r\n  পরিচয় জানো না বুঝি ?&quot; সবজান্তা এবার আর ''জানি'' বলিতে পারিল না, আম্‌তা আম্‌তা \r\n  করিয়া মাথা চুলকাইতে লাগিল । ভদ্রলোকটি তখন বেশ একটু মুচকি মুচকি হাসিয়া আমাদের \r\n  শুনাইয়া বলিলেন, &quot;আমার নাম রামলাল রায়, লোহারপুরের রামলাল রায় ।&quot;</p>\r\n<p>দুলিরাম অনেকক্ষণ হাঁ করিয়া দাঁড়াইয়া রহিল, তারপর \r\n  মুখখানা লাল করিয়া হঠাৎ এক দৌড়ে বাড়ির ভিতর ঢুকিয়া গেল । ব্যাপার দেখিয়া ছেলেরা \r\n  রাস্তার উপর হো হো করিয়া হাসিতে লাগিল । তার পরের দিন আমরা স্কুলে আসিয়া দেখিলাম&mdash; \r\n  সবজান্তা আসে নাই, তাহার নাকি মাথা ধরিয়াছে । নানা অজুহাতে সে দু-তিনদিন কামাই \r\n  করিল, তারপর যেদিন স্কুলে আসিল, তখন তাহাকে দেখিবা মাত্র তাহারই কয়েকজন চেলা &quot;কিহে, \r\n  রামলালবাবুর চিঠিপত্র পেলে ?&quot; বলিয়া তাহাকে একেবারে ঘিরিয়া দাঁড়াইল । তারপর \r\n  যতদিন সে স্কুলে ছিল, ততদিন তাহাকে খেপাইতে হইলে বেশি কিছু করা দরকার হইত না, একটিবার \r\n  রামলালবাবুর খবর জিজ্ঞাসা করিলেই বেশ তামাশা দেখা যাইত ।</p>\r\n<p align="center">_______</p>', '', '2013-03-19 00:00:00', '2014-08-21 16:53:01', 331, '');
INSERT INTO `articles` (`id`, `category_id`, `top_image`, `title`, `article_slug`, `first_line`, `body`, `audio`, `added_on`, `modified_on`, `views`, `sc_track_id`) VALUES
(119, 2, '', 'হেঁসোরাম হুঁশিয়ারের ডায়েরি', 'হেঁসোরাম-হুঁশিয়ারের-ডায়েরি', '', '<p class=''smaller_font''>(প্রফেসর হুঁশিয়ার আমাদের উপর ভারি রাগ করেছেন। আমরা সেকালের জীবজন্তু সম্বন্ধে নানাকথা ছাপিয়েছি; কিন্তু কোথাও তাঁর অদ্ভুত শিকার কাহিনীর কোনো উল্লেখ করি নি। সত্যি এ আমাদের ভারি অন্যায়। আমরা সে-সব কাহিনী কিছুই জানতাম না। কিন্তু প্রফেসর হুঁশিয়ার তাঁর শিকারের ডায়েরি থেকে কিছু কিছু উদ্ধার করে আমাদের পাঠিয়েছেন। আমরা তারই কিছু কিছু ছাপিয়ে দিলাম। এ-সব কথা সত্যি কি মিথ্যা তা তোমরা বিচার করে নিয়ো।)</p><br><br>\r\n\r\n২৬শে জুন ১৯২২- কারাকোরম্, বন্দাকুশ পাহাড়ের দশ মাইল উত্তর। আমরা এখন সবসুদ্ধ দশজন- আমি, আমার ভাগ্নে চন্দ্রখাই, দুইজন শিকারী (ছক্কড় সিং আর লক্কড় সিং) আর ছয়জন কুলি। আমার কুকুরটাও সঙ্গে সঙ্গেই চলেছে।<br><br>\r\nনদীর ধারে তাঁবু খাটিয়ে জিনিসপত্র সব কুলিদের জিম্মায় দিয়ে, আমি, চন্দ্রখাই আর শিকারী দুজনকে সঙ্গে করে বেরিয়ে পড়লাম। সঙ্গে বন্দুক, ম্যাপ আর একটা মস্ত বাক্স, তাতে আমাদের যন্ত্রপাতি আর খাবার জিনিস। দুঘণ্টা পথ চলে আমরা এক জায়গায় এলাম, সেখানকার সবই কেমন অদ্ভুতরকম। বড়ো-বড়ো গাছ, তার একটারও নাম আমরা জানি না। একটা গাছে প্রকাণ্ড বেলের মতো মস্ত-মস্ত লাল রঙের ফল ঝুলছে; একটা ফুলের গাছ দেখলাম, তাতে হলদে সাদা ফুল হয়েছে, এক-একটা দেড় হাত লম্বা। আর-একটা গাছে ঝিঙের মতো কি সব ঝুলছে, পঁচিশ হাত দূর থেকে তার ঝাঁঝালো গন্ধ পাওয়া যায়। আমরা অবাক হয়ে এই-সব দেখছি, এমন সময় হঠাত্ হুপ্‌হাপ গুব্‌গাপ্ শব্দে পাহাড়ের উপর থেকে ভয়ানক একটা কোলাহল শোনা গেল।<br><br>\r\n<center><img src="images/hh_dalbal.gif"></center>\r\nআমি আর শিকারী দুজন তৎ‌ক্ষনাৎ‌  বন্দুক নিয়ে খাড়া; কিন্তু চন্দ্রখাই বাক্স থেকে দুই টিন জ্যাম বের করে নিশ্চিন্তে বসে খেতে লাগল। ঐটে তার একটা মস্ত দোষ; খাওয়া পেলে আর তার বিপদ আপদ কিছু জ্ঞান থাকে না। এইভাবে প্রায় মিনিট দুই দাঁড়িয়ে থাকবার পর লক্কড় সিং হঠাত্ দেখতে পেল হাতির চাইতেও বড়ো একটা জন্তু গাছের উপর থেকে তার দিকে তাকিয়ে তাকিয়ে হাসছে। প্রথমে দেখে মনে হল একটা প্রকাণ্ড মানুষ, তার পর মনে হল মানুষ নয় বাঁদর, তার পর দেখি মানুষও নয়, বাঁদরও নয়- একেবারে নতুন রকমের জন্তু। সে লাল লাল ফলগুলোর খোসা ছাড়িয়ে খাচ্ছে আর আমাদের দিকে ফিরে ফিরে ঠিক মানুষের মতো করে হাসছে। দেখতে দেখতে পঁচিশ-ত্রিশটা ফল সে টপাটপ্ খেয়ে শেষ করল। আমরা এই সুযোগে তার কয়েকখানা ছবি তুলে ফেললাম। তার পর চন্দ্রখাই ভরসা করে এগিয়ে গিয়ে তাকে কিছু খাবার দিয়ে আসল।\r\n \r\n জন্তুটা মহা খুশি হয়ে এক গ্রাসে আস্ত একখানা পাঁউরুটি আর প্রায় আধসের গুড় শেষ করে, তার পর পাঁচ-সাতটা সিদ্ধ ডিম খোলাসুদ্ধ কড়মড়িয়ে খেয়ে ফেলল। একটা টিনে করে গুড় দেওয়া হয়েছিল, সেই টিনটাও সে খাবার মতলব করেছিল, কিন্তু খানিকক্ষণ চিবিয়ে হঠাত্ বিশ্রী মুখ করে সে কান্নার সুরে গাঁও গাঁও শব্দে বিকট চীৎ‌কার করে জঙ্গলের মধ্যে কোথায় মিলিয়ে গেল। আমি জন্তুটার নাম দিয়েছি হ্যাংলাথেরিয়াম্।<br><br>\r\n২৪শে জুলাই, ১৯২২- বন্দাকুশ পাহাড়ের একুশ মাইল উত্তর। এখানে এত দেখবার জিনিস আছে, নতুন নতুন এত-সব গাছপালা জীবজন্তু, যে তারই সন্ধান করতে আর নমুনা সংগ্রহ করতে আমাদের সময় কেটে যাচ্ছে। দুশোরকম পোকা আর প্রজাপতি আর পাঁচশো রকম গাছপালা ফুলফল সংগ্রহ করেছি; আর ছবি যে কত তুলেছি তার সংখ্যাই হয় না। একটা কোনো জ্যান্ত জানোয়ার ধরে সঙ্গে নেয়ার ইচ্ছা, দেখা যাক কতদূর কি হয়। সেবার যখন কট্ক টোডন্ আমায় তাড়া করেছিল, তখন সে কথা কেউ বিশ্বাস করে নি। এবার তাই জলজ্যান্ত প্রমাণ সংগ্রহ করে নিচ্ছি।\r\n<br><br>\r\nআমরা যখন বন্দাকুশ পাহাড়ে উঠেছিলাম, তখন পাহাড়টা কত উঁচু তা মাপা হয়নি। সেদিন জরীপের যন্ত্র দিয়ে আমি আর চন্দ্রখাই পাহাড়টাকে মেপে দেখলাম। আমার হিসেব হল ষোলোহাজার ফুট। কিন্তু চন্দ্রখাই হিসাব করল বেয়াল্লিশহাজার। তাই আজ আবার সাবধানে দুজনে মিলে মেপে দেখলাম, এবার হল মোটে দু''হাজার সাতশো ফুট। বোধ হয় আমাদের যন্ত্রে কোনো দোষ হয়ে থাকবে! যাহোক এটা নিশ্চয় যে এপর্যন্ত ঐ পাহাড়ের চুড়োয় আর কেউ ওঠে নি। এ-এক সম্পূর্ণ অজানা দেশ, কোথাও জন মানুষের চিহ্নমাত্র নাই, নিজেদের ম্যাপ নিজেরা তৈরি করে পথ চলতে হয়।\r\n<br><br>\r\nআজ সকালে এক কাণ্ড হয়ে গেছে। লক্কড় সিং একটা গাছে হলদে রঙের ফল ফলেছে দেখে তারই একটুখানি খেতে গিয়েছিল। এক কামড় খেতেই হঠাত্ হাত-পা খিঁচিয়ে সে আর্তনাদ করে মাটিতে পড়ে ছট্ফট্ করতে লাগল। তাই দেখে ছক্কড় সিং "ভাইয়া রে, ভাইয়া" বলে কেঁদে অস্থির।<img src="images/gomratherium.gif" align="left">\r\n যাহোক মিনিট দশেক ঐরকম হাত-পা ছুঁড়ে লক্কড় সিং একটু ঠাণ্ডা হয়ে উঠে বসল। তখন আমাদের চোখে পড়ল যে একটা জন্তু কাছেই ঝোপের আড়াল থেকে অত্যন্ত বিরক্ত মতন মুখ করে আমাদের দিকে তাকিয়ে আছে। তার চেহারা দেখলে মনে হয় যে, সংসারে তার কোনো সুখ নেই, এ-সব গোলমাল কান্নাকাটি কিছুই তার পছন্দ হচ্ছে না। আমি তার নাম দিয়েছি গোমড়াথেরিয়াম্। এমন খিট্খিটে খুঁতখুঁতে গোমড়া মেজাজের জন্তু আর আমরা দ্বিতীয় দেখি নি। আমরা তাকে তোয়াজ টোয়াজ করে খাবার দিয়ে ভোলাবার চেষ্টা করেছিলাম। সে অত্যন্ত বিশ্রী মতো মুখ করে, ফোঁস্ ফোঁস্ ঘোঁত্ ঘোঁত্ করে অনেক আপত্তি জানিয়ে, আধখানা পাঁউরুটি আর দুটো কলা খেয়ে তার পর একটুখানি পেয়ারার জেলি মুখে দিতেই এমন চটে গেল যে রেগে সারা গায়ে জেলি আর মাখন মাখিয়ে আমাদের দিকে পিছন ফিরে মাটিতে মাথা ঠুকতে লাগল।\r\n<br><br>\r\n১৪ই আগস্ট, বন্দাকুশ পাহাড়ের পঁচিশ মাইল উত্তর- ট্যাপ্ ট্যাপ্ থ্যাপ্ থ্যাপ্ ঝুপ্ ঝাপ্- সকালবেলায় খেতে বসেছি, এমন সময় এইরকম একটা শব্দ শোনা গেল। একটুখানি উঁকি মেরে দেখি আমাদের তাঁবুর কাছে প্রায় উটপাখির মতন বড়ো একটা অদ্ভুতরকম পাখি অদ্ভুত ভঙ্গিতে ঘুরে বেড়াচ্ছে। সে কোন দিকে চলবে তার কিছুই যেন ঠিক-ঠিকানা নেই। ডান পা এদিকে যায় তো বাঁ পা ওদিকে; সামনে চলবে তো পিছনভাগে চায়, দশ পা না যেতেই পায়ে পায়ে জড়িয়ে হোঁচট খেয়ে পড়ে। তার বোধ হয় ইচ্ছা ছিল তাঁবুটা ভালো করে দেখে, কিন্তু হঠাত্ আমায় দেখতে পেয়ে সে এমন ভড়কে গেল যে তক্ষুনি হুমড়ি খেয়ে হুড়্মুড়্ করে পড়ে গেল। তার পর এক ঠ্যাঙে লাফাতে লাফাতে প্রায় হাত দশেক গিয়ে আবার হেলেদুলে ঘাড় বাঁকিয়ে আমাদের দেখতে লাগল। চন্দ্রখাই বলল, "ঠিক হয়েছে, এইটাকে ধরে আমাদের সঙ্গে নিয়ে যাওয়া যাক।" তখন সকলের উত্সাহ দেখে কে! আমি ছক্কড় সিংকে বললাম, "তুমি বন্দুকের আওয়াজ কর, তা হলে পাখিটা নিশ্চয়ই চমকে পড়ে যাবে আর সেই সুযোগে আমরা চার-পাঁচজন তাকে চেপে ধরব!" ছক্কড় সিং বন্দুক নিয়ে আওয়াজ করতেই পাখিটা ঠ্যাং মুড়ে মাটির উপর বসে পড়ল, আর আমাদের দিকে তাকিয়ে ক্যাট্ ক্যাট্ শব্দ করে ভয়ানক জোরে ডানা ঝাপ্টাতে লাগল। তাই দেখে আমাদের আর এগুতে সাহস হল না।\r\n <img src="images/lagbagarnis.gif" align="right">\r\n কিন্তু লক্কড় সিং হাজার হোক তেজী লোক, সে দৌড়ে গিয়ে পাখিটার বুকে ধাঁই করে এক ছাতার বাড়ি বসিয়ে দিল। ছাতার বাড়ি খেয়ে পাখিটা তত্ক্ষণাত্ দুই পা ফাঁক করে উঠে দাঁড়াল। তার পর লক্কড় সিং-এর দাড়িতে কামড়ে ধরে তার ঘাড়ের উপর দুই পা দিয়ে ঝুলে পড়ল। ভাইয়ের বিপদ দেখে ছক্কড় সিং বন্দুকের বাঁট দিয়ে পাখিটার মাথাটা থেঁত্লে দেবার আয়োজন করেছিল। কিন্তু সে আঘাতটা পাখিটার মাথায় লাগল না, লাগল গিয়ে লক্কড় সিং-এর বুকে। তাতে পাখিটা ভয় পেয়ে লক্কড় সিংকে ছেড়ে দিল বটে, কিন্তু দুই ভাইয়ে এমন মারামারি বেঁধে উঠল যে আমরা ভাবলাম দুটোই এবার মরে বুঝি। দুজনের তেজ কি তখন!\r\n আমি আর দুজন কুলি লক্কড় সিং-এর জামা ধরে টেনে রাখছি, সে আমাদের সুদ্ধ হিঁচড়ে নিয়ে ভাইয়ের নাকে ঘুষি চালাচ্ছে। চন্দ্রখাই রীতিমতো ভারিক্কে মানুষ; সে ছক্কড় সিং-এর কোমর ধরে লটকে আছে, ছক্কড় সিং তাইসুদ্ধ মাটি থেকে তিন হাত লাফিয়ে উঠে বন্বন্ করে বন্দুক ঘোরাচ্ছে। হাজার হোক পাঞ্জাবের লোক কি না। মারামারি থামাতে গিয়ে সেই ফাঁকে পাখিটা যে কখন পালালো তা আমরা টেরই পেলাম না। যা হোক এই ল্যাগ্ব্যাগ পাখি বা ল্যাগ-ব্যাগর্নিসের কতকগুলো পালক আর কয়েকটা ফোটোগ্রাফ সংগ্রহ হয়েছিল। তাতেই যথেষ্ট প্রমাণ হবে।<br><br>\r\n ১লা সেপ্টেম্বর, কাঁকড়ামতী নদীর ধারে- আমাদের সঙ্গের খাবার ইত্যাদি ক্রমেই ফুরিয়ে আসছে। তরিতরকারি যা ছিল, তা তো আগেই ফুরিয়েছে। টাটকা জিনিসের মধ্যে সঙ্গে কতগুলো হাঁস আর মুরগী আছে, তারা রোজ কয়েকটা করে ডিম দেয়, তা ছাড়া খালি বিস্কুট, জ্যাম, টিনের দুধ আর ফল, টিনের মাছ, আর মাংস। এই-সব কয়েক সপ্তাহের মতো আছে, সুতরাং এই কয়েক সপ্তাহের মধ্যে আমাদের ফিরতে হবে। আমরা এই সব জিনিস গুনছি আর সাজিয়ে গুছিয়ে রাখছি, এমন সময় ছক্কড় সিং বলল, যে লক্কড় সিং ভোরবেলা কোথায় বেরিয়েছে, এখন পর্যন্ত ফেরে নি। আমরা বললাম, "ব্যস্ত কেন, সে আসবে এখন। যাবে আবার কোথায়?" কিন্তু তার পরেও দুই-তিন ঘণ্টা গেল অথচ লক্কড় সিং-এর দেখা পাওয়া গেল না।\r\n  <img src="images/hanglatherium.gif" align="left">\r\n  আমরা তাকে খুঁজতে বেরুবার পরামর্শ করছি, এমন সময় হঠাত্ একটা ঝোপের উপর দিয়ে একটা প্রকাণ্ড জানোয়ারের মাথা দেখা গেল। মাথাটা উঠছে নামছে আর মাতালের মতো টলছে। দেখেই আমরা সুড়্সুড়্ করে তাঁবুর আড়ালে পালাতে যাচ্ছি, এমন সময় শুনলাম লক্কড় সিং চেঁচিয়ে বলছে, "পালিয়ো না, পালিয়ো না, ও কিছু বলবে না।" তার পরের মুহূর্তেই দেখি লক্কড় সিং বুক ফুলিয়ে সেই ঝোপের আড়াল থেকে বেরিয়ে এল। <br>\r\n  \r\n  <br>তার পাগড়ীর কাপড় দিয়ে সে ঐ অত বড়ো জানোয়ারটাকে বেঁধে নিয়ে এসেছে। আমাদের প্রশ্নের উত্তরে লক্কড় সিং বলল, যে সে সকালবেলায় কুঁজো নিয়ে নদী থেকে জল আনতে গিয়েছিল। ফিরবার সময় এই জন্তুটার সাথে তার দেখা। তাকে দেখেই জন্তুটা মাটিতে শুয়ে কোঁ-কোঁ শব্দ করতে লাগল। সে দেখল জন্তুটার পায়ে কাঁটা ফুটেছে আর তাই দিয়ে দর্দর্ করে রক্ত পড়ছে। লক্কড় সিং খুব সাহস করে তার পায়ের কাঁটাটি তুলে, বেশ করে মুছে, নিজের রুমাল দিয়ে বেঁধে দিল। তার পর জানোয়ারটা তার সঙ্গে সঙ্গে আসছে দেখে সে তাকে পাগড়ি দিয়ে বেঁধে নিয়ে এসেছে। আমরা সবাই বললাম, "তা হলে এটা ঐরকম বাঁধাই থাক, দেখি ওটাকে সঙ্গে করে দেশে নিয়ে যাওয়া যায় কিনা।" জন্তুটার নাম রাখা গেল ল্যাংড়াথেরিয়াম্।<br><br>\r\n  \r\n  সকালে তো এই কাণ্ড হল; বিকালবেলা আর এক ফ্যাসাদ উপস্থিত। তখন আমরা সবেমাত্র তাঁবুতে ফিরেছি। হঠাত্ আমাদের তাঁবুর বেশ কাছেই একটা বিকট চীত্কারের শব্দ শোনা গেল। অনেকগুলো চিল আর পেঁচা একসঙ্গে চেঁচালে যেরকম আওয়াজ হয়, কতকটা সেইরকম। ল্যাংড়াথেরিয়ামটা ঘাসের উপর শুয়ে শুয়ে একটা গাছের লম্বা-লম্বা পাতা ছিঁড়ে খাচ্ছিল; চীত্কার শুনবামাত্র সে, ঠিক শেয়াল যেমন করে ফেউ ডাকে সেইরকম ধরনের একটা বিকট শব্দ করে, বাঁধন-টাঁধন ছিঁড়ে, কতক লাফিয়ে কতক দৌড়িয়ে এক মুহূর্তের মধ্যে গভীর জঙ্গলের ভিতর মিলিয়ে গেল। আমরা ব্যাপারটা কিছুই বুঝতে না পেরে, ভয়ে ভয়ে খুব সাবধানে এগিয়ে গিয়ে দেখি, একটা প্রকাণ্ড জন্তু- সেটা কুমিরও নয়, সাপও নয়, মাছও নয়, অথচ তিনটারই কিছু আদল আছে- সে এক হাত মস্ত হাঁ করে প্রাণপণে চেঁচাচ্ছে; আর একটা ছোটো নিরীহ গোছের কি যেন জানোয়ার হাত-পা এলিয়ে ঠিক তার মুখের সামনে আড়ষ্ট হয়ে বসে আছে। আমরা মনে করলাম, এইবার বেচারাকে খাবে বুঝি, কিন্তু পাঁচ মিনিট গেল, দশ মিনিট গেল, কেবল চীৎকারই চলতে লাগল; খাবার কোনো চেষ্টা দেখা গেল না। লক্কড় সিং বলল, "“আমি ওটাকে গুলি করি।" আমি বললাম, "কাজ নেই, গুলি যদি ঠিকমতো না লাগে, তা হলে জন্তুটা ক্ষেপে গিয়ে কি জানি করে বসবে, তা কে জানে?" এই বলতে বলতেই ধেড়ে জন্তুটা চীত্কার থামিয়ে সাপের মতো এঁকেবেঁকে নদীর দিকে চলে গেল। চন্দ্রখাই বলল, "এ জন্তুটার নাম দেওয়া যাক চিল্লানোসরাস্।" ছক্কড় সিং বলল, "উ বাচ্চাকো নাম দেও, বেচারাথেরিয়াম্।"<br><br>\r\n  <p class=''center_text''><img src="images/langratherium.gif" align="middle"></p><br>\r\n  ৭ই সেপ্টেম্বর, কাঁকড়ামতী নদীর ধারে- নদীর বাঁক ধরে হাঁটতে হাঁটতে আমরা পাহাড়ের একেবারে শেষ কিনারায় এসে পড়েছি। \r\n    আর কোনোদিকে এগোবার জো নাই। দেওয়ালের মতো খাড়া পাহাড়, সোজা দুশো তিনশো হাত নীচে সমতল জমি পর্যন্ত নেমে গিয়েছে। যেদিকে তাকাই সেই দিকেই এরকম। নীচের যে সমতল জমি সে একেবারে মরুভূমির মতো; কোথাও গাছপালা, জনপ্রাণীর চিহ্নমাত্র নাই। আমরা একেবারে পাহাড়ের কিনারায় ঝুঁকে পড়ে এই-সব দেখছি, এমন সময় আমাদের ঠিক হাত পঞ্চাশেক নীচেই কি যেন ধড়্ফড়্ করে উঠল। দেখলাম বেশ একটা মাঝারি গোছের তিমি মাছের মতো মস্ত কি একটা জন্তু পাহাড়ের গায়ে আঁকড়ে ধরে বাদুড়ের মতো মাথা নিচু করে ঘুমাচ্ছে। তখন এদিক-ওদিক তাকিয়ে এইরকম আরো পাঁচ-সাতটা জন্তু দেখতে পেলাম। কোনোটা ঘাড় গুঁজে ঘুমাচ্ছে, কোনোটা লম্বা গলা ঝুলিয়ে দোল খাচ্ছে, আর অনেক দূরে একটা পাহাড়ের ফাটলের মধ্যে ঠোঁট ঢুকিয়ে কি যেন খুঁটে খুঁটে বের করে খাচ্ছে।\r\n     \r\n     এইরকম দেখছি এমন সময় হঠাত্ কট্ কটাং কট্ শব্দ করে সেই প্রথম জন্তুটা হুড়ুত্ করে ডানা মেলে একেবারে সোজা আমাদের দিকে উড়ে আসতে লাগল। ভয়ে আমাদের হাত-পাগুলো গুটিয়ে আসতে লাগল; এমন বিপদের সময়ে যে পালানো দরকার, তা পর্যন্ত আমরা ভুলে গেলাম। জন্তুটা মুহূর্তের মধ্যে একেবারে আমাদের মাথার উপরে এসে পড়ল। তার পর যে কি হল তা আমার ভালো করে মনে নাই- খালি একটু একটু মনে পড়ে, একটা অসম্ভব বিটকেল গন্ধের সঙ্গে ঝড়ের মতো ডানা ঝাপটানো আর জন্তুটার ভয়ানক কট্ কটাং আওয়াজ। একটুখানি ডানার ঝাপটা আমার গায়ে লেগেছিল তাতেই আমার দম বেরিয়ে প্রাণ বের হবার যোগাড় করেছিল। অন্যসকলের অবস্থাও সেইরকম অথবা তার চাইতেও খারাপ। যখন আমার হুঁশ হল তখন দেখি সকলেরই গা বেয়ে রক্ত পড়ছে। ছক্কড় সিং-এর একটা চোখ ফুলে প্রায় বন্ধ হবার যোগাড় হয়েছে, লক্কড় সিং-এর বাঁ হাতটা এমন মচকে গিয়েছে যে সে যন্ত্রণায় আর্তনাদ করছে, আমারও সমস্ত বুকে পিঠে বেদনা ধরে গিয়েছে; কেবল চন্দ্রখাই এক হাতে রুমাল দিয়ে কপালের আর ঘাড়ের রক্ত মুছছে, আর-এক হাতে একমুঠো বিস্কুট নিয়ে খুব মন দিয়ে খাচ্ছে। আমরা তখনই আর বেশি আলোচনা না করে জিনিসপত্র গুটিয়ে বন্দাকুশ পাহাড়ের দিকে ফিরে চললাম।<br><br>\r\n     <p class=''center_text''><img src="images/becharatherium_chillanosaurus.gif" align="middle"></p><br>\r\n     \r\n\r\n[ প্রফেসর হুঁশিয়ারের ডায়েরি এইখানেই শেষ। কিন্তু আমরা আরো খবর জানবার জন্য তাঁকে চিঠি লিখেছিলাম। তার উত্তরে তিনি তাঁর ভাগ্নেকে পাঠিয়ে দিয়ে লিখলেন, "এর কাছেই সব খবর পাবে।" চন্দ্রখাই-এর সঙ্গে আমাদের যে কথাবার্তা হয় খুব সংক্ষেপে তা হচ্ছে এই-<br>\r\n\r\nআমরা। আপনারা যে-সমস্ত নমুনা সংগ্রহ করেছিলেন সে-সব কোথায় গেলে দেখতে পাওয়া যায়?<br>\r\n\r\nচন্দ্র।&nbsp;&nbsp; সে-সব হারিয়ে গেছে।<br>\r\n<img src="images/timimaachher_mato_baro.gif" align="right">\r\nআমরা। বলেন কি! হারিয়ে গেল? এমন সব জিনিস হারিয়ে ফেললেন!<br>\r\n\r\nচন্দ্র। হ্যাঁ, প্রাণটুকু যে হারায় নি তাই যথেষ্ট। সে-দেশের ঝড় তো আপনারা দেখেন নি। তার এক-এক ঝাপটায় আমাদের যন্ত্রপাতি, বড়ো-বড়ো তাঁবু আর নমুনার বাক্স, সব কাগজের মতো হুস্ করে উড়িয়ে নেয়। আমাকেই তো পাঁচ-সাতবার উড়িয়ে নিয়েছিল। একবার তো ভাবলাম মরেই গেছি। কুকুরটাকে যে কোথায় উড়িয়ে নিল, সে তো আর খুঁজেই পেলাম না। সে যা বিপদ! কাঁটা কম্পাস, প্ল্যান ম্যাপ, খাতাপত্র কিছুই আর বাকি রাখে নি। কি করে যে ফিরলাম, তা শুনলে আপনার ঐ চুল দাড়ি সব সজারুর কাঁটার মতো খাড়া হয়ে উঠবে। আধপেটা খেয়ে, কোনোদিন না খেয়ে, আন্দাজে পথ চলে, দুই সপ্তাহের রাস্তা পার হতে আমাদের পুরো তিনমাস লেগেছিল।<br>\r\n\r\nআমরা। তা হলে আপনাদের প্রমাণ-টমান যা কিছু ছিল সব নষ্ট হয়েছে?<br>\r\n\r\nচন্দ্র। এই তো আমি রয়েছি, মামা রয়েছেন, আবার কি প্রমাণ চাই, আর এই আপনাদের জন্য কতকগুলো ছবি এঁকে এনেছি; এতেও অনেকটা প্রমাণ হবে।<br>\r\n\r\nআমাদের ছাপাখানার একটা ছোকরা ঠাট্টা করে বলল, "আপনি কোন থেরিয়াম?" আর-একজন বলল, "উনি হচ্ছেন গপ্পথেরিয়াম- বসে বসে গপ্প মারছেন।" শুনে চন্দ্রখাই ভীষণ রেগে আমাদের টেবিল থেকে একমুঠো চীনেবাদাম আর গোটা আষ্টেক পান উঠিয়ে নিয়ে গজ্গজ্ করতে করতে বেরিয়ে গেল। ব্যাপার তো এই। এখন তোমরা কেউ যদি আরো জানতে চাও, তা হলে আমাদের ঠিকানায় প্রফেসর হুঁশিয়ারকে চিঠি লিখলে আমরা তার জবাব আনিয়ে দিতে পারি। ] ', 'fLgnWrvBStg', '2013-03-19 00:00:00', '2014-08-21 16:53:01', 560, '66065981'),
(120, 2, '', 'দ্রিঘাংচু', 'দ্রিঘাংচু', '', 'এক ছিল রাজা।<br/><br/>\r\nরাজা একদিন সভায় বসেছেন- চারিদিকে তাঁর পাত্র-মিত্র আমির ওমরা সিপাই শান্ত্রী গিজ গিজ করছে- এমন সময় কোথা থেকে একটা দাঁড়কাক উড়ে এসে সিংহাসনের ডান দিকে উঁচু থামের ওপর বসে ঘাড় নিচু ক''রে চারিদিক তাকিয়ে, অত্যন্ত গম্ভীর গলায় বলল,"কঃ"।<br/>\r\n<br/>\r\nকথা নেই বার্তা নেই হঠাৎ‌ এরকম গম্ভীর শব্দ- সভাসুদ্ধ সকলের চোখ এক সঙ্গে গোল হয়ে উঠল- সকলে একেবারে এক সঙ্গে হাঁ করে রইল। মন্ত্রী এক তাড়া কাগজ নিয়ে কি যেন বোঝাতে যাচ্ছিলেন, হঠাৎ বক্তৃতার খেই হারিয়ে তিনি বোকার মত তাকিয়ে রইলেন। দরজার কাছে একটা ছেলে বসে ছিল, সে হঠাৎ‌  ভ্যাঁ করে কেঁদে উঠল, যে লোকটা চামর দোলাচ্ছিল, চামরটা তার হাত থেকে ঠাঁই করে রাজার মাথার ওপর পড়ে গেল। রাজামশায়ের চোখ ঘুমে ঢুলে এসেছিল, তিনি হঠাৎ‌ জেগে উঠেই বললেন, "জল্লাদ ডাক।"\r\n<br/><br/>\r\nবলতেই জল্লাদ এসে হাজির। রাজামশাই বললেন, "মাথা কেটে ফেল।" সর্বনাশ! কার মাথা কাটতে বলে; সকলে ভয়ে ভয়ে নিজের মাথায় হাত বুলাতে লাগল। রাজামশাই খানিকক্ষণ ঝিমিয়ে আবার তাকিয়ে বললেন, "কই, মাথা কই?" জল্লাদ বেচারা হাত জোড় করে বলল, "আজ্ঞে মহারাজ, কার মাথা?" রাজা বললেন, "বেটা গোমুখ্যু কোথাকার, কার মাথা কিরে! যে ঐরকম বিটকেল শব্দ করেছিল, তার মাথা।" শুনে সভাসুদ্ধ সকলে হাঁফ ছেড়ে এমন ভয়ানক নিশ্বাস ফেলল যে, কাকটা হঠাৎ ধড়ফড় করে সেখান থেকে উড়ে পালাল।\r\n<br/><br/>\r\nতখন মন্ত্রীমশাই রাজাকে বুঝিয়ে বললেন যে, ঐ কাকটাই ওরকম আওয়াজ করেছিল! <br/> <img src="images/drighangchu.gif" alt="Drighangchu"><br/> তখন রাজামশাই বললেন, "ডাকো পণ্ডিত সভার যত পণ্ডিত সবাইকে।" হুকুম হওয়া মাত্র পাঁচ মিনিটের মধ্যে রাজ্যের যত পণ্ডিত সব সভায় এসে হাজির। তখন রাজামশাই পণ্ডিতদের জিজ্ঞাসা করলেন, "এই যে একটা কাক এসে আমার সভার মধ্যে আওয়াজ করে এমন গোল বাধিয়ে গেল, এর কারণ কিছু বলতে পার?"\r\n<br/><br/>\r\nকাকে আওয়াজ করল তার আবার কারণ কি? পণ্ডিতেরা সকলে মুখ চাওয়া-চাওয়ি করতে লাগলেন। একজন ছোকরা মতো পণ্ডিত খানিকক্ষণ কাঁচুমাচু ক''রে জবাব দিল, "আজ্ঞে, বোধ হয় তার খিদে পেয়েছিল।" রাজামশাই বললেন, "তোমার যেমন বুদ্ধি! খিদে পেয়েছিল, তা সভার মধ্যে আসতে যাবে কেন? এখানে কি মুড়ি-মুড়কি বিক্রি হয়! মন্ত্রী ওকে বিদেয় করে দাও- " সকলে মহা তম্বি ক''রে বললে, "হাঁ হাঁ, ঠিক ঠিক, ওকে বিদায় করুন।"\r\n<br/><br/>\r\nআর-একজন পণ্ডিত বললেন, "মহারাজ, কার্য থাকলেই তার কারণ আছে- বৃষ্টি হলেই বুঝবে মেঘ আছে, আলো দেখলেই বুঝবে প্রদীপ আছে, সুতরাং বায়স পক্ষীর কণ্ঠ নির্গত এই অপরূপ ধ্বনিরূপ কার্যের নিশ্চয়ই কোন কারণ থাকবে, এতে আশ্চর্য কি?"\r\n<br/><br/>\r\nরাজা বললেন, "আশ্চর্য এই যে, তোমার মতো মোটা বুদ্ধি লোকেও এইরকম আবোল তাবোল বকে মোটা মোটা মাইনে পাও। মন্ত্রী, আজ থেকে এঁর মাইনে বন্ধ কর।" অমনি সকলে হাঁ হাঁ করে উঠলেন, "মাইনে বন্ধ কর।"\r\n<br/><br/>\r\nদুই পণ্ডিতের এরকম দুর্দশা দেখে সবাই কেমন ঘাবড়ে গেল। মিনিটের পর মিনিট যায়, কেউ আর কথা কয় না। তখন রাজামশাই দস্তুরমত খেপে গেলেন। তিনি হুকুম দিলেন, এর জবাব না পাওয়া পর্যন্ত কেউ যেন সভা ছেড়ে না ওঠে। রাজার হুকুম- সকলে আড়ষ্ট হয়ে বসে রইল। ভাবতে ভাবতে কেউ কেউ ঘেমে ঝোল হয়ে উঠল, চুলকে চুলকে কারো কারো মাথায় প্রকাণ্ড টাক পড়ে গেল। বসে বসে সকলের খিদে বাড়তে লাগল- রাজামশাইয়ের খিদেও নেই, বিশ্রামও নেই- তিনি বসে বসে ঝিমুতে লাগলেন।\r\n<br/><br/>\r\nসকলে যখন হতাশ হয়ে এসেছে, আর মনে মনে পণ্ডিতদের ''মূর্খ অপদার্থ নিষ্কর্মা'' বলে গাল দিচ্ছে, এমন সময় রোগা শুঁটকো মতো একজন লোক হঠাৎ বিকট চিৎকার করে সভার মাঝখানে পড়ে গেল। রাজা মন্ত্রী পাত্র-মিত্র উজির-নাজির সবাই ব্যস্ত হয়ে বললেন, "কি হলো, কি হলো?" তখন অনেক জলের ছিটে পাখার বাতাস আর বলা কওয়ার পর লোকটা কাঁপতে কাঁপতে উঠে বলল, "মহারাজ সেটা কি দাঁড়কাক ছিল?" সকলে বলল, "হাঁ-হাঁ-হাঁ, কেন বল দেখি?" লোকটা আবার বলল, "মহারাজ, সে কি ঐ মাথার উপর দক্ষিণ দিকে মুখ করে বসেছিল- আর মাথা নিচু করেছিল, আর চোখ পাকিয়েছিল, আর ''কঃ'' করে শব্দ করেছিল?" সকলে ভয়ানক ব্যস্ত হয়ে বললে, "হাঁ, হাঁ- ঠিক ঐরকম হয়েছিল।" তাই শুনে লোকটা আবার ভেউ ভেউ করে কাঁদতে লাগল- আর বলতে লাগল, "হায় হায়, সেই সময়ে কেউ আমায় খবর দিলে না কেন?"\r\n<br/><br/>\r\nরাজা বললেন, "তাই তো, একে তোমরা তখন খবর দাও নি কেন?" লোকটাকে কেউই চেনে না, তবু সে কথা বলতে সাহস পেল না, সবাই বললে, "হ্যাঁ, ওকে একটা খবর দেওয়া উচিত ছিল"- যদিও কেন তাকে খবর দেবে, আর কি খবর দেবে, এ কথা কেউ বুঝতে পারল না। লোকটা তখন খুব খানিকটা কেঁদে তারপর মুখ বিকৃত করে বলল, "দ্রিঘাংচু!" সে আবার কি! সবাই ভাবল, লোকটা খেপে গেছে।\r\n<br/><br/>\r\nমন্ত্রী বললেন, "দ্রিঘাঞ্চু কি হে?" লোকটা বলল, "দ্রিঘাঞ্চু নয়, দ্রিঘাংচু।" কেউ কিছু বুঝতে পারল না- তবু সবাই মাথা নেড়ে বলল, "ও!" তখন রাজামশাই জিজ্ঞাসা করলেন, "সে কিরকম হে," লোকটা বলল, "আজ্ঞে আমি মূর্খ মানুষ, আমি কি অত খবর রাখি, ছেলেবেলা থেকে দ্রিঘাংচু শুনে আসছি, তাই জানি দ্রিঘাংচু যখন রাজার সামনে আসে, তখন তাকে দেখতে দেখায় দাঁড়কাকের মতো। সে যখন সভায় ঢোকে, তখন সিংহাসনের ডান দিকের থামের উপর বসে মাথা নিচু করে দক্ষিণ দিকে মুখ করে, চোখ পাকিয়ে ''কঃ'' বলে শব্দ করে। আমি তো আর কিছুই জানি না- তবে পণ্ডিতেরা যদি জানেন।" পণ্ডিতেরা তাড়াতাড়ি ব্যস্ত হয়ে বললেন, "না, না, ওর সম্বন্ধে আর কিছু জানা যায় নি।"\r\n<br/><br/>\r\nরাজা বললেন, "তোমায় খবর দেয় নি ব''লে কাঁদছিলে, তুমি থাকলে করতে কি?" লোকটা বলল, "মহারাজ, সে কথা বললে যদি লোকে বিশ্বাস না করে, তাই বলতে সাহস হয় না।"\r\n<br/><br/>\r\nরাজা বললেন, "যে বিশ্বাস করবে না, তার মাথা কাটা যাবে- তুমি নির্ভয়ে বলে ফেল।" সভাসুদ্ধ লোক তাতে হাঁ হাঁ করে সায় দিয়ে উঠল।\r\n<br/><br/>\r\nলোকটা তখন বলল, "মহারাজ, আমি একটা মন্ত্র জানি, আমি যুগজন্ম ধরে বসে আছি, দ্রিঘাংচুর দেখা পেলে সেই মন্ত্র তাকে যদি বলতে পারতাম তা হলে কি যে আশ্চর্য কাণ্ড হত তা কেউ জানে না। কারণ, তার কথা কোন বইয়ে লেখে নি। হায় রে হায়, এমন সুযোগ আর কি পাব?" রাজা বললেন, "মন্ত্রটা আমায় বল তো।" লোকটা বলল, "সর্বনাশ! সে মন্ত্র দ্রিঘাংচুর সামনে ছাড়া কারুর কাছে উচ্চারণ করতে নেই। আমি একটা কাগজে লিখে দিচ্ছি- আপনি দু''দিন উপোস ক''রে তিন দিনের দিন সকালে উঠে সেটা পড়ে দেখবেন। আপনার সামনে দাঁড়কাক দেখলে, তাকে আপনি মন্ত্র শোনাতে পারেন, কিন্তু খবরদার, আর কেউ যেন তা না শোনে- কারণ, দাঁড়কাক যদি দ্রিঘাংচু না হয়, আর তাকে মন্ত্র বলতে গিয়ে অন্য লোকে শুনে ফেলে, তা হলেই সর্বনাশ!"\r\n<br/><br/>\r\nতখন সভা ভঙ্গ হল। সভার সকলে এতক্ষণ হাঁ ক''রে শুনছিল, তারা হাঁফ ছেড়ে বাঁচল; সকলে দ্রিঘাংচুর কথা, মন্ত্রের কথা আর আশ্চর্য ফল পাওয়ার কথা বলা-বলি করতে করতে বাড়ি চলে গেল।\r\n<br/><br/>\r\nতারপর রাজামশাই দু''দিন উপোস করে তিন দিনের দিন সকালবেলা- সেই লোকটার লেখা কাগজখানা খুলে পড়লেন। তাতে লেখা আছে-\r\n<br/><br/>\r\n\r\n<p class=''indented_text''>"হল্‌‌দে সবুজ ওরাং ওটাং<br/>\r\nইঁট পাট্‌‌কেল চিৎ‌ পটাং<br/>\r\nমুস্কিল আসান উড়ে মালি<br/>\r\nধর্মতলা কর্মখালি।"</p>\r\n<br/>\r\nরাজামশাই গম্ভীরভাবে এটা মুখস্থ করে নিলেন। তারপর থেকে তিনি দাঁড়কাক দেখলেই লোকজন সব তাড়িয়ে তাকে মন্ত্র শোনাতেন, আর চেয়ে দেখতেন কোনরকম আশ্চর্য কিছু হয় কি না! কিন্তু আজ পর্যন্ত তিনি দ্রিঘাংচুর কোনো সন্ধান পান নি।', 'p0wyl9ve4R8', '2013-03-19 00:00:00', '2014-08-21 16:53:01', 623, '112400358');
INSERT INTO `articles` (`id`, `category_id`, `top_image`, `title`, `article_slug`, `first_line`, `body`, `audio`, `added_on`, `modified_on`, `views`, `sc_track_id`) VALUES
(121, 2, '', 'ভোলানাথের সর্দারি', 'ভোলানাথের-সর্দারি', '', '<p>\r\n        সকল বিষয়েই সর্দারি করিতে যাওয়া ভোলানাথের ভারি একটা বদ অভ্যাস। যেখানে তাহার কিছু বলিবার দরকার নাই, সেখানে সে বিজ্ঞের মতো উপদেশ দিতে যায়, যে কাজের সে কিছুমাত্র বোঝে না, সে কাজেও সে চট্‌‌পট্‌‌ হাত লাগাইতে ছাড়ে না। এইজন্য গুরুজনেরা তাহাকে বলেন ''জ্যাঠা''- আর সমবয়সীরা বলে ''ফড়ফড়ি রাম'' ! কিন্তু তাহাতে তাহার কোনো দুঃখ নাই, বিশেষ লজ্জাও নাই। সেদিন তাহার তিন ক্লাশ উপরের বড় বড় ছেলেরা যখন নিজেদের পড়াশুনা ল‌‌ইয়া আলোচনা করিতেছিল, তখন ভোলানাথ মুরুব্বির মতো গম্ভীর হ‌‌ইয়া বলিল, "ওয়েব্‌‌স্টারের ডিক্‌‌সনারি সব চাইতে ভালো। আমার বড়দা যে দু''ভলুম ওয়েব্‌‌স্টারের ডিক্‌‌সনারি কিনেছেন্, তার এক-একখানা ব‌‌ই এত্তোখানি বড় আর এম্নি মোটা আর লাল চামড়া দিয়ে বাঁধানো"। উঁচু ক্লাশের একজন ছাত্র আচ্ছা করিয়া তাহার কান মলিয়া বলিল, "কি রকম লাল হে? তোমার এই কানের মতো?" তবু ভোলানাথ এমন বেহায়া, সে তার পরদিন‌‌ই সেই তাহাদের কাছে ফুটবল সম্বন্ধে কি যেন মতামত দিতে গিয়া এক চড় খাই‌‌য়া আসিল। \r\n    </p>\r\n    <p>\r\n        বিশুদের একটা ইঁদুর ধরিবার কল ছিল। ভোলানাথ হঠাৎ‌ একদিন্‌‌ , "এটা কিসের কল ভাই?" বলিয়া সেটাকে নাড়িয়া চাড়িয়া কলকব্জা এমন বিগড়াইয়া দিল যে, কলটা একেবারেই নষ্ট হ‌‌ইয়া গেল। বিশু বলিল, "না, জেনে শুনে কেন টানাটানি করতে গেলি?" ভোলানাথ কিছুমাত্র অপ্রস্তুত না হ‌‌ইয়া বলিল, "আমার দোষ হল বুঝি? দেখ্‌‌ তো হাতলটা কিরকম বিচ্ছিরি বানিয়েছে। ওটা আরও অনেক মজবুত করা উচিত ছিল। কলওয়ালা ভয়ানক ঠকিয়েছে।"\r\n    </p>\r\n    <p>\r\n        ভোলানাথ পড়াশুনায় যে খুব ভালো ছিল তাহা নয়, কিন্তু মাস্টার মহাশয় যখন কঠিন কঠিন প্রশ্ন জিজ্ঞাসা করিতেন, তখন সে জানুক আর না জানুক সাত তাড়াতাড়ি সকলের আগে জবাব দিবার জন্য ব্যস্ত হ‌‌ইয়া উঠিত। জবাবটা অনেক সময়েই বোকার মত হ‌‌ইত, শুনিয়া মাস্টার মহাশয় ঠাট্টা করিতেন, ছেলেরা হাসিত; কিন্তু ভোলানাথের উৎ‌সাহ তাহাতে কমিত না। \r\n    </p>\r\n    <p>\r\n        সেই যেবার ইস্কুলে ব‌‌ই চুরির হাঙ্গামা হয়, সেবারও সে এইরকম সর্দারি করিতে গিয়া খুব জব্দ হয়। হেডমাস্টার মহাশয় ক্রমাগত ব‌‌ই চুরির নালিশে বিরক্ত হ‌‌ইয়া, একদিন প্রত্যেক ক্লাশে গিয়া জিগ্‌‌গেস করিলেন, "কে ব‌‌ই চুরি করছে তোমরা কেউ কিছু জানো?" ভোলানাথের ক্লাশে এই প্রশ্ন করিবামাত্র ভোলানাথ তড়াক করিয়া লাফাইয়া উঠিয়া বলিল, "আজ্ঞে, আমার বোধ হয় হরিদাস চুরি করে।"  জবাব শুনিয়া আমরা সবাই অবাক হইয়া গেলাম। হেডমাস্টার মহাশয় বলিলেন, "কি করে জানলে যে হরিদাস চুরি করে?" ভোলানাথ অম্লানবদনে বলিল, "তা জানিনে, কিন্তু আমার মনে হয়।" মাষ্টার মহাশয় ধমক দিয়া বলিলেন, "জানো না, তবে অমন কথা বললে কেন? ও রকম মনে করবার তোমার কি কারণ আছে?" ভোলানাথ আবার বলিল, "আমার মনে হচ্ছিল, বোধহয় ও নেয়&mdash; তাই তো বললাম। আর তো কিছু আমি বলিনি।" মাষ্টার মহাশয় গম্ভীর হ‌‌ইয়া বলিলেন, "যাও হরিদাসের কাছে ক্ষমা চাও।" তখন‌‌ই তাহার কান ধরিয়া হরিদাসের কাছে ক্ষমা চাওয়ানো হ‌‌ইল। কিন্তু তবু কি তাহার চেতনা হয়?\r\n    </p>\r\n    <p>\r\n        ভোলানাথ সাঁতার জানে না, কিন্তু তবু সে বাহাদুরি করিয়া হরিশের ভাইকে সাঁতার শিখা‌‌ইতে গেল। রামবাবু হঠাৎ‌ ঘাটে আসিয়া পড়েন, তাই রক্ষা। তা না হ‌‌ইলে দুজনকেই সেদিন ঘোষপুকুরে ডুবিয়া মরিতে হ‌‌ইত। কলিকাতায় মামার নিষেধ না শুনিয়া চল্‌‌তি ট্রাম হ‌‌ইতে নামিতে গিয়া ভোলানাথ কাদার উপর আছাড় খাইয়াছিল, তিন মাস পর্যন্ত তাহার আঁচড়ের দাগ তাহার নাকের উপর ছিল। আর বেদেরা শেয়াল ধরিবার জন্য যেবার ফাঁদ পাতিয়া রাখে, সেবার সেই ফাঁদ ঘাঁটিতে গিয়া ভোলানাথ কি রকম আটকা পড়িয়াছিল, সেকথা ভাবলে আজও আমাদের হাসি পায়। কিন্তু সব চাইতে যেবার সে জব্দ হ‌‌ইয়াছিল সেবারের কথা বলি শোনো। \r\n    </p>\r\n    <p>\r\n        আমাদের ইস্কুলে আসিতে হ‌‌ইলে কলেজবাড়ির পাশ দিয়া আসিতে হয়। সেখানে একটা ঘর আছে, তাহাকে বলে ল্যাবরেটরি। সেই ঘরে নানারকম অদ্ভুত কলকারখানা থকিত। ভোলানাথের সবটাতেই বাড়াবাড়ি, সে একদিন একেবারে কলেজের ভিতর গিয়া দেখিল, একটা কলের চাকা ঘুরা‌‌নো হ‌‌ইতেছে, আর কলের একদিকে চড়াক্‌‌ চড়াক্‌‌ করিয়া বিদ্যুতের মতো ঝিলিক্‌‌ জ্বলিতেছে। দেখিয়া ভোলানাথের ভারি শখ হ‌‌ইল সে একবার কল ঘুরা‌‌ইয়া দেখে ! কিন্তু কলের কাছে যাওয়া মাত্র, কে একজন তাহাকে এমন ধমক দিয়া উঠিল যে, ভয়ে এক দৌড়ে সে ইস্কুলে আসিয়া হাঁপা‌‌ইতে লাগিল। কিন্তু কলটা একবার নাড়িয়া দেখিবার ইচ্ছা তাহার কিছুতেই গেল না। একদিন বিকালে যখন সকলে বাড়ি যাইতেছি তখন ভোলানাথ যে কোন সময়ে কলেজবাড়িতে ঢুকিল, তাহা আমরা বুঝিতে পারি নাই। সে চুপিচুপি কলেজবাড়ির ল্যাবরেটরি বা যন্ত্রখানায় ঢুকিয়া, অনেকক্ষন এদিক ওদিক চাহিয়া দেখে, ঘরে কেউ নাই। তখন‌‌ই ভরসা করিয়া ভিতরে ঢুকিয়া সে কলকব্জা দেখিতে লাগিল। সেই দিনের সেই কলটা আলমারির আড়ালে উঁচু তাকের উপর তোলা রহিয়াছে, সেখানে তাহার হাত যায় না। অনেক কষ্টে সে টেবিলের পিছন হ‌‌ইতে একখানা বড় চৌকি ল‌‌ইয়া আসিল। এদিকে কখন যে কলেজের কর্মচারী চাবি দিয়া ঘরের তালা আঁটিয়া চলিয়া গেল, সেও ভোলানাথকে দেখে নাই, ভোলানাথের‌‌ও সেদিকে চোখ নাই।  চৌকির উপর দাঁড়া‌‌ইয়া ভোলানাথ দেখিল কলটার কাছে একটা অদ্ভুত বোতল। সেটা যে বিদ্যুতের বোতল, ভোলানাথ তাহা জানে না। সে বোতলটিকে ধরিয়া সরা‌‌ইয়া রাখিতে গেল। অমনি বোতলের বিদ্যুত তাহার শরীরের ভিতর দিয়া ছুটিয়া গেল, মনে হ‌‌ইল যেন তাহার হাড়ের ভিতর পর্যন্ত কিসের একটা ধাক্কা লাগিল, সে মাথা ঘুরিয়া চৌকি হ‌‌ইতে পড়িয়া গেল। \r\n    </p>\r\n    <p>\r\n        বিদ্যুতের ধাক্কা খা‌‌ইয়া ভোলানাথ খানিকক্ষন হতভম্ব হ‌‌ইয়া রহিল। তারপর ব্যস্ত হ‌‌ইয়া পলা‌‌ইতে গিয়া দেখে দরজা বন্ধ!  অনেকক্ষন দরজায় ধাক্কা দিয়া, কিল ঘুষি লাথি মারিয়াও দরজা খুলিল না। জানালাগুলি অনেক উঁচুতে আর বাহির হ‌‌ইতে বন্ধ করা- চৌকিতে উঠিয়াও নাগাল পাওয়া গেল না। তাহার কপালে দরদর করিয়া ঘাম ঝরিতে লাগিল। সে ভাবিল প্রাণপণে চীৎ‌কার করা যাক্, যদি কেউ শুনিতে পায়। কিন্তু তাহার গলার স্বর এমন বিকৃত শোনা‌‌ইল, আর মস্ত ঘরটাতে এমন অদ্ভুত প্রতিধ্বনি হ‌‌ইতে লাগিল যে, নিজের আওয়াজে নিজেই সে ভয় পা‌‌ইয়া গেল। ওদিকে প্রায় সন্ধ্যা হ‌‌ইয়া আসিয়াছে। কলেজের বট গাছটির উপর হ‌‌ইতে একটা পেঁচা হঠাৎ‌ ''ভুত-ভুতুম-ভুত'' বলিয়া বিকট শব্দে ডাকিয়া উঠিল। সেই শব্দে একেবারে দাঁতে দাঁতে লাগিয়া ভোলানাথ এক চীৎ‌কারেই অজ্ঞান! \r\n    </p>\r\n    <p>\r\n        কলেজের দারোয়ান তখন আমাদের ইস্কুলের পাঁড়েজি আর দু-চারটি দেশ-ভা‌‌ইয়ের সঙ্গে জুটিয়া মহা উৎ‌সাহে ''হাঁ হাঁ করে কাঁহা গয়ো রাম'' বলিয়া ঢোল কর্তাল পিটা‌‌ইতেছিল, তাহারা কোনোরূপ চীৎ‌কার শুনিতে পায় নাই। রাতদুপুর পর্যন্ত তাহাদের কীর্তনের হল্‌‌লা চলিল;  সুতরাং জ্ঞান হ‌‌ইবার পর ভোলানাথ যখন দরজায় দুম্‌‌দুম্‌‌ লাথি মারিয়া চেঁচা‌‌ইতেছিল, তখন সে শব্দ গানের ফাঁকে ফাঁকে তাহাদের কানে একটু-আধটু আসিলেও তাহারা গ্রাহ্য করে নাই। পাঁড়েজি একবার খালি বলিয়াছিল, কিসের শব্দ একবার খোঁজ লওয়া যাক, তখন অন্যেরা বাধা দিয়া বলিয়াছিল, "আরে চিল্‌‌ল্যানে দেও।" এমনি করিয়া রাত বারোটার সময় যখন তাহাদের উৎ‌সাহ ঝিমাইয়া আসিল, তখন ভোলানাথের বাড়ির লোকেরা লণ্ঠন হাতে হাজির হ‌‌ইল। তাহারা বাড়ি বাড়ি ঘুরিয়া কোথাও তাহাকে আর খুঁজিতে বাকি রাখে নাই। দারোয়ানদের জিজ্ঞাসা করায় তাহার একবাক্যে বলিল, ''ইস্কুল বাবুদের'' কোথাও তাহারা দেখে নাই। এমন সময় সেই দুম্‌‌দুম্‌‌ শব্দ আর চীৎ‌কার আবার শোনা গেল। \r\n    </p>\r\n    <p>\r\n        তারপর ভোলানাথের সন্ধান পা‌‌ইতে আর বেশি দেরি হ‌‌ইল না। কিন্তু তখনও উদ্ধার নাই- দরজা বন্ধ, চাবি গোপালবাবুর কাছে, গোপালবাবু বাসায় নাই, ভাইজির বিবাহে গিয়াছেন, সোমবার আসিবেন। তখন অগত্যা ম‌‌ই আনা‌‌ইয়া, জানালা খুলিয়া, সার্সির কাঁচ ভাঙিয়া, অনেক হাঙ্গামার পর ভয়ে মৃতপ্রায় ভোলানাথকে বাহির করা হ‌‌ইল। সে ওখানে কি করিতেছিল, কেন আসিয়াছিল, কেমন করিয়া আটকা পড়িল ইত্যাদি করিবার জন্য তাহার বাবা প্রকাণ্ড এক চড় তুলিতেছিলেন, কিন্তু ভোলানাথের ফ্যাকাশে মুখখানা দেখিবার পর সে চড় আর তাহার গালে নামে নাই। \r\n    </p>\r\n    <p>\r\n        নানাজনে জেরা করিয়া তাহার কাছে যে সমস্ত কথা আদায় করিয়াছেন, তাহা শুনিয়াই আমরা আটকা পড়িবার বর্ণনাটা দিলাম। কিন্তু আমাদের কাছে এত কথা কবুল করে নাই। আমাদের কাছে সে আরও উল্টা বুঝাইতে চাহিয়াছিল যে, সে ইচ্ছা করিয়াই বাহাদুরির জন্য কলেজবাড়িতে রাত কাটাইবার চেষ্টায় ছিল। যখন সে দেখিল যে, তাহার কথা কেহ বিশ্বাস করে না, বরং আসল কাথাটা ক্রমে ফাঁস হ‌‌ইয়া পড়িতেছে, তখন সে এমন মুষড়াইয়া গেল যে, অন্তত মাস তিনেকের জন্য তাহার সর্দারির অভ্যাসটা বেশ একটু দমিয়া পড়িয়াছিল।         \r\n    </p>', '', '2013-03-19 00:00:00', '2014-08-21 16:53:01', 248, ''),
(122, 2, '', 'আশ্চর্য কবিতা', 'আশ্চর্য-কবিতা', '', '<p>\r\n        আমাদের ক্লাশে একটি নূতন ছাত্র আসিয়াছে। সে আসিয়া প্রথম দিন‌‌ই সকলকে জানাইল, "আমি পোইট্‌‌রি লিখতে পারি !" একথা শুনিয়া ক্লশের অনেকেই অবাক হ‌‌ইয়া গেল; কেবল দুই-একজন হিংসা করিয়া বলিল, "আমরাও ছেলেবেলায় ঢের ঢের কবিতা লিখেছি।" নতুন ছাত্রটি বোধহয় ভাবিয়াছিল, সে কবিতা লিখিতে পারে শুনিয়া ক্লাশে খুব হুলুস্থুল পড়িয়া যাইবে এবং কবিতার নমুনা শুনিবার জন্য সকলে হা হা করিয়া উঠিবে। যখন সেরূপ কিছুর‌‌ই লক্ষণ দেখা গেল না তখন বেচারা, যেন আপন মনে কি কথা বলিতেছে, এরূপভাবে, যাত্রার মতো সুর করিয়া একটা কবিতা আওড়া‌‌ইতে লাগিল&mdash; \r\n    </p>\r\n    <p class="indented_text">\r\n        ওহে বিহঙ্গম তুমি কিসের আশায়<br />\r\n		বসিয়াছ উচ্চ ডালে সুন্দর বাসায়?<br />\r\n		নীল নভোমণ্ডলেতে উড়িয়া উড়িয়া<br />\r\n		কত সুখ পাও, আহা ঘুরিয়া ঘুরিয়া।<br />\r\n		যদ্যপি থাকিত মম পুচ্ছ এবং ডানা<br />\r\n		উড়ে যেতাম তব সনে নাহি শুনে মানা-<br />\r\n    </p>\r\n    <p>\r\n        কবিতা শেষ হ‌‌ইতে না হ‌‌ইতে, ভবেশ তাহার মতো সুর করিয়া মুখভঙ্গী করিয়া বলিল-\r\n    </p>\r\n    <p class="indented_text">\r\n        আহা যদি থাকত তোমার<br />\r\n			<span style="margin-left: 3em;">ল্যাজের উপর ডানা</span><br />\r\n		উড়ে গেলেই আপদ যেত-<br />\r\n			<span style="margin-left: 3em;">করত না কেউ মানা!</span><br />\r\n    </p>\r\n    <p>\r\n                নূতন ছাত্র তাহাতে রাগিয়া বলিল, "দেখ বাপু, নিজেরা যা পার না, তা ঠাট্টা করে উড়িয়ে দেওয়া ভারি সহজ। শৃগাল ও দ্রাক্ষাফলের গল্প শোনোনি বুঝি?" একজন ছেলে অত্যন্ত ভালোমানুষের মতো মুখ করিয়া বলিল, "শৃগাল এবং দ্রাক্ষাফল! সে আবার কি গল্প?" অমনি নূতন ছাত্রটি আবার সুর ধরিল-\r\n    </p>\r\n    <p class="indented_text">\r\n        বৃক্ষ হতে দ্রাক্ষাফল ভক্ষণ করিতে<br />\r\n		লোভী শৃগাল প্রবেশিল এক দ্রাক্ষা ক্ষেতে<br />\r\n		কিন্তু হায় দ্রাক্ষা যে অত্যন্ত উচ্চে থাকে<br />\r\n		শৃগাল নাগাল পাবে কিরূপে তাহাকে,<br />\r\n		বারম্বার চেষ্টায় হয়ে অকৃতকার্য<br />\r\n		''দ্রাক্ষা টক'' বলিয়া পালাল ছেড়ে রাজ্য।<br />\r\n    </p>\r\n    <p>\r\n        সেই হ‌‌ইতে আমাদের হরেরাম একেবারে তাহার চেলা হ‌‌ইয়া গেল। হরেরামের কাছে আমরা শুনিলাম যে ছোকরার নাম শ্যামলাল। সে নাকি এত কবিতা লিখিয়াছে যে, একখানা আস্ত খাতা প্রায় ভরতি হ‌‌ইয়াছে আর আট-দশটি কবিতা হ‌‌ইলেই তাহার একশোটা পুরো হয়; তখন সে নাকি ব‌‌ই ছাপা‌‌ইবে।\r\n    </p>\r\n    <p>\r\nইহার মধ্যে একদিন এক কাণ্ড হ‌‌ইল। গোপাল বলিয়া একটি ছেলে স্কুল ছড়িয়া যা‌‌ইবে এই উপলক্ষে শ্যামলাল এক প্রকাণ্ড কবিতা লিখিয়া ফেলিল। তাহার মধ্যে ''বিদায় বিদায়'' বলিয়া অনেক ''অশ্রুজল'' ''দুঃখশোক'' ইত্যাদি কথা ছিল। গোপাল কবিতার আধখানা শুনিয়াই একেবারে তেলে-বেগুনে জ্বলিয়া উঠিল। সে বলিল, "ফের যদি আমার নামে পো‌‌ইট্‌‌রি লিখবি তো মারব এক থাপ্পড়।" হরেরাম বলিল, "আহা বুঝলে না? তুমি স্কুল ছেড়ে যাচ্ছ কিনা, তা‌‌ই ও লিখেছে।" গোপাল বলিল, "ছেড়ে যাচ্ছি তা যাচ্ছি, তোর তাতে কি রে? ফের জ্যাঠামি করবি তো তোর কবিতার খাতা ছিঁড়ে দেব।"\r\n    </p>\r\n    <p>\r\n        দেখিতে দেখিতে শ্যামলালের কথা ইস্কুলময় রাষ্ট্র হ‌‌ইয়া পড়িল। তাহার দেখাদেখি আরও অনেকেই কবিতা লিখিতে শুরু করিল। ক্রমে কবিতা লেখার বাতিকটা ভয়ানক রকম ছোঁয়াচে হ‌‌ইয়া নিচের ক্লাশের প্রায় অর্ধেক ছেলেকে পা‌‌ইয়া বসিল। ছোট ছোট ছেলেদের পকেটে ছোট ছোট কবিতার খাতা দেখা দিল। বড়দের মধ্যে কেহ শ্যামলালের চেয়েও ভালো কবিতা লিখিতে পারে বলিয়া শোনা যা‌‌ইতে লগিল। ইস্কুলের দেয়ালে, পড়ার কেতাবে, পরীক্ষার খাতায়, চরিদিকে কবিতা গজাইয়া উঠিল। \r\n    </p>\r\n    <p>\r\n        পাঁড়েজির বৃদ্ধ ছাগল যেদিন শিং নাড়িয়া দড়ি ছিঁড়িয়া ইস্কুলের উঠানে দাপাদাপি করিয়াছিল, আর শ্যামলালকে তাড়া করিয়া খানায় ফেলিয়াছিল, তাহার পরদিন ভারতবর্ষের বড় ম্যাপের উপর বড় বড় অক্ষরে লেখা বাহির হ‌‌ইল&mdash;\r\n    </p>\r\n    <p class="indented_text">\r\n        পাঁড়েজির ছাগলের একহাত দাড়ি,<br />\r\n		অপরূপ রূপ তার যা‌‌ই বলিহারি !<br />\r\n		উঠানে দাপটি করি নেচেছিল কাল<br />\r\n		তারপর কি হ‌‌ইল জানে শ্যামলাল।<br />\r\n    </p>\r\n    <p>\r\n        শ্যামলালের রঙটি কালো কিন্তু কবিতা পড়িয়া সে যথার্থ‌‌ই চটিয়া লাল হ‌‌ইল, এবং তখনি তাহার নিচে একটা কড়া জবাব লিখিতে লাগিল। সে সবেমাত্র লিখিয়াছে&mdash; ''রে অধম কাপুরুষ পাষণ্ড বর্বর-'' এমন সময় গুরু গম্ভীর গলা শোনা গেল&mdash; "ম্যাপের উপর কি লেখা হচ্ছে?" ফিরিয়া দেখে হেড মাস্টার মহাশয় !  শ্যামলাল একেবারে থতমত খা‌‌ইয়া বলিল, "আজ্ঞে স্যার, ওরা আগে লিখেছিল।" "ওরা কারা?"  শ্যামলাল বোকার মত একবার আমাদের দিকে, একবার কড়িকাঠের দিকে তাকাইতে লাগিল, কাহার নাম করিবে বুঝিতে পারিল না। মাস্টার মহাশয় আবার বলিলেন, "ওরা যদি পরের বাড়িতে সিঁদ কাটতে যায়, তুমিও কাটবে?" যাহা হ‌‌উক সেদিন অল্পের উপর দিয়াই গেল, শ্যামলাল একটু ধমক-ধামক খা‌‌ইয়াই খালাস পা‌‌ইল।\r\n    </p>\r\n    <p>\r\n        ইহার মধ্যে একদিন আমাদের পণ্ডিতমশাই গল্প করিলেন যে, তাঁহার সঙ্গে যাহারা এক ক্লাশে পড়িত, তাহাদের মধ্যে একজন নাকি অতি সুন্দর কবিতা লিখিত। একবার ইন্‌‌স্পেক্টর ইস্কুল দেখিতে আসিয়া, তাহার কবিতা শুনিয়া এমন খুশি হ‌‌ইয়াছিলেন যে, তাহাকে একটা সুন্দর ছবিওয়ালা ব‌‌ই উপহার দিয়াছিলেন।\r\n    </p>\r\n    <p>\r\n        ইহার মাসখানেক পরেই ইন্‌‌স্পেক্টর ইস্কুল দেখিতে আসিলেন। প্রায় বিশ-পঁচিশটি ছেলে সাবধানে পকেটের মধ্যে লুকা‌‌ইয়া কবিতার কাগজ আনিয়াছে। বড় হলের মধ্যে সমস্ত স্কুলের ছেলেদের দাঁড় করানো হ‌‌ইয়াছে, হেডমাস্টার মহাশয় ইন্সপেক্টরকে ল‌‌ইয়া ঘরে ঢুকিতেছেন&mdash; এমন সময় শ্যামলাল আস্তে আস্তে পকেট হ‌‌ইতে একটি কাগজ বহির করিল। আর যায় কোথা !  পাছে শ্যামলাল আগেই তাহার কবিতা পড়িয়া ফেলে, এই ভয়ে ছোট বড় একদল কবিতাওয়ালা একসঙ্গে নানাসুরে চীৎ‌কার করিয়া যে যার কবিতা হাঁকিয়া উঠিল। মনে হ‌‌ইল, সমস্ত বাড়িটা কর্তালের মতো ঝন্‌‌ঝন্‌‌ করিয়া বাজিয়া উঠিল, ইন্‌‌স্পেক্টর মহাশয় মাথা ঘুরিয়া মাঝ পথেই মেঝের উপর বসিয়া পড়িলেন। ছাদের উপর একটা বিড়াল ঘুমা‌‌ইতেছিল, সেটা হঠাৎ‌ হাত পা ছুড়িয়া তিনতলা হ‌‌ইতে পড়িয়া গেল, ইস্কুলের দারোয়ান হ‌‌ইতে অফিসের কেশিয়ার বাবু পর্যন্ত হাঁ হাঁ করিয়া ছুটিয়া আসিল। সকলে সুস্থ হ‌‌ইলে পর মাস্টার মহাশয় বলিলেন, "এতো চেঁচালে কেন?" সকলে চুপ করিয়া রহিল। আবার জিজ্ঞাসা করা হ‌‌ইল, "কে কে চেঁচিয়েছিল?" পাঁচ-সাতটি ছেলে একসঙ্গে বলিয়া উঠিল&mdash; "শ্যামলাল।" শ্যামলাল যে একা অত মারাত্মক রকম চেঁচা‌‌ইতে পারে, এ কথা কেহ‌‌ই বিশ্বাস করিল না। যতগুলি ছেলের পকেটে কবিতার খাতা পাওয়া গেল, স্কুলের পর তাহাদের দেড়ঘণ্টা আটকা‌‌ইয়া রাখা হ‌‌ইল।  \r\n    </p>\r\n    <p>\r\n        অনেক তম্বিতম্বার পর একে একে সমস্ত কথা বাহির হ‌‌ইয়া পড়িল। তখন হেডমাস্টার মহাশয় বলিলেন, "কবিতা লেখার রোগ হয়েছে? ও রোগের ওষুধ কি?" বৃদ্ধ পণ্ডিতমহাশয় বলিলেন, "বিষস্য বিষমৌষধম্‌‌, বিষের ওষুধ বিষ। বসন্তের ওষুধ যেমন বসন্তের টিকা, কবিতার ওষুধ তস্য টিকা। তোমরা যে যে কবিতা লিখেছ তার টিকা করে দিচ্ছি। তোমরা এক মাস প্রতিদিন পঞ্চাশ বার করে এটা লিখে এনে স্কুলে আমায় দেখাবে" এই বলিয়া তিনি টিকা দিলেন&mdash;\r\n    </p>\r\n    <p class="indented_text">\r\n        পদে পদে মিল খুঁজে, গুণে দেখি চৌদ্দ<br />\r\n		এই দেখ লিখে দিনু কি ভীষণ পদ্য !<br />\r\n		এক চোটে এইবারে উড়ে গেল সবি তা,<br />\r\n		কবিতার গুতো মেরে গিলে ফেলি কবিতা।<br />\r\n    </p>\r\n    <p>\r\n        একমাস তিনি কবিদের এই লেখা প্রতিদিন পঞ্চাশবার আদায় না করিয়া ছাড়িলেন না। এ কবিতার কি আশ্চর্যগুণ&mdash; তারপর হ‌‌ইতে কবিতা লেখার ফ্যাশান স্কুল হ‌‌ইতে একেবারেই উঠিয়া গেল। \r\n    </p>', '', '2013-03-19 00:00:00', '2014-08-21 16:53:01', 814, '');
INSERT INTO `articles` (`id`, `category_id`, `top_image`, `title`, `article_slug`, `first_line`, `body`, `audio`, `added_on`, `modified_on`, `views`, `sc_track_id`) VALUES
(123, 2, '', 'নন্দলালের মন্দ কপাল', 'নন্দলালের-মন্দ-কপাল', '', '<p>\r\n            নন্দলালের ভারি রাগ, অঙ্কের পরীক্ষায় মাস্টার তাহাকে গোল্লা দিয়াছেন। সে যে খুব ভালো লিখিয়াছিল তাহা নয়, কিন্তু তা বলিয়া একেবারে গোল্লা দেওয়া কি উচিত ছিল? হাজার হোক সে একখানা পুরা খাতা লিখিয়াছিল তো ! তার পরিশ্রমের কি কোনো মূল্য নাই? ঐ যে ত্রৈরাশিকের অঙ্কটা, সেটা তো তার প্রায় ঠিক‌‌ই হ‌‌ইয়াছিল, কেবল একটুখানি হিসেবের ভুল হওয়াতে উত্তরটা ঠিক মেলে নাই। আর ঐ যে একটা ডেসিমাল অঙ্ক ছিল, সেটাতে গুণ করিতে গিয়া সে ভাগ করিয়া বসিয়াছিল, তাই বলিয়া কি একটা নম্বরও দিতে নাই? আরও অন্যায় এই যে, এই কথাটা মাস্টার মহাশয় ক্লাশের ছেলেদের কাছে ফাঁস করিয়া ফেলিয়াছেন। কেন? আর একবার হরিদাস যখন গোল্লা পা‌‌ইয়াছিল, তখন তো সে  কথাটা রাষ্ট্র হয় নাই !\r\n    </p>\r\n    <p>\r\n        সে যে ইতিহাসের একশোর মধ্যে পঁচিশ পা‌‌ইয়াছে, সেটা বুঝি কিছু নয়? খালি অঙ্ক ভালো পারে নাই বলিয়াই তাহাকে লজ্জিত হ‌‌ইতে হ‌‌ইবে? সব বিষয়ে যে সকলকে ভালো পারিতে হ‌‌ইবে তাহারি বা অর্থ কি? স্বয়ং নেপোলিয়ান যে ছেলেবেলায় ব্যাকরণে একেবারে আনাড়ী ছিলেন, সে বেলা কি? তাহার এই যুক্তিতে ছেলেরা দমিল না, এবং মাস্টারের কাছে এই তর্কটা তোলাতে তাঁহারাও যে যুক্তিটাকে খুব চমৎ‌কার ভাবিলেন, এমন বোধ হ‌‌ইল না। তখন নন্দলাল বলিল, তাহার কপাল‌‌ই মন্দ&mdash; সে নাকি বরাবর তাহা দেখিয়া আসিয়াছে।\r\n    </p>\r\n    <p>\r\n        সেই তো যেবার ছুটির আগে তাহাদের পাড়ায় হাম দেখা দিয়াছিল, তখন বাড়িসুদ্ধো সকলেই হামে ভুগিয়া দিব্যি মজা করিয়া স্কুল কামাই করিল, কেবল বেচারা নন্দলালকেই নিয়মমতো প্রতিদিন স্কুলে হাজিরা দিতে হ‌‌ইয়াছিল। তারপর যেমন ছুটি আরম্ভ হ‌‌ইল, অমনি তাহাকে জ্বরে আর হামে ধরিল&mdash; ছুটির অর্ধেকটাই মাটি ! সেই যেবার সে মামার বাড়ি গিয়াছিল, সেবার তাহার মামাতো ভাই‌‌য়েরা কেহ ছিল না- ছিলেন কোথাকার এক বদমেজাজি মেশো, তিনি উঠিতে বসিতে কেবল ধমক আর শাসন ছাড়া আর কিছুই জানিতেন না। তাহার উপর সেবার এমন বৃষ্টি হ‌‌ইয়াছিল, একদিনও ভালো করিয়া খেলা জমিল না, কোথাও বেড়ানো গেল না। এই জন্য পরের বছর যখন আর সকলে মামার বাড়ি গেল তখন সে কিছুতেই যা‌‌ইতে চাহিল না। পরে শুনিল সেবার নাকি সেখানে চমৎ‌কার মেলা বসিয়াছিল, কোন রাজার দলের সঙ্গে পঁচিশটি হাতি আসিয়াছিল, আর বাজি যা পোড়ানো হ‌‌ইয়াছিল সে একেবারে আশ্চর্যরকম ! নন্দলালের ছোট ভাই যখন বার বার উৎ‌সাহ করিয়া তাহার কাছে এই সকলের বর্ণনা করিতে লাগিল, তখন নন্দ তাহাকে এক চড় মাড়িয়া বলিল, "যা যা ! মেলা বক্‌‌বক্‌‌ করিসনে !" তাহার কেবল মনে হ‌‌ইতে লাগিল সেবার সে মামার বাড়ি গিয়াও ঠকিল, এবার না গিয়াও ঠকিল ! তাহার মতো কপাল-মন্দ আর কেহ‌‌ নাই। \r\n    </p>\r\n    <p>\r\n        স্কুলেও ঠিক তাই। সে অঙ্ক পারে না- অথচ অঙ্কের জন্য দুই-একটা প্রা‌‌ইজ আছে- এদিকে ভুগোল ইতিহাস তাহার কণ্ঠস্থ কিন্তু ঐ দুইটার একটাতেও প্রাইজ নাই। অবশ্য সংস্কৃতেও সে নেহাত কাঁচা নয়, ধাতু প্রত্যয় বিভক্তি সব চট্‌‌পট্‌‌ মুখস্থ করিয়া ফেলে- চেষ্টা করিলে কি পড়ার ব‌‌ই আর অর্থ-পুস্তকটাকেও সড়গড় করিতে পারে না? ক্লাশের মধ্যে ক্ষুদিরাম একটু আধটু সংস্কৃত জানে- কিন্তু তাহা তো বেশি নয়। নন্দলাল ইচ্ছা করিলে কি তাহাকে হারা‌‌ইতে পারে না? নন্দ জেদ করিয়া স্থির করিল, ''একবার ক্ষুদিরামকে দেখে নেব। ছোকরা এ বছর সংস্কৃতের প্রাইজ পেয়ে ভারি দেমাক করছে&mdash; আবার অঙ্কের গোল্লার জন্য আমাকে খোঁটা দিতে এসেছিল। আচ্ছা, এবার দেখা যাবে।''\r\n    </p>\r\n    <p>\r\n        নন্দলাল কাহাকেও কিছু না জানাইয়া সেইদিন হ‌‌ইতেই বাড়িতে ভয়ানক ভাবে পড়িতে শুরু করিল। ভোরে উঠিয়াই সে ''হসতি হসত হসন্তি'' শুরু করে, রাত্রেও ''অস্তি গোদাবরী তীরে বিশাল শল্মলীতরু'' বলিয়া ঢুলিতে থাকে। কিন্তু ক্লাশের ছেলেরা এ কথার বিন্দুবিসর্গও জানে না। পণ্ডিত মহাশয় যখন ক্লাশে প্রশ্ন করেন, তখন মাঝে মাঝে উত্তর জানিয়াও সে চুপ করিয়া মাথা চুলকা‌‌ইতে থাকে, এমন কি কখনো ইচ্ছা করিয়া দুই-একটা ভুল বলে, পাছে ক্ষুদিরাম তাহার পড়ার খবর টের পাইয়া আরও বেশি করিয়া পড়ায় মন দিতে থাকে।  তাহার ভুল উত্তর শুনিয়া ক্ষুদিরাম মাঝে মাঝে ঠাট্টা করে, নন্দলাল তাহার কোনো জবাব দেয় না; কেবল ক্ষুদিরাম যখন নিজে যখন এক-একটা ভুল করে, তখন সে মুচকি মুচকি হাসে, আর ভাবে, ''পরীক্ষার সময় অম্নি ভুল করলেই এবার ওঁকে সংস্কৃতের প্রাইজ আর পেতে হবে না।''\r\n    </p>\r\n    <p>\r\n        ওদিকে ইতিহাসের ক্লাশে নন্দলালের প্রতিপত্তি কমিতে লাগিল। কারণ, ইতিহাস আর ভুগোল নাকি এক রকম শিখিলেই পাশ করা যায়&mdash; তাহার জন্য নন্দর কোনো ভবনা নাই। তাহার সমস্ত মনটা রহিয়াছে ঐ সংস্কৃত পড়ার উপরে&mdash; অর্থাৎ‌ সংস্কৃত প্রাইজটার উপরে ! একদিন মাস্টার মহাশয় বলিলেন, "কি হে নন্দলাল, আজকাল বুঝি বাড়িতে কিছু পড়াশুনা কর না? সব বিষয়েই সে তোমার এমন দুর্দশা হচ্ছে তার অর্থ কি?" নন্দ আর একটু হ‌‌ইলেই বলিয়া ফেলিত, "আজ্ঞে সংস্কৃত পড়ি" কিন্তু কথাটাকে হঠাৎ‌ সামলা‌‌ইয়া "আজ্ঞে সংস্কৃত&mdash; না সংস্কৃত নয়" বলিয়াই সে থতমত খাইয়া গেল। ক্ষুদিরাম তাড়াতাড়ি বলিয়া উঠিল, "ক‌‌ই সংস্কৃতও তো কিছু পারে না।" শুনিয়া ক্লাশসুদ্ধ ছেলে হাসিতে লাগিল। নন্দ একটু অপ্রস্তুত হ‌‌ইল বটে, কিন্তু সেই সঙ্গে হাঁপ ছাড়িয়া বাঁচিল&mdash; ভাগ্যিস্‌‌ তাহার সংস্কৃত পড়ার কথাটা ফাঁস হ‌‌ইয়া যায় নাই। \r\n    </p>\r\n    <p>\r\n        দেখিতে দেখিতে বছর কাটিয়া আসিলে, পরীক্ষার সময় প্রায় উপস্থিত। সকলে পড়ার কথা, পরীক্ষার কথা আর প্রাইজের কথা বলাবলি করিতেছে, এমন সময় একজন জিজ্ঞাসা করিল, "কি হে! নন্দ এবার কোন প্রাইজটা নিচ্ছ?" ক্ষুদিরাম নন্দর মতো গলা করিয়া ঘাড় নাড়িয়া বলিল, "আজ্ঞে সংস্কৃত&mdash; না সংস্কৃত নয়।" সকলে হাসিল, নন্দও খুব উৎ‌সাহ করিয়া সেই হাসিতে যোগ দিল। মনে ভাবিল, ''বাছাধন, এ হাসি আর তোমার মুখে বেশিদিন থকছে না।'' \r\n    </p>\r\n    <p>\r\n        যথাসময়ে পরীক্ষা আরম্ভ হ‌‌ইল এবং যথাসময়ে শেষ হ‌‌ইল। পরীক্ষার ফল জানিবার জন্য সকলে আগ্রহ করিয়া আছে, নন্দও রোজ নোটিশবোর্ডে গিয়া দেখে, তাহার নামে সংস্কৃত প্রাইজ পাওয়ার কোনো বিজ্ঞাপন আছে কিনা। তারপর একদিন হেডমাস্টার মহাশয় এক তাড়া কাগজ ল‌‌ইয়া ক্লাশে আসিলেন, আসিয়াই গম্ভীর ভাবে বলিলেন, এবার দুই-একটা নতুন প্রাইজ হয়েছে আর অন্য বিষয়েও কোনো কোনো পরিবর্তন হয়েছে।" এই বলিয়া তিনি পরীক্ষার ফলাফল পড়িতে লাগিলেন। তাহাতে দেখা গেল, ইতিহাসের জন্য কে যেন একটা রূপার মেডেল দিয়াছেন। ক্ষুদিরাম ইতিহাসে প্রথম হ‌‌ইয়াছে, সে-ই ঐ মেডেলটা ল‌‌ইবে। সংস্কৃতে নন্দ প্রথম, ক্ষুদিরাম দ্বিতীয়&mdash; কিন্তু এবার সংস্কৃতে কোনো প্রাইজ নাই। \r\n    </p>\r\n    <p>\r\n        হায় হায় ! নন্দর অবস্থা তখন শোচনীয় ! তাহার ইচ্ছা হ‌‌ইতেছিল সে ছুটিয়া গিয়া ক্ষুদিরামকে কয়েকটা ঘুঁষি লাগাইয়া দেয়। কে জানিত এবার ইতিহাসের জন্য প্রাইজ থাকিবে, আর সংস্কৃতের জন্য থাকিবে না। ইতিহাসের মেডেলটা তো সে অনায়াসেই পাইতে পারিত। কিন্তু তাহার মনের কষ্ট কেহ বুঝিল না&mdash; সবাই বলিল, "বেড়ালের ভাগ্যে শিকে ছিঁড়েছে&mdash; কেমন করে ফাঁকি দিয়ে নম্বর পেয়েছে।" দীর্ঘশ্বাস ছাড়িয়া নন্দ বলিল, "কপাল মন্দ!"\r\n    </p>', '', '2013-03-19 00:00:00', '2014-08-21 16:53:01', 414, ''),
(124, 2, '', 'নতুন পণ্ডিত', 'নতুন-পণ্ডিত', '', '<p>\r\n        আগে যিনি আমাদের পণ্ডিত ছিলেন, তিনি লোক বড় ভালো। মাঝে মাঝে আমাদের যে ধমক-ধামক না করিতেন, তাহা নয়, কিন্তু কখনও কাহাকেও অন্যায় শাস্তি দেন নাই। এমন কি ক্লাশে আমরা কত সময় গোল করিতাম, তিনি মাঝে মাঝে ''আঃ'' বলিয়া ধমক দিতেন। তাঁর হাতে একটা ছড়ি থাকিত, খুব বেশি রাগ করিলেই সেই ছড়িটাকে টেবিলের উপর আছড়াইতেন&mdash; সেটিকে কোনোদিন কাহারো পিঠে পড়িতে দেখি নাই । তাই আমরা কেউ তাঁহাকে মানিতাম না। \r\n    </p>\r\n    <p>\r\n        আমাদের হেডমাস্টার মশাইটি দেখিতে তাঁর চাইতেও নিরীহ ভালোমানুষ। ছোট্ট বেঁটে মানুষটি, গোঁফ দাড়ি কামানো, গোলগাল মুখ, তাহাতে সর্বদাই যেন হাসি লাগিয়াই আছে। কিন্তু চেহারায় কি হয়? তিনি যদি ''শ্যামাচরণ কার নাম?'' বলিয়া ক্লাশে আসিয়াই আমায় ডাক দিতেন, তবে তাঁর গলার আওয়াজেই আমার হাত পা যেন পেটের মধ্যে ঢুকিয়া যাইত। তাঁর হাতে কোনো দিন বেত দেখি নাই, কারণ বেতের কোনো দরকার হ‌‌ইত না&mdash; তাঁর হুঙ্কারটি যাহার উপর পড়িত সেই চক্ষে অন্ধকার দেখিত।\r\n    </p>\r\n    <p>\r\n        একদিন পণ্ডিতমহাশয় বলিলেন, "সোমবার থেকে আমি আর পড়াতে আসব না- কিছু দিনের ছুটি নিয়েছি। আমার জায়গায় আর একজন আসবেন। দেখিস তাঁর ক্লাশে তোরা যেন গোল করিস্‌‌নে।" শুনিয়া আমাদের ভারি উৎ‌সাহ লাগিল; তারপর যে কয়দিন পণ্ডিতমহাশয় স্কুলে ছিলেন, আমরা ক্লাশে এক মিনিটও পড়ি নাই। একদিন বেহারীলাল পড়ার সময় পড়িয়াছিল, সেজন্য আমরা পরে চাঁদা করিয়া তাহার কান মলিয়া দিয়াছিলাম। যাহা হ‌‌উক, পণ্ডিতমহাশয় সোমবার আর আসিলেন না&mdash; তাঁর বদলে যিনি আসিলেন তাঁর গোল কালো চশমা, মুখভরা গোঁফের জঙ্গল আর বাঘের মত আওয়াজ শুনিয়া আমাদের উৎ‌সাহ দমিয়া গেল। তিনি ক্লাশে আসিয়াই বলিলেন, "পড়ার সময় কথা বলবে না, হাসবে না, যা বলব তাই করবে। রোজকার পড়া রোজ করবে। আর যদি তা না কর, তা হলে তুলে আছড় দেব।" শুনিয়া আমাদের তো চক্ষু স্থির !\r\n    </p>\r\n    <p>\r\n        ফকির চাঁদের তখন অসুখ ছিল, সে বেচারা কদিন পরে ক্লাশে আসিতেই নূতন পণ্ডিতমহাশয় তাহাকে পড়া জিজ্ঞাসা করিয়া বসিলেন। ফকির থতমত খাইয়া ভয়ে আম্‌‌তা আম্‌‌তা করিয়া বলিল&mdash; "আজ্ঞে&mdash; আমি ইস্কুলে আসিনি&mdash;" পণ্ডিতমহাশয় রাগিয়া বলিলেন, "ইস্কুলে আসনি তো কোথায় এসেছ? তোমার মামার বাড়ি?" বেচারা কাঁদ-কাঁদ হ‌‌ইয়া বলিল, "সাতদিন ইস্কুলে আসিনি, কি করে পড়া বলব?" পণ্ডিতমহাশয় "চোপরাও বেয়াদব&mdash; মুখের উপর মুখ" বলিয়া এমন গর্জন করিয়া উঠিলেন যে ভয়ে ক্লাশ সুদ্ধ ছেলের মুখের তালু শুকাইয়া গেল। \r\n    </p>\r\n    <p>\r\n        আমাদের হরিপ্রসন্ন অতি ভালো ছেলে। সে একদিন ইস্কুলের অফিসে গিয়া খবর পাইল&mdash; সে নাকি এবার প্রাইজ পাইবে। খবরটা শুনিয়া সে বেচারা ভারি খুশি হ‌‌ইয়া ক্লাশে আসিতেছিল, এমন সময় নূতন পণ্ডিতমহাশয় "হাসছ কেন?" বলিয়া হঠাৎ‌ এমন ধমক দিয়া উঠিলেন যে মুখের হাসি এক মুহূর্তে আকাশে উড়িয়া গেল। তাহার পরদিন আমাদের ক্লাশের বাহিরে রাস্তার ধারে কে যেন হো হো করিয়া হাসিতেছিল, শুনিয়া পণ্ডিতমহাশয় পাশের ঘর হ‌‌ইতে হাঁ হাঁ করিয়া ছুটিয়া আসিলেন। আসিয়া আর কথাবার্তা নাই&mdash; "কেবল হাসি?" বলিয়া হরিপ্রসন্নর গালে ঠাস্‌‌ ঠাস্‌‌ করিয়া কয়েক চড় লাগাইয়া, আবার হন্‌‌ হন্‌‌ করিয়া চলিয়া গেলেন। সেই অবধি হরিপ্রসন্নর উপর তিনি বিনা কারণে যখন তখন খাপ্পা হ‌‌ইয়া উঠিতেন। দেখিতে দেখিতে ইস্কুল সুদ্ধ ছেলে নুতন পণ্ডিতের উপর হাড়ে হাড়ে চটিয়া গেল। \r\n    </p>\r\n    <p>\r\n       একদিন আমাদের অঙ্কের মাস্টার আসেন নাই, হেডমাস্টার রামবাবু বলিয়া গেলেন&mdash; তোমরা ক্লাশে বসিয়া পুরাতন পড়া পড়িতে থাক। আমরা পড়িতে লাগিলাম, কিন্তু খানিক বাদেই পণ্ডিতমহাশয় পাশের ঘর হ‌‌ইতে "পড়ছ না কেন?" বলিয়া টেবিলে প্রকাণ্ড এক ঘুঁষি মারিলেন। আমরা বলিলম, "আজ্ঞে হ্যাঁ, পড়ছি তো। " তিনি আবার বলিলেন, "তবে শুনতে পাচ্ছি না কেন, চেঁচিয়ে পড়।" যেই বলা অমনি বোকা ফকিরচাঁদ \r\n    </p>\r\n    <p class="indented_text">\r\n        ''অন্ধকারে চৌরাশিটা নরকের কুণ্ড<br />\r\n		তাহাতে ডুবায়ে ধরে পাতকীর মুণ্ড&mdash;''\r\n    </p>\r\n    <p>\r\n        বলিয়া এমন চেঁচাইয়া উঠিল যে, পণ্ডিতমহাশয়ের চোখ হ‌‌ইতে চশমাটা পড়িয়া গেল। মাস্টার মহাশয় গম্ভীরভাবে ঘরের মধ্যে ঢুকিলেন, তারপর কেন জানি না হরিপ্রসন্নর কান ধরিয়া তাহাকে সমস্ত স্কুল ঘুরাইয়া আনিলেন, তাহাকে তিনদিন ক্লাশে দাঁড়াইয়া থাকিতে হুকুম দিলেন, একটানা জরিমানা করিলেন, তাহার কালো কোটের পিঠে খড়ি দিয়া ''বাঁদর'' লিখিয়া দিলেন, আর ইস্কুল হ‌‌ইতে তাড়াইয়া দিবেন বলিয়া শাসাইয়া রাখিলেন। \r\n    </p>\r\n    <p>\r\n        পরদিন রামবাবু হরিপ্রসন্নকে ডাকাইয়া পাঠাইলেন এবং তার কাছে সমস্ত কথা শুনিয়া আমাদের ক্লাশে খোজ করিতে আসিলেন। তখন ফকিরচাঁদ বলিল, ''আজ্ঞে হরে চেঁচায়নি, আমি চেঁচিয়েছি।" রামবাবু বলিলেন, ''পণ্ডিতমশাইকে পাতকী বলিয়া কি গালাগালি করিয়াছিলে?'' ফকির বলিল, ''পণ্ডিতমশাইকে কিছুই বলিনি, আমি পড়ছিলাম&mdash;\r\n    </p>\r\n    <p class="indented_text">\r\n        ''অন্ধকারে চৌরাশিটা নরকের কুণ্ড<br />\r\n		তাহাতে ডুবায়ে ধরে পাতকীর মুণ্ড&mdash;''\r\n    </p>\r\n    <p>\r\n        এই সময় নূতন পণ্ডিতমহাশয় ক্লাশের পাশ দিয়া যাইতেছিলেন। তিনি বোধহয় শেষ কথাটুকু শুনিতে পাইয়াছিলেন এবং ভাবিয়াছিলেন তাঁহাকে ল‌‌ইয়া কিছু একটা ঠাট্টা করা হ‌‌ইয়াছে। তিনি রাগে দিগ্বিদিক জ্ঞান হারাইয়া হাঁ হাঁ করিয়া ক্লাশের মধ্যে আসিয়া রামবাবুর কালো কোট দেখিয়াই "তবে রে হরিপ্রসন্ন" বলিয়া হেডমাস্টার মহাশয়কে পিটাইতে লাগিলেন। আমরা ভয়ে কাঠ হ‌‌ইয়া রহিলাম। হেডমাস্টার মহাশয় অনেক কষ্টে পণ্ডিতের হাত ছাড়াইয়া তাঁহার দিকে ফিরিয়া দাঁড়াইলেন। \r\n    </p>\r\n    <p>\r\n        তখন যদি পণ্ডিতের মুখ দেখিতে ! বেচারা ভয়ে একেবারে জুজু ! তিন-চারবার হাঁ করিয়া আবার মুখ বুজিলেন, তারপর এদিক ওদিক চাহিয়া এক দৌড়ে সে যে ইস্কুল হ‌‌ইতে পালাইয়া গেলেন, আর কোনো দিন তাঁকে ইস্কুলে দেখি নাই।\r\n    </p>\r\n    <p>\r\n        দুইদিন পরে পুরাতন পণ্ডিতমহাশয় আবার ফিরিয়া অসিলেন। তাঁর মুখ দেখিয়া আমাদের যেন ধরে প্রাণ আসিল, আমরা হাঁফ ছাড়িয়া বাচিলাম। সকলে প্রতিজ্ঞা করিলাম, আর তাঁর ক্লাশে গোলমাল করিব না, যত‌‌ই পড়া দিন না, খুব ভালো করিয়া পড়িব। \r\n    </p>', '', '2013-03-19 00:00:00', '2014-08-21 16:53:01', 324, ''),
(125, 2, '', 'সবজান্তা দাদা', 'সবজান্তা-দাদা', '', '<p>\r\n        "এই দ্যাখ্‌‌ টেঁপি, দ্যাখ্‌‌ কি রকম করে হাউই ছাড়তে হয়। বড় যে রাজুমামাকে ডাকতে চাচ্ছিলি? কেন, রাজুমামা না হলে বুঝি হাউই ছোটানো যায় না? এই দ্যাখ্‌‌।"\r\n    </p>\r\n    <p>\r\n        দাদার বয়স প্রায় বছর দশেক হবে, টেঁপির বয়স মোটে আট, অন্য অন্য ভাইবোনেরা আরো ছোট। সুতরাং দাদার দাদাগিরির অন্ত নেই। দাদাকে হাউই ছাড়তে দেখে টেঁপির বেশ একটু ভয় হয়েছিল, পাছে দাদা হাউইয়ের তেজে উড়ে যায়, কি পুড়ে যায়, কিম্বা সাংঘাতিক একটা কিছু ঘটে যায়। কিন্তু দাদার ভরসা দেখে তারও একটু ভরসা হল। \r\n    </p>\r\n    <p>\r\n        দাদা হাউইটিকে হাতে নিয়ে, একটুখানি বাঁকিয়ে ধরে বিজ্ঞের মতো বলতে লাগল, "এই সল্‌‌তের মতো দেখছিস্‌‌ এইখানে আগুন ধরাতে হয়। সল্‌‌তেটা জ্বলতে জ্বলতে যেই হাউই ভস্‌‌ করে ওঠে অমনি ঠিক সময়টি বুঝে&mdash; এই এম্নি করে হাউইটিকে ছেড়ে দিতে হয়। এইখানেই হচ্ছে আসল বাহাদুরি। কাল দেখলি তো, প্রকাশটা কি রকম আনাড়ীর মতো করেছিল। হাউই জ্বলতে না জ্বলতে ফস করে ছেড়ে দিচ্ছিল। সেইজন্য‌‌ই হাউইগুলো আকাশের দিকে না উঠে নিচু হতে এদিক ওদিক বেঁকে যাচ্ছিল।" \r\n    </p>\r\n    <p>\r\n        ফ্যাঁস&mdash;ফোঁস্‌‌&mdash;ছররর! এত শিগ্‌‌গির হাউইয়ে আগুন ধরে যায় সেটা দাদার খেয়ালেই ছিল না, দাদা তখনো ঘাড় বাঁকিয়ে হাসি হাসি মুখ করে নিজের বাহাদুরির কথা ভাবছেন। কিন্তু হাসিটি না  ফুরাতেই  হাউই যখন ফ্যাঁস্‌‌ করে উঠল তখন সেই সঙ্গে দাদার মুখ থেকেও হাঁউ-মাউ গোছের একটা বিকট শব্দ আপনা থেকেই বেরিয়ে এল। আর তার পরেই দাদা যে একটা লম্ফ দিলেন, তার ফলে দেখা গেল মাঝখানে চিৎ‌পাত হয়ে অত্যন্ত আনাড়ীর মতো তিনি হাত পা ছুঁড়ছেন। কিন্তু তা দেখবার অবসর টেঁপিদের হয়নি। কারণ দাদার চিৎ‌কার আর লম্ফভঙ্গীর সঙ্গে সঙ্গে তারাও কান্নার সুর চড়িয়ে বাড়ির ভিতরদিকে রওনা হয়েছিল।\r\n    </p>\r\n    <p>\r\n        কান্না-টান্না থামলে পর রাজুমামা যখন দাদার কান ধরে তাকে ভিতরে নিয়ে এলেন, তখন দেখা গেল যে, দাদার হাতের কাছে ফোস্‌‌কা পড়ে গেছে আর গায়ের দু-তিন জায়গায় পোড়ার দাগ লেগেছে। কিন্তু তার জন্য দাদার দুঃখ নেই, তার আসল দুঃখ এই যে টেঁপির কাছে তার বিদ্যেটা এমন ভাবে ফাঁস হয়ে গেল। রাজুমামা চলে যেতেই সে হাতে মলম মাখতে মাখতে বলতে লাগল, কোথাকার কোন দোকান থেকে হাউই কিনে এনেছে&mdash; ভালো করে মশলা মেশাতেও জানে না। বিষ্টু পাঠকের দোকান থেকে হাউই আনলেই হত। বার বার বলেছি&mdash; রাজু মামা হাউই চেনে না, তবু তাকেই দেবে হাউই কিনতে।\r\n    </p>\r\n    <p>\r\n        তারপর সে টেঁপিকে আর ভোলা, ময়না আর খুক্‌‌নুকে বেশ করে বুঝিয়ে দিল যে, সে যে চেঁচিয়েছিল আর লাফ দিয়েছিল, সেটা ভয়ে নয়, হঠাৎ‌ ফুর্তির চোটে। \r\n    </p>', '', '2013-03-19 00:00:00', '2014-08-21 16:53:01', 305, '');
INSERT INTO `articles` (`id`, `category_id`, `top_image`, `title`, `article_slug`, `first_line`, `body`, `audio`, `added_on`, `modified_on`, `views`, `sc_track_id`) VALUES
(126, 2, '', 'যতীনের জুতো', 'যতীনের-জুতো', '', '<p>\r\n        যতীনের নতুন জুতো কিনে এনে তার বাবা বললেন, "এবার যদি অমন করে জুতো নষ্ট কর তবে ওই ছেঁড়া জুতোই পরে থাকবে।"\r\n    </p>\r\n    <p>\r\n        যতীনের চটি লাগে প্রতিমাশে একজোড়া। ধুতি তার দুদিন যেতে না যেতেই ছিঁড়ে যায়। কোনো জিনিসে তার যত্ন নেই। ব‌‌ইগুলো সব মলাট ছেঁড়া, কোণ দুমড়ান, শ্লেটটা উপর থেকে নিচ পর্যন্ত ফাটা। শ্লেটের পেন্‌‌সিলগুলি সর্বদাই তার হাত থেকে পড়ে যায়, কাজেই ছোট ছোট টুক্‌‌রো টুক্‌‌রো। আরেকটা তার মন্দ অভ্যাস ছিল লেড্‌‌পেন্‌‌সিলের গোড়া চিবানো। চিবিয়ে চিবিয়ে তার পেন্‌‌সিলের কাঠটা বাদামের খোলার মতো খেয়ে গিয়েছিল। তাই দেখে ক্লাশের মাস্টারমশাই বলতেন, "তুমি কি বাড়িতে ভাত খেতে পাওনা?"\r\n    </p>\r\n    <p>\r\n        নতুন চটি পায়ে দিয়ে প্রথম দিন যতীন খুব সাবধানে র‌‌ইল, পাছে ছিঁড়ে যায়। সিঁড়ি দিয়ে আস্তে আস্তে নামে, চৌকাঠ ডিঙ্গোবার সময় সাবধানে থাকে, যাতে না ঠোক্কর খায়। কিন্তু এই পর্যন্ত‌‌ই। দুদিন পর আবার যেই কে সেই। চটির মায়া ছেড়ে দুড়্‌‌ দুড়্‌‌ করে সিঁড়ি দিয়ে নামা, যেতে যেতে দশবার হোঁচট খাওয়া, সব আরম্ভ হল। কাজেই একমাস যেতে না যেতে চটির একটা পাশ একটু হাঁ করল। মা বললেন, "ওরে, এই বেলা মুচি ডেকে সেলাই করা, না হলে একেবারে যাবে।" কিন্তু মুচি ডাকা হয় না। চটির হাঁ বেড়ে চলে।\r\n    </p>\r\n    <p>\r\n        একটা জিনিসের যতীন খুব যত্ন করত। সেটি হচ্ছে তার ঘুড়ি। যে ঘুড়িটা তার মনে লাগত সেটিকে সে সযত্নে জোড়াতাড়া দিয়ে যতদিন সম্ভব টিকিয়ে রাখত। খেলার সময়টা সে প্রায় ঘুড়ি উড়িয়েই কাটিয়ে দিত। এই ঘুড়ির জন্য কত সময়ে তাকে তাড়া খেতে হত। ঘুড়ি ছিঁড়ে গেলে সে রান্নাঘরে গিয়ে উৎ‌পাত করত তার আঠা চাই বলে। ঘুড়ির লেজ লাগাতে কিংবা সুতো কাটতে কাঁচি দরকার হলে সে মায়ের সেলাইয়ের বাক্স ঘেঁটে রেখে দিত। ঘুড়ি উড়াতে আরম্ভ করলে তার খাওয়া-দাওয়া মনে থাকত না। সেদিন যতীন ইস্কুল থেকে বাড়িতে ফিরছে ভয়ে ভয়ে। গাছে চড়তে গিয়ে তার নতুন কাপড়খানা অনেকখানি ছিঁড়ে গেছে। ব‌‌ই রেখে চটি পায়ে দিতে গিয়ে দেখে, চটিটা এত ছিঁড়ে গেছে যে আর পরাই মুশকিল। কিন্তু সিঁড়ি নামবার সময় তার সে কথা মনে র‌‌ইল না, সে দু-তিন সিঁড়ি ডিঙ্গিয়ে নামতে লাগল। শেষকালে চটির হাঁ বেড়ে বেড়ে, সমস্ত দাঁত বের করে ভেংচাতে লাগল ! যেমনি সে শেষ তিনটা সিঁড়ি গেল, আর ছেঁড়া চটি তাকে নিয়ে সাঁই সাঁই করে শূন্যের উপর দিয়ে কোথায় যে নিয়ে চলল তার ঠিকঠিকানা নেই।\r\n    </p>\r\n    <p>\r\n        ছুটতে ছুটতে ছুটতে ছুটতে চটি যখন থামল, তখন যতীন দেখল সে কোন অচেনা দেশে এসে পড়েছে। সেখানে চরদিকে অনেক মুচি বসে আছে। তারা যতীনকে দেখে কাছে এল, তারপর তার পা থেকে ছেঁড়া চটিজোড়া খুলে নিয়ে সেগুলোকে যত্ন করে ঝাড়তে লাগল। তাদের মধ্যে একজন মাতব্বর গোছের, সে যতীনকে বলল, "তুমি দেখছি ভারি দুষ্টু। জুতোজোড়ার এমন দশা করেছ? দেখ দেখি, আর একটু হলে বেচারিদের প্রাণ বেরিয়ে যেত।" যতীনের ততক্ষণে একটু সাহস হয়েছে। সে বলল, "জুতোর আবার প্রাণ থাকে নাকি?" মুচিরা বলল, "তা না তো কি? তোমরা বুঝি মনে কর, তোমরা যখন জুতো পায়ে দিয়ে জোরে ছোটো তখন ওদের লাগে না? খুব লাগে। লাগে বলেই তো ওরা মচ্‌‌মচ্‌‌ করে। যখন তুমি চটি পায়ে দিয়ে দুড়্‌‌দুড়্‌‌ করে সিঁড়ি দিয়ে ওঠানামা করেছিলে আর তোমার পায়ের চাপে ওর পাশ কেটে গিয়েছিল, তখন কি ওর লাগেনি? খুব লেগেছিল। সেইজন্যেই ও তোমাকে আমাদের কাছে নিয়ে এসেছে। যত রাজ্যের ছেলেদের জিনিসপত্রের ভার আমাদের উপর। তারা সে সবের অযত্ন করলে আমরা তাদের শিক্ষা দেই।" মুচি যতীনের হাতে ছেঁড়া চটি দিয়ে বলল, "নাও, সেলাই কর।" যতীন রেগে বলল, "আমি জুতো সেলাই করি না, মুচিরা করে।" মুচি একটু হেসে বলল, "একি তোমাদের দেশ পেয়েছ যে করব না বললেই হল? এই ছুঁচ সুতো নাও, সেলাই কর।" যতীনের রাগ তখন কমে এসেছে, তার মনে ভয় হয়েছে। সে বলল, "আমি জুতো সেলাই করতে জানি না।" মুচি বলল, "আমি দেখিয়ে দিচ্ছি, সেলাই তোমাকে করতেই হবে।" যতীন ভয়ে ভয়ে জুতো সেলাই করতে বসল। তার হাতে ছুঁচ ফুটে গেল, ঘাড় নিচু করে থেকে থেকে ঘাড়ে ব্যথা হয়ে গেল, অনেক কষ্টে সারাদিনে একপাটি চটি সেলাই হল। তখন সে মুচিকে বলল, "কাল অন্যটা করব। এখন ক্ষিদে পেয়েছে।" মুচি বলল, "সে কি ! সব কাজ শেষ না করে তুমি খেতেও পাবে না, ঘুমোতেও পাবে না। একপাটি চটি এখনও বাকি আছে। তারপর তোমাকে আস্তে আস্তে চলতে শিখতে হবে, যেন আর কোনো জুতোর উপর অত্যাচার না কর। তারপর দরজীর কাছে গিয়ে ছেঁড়া কাপড় সেলাই করতে হবে। তরপর আর কি কি জিনিস নষ্ট কর দেখা যাবে।"\r\n    </p>\r\n    <p>\r\n        যতীনের তখন চোখ দিয়ে জল গড়িয়ে পড়ছে। সে কাঁদতে কাঁদতে কোনো রকমে অন্য চটিটা সেলাই করল। ভাগ্যে এ পাটি বেশি ছেঁড়া ছিল না। তখন মুচিরা তাকে একটা পাঁচতলা উঁচু বাড়ির কাছে নিয়ে গেল। সে বাড়িতে সিঁড়ি বরাবর একতলা থেকে পাঁচতলা পর্যন্ত উঠে গেছে। তারা যতীনকে সিঁড়ির নিচে দাঁড় করিয়ে দিয়ে বলল, "যাও, একেবারে পাঁচতলা পর্যন্ত উঠে যাও, আবার নেমে এস। যতীন পাঁচতলা পর্যন্ত উঠে গিয়ে লেমে এল। সে নিচে আসলে মুচিরা বলল, "হয়নি। তুমি তনবার দুটো করে সিঁড়ি এক সঙ্গে উঠেছ, পাঁচবার লাফিয়েছ, দুবার তিনটে করে সিঁড়ি ডিঙ্গিয়েছ। আবার ওঠ। মনে থাকে যেন, একটুও লাফাবে না, একটাও সিঁড়ি ডিঙ্গোবে না।" এতটা সিঁড়ি উঠে নেমে যতীন বেচারীর পা টনটন করছিল। সে এবার আস্তে আস্তে উপরে উঠল, আস্তে আস্তে নেমে এল। তারা বলল, আচ্ছা, এবার মন্দ হয়নি। তাহলে চল দরজীর কাছে।" \r\n    </p>\r\n    <p>\r\n        এই বলে তারা তাকে আর একটা মাঠে নিয়ে এল, সেখানে খালি দরজীরা বসে বসে সেলাই করছে। যতীনকে দেখেই তারা জিগ্‌‌গেস করল, "কি? কি ছিঁড়েছে?" মুচিরা উত্তর দিল, "নতুন ধুতিটা, দেখ কতটা ছিঁড়ে ফেলেছে।" দরজীরা মাথা নেড়ে বলল, "বড় অন্যায়, বড় অন্যায় ! শিগ্‌‌গির সেলাই কর।" যতীনের আর ''না'' বলবার সাহস হল না। সে ছুঁচ-সুতো নিয়ে ছেঁড়া কাপড় জুড়তে বসে গেল। সবে মাত্র দুফোঁড় দিয়েছে অমনি দরজীরা চেঁচিয়ে উঠল, "ওকে কি সেলাই বলে? খোল, খোল।" অমনি করে, বেচরি যতবার সেলাই করে, ততবার তারা বলে," খোল খোল !" শেষে সে একেবারে কেঁদে ফেলে বলল, আমার বড্ড খিদে পেয়েছে।" তাতে দরজীরা হাসতে হাসতে বলল, "খিদে পেয়েছে? তা তোমার খাবার জিনিস তো আমাদের কাছে ঢের আছে" , এই বলে  তারা তাদের কাপড়ে দাগ দেবার পেন্‌‌সিল কতগুলো এনে দিল। "তুমি তো পেন্‌‌সিল চিবোতে ভালোবাস, এইগুলো চিবিয়ে খাও, আর কিছু আমাদের কাছে নেই।"\r\n    </p>\r\n    <p>\r\n        এই বলে তারা যার যার কাজে চলে গেল। শ্রন্ত ক্লান্ত হয়ে যতীন কাঁদতে কাঁদতে মাটিতে শুয়ে পরল। এমন সময় আকাশে বোঁ বোঁ করে কিসের শব্দ  হল, আর যতীনের তালি-দেওয়া সাধের ঘুড়িটা গোঁৎ‌ খেয়ে এসে তার কোলের উপর পড়ল। ঘুড়িটা ফিস্‌‌ফিস্‌‌ করে বলল, "তুমি আমাকে যত্ন করেছ, তাই আমি তোমাকে সাহায্য করতে এসেছি। শিগ্‌‌গির আমার লেজটা ধর।" যতীন তাড়াতাড়ী ঘুড়ির লেজটা ধরল। ঘুড়িটা অমনি তাকে নিয়ে সোঁ করে আকাশে উঠে গেল। সেই শব্দ শুনে দরজীরা বড় বড় কাঁচি নিয়ে ছুটে এল ঘুড়ির সুতোটা কেটে দিতে। হঠাৎ‌ ঘুড়ি আর যতীন জড়াজড়ি করে নিচের দিকে পড়তে লাগল। পড়তে পড়তে যেই মাটিটা ধাঁই করে যতীনের মাথায় লাগল, অমনি সে চমকে উঠল। ঘুড়িটা কি হল কে জানে ! যতীন দেখল সে সিঁড়ির নিচে শুয়ে আছে, আর তার মাথায় ভয়ানক বেদনা হয়েছে। \r\n    </p>\r\n    <p>\r\n        কিছুদিন ভুগে যতীন সেরে উঠল। তার মা বলতেন, "আহা , সিঁড়ি থেকে পড়ে গিয়ে, এই ভোগানিতে বাছা আমার বড় দুর্বল হয়ে গেছে। সে স্ফুর্তি নেই, সে লাফিয়ে ঝাঁপিয়ে চলা নেই, কিছুই নেই। তা ন‌‌ইলে এক জোড়া জুতো চার মাস যায়?" \r\n    </p>\r\n    <p>\r\n        আসল কথা&mdash; যতীন এখনও সেই মুচিদের আর দরজীদের কথা ভুলতে পারেনি। \r\n    </p>', '', '2013-03-19 00:00:00', '2014-08-21 16:53:01', 412, ''),
(127, 2, '', 'ডিটেক্‌‌টিভ', 'ডিটেক্‌‌টিভ', '', '<p>\r\n        জলধরের মামা পুলিশের চাকরি করেন, আর তার পিশেমশাই লেখেন ডিটেক্‌‌টিভ উপন্যাস। সেইজন্য জলধরের বিশ্বাস যে, চোর-ডাকাত জাল-জুয়াচোর জব্দ করবার সব রকম সঙ্কেত সে যেমন জানে এমনি তার মামা আর পিশেমশাই ছাড়া কেউ জানে না। কারও বাড়িতে চুরি-টুরি হলে জলধর সকলের আগে সেখানে হাজির হয়। আর, কে চুরি করল, কি করে চুরি হল, সে থাকলে এমন অবস্থায় কি করত, এ-সব বিষয়ে খুব বিজ্ঞের মত কথা বলতে থাকে। যোগেশবাবুর বাড়িতে যখন বাসন চুরি হল, তখন জলধর তাদের বললে, "আপনার একটু সবধান হতে জানেন না, চুরি তো হবেই। দেখুন তো ভাঁড়ার ঘরের পাশেই অন্ধকার গলি। তার উপর জানালার গরাদ নেই। একটু সেয়ানা লোক হলে এখান দিয়ে বাসন নিয়ে পালাতে কতক্ষণ? আমাদের বাড়িতে ওসব হবার জো নেই। আমি রামদিনকে ব''লে রেখেছি, জানলার গায়ে এমনভাবে বাসনগুলো ঠেকিয়ে রাখবে যে জানালা খুলতে গেলেই বাসনপত্র সব ঝন্‌‌ঝন্‌‌ করে মাটিতে পড়বে। চোর জব্দ করতে ও-সব কায়দা জানতে হয়।" সে সময়ে আমরা সকলেই জলধরের বুদ্ধির খুব প্রশংসা করেছিলাম, কিন্তু পরের দিন যখন শুনলাম সেই রাত্রে জলধরের বাড়িতে মস্ত চুরি হয়ে গেছে, তখন মনে হল, আগের দিন অতটা প্রশংসা করা উচিত হয়নি। জলধর কিন্তু তাতেও কিছুমাত্র দমেনি। বলল, "ঐ আহাম্মক রামদিনটার বোকামিতে সব মাটি হয়ে গেল। যাক্‌‌, আমার জিনিস চুরি করে তাকে আর হজম করতে হবে না। দিন দুচার যেতে দাও না।"\r\n    </p>\r\n    <p>\r\n        দু মাস গেল, চার মাস গেল, চোর কিন্তু ধরাই পড়ল না। চোরের উপদ্রবের কথা আমরা সবাই ভুলে গেছি, এমন সময় হঠাৎ‌ আমাদের ইস্কুলে আবার চুরির হাঙ্গামা শুরু হল। ছেলেরা অনেকে টিফিন নিয়ে আসে, তা থেকে খাবার চুরি যেতে লাগল। প্রথম দিন রামপদর খাবার চুরি যায়। সে বেঞ্চির উপর খানিকটা রাবড়ি আর লুচি রেখে হাত ধুয়ে আসতে গেছে&mdash; এর মধ্যে কে এসে লুচিটুচি বেমালুম খেয়ে গিয়েছে। তারপর ক্রমে ক্রমে আরো দু-চারটি ছেলের খাবার চুরি হল। তখন আমরা জলধরকে বললাম, "কি হে ডিটেক্‌‌টিভ! এই বেলা যে তোমার চোর-ধরা বুদ্ধি খোলে না, তার মানেটা কি বল দেখি?" জলধার বলল, "আমি কি আর বুদ্ধি খাটাচ্ছি না? সবুর কর না।" তখন সে খুব সাবধানে আমাদের কানে কানে এ কথা জানিয়ে দিল যে, ইস্কুলের যে নতুন ছোকরা বেয়ারা এসেছে তাকেই সে চোর বলে সন্দেহ করে। কারণ, সে আসবার পর থেকে চুরি আরম্ভ হয়েছে। আমরা সবাই সেদিন থেকে তার উপর চোখ রাখতে শুরু করলাম। কিন্তু দুদিন না যেতেই আবার চুরি ! পাগলা দাশু বেচারা বাড়ি থেকে মাংসের চপ এনে টিফিন ঘরের বেঞ্চের তলায় লুকিয়ে রেখেছিল, কে এসে তার আধখানা খেয়ে বাকিটুকু ধুলোয় ফেলে নষ্ট করে দিয়েছে। পাগলা তখন রাগের চোটে চীৎ‌কার করে গাল দিয়ে ইস্কুল বাড়ি মাথায় করে তুলল। আমরা সবাই বললাম, "আরে চুপ্‌‌ চুপ্‌‌, অত চেঁচাসনে। তা হলে চোর ধরা পড়বে কি করে?" কিন্তু পাগলা কি সে-কথা শোনে? তখন তাকে জলধর বুঝিয়ে বলল, "আর দুদিন সবুর কর, ঐ নতুন ছোকরাটাকে আমি হাতে হাতে ধরিয়ে দিচ্ছি&mdash; এ সমস্ত ওর‌‌ই কারসাজি।" শুনে দাশু বলল, "তোমার যেমন বুদ্ধি ! ওরা হল পশ্চিমা ব্রাহ্মণ, ওরা আবার মাংস খায় নাকি? দারোয়ানজীকে জিগ্‌‌গেস কর তো?" সত্যি‌‌ই আমাদের তো সে খেয়াল হয়নি ! ছোকরা তো কতদিন রুটি পাকিয়ে খায়, ক‌‌ই একদিনও তো ওকে মাছ-মাংস খেতে দেখি না। দাশু পাগলা হোক আর যাই হোক, তার কথাটা সবাইকে মানতে হল। জলধার কিন্তু অপ্রস্তুত হবার ছেলেই নয়। সে এক গাল হেসে বলল, আমি ইচ্ছে করে তোদের ভুল বুঝিয়েছিলাম। আরে, চোরকে না ধরা পর্যন্ত কি কিছু বলতে আছে, কোনো পাকা ডিটেক্‌‌টিভ ও-রকম করে না। আমি মনে মনে যাকে চোর বলে ধরেছি, সে আমি‌‌ই জানি।"\r\n    </p>\r\n    <p>\r\n        তারপর কদিন আমরা খুব হুঁশিয়ার ছিলাম, আট-দশদিন আর চুরি হয়নি। তখন জলধর বললে, "তোমরা গোলমাল করেই তো সব মাটি করলে। চোরটা টের পেয়ে গেল যে আমি তার পেছনে লেগেছি। আর কি সে চুরি করতে সাহস পায়? তবু ভাগ্যিস তোমাদের কাছে আসল নামটা ফাঁস করিনি।" কিন্তু সেই দিন‌‌ই আবার শোনা গেল, স্বয়ং হেডমাস্টার মশাইয়ের ঘর থেকে তার টিফিন খাবার চুরি হয়ে গেছে। আমরা বললাম, "ক‌‌ই হে? চোর না তোমার ভয়ে চুরি করতে পারছিল না? তার ভয় ঘুচে গেল দেখছি।"\r\n    </p>\r\n    <p>\r\n        তারপর দুদিন ধরে জলধরের মুখে আর হাসি দেখা গেল না। চোরের ভাবনা ভেবে ভেবে তার পড়াশুনা এম্নি ঘুলিয়ে গেল যে পণ্ডিত মহাশয়ের ক্লাশে সে আরেকটু হলেই মার খেত আর কি ! দুদিন পর সে আমাদের সকলকে ডেকে একত্র করল, আর বলল, তার চোর ধরবার বন্দোবস্ত ঠিক হয়েছে। টিফিনের সময় সে একটা ঠোঙায় করে সরভাজা, লুচি আর আলুর দম রেখে চলে আসবে। তারপর কেউ যেন সেদিকে না যায়। ইস্কুলের বাইরে থেকে লুকিয়ে টিফিনের ঘরটা দেখা যায়। আমরা কয়েকজন বাড়ি যাবার ভান করে সেখানে থাকব। আর কয়েকজন থাকবে উঠোনের পশ্চিম কোণের ছোট ঘরটাতে। সুতরাং, চোর যেদিক থেকেই আসুক, টিফিন ঘরে ঢুকতে গেলেই তাকে দেখা যাবে।\r\n    </p>\r\n    <p>\r\n        সেদিন টিফিনের ছুটি পর্যন্ত কারও আর পড়ায় মন বসে না। সবাই ভাবছে কতক্ষণে ছুটি হবে, আর চোর কতক্ষণে ধরা পড়বে। চোর ধরা পড়লে তাকে নিয়ে কি করা যাবে, সে বিষয়েও কথাবার্তা হতে লাগল। মাস্টারমশাই বিরক্ত হয়ে ধমকাতে লাগলেন, পরেশ আর বিশ্বনাথকে বেঞ্চির উপর দাঁড়াতে হল- কিন্তু সময়টা যেন কাটতেই চায় না। টিফিনের ছুটি হতেই জলধর তার খাবারের ঠোঙাটা টিফিন-ঘরে রেখে এলো। জলধর, আমি আর দশ-বারোজন উঠোনের কোণের ঘরটাতে র‌‌ইলাম, আর একদল ছেলে বাইরে জিমনাস্টিকের ঘরে লুকিয়ে থাকল। জলধর বলল, "দেখ চোরটা যে রকম সেয়ানা দেখছি, আর তার যে রকম সাহস, তাকে মারধর করা ঠিক হবে না। লোকটা নিশ্চয় খুব ষণ্ডা হবে। আমি বলি, সে যদি এদিকে আসে তাহলে সবাই মিলে তার গায়ে কালি ছিটিয়ে দেব আর চেঁচিয়ে উঠব। তাহলে দারোয়ান-টারোয়ান সব ছুটে আসবে। আর, লোকটা পালাতে গেলেও ঐ কালির চিহ্ন দেখে ঠিক ধরা যাবে।" আমাদের রামপদ ব''লে উঠল, "কেন? সে যে খুব ষণ্ডা হবে তার মানে কি? সে কিছু রাক্ষসের মতো খায় বলে তো মনে হয় না। যা চুরি করে নিচ্ছে সে তো কোনোদিন‌‌ই খুব বেশি নয়।" জলধর বলল, "তুমিও যেমন পণ্ডিত।  রাক্ষসের মতো খানিকটা খেলেই বুঝি ষণ্ডা হয়? তাহলে তো আমাদের শ্যামাদাসকেই সকলের চেয়ে ষণ্ডা বলতে হয়। সেদিন ঘোষেদের নেমন্তন্নে ওর খাওয়া দেখেছিলে তো ! বাপু হে, আমি যা বলেছি তার উপর ফোড়ন দিতে যেও না। আর তোমার যদি নেহাৎ‌ বেশি সাহস থাকে, তুমি গিয়ে চোরের সঙ্গে লড়াই কর। আমরা কেউ তাতে আপত্তি করব না। আমি জানি, এ-সমস্ত নেহাৎ‌ যেমন-তেমন চোরের সাধ্য নয়। আমর খুব বিশ্বাস, যে লোকটা আমাদের বাড়িতে চুরি করেছিল&mdash; এ-সব তার‌‌ই কাণ্ড!" \r\n    </p>\r\n    <p>\r\n        এমন সময় হঠাৎ‌ টিফিন-ঘরের বাঁ দিকের জানলাটা খানিকটা ফাঁক হয়ে গেল, যেন কেউ ভিতর থেকে ঠেলছে। তার পরেই শাদা মতন কি একটা ঝুপ করে উঠোনের মধ্যে লাফিয়ে পড়ল। আমরা চেয়ে দেখলাম, একটা মোটা হুলো বেড়াল&mdash; তার মুখে জলধরের সরভাজা ! তখন যদি জলধরের মুখখানা দেখতে, সে এক বিঘৎ‌ উঁচু হাঁ করে উঠোনের দিকে তাকিয়ে র‌‌ইল। আমরা জিগ্‌‌গেস করলাম, "কেমন হে ডিটেক্‌‌টিভ ! ঐ ষণ্ডা চোরটাই তো তোমার বাড়িতে চুরি করেছিল? তাহলে এখন ওকেই পুলিশে দি‌‌ই?"\r\n    </p>', '', '2013-03-19 00:00:00', '2014-08-21 16:53:01', 302, ''),
(128, 2, '', 'ব্যোমকেশের মাঞ্জা', 'ব্যোমকেশের-মাঞ্জা', '', '<p>\r\n        ''টোকিয়ো&mdash;কিয়োটো&mdash;নাগাসাকি&mdash;য়োকোহামা''&mdash; বোর্ডের উপর প্রকাণ্ড ম্যাপ ঝুলিয়ে হারাণচন্দ্র জাপানের প্রধান নগরগুলি দেখিয়ে যাচ্ছে। এর পরেই ব্যোমকেশের পালা, কিন্তু ব্যোমকেশের সে খেয়াল‌‌ই নেই। কাল বিকেলে ডাক্তারবাবুর ছোট্ট ছেলেটার সঙ্গে প্যাঁচ খেলতে গিয়ে তার দু-দুটো ঘুড়ি কাটা গিয়েছিল, সে কথাটা কিছুতেই আর ভুলতে পারছে না। তাই সে বসে বসে সুতোর জন্যে কড়া রকমের একটা মাঞ্জা তৈরির উপায় চিন্তা করছে। চীনে শিরিস লাগিয়ে, তার মধ্যে বোতলচুর আর কড়্‌‌কড়ে এমেরি পাউডার মিশিয়ে সুতোয় মাখালে পর কি রকম চমৎ‌কার মাঞ্জা হবে, সেই কথা ভাবতে ভাবতে উৎ‌সাহে তার দুই চোখ কড়িকাঠের দিকে গোল হয়ে উঠছে। মনে মনে মাঞ্জা দেওয়া ঘুড়ির সুতোটা সবে তাল গাছের আগা পর্যন্ত উঠে, আশ্চর্য কায়দায় ডাক্তারের ছেলের ঘুড়িটাকে কাটতে যাচ্ছে এমন সময়ে গম্ভীর গলায় ডাক পড়ল&mdash; "তারপর, ব্যোমকেশ এস দেখি।"\r\n    </p>\r\n    <p>\r\n        ঐ রকম ভীষণ উত্তেজনার মধ্যে, সুতো-মাঞ্জা, ঘুড়ির প্যাঁচ সব ফেলে আকাশের উপর থেকে ব্যোমকেশকে হঠাৎ‌ নেমে আসতে হল একেবারে চীন দেশের মধ্যিখানে। একে তো ও দেশটার সঙ্গে তার পরিচয় খুব বেশি ছিল না, তার উপর যা-ও দু-একটা চীনদেশী নাম সে জানত, ওরকম হঠাৎ‌ নেমে আসবার দরুন, সেগুলো‌‌ও তার মাথার মধ্যে কেমন বিচ্ছিরি রকম ঘণ্ট পাকিয়ে গেল। পাহাড়, নদী, দেশ, উপদেশের মধ্যে ঢুকতে না ঢুকতে বেচারা বেমালুম পথ হারিয়ে ফেলল। তার কেবল‌‌ই মনে হতে লাগল যে চীনদেশের চীনে শিরিষ, তাই দিয়ে হয় মাঞ্জা। মাস্টারমশাই দু-দুবার তাড়া দিয়ে যখন তৃতীয়বার চড়া গলায় বললেন, "চীন দেশের নদী দেখাও" তখন বেচারা একেবারেই দিশেহারা আর মরিয়া মতন হয়ে বললে- "সাংহাই।" সাংহাই বলবার আর কোনো কারণ ছিল না, বোধহয় তার সেজমামার যে স্ট্যাম্পের সংগ্রহের খাতা আছে তার মধ্যে ঐ নামটাকে সে পেয়ে থাকবে- বিপদের ধাক্কায় হঠাৎ‌ কেমন করে ঐটেই তার মুখ দিয়ে বেরিয়ে পড়েছে।\r\n    </p>\r\n    <p>\r\n        গোটা দুই চড়-চাপড়ের পর ব্যোমকেশবাবু তাঁর কানের উপর মাস্টারমশায়ের প্রবল আকর্ষণ অনুভব করে, বিনা আপত্তিতে বেঞ্চের উপর আরোহন করলেন। কিন্তু কানদুটো জুড়োতে না জুড়োতেই মনটা তার খেই-হারানো ঘুড়ির পিছনে উধাও হয়ে, আবার সুতোর মাঞ্জা তৈরি করতে বসল। সারাটা দিন বকুনি খেয়েই তার সময় কাটল, কিন্তু এর মধ্যে কত উঁচুদরের মাঞ্জা-দেওয়া সুতো তৈরি হল আর কত যে ঘুড়ি গণ্ডায় গণ্ডায় কাটা পড়ল সে জানে কেবল ব্যোমকেশ।\r\n    </p>\r\n    <p>\r\n       বিকেলবেলায় সবাই যখন বাড়ি ফিরছে, তখন ব্যোমকেশ দেখল, ডাক্তারের ছেলেটা দোকানের সামনে দাঁড়িয়ে মস্ত একটা লাল রঙের ঘুড়ি কিনছে। দেখে ব্যোমকেশ বন্ধু পাঁচকড়িকে বলল, "দেখেছিস, পাঁচু, আমাদের দেখিয়ে দেখিয়ে আবার ঘুড়ি কেনা হচ্ছে ! এ-সব কিন্তু নেহাত বাড়াবাড়ি। না হয় দুটো ঘুড়িই কেটেছিস বাপু, তার জন্য এত কি গিরিম্বাড়ি !" এই ব''লে সে পাঁচুর কাছে তার মাঞ্জা তৈরির মতলবটা খুলে বলল। শুনে পাঁচু গম্ভীর হয়ে বলল, "তা যদি বলিস, তুই আর মাঞ্জা তৈরি করে ওদের সঙ্গে পারবি ভেবেছিস? ওরা হল ডাক্তারের ছেলে, নানা রকম ওষুধ-মশলা জানে। এই তো সেদিন ওর দাদাকে দেখলুম, শানের উপর কি একটা আরক ঢেলে দিলে, আর ভস্‌‌ভস্‌‌ করে গ্যাঁজালের মতো তেজ বেরুতে লাগল। ওরা যদি মাঞ্জা বানায়, তা হলে কারু মাঞ্জার সাধ্যি নেই যে তার সঙ্গে পেরে ওঠে। " শুনে ব্যোমেকেশের মনটা কেমন দমে গেল। তার ধ্রুব এরকম বিশ্বাস হল যে, ডাক্তারের ছেলেটা নিশ্চয় কোনো আশ্চর্য রকম মাঞ্জার খবর জানে। তা ন‌‌ইলে ব্যোমকেশের চাইতেও চার বছরের ছোট হয়ে, সে কেমন করে তার ঘুড়ি কাটল? ব্যোমকেশ স্থির করল, যেমন করে হোক ওদের বাড়ির মাঞ্জা খানিকটা যোগার করতেই হবে। সেটা একবার আদায় করতে পারলে, তারপর সে ডাক্তারের ছেলেকে দেখে নেবে। \r\n    </p>\r\n    <p>\r\n        বাড়ি গিয়ে তাড়াতাড়ি জলখাবার সেরে নিয়ে ব্যোমকেশ দৌড়ে গেল ডাক্তারবাবুর ছেলেদের সঙ্গে আলাপ করতে। সেখানে গিয়ে দেখে কি, কোণের বারান্দায় বসে সেই ছোট্ট ছেলেটা একটা ডাক্তারি থলের মধ্যে কি যেন মশলা ঘুঁটছে। ব্যোমকেশকে দেখে সে একটা চৌকির তলায় সব লুকিয়ে ফেলে সেখান থেকে সরে পড়ল। ব্যোমকেশ মনে মনে বললে, ''বাপু হে ! এখন আর লুকিয়ে করবে কি? তোমার আসল খবর আমি পেয়েছি''&mdash; এই ব''লে সে এদিক ওদিক তাকিয়ে দেখলে, কোথাও কেউ নেই। একবার সে ভাবল, কেউ আস্‌‌লে এর একটুখানি চেয়ে নেব। আবার মনে হল, কি জানি চাইলে যদি না দেয়? তারপর ভাবলে, দূর ! ভারি তো জিনিস তা আবার চাইবার দরকার কি? এই একটুকু মাঞ্জা হলেই প্রায় দুশো গজ সুতোয় শান দেওয়া হবে। এই ভেবে সে চৌকির তলা থেকে এক খাবলা মশলা তুলে নিয়েই এক দৌড়ে বাড়ি এসে হাজির।\r\n    </p>\r\n    <p>\r\n       আর কি তখন দেরি সয়? দেখতে দেখতে দক্ষিণের বারান্দা জুড়ে সুতো খাটিয়ে, মহা উৎ‌সাহে মাঞ্জা দেওয়া শুরু হল। যাই বল, মাঞ্জাটা কিন্তু ভারি অদ্ভুত- ক‌‌ই, তেমন কড়কড় করছে না তো। বোধ হয়, খুব মিহি গুড়োর তৈরি- আর কালো কাচের গুঁড়ো। দুঃখের বিষয়, বেচারার কাজটা শেষ না হতেই সন্ধ্যা হয়ে এল, আর তার বড়দা এসে বললে, "যা, যা ! আর সুতো পাকাতে হবে না, এখন পড়গে যা।" \r\n    </p>\r\n    <p>\r\n        সে রাত্তিরে ব্যোমকেশের ভালো করে ঘুম‌‌ই হল না। সে স্বপ্ন দেখল যে, ডাক্তারের ছেলেটা হিংসে করে তার চমৎ‌কার সুতোয় জল ফেলে সব নষ্ট করে দিয়েছে। সকাল হতে না হতেই ব্যোমকেশ দৌড়ে গেল তার সুতোর খবর নিতে। কিন্তু গিয়েই দেখে, কে এক বুড়ো ভদ্রলোক ঠিক বারান্দার দরজার সামনে বসে তার দাদার সঙ্গে গল্প করছেন, তামাক খাচ্ছেন। ব্যোমকেশ ভাবলে, দেখ তো কি অন্যায়! এর মধ্যে থেকে এখন সুতোটা আনি কেমন করে? যাহোক, অনেক্ষণ ইতস্তত করে সে খুব সাহসের সঙ্গে গিয়ে, চট করে তার সুতো খুলে নিয়ে আসছে&mdash; এমন সময়, হঠাৎ‌ কাশতে গিয়ে বুড়ো লোকটির কল্‌‌কে থেকে খানিকটা টিকে গেল মাটিতে পড়ে। বৃদ্ধ তখন ব্যস্ত হয়ে হাতের কাছে কিছু না পেয়ে, ব্যোমকেশের সেই মাঞ্জা মাখানো কাগজটা দিয়ে টিকেটাকে তুলতে গেলেন।\r\n    </p>\r\n    <p>\r\n        সর্বনাশ ! যেমন টিকের উপর কাগজ ছোঁয়ানো, অমনি কিনা ভস্‌‌ভস্‌‌ করে কাগজ জ্বলে উঠে ভদ্রলোকের আঙুল-টাঙুল পুড়ে, বারান্দার বেড়ায় আগুন-টাগুন লেগে এক হুলুস্থুল কাণ্ড ! অনেক চেঁচামেচি ছুটোছুটি আর জল ঢালাঢালির পর যখন আগুনটা নিভে এল, আর ভদ্রলোকের আঙুলের ফোস্কায় মলম দেয়া হল, তখন তার দাদা এসে তার কান ধরে বললেন, "হতভাগা ! কি রেখেছিলি কাগজের মধ্যে বল্‌‌ তো?" ব্যোমকেশ কাঁদ-কাঁদ হয়ে বললে, "কিচ্ছু তো রাখিনি, খালি সুতোর মাঞ্জা রেখেছিলাম। " দাদা তার কৈফিয়ৎ‌টাকে নিতান্ত‌‌ই আজগুবি মনে করে, "আবার এয়ার্কি হচ্ছে?" ব''লে বেশ দু-চার ঘা কষিয়ে দিলেন। বেচারা ব্যোমকেশ এই ব''লে তার মনকে খুব খানিকটা সান্ত্বনা দিল যে, আর যাই হোক, তার সুতোটুকু রক্ষা পেয়েছে। ভাগ্যিস সে সময়মতো খুলে এনেছিল, ন‌‌ইলে তার সুতো‌‌ও যেত, পরিশ্রমও নষ্ট হত।\r\n    </p>\r\n    <p>\r\n        বিকেলে সে বাড়ি এসেই চটপট ঘুড়ি আর লাটাই নিয়ে ছাতের উপর উঠল। মনে মনে বলল, ''ডাক্তারের পো আজ একবার আসুক না, দেখিয়ে দেব প্যাঁচ খেলাটা কাকে বলে।'' এমন সময়ে পাঁচকড়ি এসে বড় বড় চোখ করে বললে, "শুনেছিস্‌‌?" ব্যোমকেশ বললে, "না- কি হয়েছে?" পাঁচু বললে, "ওদের সেই ছেলেটাকে দেখে এলুম, সে নিজে নিজে দেশলাইয়ের মশলা বানিয়েছে, আর চমৎ‌কার লাল নীল দেশলাই তৈরি করছে।" ব্যোমকেশ হঠাৎ‌ লাটাই-টাটাই রেখে, এত বড় হাঁ করে জিগ্‌‌গেস করলে, "দেশলাই কিরে ! মাঞ্জা বল?" শুনে পাঁচু বেজায় চটে গেল, "বলছি লাল নীল আলো জ্বলছে, তবু বলবে মাঞ্জা, আচ্ছা গাধা যা হোক !"\r\n    </p>\r\n    <p>\r\n        ব্যোমকেশ কোনো জবাব না দিয়ে, দেশলাইয়ের মশলা-মাখানো সুতোটার দিকে ফ্যাল্‌‌ ফ্যাল্‌‌ করে তাকিয়ে র‌‌ইল। সেই সময় ডাক্তারের বাড়ি থেকে লাল রঙের ঘুড়ি উড়ে এসে, ঠিক ব্যোমকেশের মাথার উপর ফরফর করে তাকে যেন ঠাট্টা করতে লাগল। তখন সে তাড়াতাড়ি নিজের ঘরে বিছানায় গিয়ে শুয়ে পড়ল। বলল, "আমার অসুখ করেছে। "\r\n    </p>', '', '2013-03-19 00:00:00', '2014-08-21 16:53:01', 241, '');
INSERT INTO `articles` (`id`, `category_id`, `top_image`, `title`, `article_slug`, `first_line`, `body`, `audio`, `added_on`, `modified_on`, `views`, `sc_track_id`) VALUES
(129, 2, '', 'জগ্যিদাসের মামা', 'জগ্যিদাসের-মামা', '', '<p>\r\n        তার আসল নামটি যজ্ঞদাস। সে প্রথম যেদিন আমাদের ক্লাশে এল পণ্ডিতমশাই তার নাম শুনেই ভ্রূকুটি করে বললেন, "যজ্ঞের আবার দাস কি? যজ্ঞেশ্বর বললে তবু না হয় বুঝি।" ছেলেটি বলল, "আজ্ঞে, আমি তো নাম রাখিনি, নাম রেখেছেন খুড়োমশাই।"\r\n    </p>\r\n    <p>\r\n        এই শুনে আমি একটু ফিক করে হেসে ফেলেছিলাম, তাই পণ্ডিতমশাই আমার দিকে তাকিয়ে বললেন, "বানান কর যজ্ঞদাস।" আমি থতমত খেয়ে বললাম, "বর্গীয় জ"- পণ্ডিতমশাই বললেন, "দাঁড়িয়ে থাক।" তারপর একটি ছেলে ঠিক বানান বললে পর তিনি আরেকজনকে বললেন, "সমাস কর।" সে তার সংস্কৃত বিদ্যা জাহির করে বললো, "যোগ্য শ্চেতি দাসশ্চসৌ।" পণ্ডিতমশাই তার কান ধরে বললেন, "বেঞ্চির উপর দাঁড়িয়ে থাক।"\r\n    </p>\r\n    <p>\r\n      দুদিন না যেতেই বোঝা গেল যে, জগ্যিদাসের আর কোনো বিদ্যে থাকুক আর নাই থাকুক আজগুবি গল্প বলবার ক্ষমতাটি তার অসাধারণ। একদিন সে ইস্কুলে দেরি করে এসেছিল, কারণ জিগ্‌‌গেস করাতে সে বলল, "রাস্তায় আসতে পঁচিশটা কুকুর হাঁ হাঁ করে আমায় তেড়ে এসেছিল। ছুটতে ছুটতে হাঁপাতে হাঁপাতে সেই কুণ্ডুদের বাড়ি পর্যন্ত চলে গিয়েছিলাম।" পঁচিশটা দূরের কথা, দশটা কুকুরও আমরা এক সঙ্গে চোখে দেখিনি, কাজেই কথাটা মাস্টারমশাইও বিশ্বাস করেননি। তিনি জিগ্‌‌গেস করলেন, "এত মিছে কথা বলতে শিখলে কার কাছে?" জগ্যিদাস বলল, "আজ্ঞে মামার কাছে।" সেদিন হেডমাস্টারের ঘরে জগ্যিদাসের ডাক পড়েছিল, সেখানে কি হয়েছিল আমরা জানি না, কিন্তু জগ্যিদাস যে খুশি হয়নি সেটা বেশ বোঝা গেল।\r\n    </p>\r\n    <p>\r\n       কিন্তু সত্যি হোক আর মিথ্যে হোক, তার গল্প বলার বাহাদুরি ছিল। সে যখন বড় বড় চোখ করে গম্ভীর গলায় তার মামাবাড়ির ডাকাত ধরার গল্প বলত, তখন বিশ্বাস করি আর না করি শুনতে শুনতে আমাদের মুখ আপনা হতেই হাঁ হয়ে আসত। জগ্যিদাসের মামার কথা আমাদের ভারি আশ্চর্য ঠেকত। তার গায়ে নাকি যেমন জোর তেমনি অসাধারণ বুদ্ধি। তিনি যখন ''রামভজন'' বলে চাকরকে ডাক দিতেন, তখন ঘর বাড়ি সব থরথর করে কেঁপে উঠত। কুস্তি বল, লাঠি বল, ক্রিকেট বল, সবটাতেই তাঁর সমান দখল। প্রথমটা আমরা বিশ্বাস করিনি, কিন্তু একদিন সে তার মামার ফটো এনে দেখাল। দেখলাম পালোয়ানের মতো চেহারা বটে ! এক-একবার ছুটি হত আর জগ্যিদাস তার মামার বাড়ি যেত, আর এসে সে সব গল্প বলত তা কাগজে ছাপবার মতো।  একদিন স্টেশনে আমার সঙ্গে জগ্যিদাসের দেখা, একটা গাড়ির মধ্যে মাথায় পাগড়ি বাঁধা  চমৎ‌কার জাঁদরেল চেহারার একটি কোন দেশী ভদ্রলোক বসে। আমি ইস্কুলে ফিরতে ফিরতে জগ্যিদাসকে জিগ্‌‌গেস করলাম, "ঐ পাগড়ি বাঁধা লোকটাকে দেখেছিলি?" জগ্যিদাস বলল, "ঐ তো আমার মামা।" আমি বললাম, "ফটোতে তো কালো দেখেছিলাম।" জগ্যিদাস বলল, "এবার সিম্‌‌লে গিয়ে ফরসা হয়ে এসেছেন।" আমি ইস্কুলে গিয়ে গল্প করলাম, "আজ জগ্যিদাসের মামাকে দেখে এলুম।" জগ্যিদাসও খুব বুক ফুলিয়ে মুখখানা গম্ভীর করে বলল, "তোমরা তো ভাই আমার কথা বিশ্বাস কর না। আচ্ছা, না হয় মাঝে মাঝে দুটো একটা গল্প ব''লে থাকি। তা ব''লে কি সব‌‌ই আমার গল্প। আমার জলজ্যান্ত মামাকে সুদ্ধ তোমরা উড়িয়ে দিতে চাও ?" এ-কথায় অনেকেই মনে মনে ল্জ্জা পেয়ে, ব্যস্ত হয়ে বারবার বলতে লাগল, "আমরা কিন্তু গোড়া থেকেই বিশ্বাস করেছিলাম।"\r\n    </p>\r\n    <p>\r\n      তারপর থেকে মামার প্রতিপত্তি ভয়ানক বেড়ে গেল। রোজ‌‌ই সব ব্যস্ত হয়ে থাকতাম মামার খবর শুনবার জন্য। কোনোদিন শুনতাম মামা গেছেন হাতি গণ্ডার বাঘ মারতে।  কোনোদিন শুনতাম, একাই তিনি পাঁচটা কাবুলীকে ঠেঙিয়ে ঠিক করেছেন, এরকম প্রায়‌‌ই হত। তার পর একদিন সবাই আমরা টিফিনের সময় গল্প করছি, এমন সময় হেডমাস্টার মশাই ক্লাশে এসে বললেন, "যজ্ঞদাস, তোমার মামা এসেছেন।" হঠাৎ‌ যজ্ঞদাসের মুখখানা আমসির মতো শুকিয়ে গেল- সে আম্‌‌তা আম্‌‌তা ক''রে কি যেন বলতে গিয়ে আর বলতে পারল না। তারপর লক্ষ্মী ছেলেটির মতো চুপচাপ মাস্টার মশায়ের সঙ্গে চলল। আমরা বললাম, ভয় হবে না? জানো তো কি রকম মামা !" সবাই মিলে উৎ‌সাহ আর আগ্রহে মামা দেখবার জন্য একেবারে ঝুঁকে পড়লাম। গিয়ে দেখি, একটি রোগা কালো ছোকরা গোছের ভদ্রলোক, চশমা চোখে গোবেচারার মতো বসে আছেন। জগ্যিদাস তাঁকেই গিয়ে প্রণাম করল।\r\n    </p>\r\n    <p>\r\n    সেদিন সত্যিসত্যি‌‌ই আমাদের রাগ হয়েছিল। এম্নি করে ফাঁকি দেওয়া। মিথ্যে করে মামা তৈরি ! সেদিন আমাদের ধমকের চোটে জগ্যিদাস কেঁদেই ফেলল। সে তখন স্বীকার করল যে, ফটোটা কোনো এক পশ্চিমা পালোয়ানের। আর সেই ট্রেনের লোকটাকে সে চেনেই না। তারপরে কোনো আজগুবি জিনিসের কথা বলতে হলেই আমরা বলতাম, "জগ্যিদাসের মামার মতো। "</p>', '', '2013-03-19 00:00:00', '2014-08-21 16:53:01', 322, ''),
(130, 2, '', 'আজব সাজা', 'আজব-সাজা', '', '<p>\r\n       "পণ্ডিতমশাই, ভোলা আমায় ভ্যাংচাচ্ছে।" "না পণ্ডিতমশাই, আমি কান চুলকাচ্ছিলাম, তাই মুখ বাঁকা দেখাচ্ছিল !" পণ্ডিতমশাই চোখ না খুলিয়াই অত্যন্ত নিশ্চিন্ত ভাবে বলিলেন, "আঃ ! কেবল বাঁদরামি ! দাঁড়িয়ে থাক।" আধমিনিট পর্যন্ত সব চুপচাপ। তারপর আবার শোনা গেল, "দাঁড়াচ্ছিস না যে ?" "আমি দাঁড়াব কেন ?" "তোকেই তো দাঁড়াতে হবে।" "যাঃ আমায় বলেছে না আর কিছু ! গণশাকে জিগ্‌‌গেস কর ? কিরে গণশা, ওকে দাঁড়াতে বলেছে না ?" গণেশের বুদ্ধি কিছুটা মোটা, সে আস্তে আস্তে উঠিয়া গিয়া পণ্ডিতমশাইকে ডাকিতে লাগিল, "পণ্ডিতমশাই ! ও পণ্ডিতমশাই !"\r\n    </p>\r\n    <p>\r\n        পণ্ডিতমশাই বিরক্ত হ‌‌ইয়া বলিলেন, "কি বলছিস বল্‌‌ না।" গণেশচন্দ্র অত্যন্ত ব্যাকুলভাবে জিজ্ঞাসা করিল, "কাকে দাঁড়াতে বলেছেন, পণ্ডিতমশাই ?"  পণ্ডিতমশাই কট্‌‌মটে চোখ মেলিয়াই সাংঘাতিক ধমক দিয়া বলিলেন, "তোকে বলেছি, দাঁড়া।" বলিয়াই আবার চোখ বুজিলেন। \r\n    </p>\r\n    <p>\r\n        গণেশচন্দ্র দাঁড়াইয়া রহিল। আবার মিনিটখানেক সব চুপচাপ। হঠাৎ‌ ভোলা বলিল, "ওকে এক পায়ে দাঁড়াতে বলেছিল না ভাই ?" গণেশ বলিল, "কক্ষনো না, খালি দাঁড়াতে বলেছে।" বিশু বলিল, "এক আঙুল তুলে দেখিয়েছিল, তার মানেই এক পায়ে দাঁড়া।" পণ্ডিতমশাই যে ধমক দিবার সময় তর্জনী তুলিয়াছিলেন, এ কথা গণেশ অস্বীকার করিতে পারিল না। বিশু আর ভোলা জেদ করিতে লাগিল, "শিগগির এক পায়ে দাঁড়া বলছি, তা না হলে এক্ষুণি বলে দিচ্ছি।"\r\n    </p>\r\n    <p>\r\n        গণেশ বেচারা ভয়ে ভয়ে তাড়াতাড়ি এক পা তুলিয়া দাঁড়াইয়া রহিল। অমনি ভোলা আর বিশুর মধ্যে তুমুল তর্ক বাঁধিয়া গেল। এ বলে ডান পায়ে দাঁড়ানো উচিত, ও বলে, না, আগে বাঁ পা। গণেশ বেচারার মহা মুশকিল ! সে আবার পণ্ডিতমশাইকে জিজ্ঞাসা করিতে গেল, "পণ্ডিতমশাই, কোন্‌‌ পা ?"\r\n    </p>\r\n    <p>\r\n        পণ্ডিতমশাই তখন কি যেন একটা স্বপ্ন দেখিয়া অবাক হ‌‌ইয়া নাক ডাকাইতেছিলেন। গণেশের ডাকে হঠাৎ‌ তন্দ্রা ছুটিয়া যাওয়ায় তিনি সাংঘাতিক রকম বিষম খাইয়া ফেলিলেন। গণেশ বেচারা তার প্রশ্নের এ রকম জবাব একেবারেই কল্পনা করে নাই, সে ভয় পাইয়া বলিল, "ঐ যা কি হবে ?" ভোলা বলিল, "দৌড়ে জল নিয়ে আয়।" বিশু বলিল, "শিগ্‌‌গির মাথায় জল দে।" গণেশ এক দৌড়ে কোথা হ‌‌ইতে একটা কুঁজা আনিয়া ঢক্‌‌ঢক্‌‌ করিয়া পণ্ডিতমশায়ের টাকের উপর জল ঢালিতে লাগিল। পণ্ডিতমশায়ের বিষম খাওয়া খুব চট্‌‌পট্‌‌ থামিয়া গেল, কিন্তু তাঁহার মুখ দেখিয়া গণেশের হাতে জলের কুঁজা ঠক্‌‌ঠক্‌‌ করিয়া কাঁপিতে লাগিল। \r\n    </p>\r\n    <p>\r\n        ভয়ে সকলেই খুব গম্ভীর হ‌‌ইয়া রহিল, খালি শ্যামলাল বেচারার মুখটাই কেমন যেন আহ্লাদি গোছের হাসি হাসি মতো, সে কিছুতেই গম্ভীর হ‌‌ইতে পারিল না। পণ্ডিতমশায়ের রাগ হঠাৎ‌ তার উপরেই ঠিক্‌‌রাইয়া পড়িল। তিনি বাঘের মতো গুম্‌‌গুমে গলায় বলিলেন, "উঠে আয় !" শ্যামলাল ভয়ে কাঁদ কাঁদ হ‌‌ইয়া বলিল, "আমি কি করলাম? গণশা জল ঢাল্‌‌ল, তা আমার দোষ কি?" পণ্ডিতমশাই মানুষ ভালো, তিনি শ্যামলালকে ছাড়িয়া গণ্‌‌শার দিকে তাকাইয়া দেখেন তাহার হাতে তখনও জলের কুঁজা। গণেশ কোনো প্রশ্নের অপেক্ষা না করিয়াই বলিয়া ফেলিল, "ভোলা আমাকে বলেছিল।" ভোলা বলিল, "আমি তো খালি জল আনতে বলেছিলম। বিশু বলেছিল, মাথায় ঢেলে দে।" বিশু বলিল, "আমি কি পণ্ডিতমশায়ের মাথায় দিতে বলেছি? ওর নিজের মাথায় দেওয়া উচিত ছিল, তাহলে বুদ্ধিটা ঠাণ্ডা হত।"\r\n    </p>\r\n    <p>\r\n        পণ্ডিতমশাই খানিকক্ষণ কটমট করিয়া সকলের দিকে তাকাইয়া তারপর বলিলেন, "যা ! তোরা ছেলেমানুষ তাই কিছু বললাম না। খবরদার আর অমন করিসনে।" সকলে হাঁফ ছাড়িয়া বাঁচিল, কিন্তু পণ্ডিতমশাই কেন যে হঠাৎ‌ নরম হ‌‌ইয়া গেলেন কেহ তাহা বুঝিল না। পণ্ডিতমশায়ের মনে হঠাৎ‌ যে তাঁর নিজের ছেলেবেলার কোন দুষ্টুমির কথা মনে পড়িয়া গেল, তাহা কেবল তিনি‌‌ই জানেন। \r\n    </p>', '', '2013-03-19 00:00:00', '2014-08-21 16:53:01', 539, ''),
(131, 2, '', 'কালাচাঁদের ছবি', 'কালাচাঁদের-ছবি', '', '<p>কালাচাঁদ নিধিরামকে মারিয়াছে&mdash;তাই নিধিরাম হেডমাস্টার মশায়ের কাছে নালিশ করিয়াছে। হেডমাস্টার আসিয়া বলিলেন, "কি হে কালাচাঁদ, তুমি নিধিরামকে মেরেছ ?" কালাচাঁদ বলিল, "আজ্ঞে না, মারব কেন ? কান মলে দিয়েছিলাম, গালে খামচিয়ে দিয়েছিলাম, আর একটুখানি চুল ধরে ঝাঁকিয়ে মাটিতে ফেলে দিয়েছিলাম ।" হেডমাস্টার মহাশয় বলিলেন, "কেন ওরকম করেছিলে?" কালাচাঁদ খানিকটা আমতা আমতা করিয়া মাথা চুলকাইয়া বলিল, "আজ্ঞে, ও খালি খালি আমায় চটাচ্ছিল।" হেডমাস্টার মশাই জিজ্ঞাসা করিলেন, "তোমায় মেরেছিল?" "না।" "ধমকিয়েছিল?" "না।" "তবে" "বারবার ঘ্যান্‌‌ ঘ্যান্‌‌ করে বোকার মত কথা বলছিল, তাই, আমার রাগ হয়ে গেল।" হেডমাস্টার মশাই তাহার কান ধরিয়া বেশ ভালোরকম নাড়াচাড়া দিয়া বলিলেন, "মেজাজটা এখন থেকে একটু সংশোধন করতে চেষ্টা কর।"\r\n</p>\r\n<p>ছুটির পর আমরা জিজ্ঞাসা করিলাম, "হ্যাঁরে কালাচাঁদ, তুই খামকা ঐ নিধেটাকে মারতে গেলি কেন?" কালাচাঁদ বলিল, "খামকা মারব কেন? কেন মেরেছিলাম ওকেই জিজ্ঞাসা কর না !" নিধেকে জিজ্ঞাসা করিতে সে বলিল, "খামকা নয় তো কি? তুই বাপু ছবি এঁকেছিস তার কথা আমায় জিগ্‌‌গেস করতে গেলি কেন? আর যদি জিগ্‌‌গেস করলি, তাহলে তাই নিয়ে আবার মারামারি করতে এলি কেন?" আমরা বলিলাম, "আরে কি হয়েছে খুলেই বল্‌‌ না কেন।"\r\n</p>\r\n<p>নিধিরাম বলিল, "কালাচাঁদ একটা ছবি এঁকেছে, ছবির নাম&mdash;খাণ্ডব দাহন। সেই ছবিটা আমায় দেখিয়ে ও জিগ্‌‌গেস করল, ''কেমন হয়েছে?'' আমি বললাম, ''এটা কি এঁকেছ? মন্দিরের সামনে শেয়াল ছুটছে?'' কালাচাঁদ বলল, ''না, না, মন্দির কোথায়? ওটা হল রথ। আর এগুলো তো শেয়াল নয়&mdash;রথের ঘোড়া।'' আমি বললাম, ''সূর্যটাকে কালো করে এঁকেছ কেন? আর ঐ চামচিকেটা লাঠি নিয়ে ডিগবাজি খাচ্ছে কেন?'' কালাচাঁদ বলল, ''আহা তা কেন? ওটা সূর্য নয়, সুদর্শন চক্র। দেখছ না কৃষ্ণের হাতে রয়েছে? আর তালগাছ কোথায় দেখলে? ওটা তো অর্জুনের পতাকা ! আর ঐগুলোকে বুঝি পদ্মফুল বলছ? ওগুলো দেবতা&mdash;খুব দূরে আছেন কিনা তাই ছোট ছোট দেখাচ্ছে। আর এই বুঝি চামচিকে হল, ওটা তো গরুড়পাখি !'' ''আচ্ছা, ঐ কালো কাপড় পড়া মেয়েমানুষটি যে ওদের মারতে আসছে ওটি কে?'' কালাচাঁদ বলল, ''তুমি তো আচ্ছা মুখ্যু হে ! ওটা গাছে আগুন লেগে ধোঁয়া বেরুচ্ছে বুঝতে পারছ না? অবাক করলে যে !''"\r\n</p>\r\n<p>"তখন আমি বললাম, ''আচ্ছা এক কাজ কর না কেন ভাই, ওটাকে খাণ্ডব দাহন না করে সীতার অগ্নিপরীক্ষা কর না কেন? ঐ গাছটাকে শাড়ি পরিয়ে সীতা করে দাও। ঐ রথটার মাথায় জটা-টটা দিয়ে ওকে অগ্নিদেব বানাও, কৃষ্ণ অর্জুন আছেন তাঁরা হবেন রাম লক্ষ্মণ। আর ঐ সুদর্শন চক্রে নাক হাত পা জুড়ে দিলেই ঠিক বিভীষণ হয়ে যাবে। তারপর চামচিকের পিছনে একটা লম্বা ল্যাজ দিয়ে তার ডানা দুটো মুছে দাও&mdash;ওটা হনুমান হবে এখন।'' কালাচাঁদ বলল, ''হনুমানও হতে পারে, নিধিরামও হতে পারে।''"\r\n</p>\r\n<p>"আমি বললাম, ''তাহলে ভাই, আর এক কাজ কর। ওটাকে শিশুপাল-বধ করে দাও। তাহলে কৃষ্ণকে বদলাতে হবে না। চক্র তুলে শিশুপালকে মারতে যাচ্ছেন। অর্জুনের মুখে পাকা গোঁফ দাড়ি দিয়ে খুব সহজেই ভীষ্ম করে দেওয়া যাবে। আর রথটা হবে সিংহাসন, তার উপর যুধিষ্ঠিরকে বসিয়ে দিও। আর ঐ যে গরুড় আর সাপ, ঐটে একটু বদলিয়ে দিলেই গদা হাতে ভীষ্ম হয়ে যাবে। আর শিশুপাল তো আছেই&mdash;ঐ গাছটাকে একটু নাক-মুখ ফুটিয়ে দিলেই হবে। তারপর রাজসূয় যজ্ঞের কয়েকটা রাজাকে দেখালেই বাস্‌‌!''"\r\n</p>\r\n<p>"কথাটা কালাচাঁদের পছন্দ হল না, তাই আমি অনেক ভেবেচিন্তে আবার বললাম, ''তাহলে জন্মেজয়ের সর্পযজ্ঞ কর না কেন? ঐ রথটা হবে জন্মেজয় আর কৃষ্ণকে জটা-দাড়ি দিয়ে পুরুতঠাকুর বানিয়ে দাও। সুদর্শন চক্রটা হবে ঘিয়ের ভাঁড়। যজ্ঞের আগুনের মধ্যে তিনি ঘি ঢালছেন। ঐ ধোঁয়াগুলো মনে কর যজ্ঞের ধোঁয়া ! একটা সাপ আছে, আরো কয়েকটা এঁকে দিও। আর অর্জুনকে কর আস্তীক, সে হাত তুলে তক্ষককে বলছে&mdash;তিষ্ঠ, তিষ্ঠ। আর ঐ চামচিকেটা, মানে গরুড়টা, ওটাকে মুনি-টুনি কিছু একটা বানিয়ে দিও।'' পতাকাটাকে কি রকম করতে হবে সেইটা বলতে যাচ্ছি, এমন সময় কালাচাঁদ আমায় ধাক্কা দিয়ে বলল, ''থাক, থাক, আর তোমার  বিদ্যে করে কাজ নেই। সর দেখি।''"\r\n</p>\r\n<p>"আমি বললাম, ''অত রাগ কর কেন ভাই? আমি তো আর বলছি না যে আমার পরামর্শ মতো তোমাকে চলতে হবে। পছন্দ হয় কর, না হয়তো কোরো না, বাস্‌‌। এর মধ্যে আবার রাগারাগি কর কেন? আমার কথামতো না করে অন্য একটা কিছু কর না। মনে কর, ওটাকে সমুদ্র-মন্থন করে দিলেও তো হয়। ঐ ধোঁয়াওয়ালা বড় গাছটা মন্দার পর্বত, রথটা ধন্বন্তরী কিম্বা লক্ষ্মী&mdash;মন্থন থেকে উঠে এসেছেন। ওদিকে সুদর্শন চক্রটা চাঁদ হতে পারবে, অর্জুনের পিছনে কতগুলো দেবতা এঁকে দাও আর এদিকে কৃষ্ণ আর চামচিকের দিকে কতগুলো অসুর''&mdash;কথাটা ভালো করে বলতে না বলতেই কালাচাঁদ আমার কান ধরে মারতে লাগল। আচ্ছা, দেখ দেখি কি অন্যায় ! আমি বন্ধুভাবে দুটো পরমর্শ দিতে গেলাম&mdash;তা তোমার পছন্দ হয়নি বলেই আমায় মারবে? যা বলেছি সব শুনলে তো, এর মধ্যে এত রাগ করবার কি হল বাপু?"\r\n</p>\r\n<p>বাস্তবিক, কালাচাঁদের এ বড় অন্যায় ! সে রাগ করিল কিসের জন্য? নিধিরাম তাহাকে মারে নাই, ধরে নাই, বকে নাই, গাল দেয় নাই, চোখ রাঙায় নাই, মুখ ভ্যাংচায় নাই&mdash;তবে রাগ করিবার কারণটা কি? \r\n</p>\r\n<p>ব্যাপার কি বোঝা গেল না, তাই সন্ধ্যায় সবাই মিলিয়া কালাচাঁদের বাড়িতে গেলাম। আমি বলিলাম, "ভাই কালাচাঁদ, আমরা তোমার সেই ছবিটা দেখতে চাই। সেই যে সমুদ্র লঙ্ঘন না কি যেন?" রমাপ্রসাদ বলিল, "দ্যুৎ‌, সমুদ্র লঙ্ঘন কিসের? অগ্নিপরীক্ষা।" আর একজন কে যেন বলিল, "না, না, কি একটা বধ।" কেন জানিনা , কালাচাঁদ হাঁ হাঁ করিয়া একেবারে তেলে-বেগুনে জ্বলিয়া উঠিল। "যাও যাও ইয়ার্কি করতে হবে না," বলিয়া সে তাহার ছবির খাতাখানি ফড়ফড় করিয়া ছিঁড়িয়া ফেলিল&mdash;আর রাগে গজরাইতে লাগিল। আমরা হতভম্ব হ‌‌ইয়া রহিলাম। সকলেই বলিলাম, "কালাচাঁদের মাথায় বোধহয় একটু পাগলামির ছিট আছে। ন‌‌ইলে সে খামকা এত রাগ করবে কেন?"\r\n</p>', '', '2013-03-19 00:00:00', '2014-08-21 16:53:01', 391, ''),
(235, 6, '', 'রেলগাড়ির কথা', 'রেলগাড়ির-কথা', '', '<p>এমন কত জিনিস আছে যা আমরা সর্বদাই দেখে থাকি এবং তাতে কিছুমাত্র আশ্চর্যবোধ করি না। অথচ, সেই সব জিনিসকেই যখন প্রথম লোকে দেখেছিল, তখন খুব একটা হৈচৈ পড়ে গিয়েছিল। প্রথম \r\nযে বেচারা ছাতা মাথায় দিতে ইংলন্ডের রাস্তায় বেরিয়েছিল, তাকে সবাই মিলে ঢিল ছুঁড়ে এমনি তাড়াহুড়ো করেছিল যে, বেচারার প্রাণ নিয়ে টানাটানি।</p>\r\n<p>সার ওয়াল্টার র‍্যালে যখন বিলাতে আলু আর তামাকের প্রচলন করলেন, তখনও তাঁকে রীতিমত নাকাল হতে হয়েছিল। শোনা যায়, তিনি একদিন খুব আরাম করে নিজের ঘরে বসে ''পাইপ'' মুখে দিয়ে \r\nতামাক টানছিলেন, এমন সময় তাঁর চাকর এসে সেই ব্যাপার দেখে, মনিবের মুখে আগুন লেগেছে মনে করে, একেবারে হতভম্ব হয়ে গেল। কি করবে কিছু বুঝতে না পেরে সে এক বালতি জল নিয়ে সার \r\nওয়াল্টারের মাথা ঢেলে দিল। আলু খেতেও প্রথমটা লোকে কম আপত্তি করেনি। "আলু ভয়ানক বিষাক্ত জিনিস", "আলু খেয়ে লোক মরে যাচ্ছে", এইরকম সব অদ্ভুত গুজব চারিদিকে রাষ্ট্র হয়ে বহুদিন পর্যন্ত \r\nলোকের মনে ভয় জন্মিয়ে রেখেছিল।</p>\r\n<p>কলকাতায় হাওড়ার পোল যখন তৈরি হয়নি, তখন একজন সাহেব বলেন যে নৌকার উপর খিলান ভাসিয়ে এইরকম পোল তৈরি হতে পারে। এ কথাটা সে সময়ে লোকের কাছে এমনই অদ্ভুত ঠেকেছিল যে \r\nখবরের কাগজে পর্যন্ত সেই বেচারার নামে নানারকম ঠাট্টা-তামাসা বেরিয়েছিল। অথচ এখন হাজার হাজার লোকে প্রতিদিন পোলের উপর যাওয়া-আসা করছে&mdash;সেটা বাস্তবিক একটা অদ্ভুত ব্যাপার কিনা, \r\nকেউ সে কথা ভেবে দেখবার অবসর পায় না।</p>\r\n<p>রেলগাড়ির প্রচলনও এমনি করেই হয়েছিল। প্রায় একশ বৎসর আগে জর্জ স্টিফেনসন বাষ্পের জোরে গাড়ি চালিয়ে তাতে লোকের যাতায়াতের ব্যবস্থা করবার প্রস্তাব করেন। তখন সে প্রস্তাবে এতরকম \r\nআপত্তি উঠেছিল যে, ক্রমে তর্কটা পার্লামেন্ট পর্যন্ত গড়ায়। শেষটায় অনেক ঝগড়াঝাটি গণ্ডগোলের পর, স্টিফেনসনকে নানারকম জেরা করে তারপর অনুমতি দেওয়া হল&mdash;"আচ্ছা, তোমার রেলগাড়িটা না \r\nহয় একবার পরীক্ষা করে দেখা যাক!" স্টিফেনসন সহজে জেদ ছাড়বার লোকে ছিলেন না&mdash;তাই তিনি শেষ পর্যন্ত লড়াই করতে ছাড়েননি।</p>\r\n<p>এই জর্জ স্টিফেনসনের জীবনের কথা অতি অদ্ভুত। নিতান্ত গরীবের ঘরে যার জন্ম, যে লেখাপড়ার কোনরকম সুযোগ পায়নি এবং ত্রিশ বৎসর বয়স পর্যন্ত কেবল কয়লার খনিতে সামান্য কাজ করেই জীবন \r\nকাটিয়েছে, তার মনে এমন আশ্চর্য শক্তি আসে কোথা হতে, ভাবলে অবাক হয়ে যেতে হয়। স্টিফেনসনেরা ছয় ভাইবোন। বাপ মা অত্যন্ত গরীব, কাজেই ছেলেবেলা থেকেই সব ক-টি ভাইকে টাকা \r\nউপর্জনের চেষ্টা করতে হত। তারা নানান কাজ করে একেকজন দিনে প্রায় দু আনা রোজগার করত। খনিতে নানারকম কল-কারখানা থাকে, সেইগুলার উপর জর্জের ভারি নজর ছিল। সুযোগ পেলেই সে \r\nসেগুলোকে নেড়েচেড়ে তার ভিতরের কলকব্জা খুলে দেখত। বইটই কিছুমাত্র না পড়েও কেবল নিজে দেখেশুনে এ-সকল বিষয়ে তার আশ্চর্য দখল জন্মেছিল। সে সময়ে কয়লার খনিতে যে-সকল এঞ্জিন \r\nব্যবহার হত তাদের ''খাড়া এঞ্জিন'' বলা যায়&mdash;অর্থাৎ সে এঞ্জিন এক জায়গায় খাটান থাকে; তার চাকার সঙ্গে দড়ি, শিকল বা লোহার হাতল জুড়ে গাড়ি টানা, মোট বওয়া, জিনিসপত্র উঠান-নামান প্রভৃতি \r\nনানারকম কাজ চালান হয়। সেই সময় হতেই চাকায়-বসান চলন্ত এঞ্জিন গড়বার খেয়াল স্টিফেনসনের মাথায় চেপেছিল।</p>\r\n<p>যাহোক ক্রমে স্টিফেনসনের অবস্থার উন্নতি হতে লাগল। ক্রমে তার সপ্তাহে ১২ শিলিং (নয় টাকা) মাইনে হল&mdash;তার উপর জুতা সেলাই করে আর ঘড়ি মেরামত করেও সে কিছু কিছু উপর্জন করতে লাগল। \r\nসকলে বলল, "জর্জ মস্ত রোজগেরে হয়েছে।" এইভাবে প্রায় ত্রিশ বৎসর কেটে গেল, এর মধ্যেই পাকা এঞ্জিনিয়ার বলে স্টিফেনসনের বেশ একটু নাম হয়েছে।</p>\r\n<p>১৮১৪ খৃষ্টাব্দে ৩৩ বৎসর বয়সে এক খনির মালিককে রাজি করিয়ে স্টিফেনসন তাঁর প্রথম চলন্ত এঞ্জিনের পরীক্ষা করেন। এই এঞ্জিনের সঙ্গে কয়লার গাড়ি জুড়ে দেখা গেল যে ৫০০ মণ কয়লা উঁচু \r\nরাস্তায় ঘণ্টায় চার মাইল করে নেওয়া যায়। আগে ট্রামের লাইন বসিয়ে তার উপর লোকেরা গাড়ি ঠেলে নিত, সেই লাইনের উপরই এঞ্জিন বসিয়ে কয়লা চালান হতে লাগল। তারপর বছর খানেকের মধ্যে \r\nআরো ভাল দুটি এঞ্জিন তৈয়ারি হল, ক্রমে আশেপাশে আরও কত কয়লার খনিতে স্টিফেনসনের এঞ্জিন চলতে লাগল। এমনি করে আট-দশ বৎসর কেটে গেল।</p>\r\n<p>তখন স্টকটন হতে ডার্লিংটন পর্যন্ত কয়েক মাইল ট্রামের লাইন বসিয়ে ঘোড়ার ট্রাম করবার কথা হচ্ছিল। স্টিফেনসন প্রস্তাব করলেন, ঐ লাইনের উপর এঞ্জিনের গাড়ি চালান হোক। অনেক কথাবার্তা \r\nহাঁটাহাঁটির পর, ট্রামের কর্তারা রাজি হলেন। ১৮২৫ খৃষ্টাব্দে এই লাইন যেদিন খোলা হল তখন "স্টিফেনসনের লোহার ঘোড়া'' দেখবার জন্য ভিড় জমে গিয়েছিল।  কয়লা আর ময়দায় বোঝাই হয়ে \r\nএকগাড়ি যাত্রী নিয়ে স্টিফেনসনের এঞ্জিন স্টকটন হতে রওনা হল; স্টিফেনসন নিজে তার ড্রাইভার। গাড়ির আগে আগে কোম্পানির লোক ঘোড়ায় চড়ে নিশান নিয়ে ছুটল! কিন্তু স্টিফেনসন তাঁর এঞ্জিনে \r\nপুরো দম দিয়ে এমন ছুটিয়ে দিলেন যে, সে লোকটির আর সঙ্গে যাওয়া হল না। ডার্লিংটনে সমস্ত মালপত্র নামিয়ে সমস্ত ট্রেনটিকে পঙ্গপালের মতো লোক-বোঝাই করে স্টিফেনসন স্টকটনে ফিরে \r\nএলেন। না জানি সে সময়ে লোকের মনে কিরকম উতসাহ হয়েছিল।</p>\r\n<p>কিন্তু এতেও গোলমাল মিটল না। এরপর যখন মানচেস্টার থেকে লিভারপুলের পর্যন্ত রেল করবার কথা হল, তখন আবার তুমুল ঝগড়া আরম্ভ হল। রেলগাড়ি জিনিসটাতেই অনেকের আপত্তি। কেউ কেউ \r\nতাকে ''শয়তানের যন্ত্র'' বলে গাল দিতেও ছাড়েনি। এর মধ্যে আবার অন্য কয়েকটি এঞ্জিনওয়ালা এসে বললেন, "আমরা আরো ভাল এঞ্জিন বানিয়েছি&mdash;আমাদের উপর ভার দাও।" কেউ বললেন, \r\n"স্টিফেনসন আবার কোথাকার কে? নাম জানি না, ধাম জানি না&mdash;এত বড় কাজের ভার কি যার তার উপর দেওয়া যায়?" তখন চারিদিক থেকে পার্লামেন্টের কাছে দরখাস্ত যেতে লাগল। পার্লামেন্টের \r\nহুকুমে সব এঞ্জিন এক জায়গায় আনিয়ে তার পরীক্ষা নেওয়া হল। পরীক্ষায় স্টিফেনসনের এঞ্জিন আর সবকটাকে একেবারে পিছনে ফেলে ঘণ্টায় ত্রিশ মাইল ছুটে সকলের মনে এমন তাক লাগিয়ে দিল \r\nযে, যারা ঝগড়া করতে এসেছিল তাদের আর কথাটি কইবার মুখ রইল না। এঞ্জিনওয়ালারা তাদের এঞ্জিনের ঝ্যাঁকঝ্যাঁকানি বন্ধ করে লজ্জায় বাড়ি চলে গেল।</p>\r\n<p>এমনি করে ১৮৩০ খৃষ্টাব্দে রীতিমত রেলের চলাচল আরম্ভ হল। তারপর কত এঞ্জিন তৈরি হল, কত দিকে কত রেলের লাইন বসে গেল, গরীবের ছেলে স্টিফেনসন মস্ত ধনীলোক হয়ে গেলেন। কিন্তু \r\nজীবনের শেষ পর্যন্ত তাঁর সাধাসিধা চালচলন আর সহজ সরল ব্যবহারের কোন পরিবর্তন হয়নি। স্টিফেনসনের একমাত্র ছেলে রবার্টও কালে একজন নামজাদা এঞ্জিনিয়ার হয়েছিলেন; রেলের পোল তৈরির \r\nবিষয়ে তাঁর বিশেষরকম সুনাম ছিল।</p>', '', '2013-09-12 00:00:00', '2014-08-21 16:53:01', 164, '');
